	[103645] = "{id = 103645, Speaker = 4750, Text = '你已经成功完成了三个每日任务。'}",
	[103646] = "{id = 103646, Speaker = 4750, Text = '接下来我来告诉你如何领取每日任务的宝箱。'}",
	[103647] = "{id = 103647, Speaker = 4750, Text = '请注意地图上的小图标，蓝色的宝箱图标就是可以领取宝箱的宝箱任务。', Option = '宝箱图标？'}",
	[103648] = "{id = 103648, Speaker = 4750, Text = '现在你就去宝箱图标那边找朱诺的政府官员布鲁斯兑换宝箱吧。'}",
	[103649] = "{id = 103649, Speaker = 4750, Text = '兑换成功后，再回来找我。'}",
	[103650] = "{id = 103650, Speaker = 4750, Text = '你已经成功兑换了一个宝箱吗？', Option = '{兑换完毕,8},{未兑换,9}'}",
	[103651] = "{id = 103651, Speaker = 4750, Text = '你并没有领到宝箱，找地图上显示蓝色宝箱图标的政府官员布鲁斯去兑换。'}",
	[103652] = "{id = 103652, Speaker = 4750, Text = '看来你成功领取到了宝箱，是不是感觉更加了解朱诺了？'}",
	[103653] = "{id = 103653, Speaker = 4750, Text = '另外还有艾因布洛克原野和国境检查站的每日任务，可别忘记了。'}",
	[103654] = "{id = 103654, Speaker = 4751, Text = '伟大的朱诺由我们来守护。'}",
	[103655] = "{id = 103655, Speaker = 4752, Text = '黎明的曙光永远不会迟到。'}",
	[103656] = "{id = 103656, Speaker = 4753, Text = '我们肩负着共和国的未来。'}",
	[103657] = "{id = 103657, Speaker = 4750, Text = '你了解朱诺区域的每日任务吗？每日任务是以地图为单位每天都会刷新的任务，完成任务可以领取丰厚的奖励。'}",
	[103658] = "{id = 103658, Speaker = 4751, Text = '外乡人，为伟大的朱诺做点贡献吧！', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103659] = "{id = 103659, Speaker = 4751, Text = '国境检查站是从艾尔帕兰到朱诺的重要城镇，那里每天都有很多人。'}",
	[103660] = "{id = 103660, Speaker = 4751, Text = '你去看看那边是否有人需要帮助，完成三个请求，可以来找我拿宝箱奖励。', Option = '我这就去'}",
	[103661] = "{id = 103661, Speaker = 4751, Text = '你已经完成了人们的三个请求了？', Option = '{已完成,4},{还没有,5}'}",
	[103662] = "{id = 103662, Speaker = 4751, Text = '那么速去速回吧，注意安全。'}",
	[103663] = "{id = 103663, Speaker = 4751, Text = '你还没有完成三个请求，不能冒领宝箱。'}",
	[103664] = "{id = 103664, Speaker = 4751, Text = '完成得不错，这是我代表皇室授予你的宝箱。'}",
	[103665] = "{id = 103665, Speaker = 4752, Text = '嘿！冒险者！艾因布洛克原野了解一下。', Option = '{了解详情,2},{为难离开,3}'}",
	[103666] = "{id = 103666, Speaker = 4752, Text = '艾因布洛克原野上有许多魔物，我们曙光队人手有限，无法在那边增派人手。'}",
	[103667] = "{id = 103667, Speaker = 4752, Text = '请协助我们帮助遇到困难的人！如果你已经帮助了三人，请来找我，我将给予你宝箱奖励。', Option = '我这就去'}",
	[103668] = "{id = 103668, Speaker = 4752, Text = '你已经帮助了需要帮助的三个人了？', Option = '{已帮助,4},{还没有,5}'}",
	[103669] = "{id = 103669, Speaker = 4752, Text = '那么速去速回，注意安全。'}",
	[103670] = "{id = 103670, Speaker = 4752, Text = '做人要有诚信，你真的帮助了三个人吗？'}",
	[103671] = "{id = 103671, Speaker = 4752, Text = '勇敢的冒险家，感谢你的协助，这是奖励给你的宝箱。'}",
	[103672] = "{id = 103672, Speaker = 4753, Text = '想要更好的了解朱诺，就需要更了解这里的人们。'}",
	[103673] = "{id = 103673, Speaker = 4753, Text = '去帮助一下他们，更好的融入这里吧！', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103674] = "{id = 103674, Speaker = 4753, Text = '帮助了三个人之后，过来找我，我会代表大总统授予你宝箱奖励。', Option = '我这就去'}",
	[103675] = "{id = 103675, Speaker = 4753, Text = '你已经帮助了需要帮助的三个人了？', Option = '{已帮助,4},{还没有,5}'}",
	[103676] = "{id = 103676, Speaker = 4753, Text = '帮助他们也是帮助你自己，你要知道这一点。'}",
	[103677] = "{id = 103677, Speaker = 4753, Text = '朱诺不欢迎不诚实的人，你还没有帮助完三个人。'}",
	[103678] = "{id = 103678, Speaker = 4753, Text = '很好，是不是感觉对朱诺有了进一步的了解，这是奖励给你的宝箱。'}",
	[103679] = "{id = 103679, Speaker = 5684, Text = '我家的阿尖是这个世界上最可爱的土拨鼠！'}",
	[103680] = "{id = 103680, Speaker = 5685, Text = '我的梦想是永远做一只快乐土拨鼠！'}",
	[103681] = "{id = 103681, Speaker = 5684, Text = '我家的阿尖又不见了，我最宝贝的阿尖小可爱啊！！', Emoji = 24}",
	[103682] = "{id = 103682, Speaker = 5684, Text = '噢，忘了告诉你啦，阿尖是我的宠物土拨鼠，拜托你帮我一起找找它吧！', Option = '{没问题,3},{我很忙,2}'}",
	[103683] = "{id = 103683, Speaker = 5684, Text = '快点出发吧！我的小阿尖可受不了艾因布洛克的风吹雨打啊！'}",
	[103684] = "{id = 103684, Speaker = 5684, Text = '我和阿尖是在艾因布洛克原野的东南方走散的！'}",
	[103685] = "{id = 103685, Speaker = 5685, Text = '（咕噜咕噜~）阿尖的小肚皮饿扁啦！', Option = '跟我回去', Emoji = 15}",
	[103686] = "{id = 103686, Speaker = 5685, Text = '终于有人来找我啦，这次杰西卡一定也会用很多开心坚果安抚我的！', Option = '…', Emoji = 5}",
	[103687] = "{id = 103687, Speaker = 5685, Text = '我们走吧！'}",
	[103688] = "{id = 103688, Speaker = 5684, Text = '太谢谢你啦！我的小阿尖回来了~', Option = '溺爱', Emoji = 27}",
	[103689] = "{id = 103689, Speaker = 5684, Text = '这不是溺爱！这是爱！阿尖乖乖的，回家喂你吃开心坚果~'}",
	[103690] = "{id = 103690, Speaker = 5684, Text = '怎么办啊，最近我家的阿尖越来越胖了，都快变成一个球了！'}",
	[103691] = "{id = 103691, Speaker = 5684, Text = '我告诉它要是不减肥的话，我就把它的开心坚果全部没收！', Option = '然后呢？', Emoji = 12}",
	[103692] = "{id = 103692, Speaker = 5684, Text = '它和我赌气，一个人跑出去了。'}",
	[103693] = "{id = 103693, Speaker = 5684, Text = '你能帮我把这个家伙带回来么？粗暴点也没关系！', Option = '{没问题,3},{我很忙,2}'}",
	[103694] = "{id = 103694, Speaker = 5684, Text = '这是它最喜欢的开心坚果，你可以用它来引诱阿尖！', Emoji = 5}",
	[103695] = "{id = 103695, Speaker = 5685, Text = '没有开心坚果的鼠生是黑白色的！', Option = '乖乖回去', Emoji = 24}",
	[103696] = "{id = 103696, Speaker = 5685, Text = '我才不要！'}",
	[103697] = "{id = 103697, Speaker = 5685, Text = '我这就回去，别打我啦！'}",
	[103698] = "{id = 103698, Speaker = 5684, Text = '多亏了你，阿尖已经乖乖回家了~', Option = '那就好', Emoji = 9}",
	[103699] = "{id = 103699, Speaker = 5684, Text = '过它从外面回来瘦了好多，我好心疼！', Option = '……'}",
	[103700] = "{id = 103700, Speaker = 5684, Text = '还好我给它准备了好多开心坚果~'}",
	[103701] = "{id = 103701, Speaker = 5684, Text = '它过不了多久就能恢复健康了吧~', Emoji = 3}",
	[103702] = "{id = 103702, Speaker = 5686, Text = '太阳的味道真好闻啊。'}",
	[103703] = "{id = 103703, Speaker = 5686, Text = '我喜欢太阳的香味。', Option = '？？？'}",
	[103704] = "{id = 103704, Speaker = 5686, Text = '就是那种晒好的被子的香味！你能明白么？', Option = '{明白,3},{不明白,2}'}",
	[103705] = "{id = 103705, Speaker = 5686, Text = '对啊！太好闻了，我一直想制作这种味道的香水。', Option = '成功了吗？'}",
	[103706] = "{id = 103706, Speaker = 5686, Text = '我最近终于找到了适合的原料！', Option = '什么？'}",
	[103707] = "{id = 103707, Speaker = 5686, Text = '就是魔物邪恶向日葵的便便！', Option = '…'}",
	[103708] = "{id = 103708, Speaker = 5686, Text = '请去邪恶向日葵的聚集地帮我收集一些新鲜的便便吧！'}",
	[103709] = "{id = 103709, Speaker = 0, Text = '捏住鼻子绝望地捡起便便…'}",
	[103710] = "{id = 103710, Speaker = 5686, Text = '（接过便便）哇，真是辛苦你啦！', Option = '不客气', Emoji = 2}",
	[103711] = "{id = 103711, Speaker = 5686, Text = '完成后，我会送你一瓶太阳香水的！', Option = '谢谢'}",
	[103712] = "{id = 103712, Speaker = 5686, Text = '太阳香水一定会在朱诺的流行起来的，我要发财啦！', Emoji = 13}",
	[103713] = "{id = 103713, Speaker = 5687, Text = '守卫朱诺是每个皇家卫士的职责！'}",
	[103714] = "{id = 103714, Speaker = 5687, Text = '我最近巡逻的时候老是感觉有人跟踪我。', Option = '吓人'}",
	[103715] = "{id = 103715, Speaker = 5687, Text = '我担心是某种魔物对朱诺图谋不轨，想从作为巡逻者的我先下手。', Emoji = 12}",
	[103716] = "{id = 103716, Speaker = 5687, Text = '但是老被跟着总是让人感觉不舒服，你去帮我调查一下附近的树林吧。', Option = '{乐意帮忙,3},{为难离开,2}'}",
	[103717] = "{id = 103717, Speaker = 5687, Text = '哼，凭我自己的力量就够了！'}",
	[103718] = "{id = 103718, Speaker = 5688, Text = '啊啊啊~纽达大人是只属于我一个人的男神！'}",
	[103719] = "{id = 103719, Speaker = 5688, Text = '纽达大人是只属于我一个人，你凭什么接近他！？', Option = '他被跟踪'}",
	[103720] = "{id = 103720, Speaker = 5688, Text = '是谁？！我可不会让任何路人接近我家的纽达大人！'}",
	[103721] = "{id = 103721, Speaker = 5688, Text = '没时间和你聊了，我要去暗中守护我家的纽达大人啦！', Option = '原来是她', Emoji = 2}",
	[103722] = "{id = 103722, Speaker = 0, Text = '（瞬间就消失在了树林里）'}",
	[103723] = "{id = 103723, Speaker = 5687, Text = '怎么样，到底是谁在跟踪我？有什么阴谋诡计放马过来！', Option = '不是坏人'}",
	[103724] = "{id = 103724, Speaker = 5687, Text = '啊？那最好，可究竟是谁在跟踪我呢？'}",
	[103725] = "{id = 103725, Speaker = 0, Text = '（感受到周围某处有一道热烈的视线）'}",
	[103726] = "{id = 103726, Speaker = 5688, Text = '我对纽达大人的爱越来越深了呢！', Option = '…', Emoji = 2}",
	[103727] = "{id = 103727, Speaker = 5688, Text = '不行啊！一秒看不到纽达大人的英姿我就无法呼吸。', Option = '…', Emoji = 24}",
	[103728] = "{id = 103728, Speaker = 5688, Text = '你能帮我拍一张他的照片么？这样我就可以随时看到他的英姿啦！', Option = '{没问题,3},{我很忙,2}', Emoji = 4}",
	[103729] = "{id = 103729, Speaker = 5688, Text = '你真是我的救星啊！他常在艾因布洛克原野的东南边巡逻。'}",
	[103730] = "{id = 103730, Speaker = 5688, Text = '太谢谢你啦，我家纽达真帅啊~', Emoji = 3}",
	[103731] = "{id = 103731, Speaker = 5688, Text = '我突然发现，你长得也很可口啊~', Option = '溜了溜了', Emoji = 5}",
	[103732] = "{id = 103732, Speaker = 5688, Text = '别跑啊，下次我会来找你玩哒，约好了唷！'}",
	[103733] = "{id = 103733, Speaker = 5689, Text = '你也被我的美貌迷住了吗？'}",
	[103734] = "{id = 103734, Speaker = 5689, Text = '怎么啦被我的美丽闪到眼睛啦？', Option = '你有毒？', Emoji = 5}",
	[103735] = "{id = 103735, Speaker = 5689, Text = '说的对！我的美丽就像是毒药。', Option = '…'}",
	[103736] = "{id = 103736, Speaker = 5689, Text = '我告诉你一件好事。', Option = '什么好事'}",
	[103737] = "{id = 103737, Speaker = 5689, Text = '我准备在艾因布洛克原野出道！现在还缺一张好看的照片。'}",
	[103738] = "{id = 103738, Speaker = 5689, Text = '我的新粉丝啊，为我拍照这个荣誉就交给你啦！', Option = '{乐意帮忙,3},{为难离开,2}'}",
	[103739] = "{id = 103739, Speaker = 5689, Text = '嗯~拍的不错嘛！'}",
	[103740] = "{id = 103740, Speaker = 5689, Text = '以后你就做葵花后援会会长吧！', Emoji = 9}",
	[103741] = "{id = 103741, Speaker = 5687, Text = '朱诺城里的皇家女仆康娜是我的青梅竹马。', Option = '不错嘛'}",
	[103742] = "{id = 103742, Speaker = 5687, Text = '长大后我和康娜都选择为朱诺皇室工作，每一天都在各自的岗位上努力奔波。', Option = '真辛苦'}",
	[103743] = "{id = 103743, Speaker = 5687, Text = '但是每当我巡逻到这里时，我总会想起与她一起玩耍的美好时光。', Option = '哇哦'}",
	[103744] = "{id = 103744, Speaker = 5687, Text = '你能帮我带一些这里的花给她么？她看到应该就会露出笑容吧！', Option = '{乐意帮忙,3},{为难离开,2}'}",
	[103745] = "{id = 103745, Speaker = 5687, Text = '康纳会明白我对她的心意与思念么？'}",
	[103746] = "{id = 103746, Speaker = 5690, Text = '哇，这不是我最喜欢的花嘛！是谁让你带给我的？', Emoji = 2}",
	[103747] = "{id = 103747, Speaker = 5690, Text = '果然是纽达，这么多年了，他还记得我的最爱啊~', Option = '纽达'}",
	[103748] = "{id = 103748, Speaker = 5690, Text = '辛苦你啦，看到这花，工作的压力缓解了很多呢，还是纽达最懂我啊！'}",
	[103749] = "{id = 103749, Speaker = 5691, Text = '艾因布洛克原野是我赖以生存的猎场!'}",
	[103750] = "{id = 103750, Speaker = 5691, Text = '最近我打猎时总是会突然刮起一阵妖风，眼看着到手的猎物，都被风声吓跑了！', Option = '妖风？'}",
	[103751] = "{id = 103751, Speaker = 5691, Text = '自从这诡异的妖风出现以后，我每次打猎都空手而归，真是气死我啦！', Option = '别生气啦', Emoji = 12}",
	[103752] = "{id = 103752, Speaker = 5691, Text = '我的一大家子可是都指望着我每天打猎来养活啊，这样下去可不行！'}",
	[103753] = "{id = 103753, Speaker = 5691, Text = '这妖风的背后一定有人在捣鬼，能拜托你去调查一下么？', Option = '{乐意帮忙,3},{为难离开,2}'}",
	[103754] = "{id = 103754, Speaker = 5691, Text = '我一家的生计就拜托你了，冒险者！'}",
	[103755] = "{id = 103755, Speaker = 0, Text = '看来它就是妖风的源头。'}",
	[103756] = "{id = 103756, Speaker = 0, Text = '呼啸的妖风马上停止了。'}",
	[103757] = "{id = 103757, Speaker = 5691, Text = '是嘛，原来是风暴哈比搞的鬼。', Option = '没错'}",
	[103758] = "{id = 103758, Speaker = 5691, Text = '这次多亏了你，现在大家再也不用担心原野的妖风了。'}",
	[103759] = "{id = 103759, Speaker = 5692, Text = '蒲公英是我最喜欢的花。'}",
	[103760] = "{id = 103760, Speaker = 5692, Text = '你好啊，我种的蒲公英前几天开花了。', Option = '恭喜你'}",
	[103761] = "{id = 103761, Speaker = 5692, Text = '看着它们随风飞散的美丽样子，我真的很快乐', Option = '嗯', Emoji = 9}",
	[103762] = "{id = 103762, Speaker = 5692, Text = '但是我觉得心里又空荡荡的，所以我想找回它的种子。'}",
	[103763] = "{id = 103763, Speaker = 5692, Text = '你能帮我一起找找么？', Option = '{乐意帮忙,3},{为难离开,2}'}",
	[103764] = "{id = 103764, Speaker = 5692, Text = '太谢谢你啦，我记得它们向原野西北飘去了。'}",
	[103765] = "{id = 103765, Speaker = 5693, Text = '一颗小小的蒲公英种子，拾起它么？', Option = '{拾起,6},{忽略,5}'}",
	[103766] = "{id = 103766, Speaker = 5694, Text = '一颗小小的蒲公英种子，拾起它么？', Option = '{拾起,9},{忽略,8}'}",
	[103767] = "{id = 103767, Speaker = 5695, Text = '一颗小小的蒲公英种子，拾起它么？', Option = '{拾起,12},{忽略,11}'}",
	[103768] = "{id = 103768, Speaker = 5692, Text = '你把蒲公英的种子带回来啦？', Option = '没错'}",
	[103769] = "{id = 103769, Speaker = 5692, Text = '太好了，我又可以重新将它们播种下去。'}",
	[103770] = "{id = 103770, Speaker = 5692, Text = '这3颗不起眼的小家伙，终有一天也会将自己的种子飘散出去吧？', Emoji = 2}",
	[103771] = "{id = 103771, Speaker = 5697, Text = '我可是艾因布洛克原野最厉害的原野大王！！'}",
	[103772] = "{id = 103772, Speaker = 5697, Text = '见到原野大王还不行礼？', Option = '原野大王？'}",
	[103773] = "{id = 103773, Speaker = 5697, Text = '正是本王！你这个无知的人类！', Option = '哈哈哈'}",
	[103774] = "{id = 103774, Speaker = 5697, Text = '不许笑！不许笑！不许笑！', Emoji = 12}",
	[103775] = "{id = 103775, Speaker = 5697, Text = '你！不许胡说！我是原野大王！'}",
	[103776] = "{id = 103776, Speaker = 5697, Text = '跟着我念，原野大王！', Option = '{原野大王,3},{迷你大王,2},{袖珍大王,2}'}",
	[103777] = "{id = 103777, Speaker = 5697, Text = '没错！没错！没错！再念一遍，原野大王！', Option = '{卖萌大王,4},{原野大王,5},{龅牙大王,4}'}",
	[103778] = "{id = 103778, Speaker = 5697, Text = '错啦！错啦！错啦！是原野大王才对！'}",
	[103779] = "{id = 103779, Speaker = 5697, Text = '你学得很快！很有天赋嘛，最后和我念一遍，原野大王！！', Option = '{原野大王,6},{肥仔大王,7},{坚果大王,7}'}",
	[103780] = "{id = 103780, Speaker = 5697, Text = '你是不是故意在惹本大王生气？哼！'}",
	[103781] = "{id = 103781, Speaker = 5697, Text = '哈哈哈，没想到你这个人类还挺聪明啊。'}",
	[103782] = "{id = 103782, Speaker = 5697, Text = '这是本大王给你的奖励！感恩吧~'}",
	[103783] = "{id = 103783, Speaker = 5698, Text = '你今天就算是把我的花瓣拔光，我也不会认输！'}",
	[103784] = "{id = 103784, Speaker = 5698, Text = '哼，看什么看，人类。', Option = '嗯？'}",
	[103785] = "{id = 103785, Speaker = 5698, Text = '你这是在挑衅我么？好，那就来战啊！', Option = '{接受挑战,3},{忽略离开,2}'}",
	[103786] = "{id = 103786, Speaker = 5698, Text = '没想到你还是有点实力的嘛，这才有意思。', Option = '认输吗？'}",
	[103787] = "{id = 103787, Speaker = 5698, Text = '但是认输是不可能认输的，这辈子都不可能认输的。', Option = '真的吗？'}",
	[103788] = "{id = 103788, Speaker = 5698, Text = '就算你今天拔光我的花瓣，我也不会认输的。', Option = '拔光花瓣'}",
	[103789] = "{id = 103789, Speaker = 5698, Text = '大哥，我错了，我认输还不行嘛？？', Option = '？？？'}",
	[103790] = "{id = 103790, Speaker = 5698, Text = '花瓣可是我最有骨气，最骄傲的地方！不能动啊！', Option = '放过你了', Emoji = 24}",
	[103791] = "{id = 103791, Speaker = 5698, Text = '下次等你变弱了，我随时接受挑战！', Emoji = 5}",
	[103792] = "{id = 103792, Speaker = 5692, Text = '你听说过蒲公英的花语么？', Option = '是什么呢'}",
	[103793] = "{id = 103793, Speaker = 5692, Text = '蒲公英的花语是无法停留的爱。', Option = '很悲伤呢'}",
	[103794] = "{id = 103794, Speaker = 5692, Text = '你愿意听我讲述蒲公英的故事嘛？', Option = '{愿意倾听,3},{为难离开,2}'}",
	[103795] = "{id = 103795, Speaker = 5692, Text = '没关系，毕竟大家不是都像我一样无事可做呢…'}",
	[103796] = "{id = 103796, Speaker = 5692, Text = '从前有一对相恋的情侣，但是女孩的身体很不好。'}",
	[103797] = "{id = 103797, Speaker = 5692, Text = '蒲公英是他们的定情信物。', Option = '然后呢'}",
	[103798] = "{id = 103798, Speaker = 5692, Text = '女孩必须去遥远的天空之城养病，男孩与她约好彼此要保持书信联络。', Option = '嗯'}",
	[103799] = "{id = 103799, Speaker = 5692, Text = '他们彼此写信，但随着时间流逝，女孩收到信的间隔越来越久。', Option = '是嘛'}",
	[103800] = "{id = 103800, Speaker = 5692, Text = '终于有一天，女孩再也没能等到男孩的回信。'}",
	[103801] = "{id = 103801, Speaker = 5692, Text = '她等啊等，最后化作蒲公英，无数种子从天空之城飘散到世界。', Option = '…'}",
	[103802] = "{id = 103802, Speaker = 5692, Text = '这样的话，总有一颗种子能飘到男孩身边吧。'}",
	[103803] = "{id = 103803, Speaker = 5692, Text = '（莉安娜眼角泛着泪光）这样的话，或许他会再次想起女孩。'}",
	[103804] = "{id = 103804, Speaker = 5692, Text = '对不起，我陷入回忆里太深了。', Emoji = 24}",
	[103805] = "{id = 103805, Speaker = 5692, Text = '谢谢你听完蒲公英的故事，有听众的感觉真好。', Option = '故事很棒'}",
	[103806] = "{id = 103806, Speaker = 5692, Text = '如果你愿意的话，再来找我聊天吧~'}",
	[103807] = "{id = 103807, Speaker = 5547, Text = '总感觉又有什么东西弄丢了……'}",
	[103808] = "{id = 103808, Speaker = 5548, Text = '我家的孩子可真不让人省心啊……'}",
	[103809] = "{id = 103809, Speaker = 5549, Text = '哎，我真的好想念那个人啊……'}",
	[103810] = "{id = 103810, Speaker = 5550, Text = '年轻人，要不要来了解下钓鱼的乐趣？'}",
	[103811] = "{id = 103811, Speaker = 5551, Text = '干净整洁也是检查站重要的一环！'}",
	[103812] = "{id = 103812, Speaker = 5552, Text = '嗯……应该怎么走呢……'}",
	[103813] = "{id = 103813, Speaker = 5553, Text = '春天播种，秋天收获，这就是农人的生活。'}",
	[103814] = "{id = 103814, Speaker = 5554, Text = '嘿咻，今天也是忙碌的一天呢！'}",
	[103815] = "{id = 103815, Speaker = 5555, Text = '……'}",
	[103816] = "{id = 103816, Speaker = 5556, Text = '……'}",
	[103817] = "{id = 103817, Speaker = 5557, Text = '波波利是我一生最好的伙伴~'}",
	[103818] = "{id = 103818, Speaker = 5558, Text = '波利~波波利~'}",
	[103819] = "{id = 103819, Speaker = 5559, Text = '哎，脚都走酸了，还是飞空艇方便。'}",
	[103820] = "{id = 103820, Speaker = 5560, Text = '看什么看？这是打劫！打劫懂不懂！'}",
	[103821] = "{id = 103821, Speaker = 5953, Text = '重金属蝗虫吵的我没法安心工作了，谁来帮我赶走它们呢？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103822] = "{id = 103822, Speaker = 5953, Text = '真是谢谢，你比那些死脑筋的卫兵热心多了~'}",
	[103823] = "{id = 103823, Speaker = 5953, Text = '呼，这样就安静多了，我又能好好工作了！'}",
	[103824] = "{id = 103824, Speaker = 5547, Text = '呜呜，我的钱包不见了，肯定是来的路上弄丢了。', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103825] = "{id = 103825, Speaker = 5547, Text = '谢谢你，我想钱包可能是在靠近艾尔帕兰的方向弄丢的。'}",
	[103826] = "{id = 103826, Speaker = 0, Text = '找到了钱包，看来这就是特洛遗失的那个。'}",
	[103827] = "{id = 103827, Speaker = 5547, Text = '哇，谢谢你，这下总算放心了。'}",
	[103828] = "{id = 103828, Speaker = 5547, Text = '呜呜，我的便当不见了，肯定是早上忘记带了。', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103829] = "{id = 103829, Speaker = 5547, Text = '太好了，我妈妈就在艾尔帕兰。'}",
	[103830] = "{id = 103830, Speaker = 5547, Text = '能帮我去把便当拿来么？'}",
	[103831] = "{id = 103831, Speaker = 5548, Text = '特洛那个孩子啊……', Option = '说明来意'}",
	[103832] = "{id = 103832, Speaker = 5548, Text = '请把便当给他送过去吧，不然傻孩子又要饿肚子了。'}",
	[103833] = "{id = 103833, Speaker = 5547, Text = '哇，谢谢你，这下不用饿肚子了。'}",
	[103834] = "{id = 103834, Speaker = 5549, Text = '哎，你知道么…这个地方对我有太多意义。'}",
	[103835] = "{id = 103835, Speaker = 5549, Text = '你有相机么，帮我拍张照，好么？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103836] = "{id = 103836, Speaker = 5549, Text = '请站远一些拍，我希望能把桥也拍进去。'}",
	[103837] = "{id = 103837, Speaker = 5549, Text = '谢谢，真是张漂亮的照片。'}",
	[103838] = "{id = 103838, Speaker = 5549, Text = '哎，只可惜那个人没机会看到了……'}",
	[103839] = "{id = 103839, Speaker = 5550, Text = '嗨，小子，我在桥下的河里发现了幸运锦鲤！'}",
	[103840] = "{id = 103840, Speaker = 5550, Text = '可惜我的胳膊扭伤了不能把它钓上来……', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103841] = "{id = 103841, Speaker = 5550, Text = '好极了，我把我的超级钓竿借给你吧。'}",
	[103842] = "{id = 103842, Speaker = 5550, Text = '快去把幸运锦鲤钓上来，别让它游走了！'}",
	[103843] = "{id = 103843, Speaker = 0, Text = '幸运锦鲤没有咬钩，试试撒饵吧。'}",
	[103844] = "{id = 103844, Speaker = 0, Text = '幸运锦鲤被鱼饵引诱了，快钓起来吧。'}",
	[103845] = "{id = 103845, Speaker = 0, Text = '活蹦乱跳的幸运锦鲤被钓上来了。'}",
	[103846] = "{id = 103846, Speaker = 5550, Text = '啊哈，就是它，我梦寐以求的幸运锦鲤。'}",
	[103847] = "{id = 103847, Speaker = 5550, Text = '恭喜你，小子，据说钓上它的人一年都会走好运。'}",
	[103848] = "{id = 103848, Speaker = 5550, Text = '嘿嘿，好运归你了，鱼就归我了哟。'}",
	[103849] = "{id = 103849, Speaker = 5955, Text = '高原佩瑞丝竟然偷走了检查站的印章！', Emoji = 12}",
	[103850] = "{id = 103850, Speaker = 5955, Text = '说了多少次了，那是印章，不是化妆品！', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103851] = "{id = 103851, Speaker = 5955, Text = '太好了，偷走印章的高原佩瑞丝应该还没走远。'}",
	[103852] = "{id = 103852, Speaker = 5955, Text = '你多找几只一定会找到印章的。'}",
	[103853] = "{id = 103853, Speaker = 5955, Text = '谢天谢地，印章终于回来了。'}",
	[103854] = "{id = 103854, Speaker = 5955, Text = '希望那些高原佩瑞丝别再来捣乱了。'}",
	[103855] = "{id = 103855, Speaker = 5551, Text = '噢，我的天呢，我才休假那么几天。'}",
	[103856] = "{id = 103856, Speaker = 5551, Text = '检查站怎么就闹蟑螂了？！', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103857] = "{id = 103857, Speaker = 5551, Text = '太好了，我讨厌这些臭虫子。'}",
	[103858] = "{id = 103858, Speaker = 0, Text = '小蟑螂都逃走了，出现了巨大的蟑螂。'}",
	[103859] = "{id = 103859, Speaker = 5551, Text = '太好了，总算是干净多了。'}",
	[103860] = "{id = 103860, Speaker = 5551, Text = '噢，我的天呢，清洁剂居然不够了。'}",
	[103861] = "{id = 103861, Speaker = 5551, Text = '我明明记得之前还有一大罐的啊？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103862] = "{id = 103862, Speaker = 5551, Text = '我有个好主意，邪恶向日葵的葵液能代替清洁剂的作用。'}",
	[103863] = "{id = 103863, Speaker = 5551, Text = '你能帮我去找一些来么？'}",
	[103864] = "{id = 103864, Speaker = 5551, Text = '哇噢，果然纯天然的葵液一点都不比人造的清洁剂差。'}",
	[103865] = "{id = 103865, Speaker = 5551, Text = '要是采购部门的那群蠢蛋也明白就好了。'}",
	[103866] = "{id = 103866, Speaker = 5552, Text = '可恶…这种地图到底让人怎么看啊！'}",
	[103867] = "{id = 103867, Speaker = 5552, Text = '难道我又迷路了？', Option = '我帮你'}",
	[103868] = "{id = 103868, Speaker = 5552, Text = '看你这么热心，那我勉为其难接受你的帮助。'}",
	[103869] = "{id = 103869, Speaker = 5552, Text = '带我去检查站吧。'}",
	[103870] = "{id = 103870, Speaker = 5552, Text = '哼，别以为给带路我就会感谢你。'}",
	[103871] = "{id = 103871, Speaker = 5552, Text = '其…其实我自己也找得到路的！'}",
	[103872] = "{id = 103872, Speaker = 5954, Text = '冒险者，我正在筹备下个月的见习检察官考试。'}",
	[103873] = "{id = 103873, Speaker = 5954, Text = '有兴趣尝试几道我设计的试卷题么？', Option = '{很乐意,2},{为难离开,3}'}",
	[103874] = "{id = 103874, Speaker = 5954, Text = '第一题-请问国境检查站南面最近的是哪座城市？', Option = '{艾尔帕兰,4},{普隆德拉,5},{朱诺,5}'}",
	[103875] = "{id = 103875, Speaker = 5954, Text = '第二题-请问国境检查站附近半人半鸟的魔物，全称是？', Option = '{鸟人斑比,7},{鸟人拉比,7},{鸟人哈比,6}'}",
	[103876] = "{id = 103876, Speaker = 5954, Text = '第三题-请问闻名于世的朱诺被人们尊称为？', Option = '{天空之城,8},{科技之城,9},{水晶之城,9}'}",
	[103877] = "{id = 103877, Speaker = 5954, Text = '再仔细想想吧，其实没那么难。'}",
	[103878] = "{id = 103878, Speaker = 5954, Text = '不错不错，看来你很有成为国境检察官的潜质嘛。'}",
	[103879] = "{id = 103879, Speaker = 5553, Text = '哎呀，这群鸟人哈比怎么天天弄坏庄稼。'}",
	[103880] = "{id = 103880, Speaker = 5553, Text = '可把我愁死了…', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103881] = "{id = 103881, Speaker = 5553, Text = '太好了，请帮我教训附近的鸟人哈比吧。'}",
	[103882] = "{id = 103882, Speaker = 5553, Text = '呼，这样它们应该不敢再来了。'}",
	[103883] = "{id = 103883, Speaker = 5554, Text = '嘿咻，嘿咻~'}",
	[103884] = "{id = 103884, Speaker = 5554, Text = '这些物资真是太重了，我的胳膊都要搬断了。', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103885] = "{id = 103885, Speaker = 5554, Text = '真感谢！请帮我把门口的物资搬过来分类。'}",
	[103886] = "{id = 103886, Speaker = 5554, Text = '铁箱子放在左侧，木箱子放在右侧。'}",
	[103887] = "{id = 103887, Speaker = 5554, Text = '……铁箱子应该放在？', Option = '{左侧,5},{右侧,6}'}",
	[103888] = "{id = 103888, Speaker = 5554, Text = '……木箱子应该放在？', Option = '{左侧,7},{右侧,8}'}",
	[103889] = "{id = 103889, Speaker = 5554, Text = '别着急啊，怎么能胡乱摆放物资呢？'}",
	[103890] = "{id = 103890, Speaker = 5554, Text = '哇，摆放真整齐~你真棒，比我都厉害多了！'}",
	[103891] = "{id = 103891, Speaker = 5557, Text = '波波利，波波利你在哪呀？'}",
	[103892] = "{id = 103892, Speaker = 5557, Text = '呜呜呜，谁愿意来帮帮我呀？', Option = '{了解情况,2},{没兴趣,3}'}",
	[103893] = "{id = 103893, Speaker = 5557, Text = '我的宠物波波利走失了。'}",
	[103894] = "{id = 103894, Speaker = 5557, Text = '一定是过检查站的时候太匆忙了没注意。'}",
	[103895] = "{id = 103895, Speaker = 5557, Text = '请帮我一起找找它吧？'}",
	[103896] = "{id = 103896, Speaker = 5558, Text = '呜呜呜~主人，你在哪，我不该乱跑的。'}",
	[103897] = "{id = 103897, Speaker = 5558, Text = '波波利害怕~', Option = '说明来意'}",
	[103898] = "{id = 103898, Speaker = 5558, Text = '原来是主人让你来找我的，我这就跟你回去。'}",
	[103899] = "{id = 103899, Speaker = 5557, Text = '呜，波波利你终于回来了。'}",
	[103900] = "{id = 103900, Speaker = 5558, Text = '呜呜，得救了，多亏这个冒险者。'}",
	[103901] = "{id = 103901, Speaker = 5558, Text = '我保证以后都躲在主人的背包里，再也不乱跑了。'}",
	[103902] = "{id = 103902, Speaker = 5557, Text = '嗯嗯，这里危险，到了朱诺你再出来玩。'}",
	[103903] = "{id = 103903, Speaker = 5559, Text = '饶命啊，我没钱的，我只是来旅游的啊。'}",
	[103904] = "{id = 103904, Speaker = 5559, Text = '我的天呢，谁来救救我吧。', Option = '{出手相助,2},{故意无视,3}'}",
	[103905] = "{id = 103905, Speaker = 5560, Text = '哼，多管闲事的冒险家！我们才不怕你！'}",
	[103906] = "{id = 103906, Speaker = 0, Text = '劫匪都被灰溜溜打跑了。'}",
	[103907] = "{id = 103907, Speaker = 5559, Text = '呜呜呜，可把我吓死了。', Option = '询问情况'}",
	[103908] = "{id = 103908, Speaker = 5559, Text = '受伤倒是没有，不过心理创伤可能是很严重了。'}",
	[103909] = "{id = 103909, Speaker = 5559, Text = '哎，以后还是坐飞空艇旅游吧。'}",
	[103910] = "{id = 103910, Speaker = 4754, Text = '保证飞空艇的安全是对乘客的负责！'}",
	[103911] = "{id = 103911, Speaker = 4755, Text = '需要点什么？冒险者。'}",
	[103912] = "{id = 103912, Speaker = 4756, Text = '今天的塞壬大人还是一样的帅气~'}",
	[103913] = "{id = 103913, Speaker = 4757, Text = '大总统布置的任务我一定会完美交付的！'}",
	[103914] = "{id = 103914, Speaker = 4758, Text = '我的梦想是走遍米德加尔特大陆上的所有地方~'}",
	[103915] = "{id = 103915, Speaker = 4759, Text = '朱诺是最神圣最美丽的城市~'}",
	[103916] = "{id = 103916, Speaker = 4760, Text = '喵喵~喵呜~~~~'}",
	[103917] = "{id = 103917, Speaker = 4761, Text = '朱诺是最神圣最美丽的城市~'}",
	[103918] = "{id = 103918, Speaker = 4762, Text = '这就是朱诺啊！真是气派！'}",
	[103919] = "{id = 103919, Speaker = 4763, Text = '书是个好东西，书里面什么都有。'}",
	[103920] = "{id = 103920, Speaker = 4754, Text = '哎，快要起飞了，检查还没做完，机油来不及去买了……', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103921] = "{id = 103921, Speaker = 4754, Text = '帮大忙了！贤者学院门口的广场上有个机械商人出售机油。'}",
	[103922] = "{id = 103922, Speaker = 4755, Text = '需要点什么？冒险者。', Option = '{机油,4},{螺丝,5},{木板,6}'}",
	[103923] = "{id = 103923, Speaker = 4755, Text = '确定这是您要的吗？可不要买错了。'}",
	[103924] = "{id = 103924, Speaker = 4755, Text = '这是您的东西，欢迎下次再来~'}",
	[103925] = "{id = 103925, Speaker = 4754, Text = '我代表飞空艇上的所有人感谢你，好心的冒险者。', Emoji = 6}",
	[103926] = "{id = 103926, Speaker = 4756, Text = '呐呐呐！帮我个忙好不好~', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103927] = "{id = 103927, Speaker = 4756, Text = '能不能帮我去大总统府那儿拍几张塞壬大人工作时候的照片~'}",
	[103928] = "{id = 103928, Speaker = 4756, Text = '拜托你了嘛~自己去拍实在是太害羞了~', Emoji = 2}",
	[103929] = "{id = 103929, Speaker = 4756, Text = '太感谢你了~啊~我的塞壬大人~', Emoji = 29}",
	[103930] = "{id = 103930, Speaker = 4757, Text = '市民手册的制作还差几张景点照片没有拍摄。', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103931] = "{id = 103931, Speaker = 4757, Text = '那就拜托你拍摄一张海姆达水晶的照片~'}",
	[103932] = "{id = 103932, Speaker = 4757, Text = '拍的太棒了！我马上加进我们朱诺的市民手册里。', Emoji = 20}",
	[103933] = "{id = 103933, Speaker = 4758, Text = '请问，你能帮我拍一张照片吗？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103934] = "{id = 103934, Speaker = 4758, Text = '你也来自米德加兹吗？我的故乡是普隆德拉~'}",
	[103935] = "{id = 103935, Speaker = 4758, Text = '我热爱旅行~我想把去过的地方都写进我的书里！'}",
	[103936] = "{id = 103936, Speaker = 4758, Text = '在瞭望台这边帮我和背后的天空拍张照留个纪念吧~'}",
	[103937] = "{id = 103937, Speaker = 4758, Text = '从我正面把我和背后的天空拍在一起哦~'}",
	[103938] = "{id = 103938, Speaker = 4758, Text = '真好看~不愧是天空之城朱诺。', Emoji = 17}",
	[103939] = "{id = 103939, Speaker = 4758, Text = '谢谢你！我会把这张照片做成书签，写上拍摄者[c][ffff00][PlayerName][-][/c]的名字。'}",
	[103940] = "{id = 103940, Speaker = 4759, Text = '我的库洛又去哪里了？', Option = '{了解情况,2},{没兴趣,3}'}",
	[103941] = "{id = 103941, Speaker = 4759, Text = '嗯，好吧，既然你闲着就帮我去找它吧。'}",
	[103942] = "{id = 103942, Speaker = 4759, Text = '库洛是一只黑色的小猫，最喜欢在皇宫附近玩了，你去那里看看吧。'}",
	[103943] = "{id = 103943, Speaker = 0, Text = '似乎不在这里。'}",
	[103944] = "{id = 103944, Speaker = 0, Text = '似乎不在这里。'}",
	[103945] = "{id = 103945, Speaker = 4760, Text = '喵~', Option = '跟我回去', Emoji = 8}",
