	[103947] = "{id = 103947, Speaker = 4759, Text = '你把它带回来了呀~'}",
	[103948] = "{id = 103948, Speaker = 4759, Text = '库洛你不要再乱跑啦~', Emoji = 30}",
	[103949] = "{id = 103949, Speaker = 4761, Text = '唔，今天忙的不可开交，谁能带梅尔在朱诺参观呢？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[103950] = "{id = 103950, Speaker = 4761, Text = '冒险者，我看你挺空闲的~带我朋友去总统府参观一下吧。'}",
	[103951] = "{id = 103951, Speaker = 4761, Text = '来朱诺怎么能不去总统府那边参观呢！'}",
	[103952] = "{id = 103952, Speaker = 4762, Text = '这就是大总统府啊！真是气派！'}",
	[103953] = "{id = 103953, Speaker = 4762, Text = '不知道能不能溜进去看看……', Option = '你说什么？', Emoji = 21}",
	[103954] = "{id = 103954, Speaker = 4762, Text = '哦不不不，我就门口看看……就看看……'}",
	[103955] = "{id = 103955, Speaker = 4762, Text = '谢谢你带我过来！我去周围看看~'}",
