	[103960] = "{id = 103960, Speaker = 0, Text = '《大陆魔物图册》，是这本书没错吧……'}",
	[103961] = "{id = 103961, Speaker = 4763, Text = '哇！是它！太谢谢你了！终于拿到了~'}",
	[103962] = "{id = 103962, Speaker = 4763, Text = '等要还书的时候我再找你帮忙哦~！'}",
