	[103964] = "{id = 103964, Speaker = 5565, Text = '这块地图看板很旧了，许多路线都已经磨损的看不清了。', Option = '{阅读看板,2},{没兴趣,3}'}",
	[103965] = "{id = 103965, Speaker = 4764, Text = '卡普拉竭诚为您提供最全面的服务！'}",
