	[103993] = "{id = 103993, Speaker = 4768, Text = '我们朱诺是学者之都，入学考试什么的对我们来说都是小意思。'}",
	[103994] = "{id = 103994, Speaker = 4768, Text = '外乡人你会做这些题目么？', Option = '{会,2},{不会,3}'}",
	[103995] = "{id = 103995, Speaker = 4768, Text = '哦？是吗？正好当考前复习，我就来问问你。'}",
	[103996] = "{id = 103996, Speaker = 4768, Text = '朱诺是为什么浮在空中的呢？', Option = '{喵星人的魔法,4},{伊米尔之心,5},{朱诺的科技,4}'}",
