	[104001] = "{id = 104001, Speaker = 4768, Text = '这题目你都不会？真是见识浅陋。'}",
	[104002] = "{id = 104002, Speaker = 4768, Text = '西芙院长真是有名啊，连你都知道了。'}",
	[104003] = "{id = 104003, Speaker = 4768, Text = '那么以下哪个不是艾因布洛克原野的魔物？', Option = '{土拨鼠,8},{鸟人哈比,8},{森灵,9}'}",
	[104004] = "{id = 104004, Speaker = 4768, Text = '这题目你都不会？真是知识贫乏。'}",
