	[104006] = "{id = 104006, Speaker = 4768, Text = '看来入学考试没什么难的~我一定能考进！'}",
	[104007] = "{id = 104007, Speaker = 4769, Text = '我的论文去哪里了！？我完美的论文啊！', Option = '{了解情况,2},{没兴趣,3}', Emoji = 7}",
	[104008] = "{id = 104008, Speaker = 4769, Text = '哦~这位冒险者~你真是太善良了！我昨天写完的论文突然就不见了！'}",
	[104009] = "{id = 104009, Speaker = 4769, Text = '一定是学院里的谁嫉妒我的才能与美貌~偷走了我的论文！'}",
	[104010] = "{id = 104010, Speaker = 4769, Text = '请你帮我找到我的论文并教训一下那家伙吧！'}",
	[104011] = "{id = 104011, Speaker = 4769, Text = '嗯？你怎么还没去？', Option = '去过哪里'}",
	[104012] = "{id = 104012, Speaker = 4769, Text = '上午我去了一趟艾因布洛克原野，回来后就一直在这里没离开过了。'}",
	[104013] = "{id = 104013, Speaker = 0, Text = '这好像就是乔伊的论文了吧。'}",
	[104014] = "{id = 104014, Speaker = 4770, Text = '我们的东西谁也不能拿走，啊——！'}",
	[104015] = "{id = 104015, Speaker = 4771, Text = '谁也不能拿走我们的东西，唷——！'}",
	[104016] = "{id = 104016, Speaker = 4770, Text = '不和你计较了！你拿走吧！啊——！'}",
	[104017] = "{id = 104017, Speaker = 4771, Text = '你拿走吧！不和你计较了！唷——！'}",
	[104018] = "{id = 104018, Speaker = 4769, Text = '我的论文！~替我教训那个偷论文的家伙了么！', Option = '是土拨鼠'}",
	[104019] = "{id = 104019, Speaker = 4769, Text = '不可能！一定是有人嫉妒我！对！一定是有人指使土拨鼠这么干的！'}",
	[104020] = "{id = 104020, Speaker = 4769, Text = '哦~我的论文是如此的完美~'}",
	[104021] = "{id = 104021, Speaker = 4772, Text = '试剂的数量有点不太对啊，是不是忘在哪里了……', Option = '{了解情况,2},{没兴趣,3}'}",
	[104022] = "{id = 104022, Speaker = 4772, Text = '这批试剂非常危险，不小心就会引起爆炸。'}",
