	[104024] = "{id = 104024, Speaker = 4772, Text = '你去餐厅跑一趟帮我取回来吧！'}",
	[104025] = "{id = 104025, Speaker = 0, Text = '周围的试剂都已经拿好了，拿回去给罗德吧。'}",
	[104026] = "{id = 104026, Speaker = 4772, Text = '忘记提醒你要轻取轻放了，没想到你安全的回来了。把试剂给我吧。'}",
	[104027] = "{id = 104027, Speaker = 4774, Text = '吶~冒险者！来帮我一个忙~', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[104028] = "{id = 104028, Speaker = 4774, Text = '我今天穿的衣服好看嘛~', Option = '{好看,4},{一般,5}'}",
	[104029] = "{id = 104029, Speaker = 4774, Text = '是吗？可是我觉得不够好看~'}",
	[104030] = "{id = 104030, Speaker = 4774, Text = '是吧！我也觉得不够好看~'}",
	[104031] = "{id = 104031, Speaker = 4774, Text = '你能帮我去挑选一条漂亮的裙子吗？我相信你的眼光！'}",
	[104032] = "{id = 104032, Speaker = 4775, Text = '这位客人您需要什么~？', Option = '{蕾丝长裙,6},{纱织短裙,6},{背带裙,6}'}",
	[104033] = "{id = 104033, Speaker = 4775, Text = '您需要什么颜色的呢~？', Option = '{白色,7},{粉色,7},{蓝色,7}'}",
	[104034] = "{id = 104034, Speaker = 4775, Text = '这条裙子很漂亮~您的眼光真好！', Emoji = 27}",
	[104035] = "{id = 104035, Speaker = 4774, Text = '你的眼光果然不错~穿着这个塞壬大人会不会多看我两眼呢……', Emoji = 27}",
	[104036] = "{id = 104036, Speaker = 4776, Text = '文森那孩子！到了吃饭时间还不回来！', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[104037] = "{id = 104037, Speaker = 4776, Text = '真是帮了我老头子的忙了。不出意外的话，他应该在飞空艇那边。'}",
	[104038] = "{id = 104038, Speaker = 4777, Text = '你是谁？为什么要我回去？', Option = '爷爷在等你'}",
	[104039] = "{id = 104039, Speaker = 4777, Text = '好啦我知道啦！你看你看！这个飞空艇是我爸爸设计的！', Emoji = 26}",
	[104040] = "{id = 104040, Speaker = 4777, Text = '不过他没能看到完工就……'}",
	[104041] = "{id = 104041, Speaker = 4777, Text = '我每天来就是替爸爸看一看他的飞空艇……'}",
	[104042] = "{id = 104042, Speaker = 4777, Text = '现在我们回去吧……'}",
	[104043] = "{id = 104043, Speaker = 4779, Text = '冒险家，能不能帮我一个忙？', Option = '{乐意帮忙,2},{为难离开,3}'}",
	[104044] = "{id = 104044, Speaker = 4779, Text = '我的妹妹康娜是皇宫里的女仆，每天都很忙，很辛苦。'}",
	[104045] = "{id = 104045, Speaker = 4779, Text = '这是我为她做的营养午餐~你能不能替我送进去给她？'}",
	[104046] = "{id = 104046, Speaker = 4778, Text = '这是姐姐做的面包！'}",
	[104047] = "{id = 104047, Speaker = 4778, Text = '姐姐可真是的，明明自己也很忙，还经常抽空给我做吃的~', Emoji = 27}",
	[104048] = "{id = 104048, Speaker = 4778, Text = '冒险者谢谢你，请转告姐姐照顾好自己~'}",
	[104049] = "{id = 104049, Speaker = 4779, Text = '我的康娜真是贴心~我作为姐姐也要努力工作！'}",
	[104050] = "{id = 104050, Speaker = 5687, Text = '康娜她怎么说？', Option = '她说你最懂她'}",
	[104051] = "{id = 104051, Speaker = 5687, Text = '真的吗？太好啦！我这就去找她！谢谢你冒险者！'}",
	[104052] = "{id = 104052, Speaker = 0, Text = '突然刮起一阵诡异的妖风！'}",
	[104053] = "{id = 104053, Speaker = 5698, Text = '等等！', Option = '这就怕了？'}",
	[104054] = "{id = 104054, Speaker = 5698, Text = '这周围都是我的朋友，怕你被我打败了出丑，我们去个人少的地方较量！', Option = '好啊'}",
	[104055] = "{id = 104055, Speaker = 5698, Text = '跟我来！'}",
	[104056] = "{id = 104056, Speaker = 5700, Text = '你终于来了！你现在后悔还来得及！', Option = '出手吧'}",
	[104057] = "{id = 104057, Speaker = 0, Text = '在运河之都——艾尔帕兰，黄金时钟在钟塔顶端静谧地守护着时空的秩序，但预言中的黑暗正逐渐转化为现世的梦魇，承载着宿命的冒险者啊，化作斩断艾尔帕兰灾厄的利剑吧！'}",
	[104058] = "{id = 104058, Speaker = 0, Text = '钟楼顶端的黄金时钟，内部存在一块伊米尔之心碎片，蕴藏着古代诸神未知力量，作为米德加尔特大陆最接近神域之地，据说若是在满月那天攀登至钟塔之巅，就能听到月狼芬里尔的哀嚎，呼唤着尘世的亲眷，呼唤着不灭的大蛇——《艾尔帕兰民俗传说》'}",
	[104059] = "{id = 104059, Speaker = 0, Text = '拥有伊米尔碎片之力的黄金时钟，百年来默默维持着艾尔帕兰的时空秩序。'}",
	[104060] = "{id = 104060, Speaker = 0, Text = '然而这份平和被一股神秘势力所打破。唯一能开启黄金时钟的秘法典籍被盗走，这引起了时钟守护者首领默斯曼·岁月的高度警觉，他派遣了两位优秀的时钟守卫者诺曼、瑞德兄弟与冒险者共同追查此事。'}",
	[104061] = "{id = 104061, Speaker = 0, Text = '冒险者发现秘法典籍的守护人死于一种古老的蛇毒。经过一系列的调查，事情的真相渐渐浮出水面……'}",
	[104062] = "{id = 104062, Speaker = 0, Text = '一切因果的幕后黑手，竟然是传说中的世界大蛇耶梦加得！他执着于得到黄金时钟，并用它的强大力量解救出被关在月亮上的兄长——魔狼芬里尔。'}",
	[104063] = "{id = 104063, Speaker = 0, Text = '耶梦加得长年蛰伏在时钟守卫者中，他用阴谋手段燃起了时钟守卫者内战的导火索。'}",
	[104064] = "{id = 104064, Speaker = 0, Text = '寻找瑞德下落的诺曼与冒险者一同潜入叛军的据点——钟楼底层。激战之后，诺曼以付出重伤的代价换得了瑞德的自由。'}",
	[104065] = "{id = 104065, Speaker = 0, Text = '然而之后情况发生了进一步的恶化，耶梦加得妄图夺得威克斯发明的秘宝——奇点球，并用其制造一个巨大虫洞以掀起更大的混乱。更令人震惊的，冒险者经过对瑞德的追查，难以置信的发现瑞德竟然就是世界大蛇的本体——耶梦加得。'}",
	[104066] = "{id = 104066, Speaker = 0, Text = '成功骗得奇点球的耶梦加得，打破了过去的时空壁垒并召唤出了可怕的古代巴风特。但令他自己都没料到的是，古代巴风特完全不听指挥，反而向作为召唤者的他发起攻击！一直将耶梦加得视作亲弟弟的诺曼并不在乎他对自己的欺瞒，舍身为他挡下了攻击。'}",
	[104067] = "{id = 104067, Speaker = 0, Text = '这个举动深深动摇了耶梦加得的内心，他与冒险者联手击败古代巴风特，并与奄奄一息的诺曼许下了守护黄金时钟的诺言。本以为一切就此结束，但艾尔帕兰的噩梦却丝毫没有苏醒的迹象……'}",
	[104068] = "{id = 104068, Speaker = 0, Text = '诺曼之死使耶梦加得放下了对黄金时钟的执念，但时空扭曲的乱象却愈发严重。失控的奇点球开启了异世界的虫洞，耶梦加得就这样和另一个世界线的自己相遇了。'}",
	[104069] = "{id = 104069, Speaker = 0, Text = '另一世界线的耶梦加得成功夺取了黄金时钟。他沉溺于力量带来的满足，迷失了拯救兄长的初心。最终，他舍弃了耶梦加得之名，称自己为轮回梦魇！并带着对力量的无尽渴求，侵入了人类的世界…'}",
	[104070] = "{id = 104070, Speaker = 0, Text = '耶梦加得为了遵守和诺曼的约定，毅然拒绝了轮回梦魇发出的联手邀约，摒弃前嫌和时钟守护者、炼金术士公会组成同盟。众人穿越到轮回梦魇所在的世界线，赶在钟楼崩塌前找到了轮回梦魇，大战一触即发！'}",
	[104071] = "{id = 104071, Speaker = 0, Text = '在艾尔帕兰联合战线处于下风的关键时刻，诺曼的怀表发出了夺目的光芒，这给了耶梦加得与冒险者击退轮回梦魇的勇气。众人用时空碎片的力量将黑洞永久封存。悠扬的钟声再次回响在大街小巷，艾尔帕兰恢复了往日的平静。'}",
	[104072] = "{id = 104072, Speaker = 0, Text = '尘埃落定后，时钟守卫者与耶梦加得的同盟关系也到此为止。默斯曼并没有阻止他带走奇点球，耶梦加得露出捉摸不透的轻笑，消失在运河之畔的人群中。“我觉得他，也不是那么的坏。”面对质疑的声音，默斯曼沉吟道。'}",
	[104073] = "{id = 104073, Speaker = 0, Text = '可又有谁能真正猜透他呢？合上手中的怀表，耶梦加得抬头望了眼天上的孤月，亦正亦邪的神明继续踏上了拯救兄长的旅途。'}",
	[104085] = "{id = 104085, Speaker = 4780, Text = '朱诺从不会对英雄吝啬自己的赞美！'}",
	[104086] = "{id = 104086, Speaker = 4781, Text = '朱诺从不会忘记有恩于它的勇者。'}",
	[104087] = "{id = 104087, Speaker = 4782, Text = '每位守护朱诺的英雄都会拥有一颗以他命名的星星。'}",
	[104088] = "{id = 104088, Speaker = 4780, Text = '诺法之剑据说是当年四勇士之一的诺法佩有的长剑。我想将它作为诺法勇气的象征带回朱诺，你能找到诺法之剑的破损遗物并带给我么？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104089] = "{id = 104089, Speaker = 4780, Text = '诺法之盾据说是当年四勇士之一的诺法背负的大盾，诺法生前用它无数次的保护了勇者小队与朱诺平民，我想将它放在诺法的纪念碑前，你能找到诺法之盾的破损遗物并带给我么？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104090] = "{id = 104090, Speaker = 4780, Text = '桃丽之弓据说是当年四勇士之一的崔桃丽持有的长弓，桃丽用它狙杀了发起暴动的魔物领主，我想将它送回桃丽的故乡——斐扬，你能找到桃丽之弓的破损遗物并带给我么？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104091] = "{id = 104091, Speaker = 4780, Text = '桃丽之箭据说是当年四勇士之一的崔桃丽使用过的箭矢，特别的制作工艺让箭矢对抗熔岩洞窟的魔物时威力惊人，我想将它送回桃丽的故乡——斐扬，你能为我找来桃丽之箭的破损遗物么？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104092] = "{id = 104092, Speaker = 4780, Text = '开这种低劣的玩笑，可不会为你带来尊重与荣耀。'}",
	[104093] = "{id = 104093, Speaker = 4780, Text = '这把剑上承载的情感如此厚重，不会错的，这就是当年诺法挥舞之剑。'}",
	[104094] = "{id = 104094, Speaker = 4780, Text = '就算经过时间的洗礼，这面盾牌依旧如此坚固呢！多亏了你才能找到它。'}",
	[104095] = "{id = 104095, Speaker = 4780, Text = '弓上还隐约沁着桃丽的鲜血，斐扬的村民见到了它，也一定会为桃丽骄傲吧！'}",
	[104096] = "{id = 104096, Speaker = 4780, Text = '这支箭完成了它的使命，就像它的主人一样，令人钦佩！'}",
	[104097] = "{id = 104097, Speaker = 4780, Text = '你这样的家伙，真的能找到诺法之剑嘛？'}",
	[104098] = "{id = 104098, Speaker = 4780, Text = '你可能根本不明白这面盾牌承载的重量。'}",
	[104099] = "{id = 104099, Speaker = 4780, Text = '要是你有四勇士一半的坚毅，弓早被找到了。'}",
	[104100] = "{id = 104100, Speaker = 4780, Text = '箭矢都拥有自己的信仰，你有吗？'}",
	[104101] = "{id = 104101, Speaker = 4781, Text = '瓦西里之斧据说是当年四勇士之一的瓦西里手中劈开熔洞大地的战斧，我想把它送回瓦西里出生的梦罗克，你能找到瓦西里之斧的破损遗物并交给我吗？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104102] = "{id = 104102, Speaker = 4781, Text = '瓦西里之冠据说是当年四勇士之一的瓦西里最引以为傲的头冠，我想把它放在瓦西里的衣冠冢上，你能找到瓦西里之冠的破损遗物并带给我吗？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104103] = "{id = 104103, Speaker = 4781, Text = '黎风之杖据说是当年的四勇士之一的斯曼玲·黎风持有的魔杖，无数强大的魔法通过这把传奇之杖的加持，击溃了成群的魔物。我想将它带回斯曼玲的故乡，你能找到黎风之杖的破损遗物并带给我吗？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104104] = "{id = 104104, Speaker = 4781, Text = '黎风典籍据说是当年的四勇士之一的斯曼玲·黎风随身携带的魔法书，里面的魔法是她智慧的结晶，我想将它带回贤者学院存放，你能找到黎风典籍的破损遗物并交给我么？我想10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104105] = "{id = 104105, Speaker = 4781, Text = '朱诺可不欢迎撒谎精。', Emoji = 12}",
	[104106] = "{id = 104106, Speaker = 4781, Text = '如此利落干脆的斧刃，看得出他的主人瓦西里也是位爽快人！'}",
	[104107] = "{id = 104107, Speaker = 4781, Text = '没想到这么普通的头冠，它的主人却是一位值得敬佩的冒险者呢。'}",
	[104108] = "{id = 104108, Speaker = 4781, Text = '这把魔杖曾经拯救了很多生命，亲眼所见真是荣幸！'}",
	[104109] = "{id = 104109, Speaker = 4781, Text = '小小的典籍，却记录了斯曼玲的一生呢，谢谢你。'}",
	[104110] = "{id = 104110, Speaker = 4781, Text = '该不会是找到了斧子，但是你搬不动吧？'}",
	[104111] = "{id = 104111, Speaker = 4781, Text = '加把劲再找找，头冠是纪念瓦西里不可少的依凭啊！'}",
	[104112] = "{id = 104112, Speaker = 4781, Text = '这把魔杖是斯曼玲的荣耀，请务必找到！'}",
	[104113] = "{id = 104113, Speaker = 4781, Text = '找到典籍以后请赶快上交，可别随便窥探其中魔法的秘密…'}",
	[104114] = "{id = 104114, Speaker = 4782, Text = '噢…年轻人，你听过能力越大责任就越大这句话么？', Option = '{听过,2},{没听过,3}'}",
	[104115] = "{id = 104115, Speaker = 4782, Text = '比起铭记这句名言，更难的是实践这句名言。'}",
	[104116] = "{id = 104116, Speaker = 4782, Text = '在许多年前，熔岩洞窟曾发生过一次魔物大暴动，灾厄之火一度逼近朱诺城。'}",
	[104117] = "{id = 104117, Speaker = 4782, Text = '四位来自各地的勇士自发组成了一支特别行动小队，他们一路披荆斩棘，将狂化的魔物群压制回了熔岩洞窟，最后精疲力竭的勇士们牺牲自己终结了灾难。'}",
	[104118] = "{id = 104118, Speaker = 4782, Text = '但随着时间的流逝，众人遗忘了英雄们的事迹，我与两位追思者想将这四位英雄的遗物带回朱诺，请你也加入我们吧。'}",
	[104119] = "{id = 104119, Speaker = 4782, Text = '诺法、桃丽、瓦西里、斯曼玲·黎风，朱诺会永远铭记这四个名字。'}",
	[104120] = "{id = 104120, Speaker = 4782, Text = '竟然这么快就收集全了八件英雄的遗物吗？真是了不起的年轻人。'}",
	[104121] = "{id = 104121, Speaker = 4782, Text = '你一定会有所成就的！', Option = '过奖了'}",
	[104122] = "{id = 104122, Speaker = 4782, Text = '英雄虽然已经牺牲，但他们的遗物会时刻提醒着我们。', Option = '什么？'}",
	[104123] = "{id = 104123, Speaker = 4782, Text = '当你拥有了强大的能力，将这力量为自己或是为他人使用。'}",
	[104124] = "{id = 104124, Speaker = 4782, Text = '这是一道永远没有对错的选择题。', Option = '的确'}",
	[104125] = "{id = 104125, Speaker = 4782, Text = '但对于那四位为了朱诺，毅然决然选择牺牲自己的勇士…'}",
	[104126] = "{id = 104126, Speaker = 4782, Text = '我们应永远献上最高的敬意与缅怀。', Option = '认同'}",
	[104127] = "{id = 104127, Speaker = 4782, Text = '朱诺永远不会忘记：诺法、桃丽、瓦西里、斯曼玲·黎风。'}",
