	[104567] = "{id = 104567, Speaker = 5819, Text = '真是的！还能不能让人好好睡个午觉。'}",
	[104568] = "{id = 104568, Speaker = 5819, Text = '那些烦人的土拨鼠，总是惊吓出去玩耍的流浪猫。'}",
	[104569] = "{id = 104569, Speaker = 5819, Text = '我的丽丽安好几次因为它们刮伤了它美丽的毛发。', Option = '原来如此'}",
	[104570] = "{id = 104570, Speaker = 5819, Text = '你就和那些原野上的土拨鼠一样烦人！'}",
	[104571] = "{id = 104571, Speaker = 5819, Text = '不过，约定就是约定。'}",
	[104572] = "{id = 104572, Speaker = 5819, Text = '拿去，这是你要的披风。'}",
	[104573] = "{id = 104573, Speaker = 7347, Text = '我已经把为宝伏手下准备的蛋糕做好了~', Option = '好'}",
	[104574] = "{id = 104574, Speaker = 7347, Text = '看似很不起眼啊，这披风。'}",
	[104575] = "{id = 104575, Speaker = 7347, Text = '真好奇是用什么材质做的~'}",
	[104576] = "{id = 104576, Speaker = 7347, Text = '我们赶快穿上试试！'}",
	[104577] = "{id = 104577, Speaker = 7347, Text = '嘿嘿~'}",
	[104578] = "{id = 104578, Speaker = 7347, Text = '塞壬！塞壬！我看起来怎么样~'}",
	[104579] = "{id = 104579, Speaker = 7348, Text = '咳咳，就这样吧…', Emoji = 14}",
	[104580] = "{id = 104580, Speaker = 7347, Text = '好了，冒险者~好戏该开演了~！'}",
	[104581] = "{id = 104581, Speaker = 7347, Text = '你准备好了吗~', Option = '我们走'}",
	[104582] = "{id = 104582, Speaker = 7350, Text = '喂喂，什么人！你们最好离这儿远一点。', Emoji = 7}",
	[104583] = "{id = 104583, Speaker = 7349, Text = '别这么无情呀，小哥哥们~', Emoji = 2}",
	[104584] = "{id = 104584, Speaker = 7351, Text = '嘿！伙计，紧张什么。只是两个小姑娘。'}",
	[104585] = "{id = 104585, Speaker = 7350, Text = '你们看起来很可疑，是做了什么亏心事吗？', Option = '{我们在秘密行动,4},{才没有做亏心事,3},{不告诉你,4}'}",
	[104586] = "{id = 104586, Speaker = 7350, Text = '你们是什么人？', Option = '{卡普拉,5},{政府派来的,6},{雷根贝勒职员,6}'}",
	[104587] = "{id = 104587, Speaker = 7350, Text = '你们来干什么？', Option = '{总统命令,8},{调查宝伏,8},{卡普拉竞选,7}'}",
	[104588] = "{id = 104588, Speaker = 7350, Text = '走开！滚远一点！最好不要出现在我的视野里！'}",
	[104589] = "{id = 104589, Speaker = 7351, Text = '是卡普拉竞选的小姐姐们来拉票，你这家伙，就是多疑！'}",
	[104590] = "{id = 104590, Speaker = 7349, Text = '卡普拉竭诚为你们服务哟~', Emoji = 2}",
	[104591] = "{id = 104591, Speaker = 7350, Text = '好了，我们会投卡普拉一票。'}",
	[104592] = "{id = 104592, Speaker = 7350, Text = '现在你们可以走了。'}",
	[104593] = "{id = 104593, Speaker = 0, Text = '赛尼亚向我使了一个眼色。'}",
	[104594] = "{id = 104594, Speaker = 7349, Text = '我们还准备了小点心请哥哥们品尝~'}",
	[104595] = "{id = 104595, Speaker = 7349, Text = '哥哥们辛苦了噢~', Emoji = 2}",
	[104596] = "{id = 104596, Speaker = 7351, Text = '好吧好吧，我们尝尝。', Emoji = 28}",
	[104597] = "{id = 104597, Speaker = 7349, Text = '味道怎么样~~'}",
	[104598] = "{id = 104598, Speaker = 7350, Text = '我怎么感觉…'}",
	[104599] = "{id = 104599, Speaker = 7351, Text = '头...有点...晕…'}",
	[104600] = "{id = 104600, Speaker = 7349, Text = '哈哈哈哈~~'}",
	[104601] = "{id = 104601, Speaker = 7348, Text = '没想到，真的能成功…', Emoji = 10}",
	[104602] = "{id = 104602, Speaker = 7348, Text = '还等什么，快进去吧！', Option = '好'}",
	[104603] = "{id = 104603, Speaker = 7365, Text = '很好，宝伏不在，我们得尽快找出线索。'}",
	[104604] = "{id = 104604, Speaker = 7364, Text = '可是，这么多研究箱和笔记。', Emoji = 18}",
	[104605] = "{id = 104605, Speaker = 7364, Text = '该从哪里着手。'}",
	[104606] = "{id = 104606, Speaker = 7365, Text = '总统阁下曾告诉过我，雷根贝勒公司为了防止研究外露。'}",
	[104607] = "{id = 104607, Speaker = 7365, Text = '有专属的语言代码。如果有写到奇怪的代码，就搜集起来！'}",
	[104608] = "{id = 104608, Speaker = 7359, Text = '龙虾的烹饪技巧：加黄油和白兰地，大火蒸制。'}",
	[104609] = "{id = 104609, Speaker = 7360, Text = '泡澡的一百种学问：不管你是用新鲜柠檬或者柠檬汁，柠檬可以收缩毛孔，恢复皮肤、让皮肤容光焕发。我们喜欢把它加入浴缸中，帮助获得快乐的情绪。 '}",
	[104610] = "{id = 104610, Speaker = 7361, Text = '伊米尔之心碎片调查报告。'}",
	[104611] = "{id = 104611, Speaker = 7362, Text = '人体复制与克隆技术。'}",
	[104612] = "{id = 104612, Speaker = 7363, Text = '一本完全空白的笔记，什么也没有写。'}",
	[104613] = "{id = 104613, Speaker = 0, Text = '虽然这本笔记上什么也没有，可说不定里面藏着些什么。我该拿给塞壬看看。'}",
	[104614] = "{id = 104614, Speaker = 7365, Text = '宝伏这个狡猾的老狐狸！'}",
	[104615] = "{id = 104615, Speaker = 7365, Text = '如果我没有猜错，他应该是把笔记上的文字用某种方法隐藏起来了。', Option = '是嘛'}",
	[104616] = "{id = 104616, Speaker = 7365, Text = '这应该就是我们要找的，'}",
	[104617] = "{id = 104617, Speaker = 7365, Text = '乘这老狐狸还没回来，我们赶紧出去！'}",
	[104618] = "{id = 104618, Speaker = 7348, Text = '我刚才看了许久，也没有解开这本笔记的玄机。'}",
	[104619] = "{id = 104619, Speaker = 4788, Text = '我一直坚信，只有全面了解人类的过去才能更好的创造未来。'}",
	[104620] = "{id = 104620, Speaker = 4789, Text = '我喜欢听各种各样的故事，在故事里我可以体验截然不同的人生。'}",
	[104621] = "{id = 104621, Speaker = 4788, Text = '冒险者，你也是来探寻这里的勇者传说的吗？', Option = '{是的,2},{不是,3}'}",
	[104622] = "{id = 104622, Speaker = 4788, Text = '真没想到还有你这样的年轻人，不错不错。'}",
	[104623] = "{id = 104623, Speaker = 4788, Text = '艾因布洛克原野的勇者传说起源于这里的一个古老的村落，当然这个村落早就不复存在了。'}",
	[104624] = "{id = 104624, Speaker = 4788, Text = '我的学徒现在正在艾因布洛克原野上收集与传说相关的物品，那边很危险，我有点担心他。'}",
	[104625] = "{id = 104625, Speaker = 4788, Text = '你能帮助一下他吗？他会告诉你很多你感兴趣的信息。之后我会给予你一个意想不到的奖励。'}",
	[104626] = "{id = 104626, Speaker = 4788, Text = '没想到你真的将所有的物品都收集齐了！'}",
	[104627] = "{id = 104627, Speaker = 4788, Text = '这样我终于能将完整的传说记录下来了！真是谢谢你。', Option = '不客气'}",
	[104628] = "{id = 104628, Speaker = 4788, Text = '人类在发展的进程中，总是有这样那样的传说留下，大部分都是真实的。'}",
	[104629] = "{id = 104629, Speaker = 4788, Text = '但是随着时间的流逝，人们的口口相传往往会遗失传说原有的形态。'}",
	[104630] = "{id = 104630, Speaker = 4788, Text = '而我的工作就是前往传说的起源之地，寻找线索，将传说真实的还原。'}",
	[104631] = "{id = 104631, Speaker = 4788, Text = '冒险者你愿不愿意听我讲述这个鸟人与勇者的传说？', Option = '{愿意,2},{没兴趣,3}'}",
	[104632] = "{id = 104632, Speaker = 4788, Text = '是嘛……真是可惜……等你想听的时候，可以来找我。'}",
	[104633] = "{id = 104633, Speaker = 4788, Text = '几千年前，在这片原野上曾有一个以打猎和采集为生的部落。'}",
	[104634] = "{id = 104634, Speaker = 4788, Text = '那时候的人类可依仗的东西并不多，没有科技也没有法术，甚至连对事物足够的认知都没有。他们可以依靠的只有自己的双手。'}",
	[104635] = "{id = 104635, Speaker = 4788, Text = '不知从何时开始，鸟人一族在村落外面扎了根。'}",
	[104636] = "{id = 104636, Speaker = 4788, Text = '为了原野上的资源，外出的勇士们经常与鸟人发生冲突，死伤甚多。'}",
	[104637] = "{id = 104637, Speaker = 4788, Text = '勇士们渐渐的不敢轻易外出猎物，村落也渐渐失去了往日的欣荣。'}",
	[104638] = "{id = 104638, Speaker = 4788, Text = '所有人都惶惶不安，祭司们不分昼夜的在祭台上祈求天神为村子指明一条生存的道路。'}",
	[104639] = "{id = 104639, Speaker = 4788, Text = '大祭司有一晚做了一个梦。在梦里，一个逆着光看不清面容的青年人举着一颗发光的金色鸟蛋，鸟蛋的光芒笼罩着村落，村民的脸上露出幸福的神情。'}",
	[104640] = "{id = 104640, Speaker = 4788, Text = '大祭司认为这个梦是天神给他们的指引，他连忙将梦境告诉了大家。'}",
	[104641] = "{id = 104641, Speaker = 4788, Text = '在村长的提议下，村落将举办一个大型祭祀，为愿意外出寻找黄金鸟蛋的勇士们祈福送行，而找到鸟蛋的勇士就是天选勇士，他将带领村落走出困境。'}",
	[104642] = "{id = 104642, Speaker = 4788, Text = '愿意外出寻找黄金鸟蛋的勇士大约有二十个，他们都是刚毅勇敢的青年，他们不在乎自己是不是那个天选勇士，他们只想找到那颗可以守护村子的鸟蛋。'}",
	[104643] = "{id = 104643, Speaker = 4788, Text = '最后黄金鸟蛋被找到了，但是回来的勇士却只有五个……', Option = '后来如何？'}",
	[104644] = "{id = 104644, Speaker = 4788, Text = '这颗鸟蛋的确具有一定的魔力，它日复一日年复一年的吸取鸟人王哈利的力量，村落外面的鸟人感受到了这股熟悉的力量，也不再与村落的人起冲突。'}",
	[104645] = "{id = 104645, Speaker = 4788, Text = '天选勇士带领着村民开荒拓土，将野外采集来的种子种植在土地里，通过不断的摸索，转变成了一个农耕村落，自然村民也都过上了安稳的生活。', Option = '原来如此'}",
	[104646] = "{id = 104646, Speaker = 4788, Text = '这个故事结束了，谢谢你帮助我们。这是勇者之冠，请你收下。', Option = '这是什么？'}",
	[104647] = "{id = 104647, Speaker = 4788, Text = '这是这个传说中找到黄金鸟蛋的天选勇士佩戴的冠冕，是我之前在调查中获得的。', Option = '不能收！'}",
	[104648] = "{id = 104648, Speaker = 4788, Text = '冒险者，你也是勇者！是你找到了黄金鸟蛋，这个勇者之冠当然属于你。答应我，好好保管。', Option = '我会的'}",
	[104649] = "{id = 104649, Speaker = 4789, Text = '黄金鸟蛋被村民当作是守护宝物。勇者们为了寻找它在这里与鸟人战斗，最后带回这颗鸟蛋的勇者就是所谓的天选勇者。如今它的碎片还在这片原野上，能替我找来10个吗？', Option = '{上交,2},{没兴趣,3}'}",
	[104650] = "{id = 104650, Speaker = 4789, Text = '这些木质头盔是村里的木匠们为勇士们制作的，虽然简朴，但是却十分耐用牢固。那些没能回来的勇士们的头盔应该还在原野上，找10个破碎的头盔回来给我吧。', Option = '{上交,2},{没兴趣,3}'}",
	[104651] = "{id = 104651, Speaker = 4789, Text = '这把黑曜石之刃是全村倾力用黑曜石雕琢而成的刀刃。它会被赐予天选勇者，而天选勇者会用这把刀刃守护全村的村民。若你找到破损的刀刃，请给我看看，10个就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104652] = "{id = 104652, Speaker = 4789, Text = '它原是一个大祭司制作的陶器，上面刻着当时村落的图腾，但现在已经碎成了好几块。图腾里往往记载着很多事情，如果能找到它的10个碎片给我，我此生都无憾了！', Option = '{上交,2},{没兴趣,3}'}",
	[104653] = "{id = 104653, Speaker = 4789, Text = '祭司们举办祭典时都会佩戴的面具，这些面具一般样貌都十分凶恶，祭司们佩戴面具是为了让邪祟感到恐惧不敢靠近。祭司们的用品可以看出一段历史的传承，如果你能找到10个残存的面具的话就拿过来给我吧。', Option = '{上交,2},{没兴趣,3}'}",
	[104654] = "{id = 104654, Speaker = 4789, Text = '一串在勇士们出发前佩戴在胸前的白檀木吊坠，由他们最亲近的人赠送，具有镇邪安神的功效。古老的物件一定会讲述当时的故事，你一定能替我找来10个遗失的白檀木的吧？', Option = '{上交,2},{没兴趣,3}'}",
	[104655] = "{id = 104655, Speaker = 4789, Text = '哎，果然很难吧，听说鸟人取回了这颗蛋，依旧守护着。'}",
	[104656] = "{id = 104656, Speaker = 4789, Text = '这个东西的数量应该挺多的，我自己去试着找找看吧。'}",
	[104657] = "{id = 104657, Speaker = 4789, Text = '这把刀刃镶嵌着黑曜石，即便是有些损坏，会不会也早就被人捡走了呢？'}",
	[104658] = "{id = 104658, Speaker = 4789, Text = '没有这个碎片，很难还原当时村落的情况啊……'}",
	[104659] = "{id = 104659, Speaker = 4789, Text = '面具看着是有点恐怖啦，但是仔细看的话，还挺可爱的呢！'}",
	[104660] = "{id = 104660, Speaker = 4789, Text = '这么小的物件，是挺难找的，哎……'}",
	[104661] = "{id = 104661, Speaker = 4789, Text = '别开玩笑了！你身上没有我要的东西，这样是无法还原传说的！'}",
	[104662] = "{id = 104662, Speaker = 4789, Text = '这就是黄金鸟蛋啊！真是漂亮。我得拿回去分析一下它的成分，看看它究竟是否拥有那神奇的力量。'}",
	[104663] = "{id = 104663, Speaker = 4789, Text = '在那个时代能做出这样的头盔，这个村落的人真厉害。'}",
	[104664] = "{id = 104664, Speaker = 4789, Text = '居然是真的黑曜石！传说故事果然没有骗我。'}",
	[104665] = "{id = 104665, Speaker = 4789, Text = '找到这么多真是太好了，这样我就能把它们拼起来了。'}",
	[104666] = "{id = 104666, Speaker = 4789, Text = '虽然有些坏了，但是还是能从上面找寻到历史的踪迹。'}",
	[104667] = "{id = 104667, Speaker = 4789, Text = '物件都是会说话的，你听，听到里面传来的声音了吗？'}",
	[104668] = "{id = 104668, Speaker = 4790, Text = '哎，为什么我只是一只小恶魔呢？一点都不威风……'}",
	[104669] = "{id = 104669, Speaker = 4791, Text = '我们小恶魔一族生存在这个世界上，就是要遵从自己的心意。'}",
	[104670] = "{id = 104670, Speaker = 4792, Text = '哈哈！我现在可是爆炎小恶魔维特了！看我的火叉攻击~！'}",
	[104671] = "{id = 104671, Speaker = 4793, Text = '老朽只能尽可能的帮助孩子们达成心愿了。'}",
	[104672] = "{id = 104672, Speaker = 4790, Text = '嘿！你是来这里帮助我的吗？冒险者。', Option = '{是的,2},{不是,3}'}",
	[104673] = "{id = 104673, Speaker = 4790, Text = '你相信命运吗？我一直想不明白，为什么我只是一只小恶魔……'}",
	[104674] = "{id = 104674, Speaker = 4790, Text = '我有一个远方亲戚家的哥哥，是一只特别帅气的爆炎小恶魔！'}",
	[104675] = "{id = 104675, Speaker = 4790, Text = '我真是好羡慕它啊！于是我就央求长老带我来熔岩洞窟，我也要变成一只帅气的爆炎小恶魔！'}",
	[104676] = "{id = 104676, Speaker = 4790, Text = '但长老说即使我现在一直呆在熔岩洞窟，我也是不能变成爆炎小恶魔的……'}",
	[104677] = "{id = 104677, Speaker = 4790, Text = '但是，如果将火焰之力直接注入我的体内！说不定就会成功了！'}",
	[104678] = "{id = 104678, Speaker = 4790, Text = '长老们好像在收集能帮我变身的物品，你去帮帮他们吧！他们会告诉你更多的信息！快去快去~我等不及了！'}",
	[104679] = "{id = 104679, Speaker = 4790, Text = '冒险者你太棒了！长老们和我说了！是你帮我找齐这些物品的！实在是太感谢了！我终于可以变成爆炎小恶魔啦！', Option = '不客气'}",
	[104680] = "{id = 104680, Speaker = 4790, Text = '不过长老们说，火焰之力这样直接注入我体内，哪怕有这些物品，很有可能我也无法控制……到时候……'}",
	[104681] = "{id = 104681, Speaker = 4790, Text = '但是！这是我一直以来的梦想！无论如何我都要试一试！'}",
	[104682] = "{id = 104682, Speaker = 4790, Text = '我已经准备好变身了！你离我远一点，我担心火焰之力伤到你。', Option = '别担心'}",
	[104683] = "{id = 104683, Speaker = 4790, Text = '那我开始了!'}",
	[104684] = "{id = 104684, Speaker = 0, Text = '维特的周身出现了火焰！'}",
	[104685] = "{id = 104685, Speaker = 0, Text = '成功了！'}",
	[104686] = "{id = 104686, Speaker = 4792, Text = '哇！我真的成功了！我现在是一只帅气的爆炎小恶魔了！', Option = '恭喜你'}",
	[104687] = "{id = 104687, Speaker = 4792, Text = '嘻嘻，我还没感谢你呢冒险者！这个送给你！这个徽章是长老给我的，我刚刚给它注入了火焰力量。'}",
	[104688] = "{id = 104688, Speaker = 4792, Text = '有了这个火纹徽章！你就不用害怕熔岩洞窟的灼热了！', Option = '谢谢你'}",
	[104689] = "{id = 104689, Speaker = 4792, Text = '我要赶紧去告诉长老~下次要来这里找我玩呀~'}",
	[104690] = "{id = 104690, Speaker = 1103, Text = '嗨冒险者，你看起来精神多了呢！', Option = '那当然'}",
	[104691] = "{id = 104691, Speaker = 1103, Text = '对了，我最近要去姜饼城参加朋友的生日派对，你看我这一身打扮合适吗？', Option = '这有点…'}",
	[104692] = "{id = 104692, Speaker = 1103, Text = '我也觉得不太合适…', Option = '至少换个帽子'}",
	[104693] = "{id = 104693, Speaker = 1103, Text = '嗯…那你说换什么帽子呢？', Option = '{小丑帽,3},{圣诞角,4},{黄金帽,3}'}",
	[104694] = "{id = 104694, Speaker = 1103, Text = '拜托，带这去生日派对像什么样啊喂…'}",
	[104695] = "{id = 104695, Speaker = 1103, Text = '好主意！可是我没有圣诞角…', Option = '帮你找找'}",
	[104696] = "{id = 104696, Speaker = 1103, Text = '那麻烦你咯，图纸就行，我最近收集了不少材料，刚好可以用来制作圣诞角。'}",
	[104697] = "{id = 104697, Speaker = 1103, Text = '这么快就回来啦，找到圣诞角图纸了吗？', Option = '{找到了,7},{没找到,6}'}",
	[104698] = "{id = 104698, Speaker = 1103, Text = '快点啦…离派对的日子越来越近了。'}",
	[104699] = "{id = 104699, Speaker = 1103, Text = '太好了，我这就可以开工自己做一个圣诞角！'}",
	[104700] = "{id = 104700, Speaker = 1103, Text = '等等，图纸上说制作需要359个礼盒纸…这，我好像不太够。'}",
	[104701] = "{id = 104701, Speaker = 1103, Text = '一个这么小的帽子居然需要这么多礼盒纸。我看看…'}",
	[104702] = "{id = 104702, Speaker = 1103, Text = '我还差60个礼盒纸！', Option = '交给我了'}",
	[104703] = "{id = 104703, Speaker = 1103, Text = '有点不好意思…图纸要你帮忙，材料也得麻烦你…', Option = '没关系'}",
	[104704] = "{id = 104704, Speaker = 1103, Text = ' 太棒了！我还没开始做呢你就把材料找齐了！', Option = '不客气'}",
	[104705] = "{id = 104705, Speaker = 1103, Text = '这是一点微薄的奖励，一点心意，请你收下。'}",
	[104706] = "{id = 104706, Speaker = 1106, Text = '成为了基因学者，意味着你需要掌握更多学识理论。'}",
	[104707] = "{id = 104707, Speaker = 1106, Text = '刚好，查理格最近在研究熔岩洞窟的温度常年只增不减的原因，你去协助他一起调查调查。', Option = '没问题'}",
	[104708] = "{id = 104708, Speaker = 4657, Text = '是固特伊介绍你来的吗?', Option = '是的'}",
	[104709] = "{id = 104709, Speaker = 4657, Text = '嗯，他前几天就给我说过会叫人一起协助我的研究，没想到是叫你来呀。', Option = '是啊'}",
	[104710] = "{id = 104710, Speaker = 4657, Text = '也好，是你我就放心了。我需要熔岩洞窟2层的岩浆用来观察实验。', Option = '没问题'}",
	[104711] = "{id = 104711, Speaker = 4657, Text = '速度要快！时间久了岩浆就凝固了。'}",
	[104712] = "{id = 104712, Speaker = 4657, Text = '还好，速度还挺快，岩浆还未凝固。我需要进一步观察一下。'}",
	[104713] = "{id = 104713, Speaker = 4657, Text = '咳咳…咳……', Option = '没事吧'}",
	[104714] = "{id = 104714, Speaker = 4657, Text = '没…没事。'}",
	[104715] = "{id = 104715, Speaker = 4657, Text = '没想到啊，就算是从洞窟带出来的环境下的岩浆，还是具有这么强的热量。'}",
	[104716] = "{id = 104716, Speaker = 4657, Text = '看来，熔岩洞窟的岩浆才是关键。'}",
	[104717] = "{id = 104717, Speaker = 4657, Text = '帮我收集80个冰之心，我想试试将这两种物质融合到一起会不会有什么变化。', Option = '没问题'}",
	[104718] = "{id = 104718, Speaker = 4657, Text = '材料带来了吗?', Option = '{没带来,4},{带来了,3}'}",
	[104719] = "{id = 104719, Speaker = 4657, Text = '速度要快！我在这等你。'}",
	[104720] = "{id = 104720, Speaker = 4657, Text = '80个冰之心，你是不是记错了？'}",
	[104721] = "{id = 104721, Speaker = 4657, Text = '很好，我这就开始实验。你回去报告固特伊吧，我暂时不需要帮忙了。'}",
	[104722] = "{id = 104722, Speaker = 1106, Text = '嗯…看来查理格已经有些想法了。'}",
	[104723] = "{id = 104723, Speaker = 1106, Text = '感谢你的帮助。'}",
	[104724] = "{id = 104724, Speaker = 1101, Text = '朱诺的艾因布洛克原野，最近好像不是很太平。', Option = '我听说了'}",
	[104725] = "{id = 104725, Speaker = 1101, Text = '现在已经身为符文骑士的你，也去贡献一些力量吧，同时也是你作为符文骑士后的第一项考验！', Option = '没问题'}",
	[104726] = "{id = 104726, Speaker = 1101, Text = '直接去和那里的卫兵交涉吧，他们应该是最头疼的。'}",
	[104727] = "{id = 104727, Speaker = 5950, Text = '冒险者!原野最近可不太安全，还是不要逗留。', Option = '说明来意'}",
	[104728] = "{id = 104728, Speaker = 5950, Text = '原来是这样，既然是符文骑士，那么我们也放心了，先代表朱诺皇家卫队表示感谢！', Option = '不客气'}",
	[104729] = "{id = 104729, Speaker = 5950, Text = '我们的战友纽达在原野深处站岗，我们只负责接应来往的行人以及守住朱诺城门。'}",
	[104730] = "{id = 104730, Speaker = 5950, Text = '而纽达就比较头疼了，你去看看他那里有没有什么事吧！', Option = '好的'}",
	[104731] = "{id = 104731, Speaker = 5687, Text = '该死！这些魔物怎么一个个突然这么暴躁！'}",
	[104732] = "{id = 104732, Speaker = 5687, Text = '又来了！'}",
	[104733] = "{id = 104733, Speaker = 1104, Text = '你来的正好，我正想找你。', Option = '有什么事吗'}",
	[104734] = "{id = 104734, Speaker = 1104, Text = '唉，说来话长，不过我长话短说。', Option = '…'}",
	[104735] = "{id = 104735, Speaker = 1104, Text = '你知道的，我们本身都是一名猎人，虽然创下了很多战功，但是毕竟已经有些慢慢力不从心。'}",
	[104736] = "{id = 104736, Speaker = 1104, Text = '我已经很久没看过梦罗克的金字塔，斐杨的树林风景，姜饼城的雪景，还有那里的玩具工厂，还有朱诺城，我都还没去过……', Option = '这就带你去'}",
	[104737] = "{id = 104737, Speaker = 1104, Text = '哎…哎…不是，我其实就是想看看。但不想自己亲自去啊。', Option = '…'}",
	[104738] = "{id = 104738, Speaker = 1104, Text = '非要我说这么明白吗？我就是想看看。', Option = '明白了'}",
	[104739] = "{id = 104739, Speaker = 1104, Text = '哈哈，那我等你哦。只要看看朱诺城就好，毕竟没去过…'}",
	[104740] = "{id = 104740, Speaker = 1104, Text = '对了，记得帮我带一些朱诺特有的东西。', Option = '是什么'}",
	[104741] = "{id = 104741, Speaker = 1104, Text = '当然是那里土拨鼠的大白牙！', Option = '好吧'}",
	[104742] = "{id = 104742, Speaker = 1104, Text = '嗯…这还差不多，快去吧！'}",
	[104743] = "{id = 104743, Speaker = 1104, Text = '你回来啦?快让我看看照片。'}",
	[104744] = "{id = 104744, Speaker = 1104, Text = '还有土拨鼠的大白牙，真的很可爱啊。'}",
	[104745] = "{id = 104745, Speaker = 1104, Text = '幸苦你啦，我要去给伊丝米儿看看朱诺的风景。', Option = '…'}",
	[104746] = "{id = 104746, Speaker = 1106, Text = '你总算来了，我实在忙不过来了！', Option = '怎么啦'}",
	[104747] = "{id = 104747, Speaker = 1106, Text = '是这样，前几天艾奇来找过我。说最近冒险者们对机甲的需求太大了，他的备货严重不足了。'}",
	[104748] = "{id = 104748, Speaker = 1106, Text = '你去帮我看看吧，我这边实在抽不出空来。', Option = '好的'}",
	[104749] = "{id = 104749, Speaker = 4683, Text = '是固特伊派你来的吗？', Option = '是的'}",
	[104750] = "{id = 104750, Speaker = 4683, Text = '终于有人来帮我了，近段时间冒险者转职机匠的越来越多，我的机甲已经供不应求了。', Option = '我能帮什么'}",
	[104751] = "{id = 104751, Speaker = 4683, Text = '现在机甲备货严重不足，我需要制造更多的机甲，现在还缺一些主要材料。', Option = '什么材料'}",
	[104752] = "{id = 104752, Speaker = 4683, Text = '机甲最主要的材料，钢铁。可以帮我去收集一些回来吗？', Option = '没问题'}",
	[104753] = "{id = 104753, Speaker = 4683, Text = '你终于回来了，钢铁已经快用完了。东西带来了吗?', Option = '带来了'}",
	[104754] = "{id = 104754, Speaker = 4683, Text = '这数量不够啊冒险者，要100个钢铁…'}",
	[104755] = "{id = 104755, Speaker = 4683, Text = '很好！我可以继续开工了。'}",
	[104756] = "{id = 104756, Speaker = 4683, Text = '你能帮我把这些钢铁加工一下吗？', Option = '没问题'}",
	[104757] = "{id = 104757, Speaker = 4683, Text = '效率真快啊，谢谢你的帮助。'}",
	[104758] = "{id = 104758, Speaker = 1106, Text = '你完成的很棒冒险者，也算是帮了我一个大忙。'}",
	[104759] = "{id = 104759, Speaker = 1102, Text = '传说有一位我们的法师前辈，倾尽一生研究魔法的奥秘，去世之前还在捣鼓他的魔法瓶。'}",
	[104760] = "{id = 104760, Speaker = 1102, Text = '据说去世的很突然，他将自己毕生的研究成果都藏在了自己的帽子里。'}",
	[104761] = "{id = 104761, Speaker = 1102, Text = '后来，这些魔法竟然在帽子里产生了效应，与帽子融为一体，这顶帽子顿时有了生命。'}",
	[104762] = "{id = 104762, Speaker = 1102, Text = '当然这只是个传说，不过好像还真的有这么一顶帽子，只不过外形相似吧。', Option = '原来如此'}",
	[104763] = "{id = 104763, Speaker = 1102, Text = '其实我对那顶帽子挺感兴趣的，不过除此之外我对这帽子一无所知。去帮我问问西芙吧，也许他能知道些什么。', Option = '没问题'}",
	[104764] = "{id = 104764, Speaker = 5999, Text = '什么？你是说纯白尖塔么?', Option = '纯白尖塔？'}",
	[104765] = "{id = 104765, Speaker = 5999, Text = '是的，不过那可不是什么和魔法融为一体的帽子，只是一顶普普通通的法师帽罢了。是老一辈大法师们给杰出的后辈的奖励。', Option = '原来如此'}",
	[104766] = "{id = 104766, Speaker = 5999, Text = '如果杜赫塔真的这么感兴趣的话，我可以制作出这顶法师帽的图纸。不过需要一些材料。', Option = '没问题'}",
	[104767] = "{id = 104767, Speaker = 5999, Text = '倒也不是很难，需要收集60个魔女星沙，就可以制作这种图纸。', Option = '好的'}",
	[104768] = "{id = 104768, Speaker = 5999, Text = '材料带了了么？', Option = '带来了'}",
	[104769] = "{id = 104769, Speaker = 5999, Text = '60个魔女星沙，你是不是记错了。'}",
	[104770] = "{id = 104770, Speaker = 5999, Text = '很好，回去通知杜赫塔。我现在就开始制作图纸。'}",
	[104771] = "{id = 104771, Speaker = 1102, Text = '真的吗?西芙已经正在制作图纸了？', Option = '是的'}",
	[104772] = "{id = 104772, Speaker = 1102, Text = '不过有点可惜，居然不是传说中的魔法帽。'}",
	[104773] = "{id = 104773, Speaker = 1102, Text = '现在就等杜赫塔将图纸制作出来吧。'}",
	[104774] = "{id = 104774, Speaker = 1105, Text = '冒险者，你来的正是时候！', Option = '嗯？'}",
	[104775] = "{id = 104775, Speaker = 1105, Text = '艾因布洛克原野的魔物最近不知道怎么了变得很暴躁，伤害了很多过往的居民。'}",
	[104776] = "{id = 104776, Speaker = 1105, Text = '还有很多朱诺的居民被困在艾因布洛克原野，我们已经派修罗们前往支援，希望你也能协助我们。', Option = '没问题'}",
	[104777] = "{id = 104777, Speaker = 5579, Text = '是来救我们的吗？'}",
	[104778] = "{id = 104778, Speaker = 5579, Text = '谢谢你冒险者，不过回去的路上还有很多魔物，可以带我回去吗！', Option = '好的'}",
	[104779] = "{id = 104779, Speaker = 5579, Text = '谢谢你冒险者，我现在安全了。', Option = '不客气'}",
	[104780] = "{id = 104780, Speaker = 1105, Text = '看来形式还是挺严峻的，我已经安排人去给居民们急救伤口。'}",
	[104781] = "{id = 104781, Speaker = 1105, Text = '朱诺特有的药草在原野，但是原野的情况不容乐观，所以现在朱诺的药草有些不够用了，你去原野采集些药草给朱诺的查理格，他会处理的。', Option = '没问题'}",
	[104782] = "{id = 104782, Speaker = 4657, Text = '药草带来了？太好了！'}",
	[104783] = "{id = 104783, Speaker = 4657, Text = '这数量不够，动作放快，救人要紧！'}",
	[104784] = "{id = 104784, Speaker = 4657, Text = '很好，原野那边的局势已经稳住了，现在只要把这些药草分发下去就好了。'}",
	[104785] = "{id = 104785, Speaker = 4657, Text = '谢谢你的帮助冒险者！'}",
	[104786] = "{id = 104786, Speaker = 1105, Text = '愿朱诺的人民都能平安回家…'}",
	[104787] = "{id = 104787, Speaker = 4791, Text = '这把弓是火焰精灵的最强弓箭手的武器。这把弓上燃烧的火焰具有非常强大的元素之力，可以帮助维特获得火焰之力。如果你能找到10个火焰之弓的话，就带来给我吧。', Option = '{上交,2},{没兴趣,3}'}",
	[104788] = "{id = 104788, Speaker = 4791, Text = '火晶球里蕴含着火焰精灵长老的精粹力量，在它的帮助下，维特可以更轻松的获得火焰之力。如果你能找到10个淬炼火晶的话，一定要带来给我。', Option = '{上交,2},{没兴趣,3}'}",
	[104789] = "{id = 104789, Speaker = 4791, Text = '火光剑是剑士克里斯的佩剑，哎，它是个正直的精灵，但是为了维特，我们还需要这把剑的帮助，这样维特可以少受些痛苦。如果你能找到10个炙热的剑的话，一定要带来给我。', Option = '{上交,2},{没兴趣,3}'}",
	[104790] = "{id = 104790, Speaker = 4791, Text = '不知道火灵眼还能不能找到了，它所蕴含的火焰力量比你想象的还要大，如果有它的话，就可以让维特更快的完成转变的过程。你能帮我去找来10个火灵晶石吗？', Option = '{上交,2},{没兴趣,3}'}",
	[104791] = "{id = 104791, Speaker = 4791, Text = '果然很难啊，毕竟它在最强的火焰精灵弓箭手手里。'}",
	[104792] = "{id = 104792, Speaker = 4791, Text = '听说是个很漂亮的晶球，我对它充满了好奇。'}",
	[104793] = "{id = 104793, Speaker = 4791, Text = '哎，克里斯是个厉害的剑士，找不到也不是你的错。'}",
	[104794] = "{id = 104794, Speaker = 4791, Text = '本来就是抱着试试的心态，果然不存在于这个世上了嘛……'}",
	[104795] = "{id = 104795, Speaker = 4791, Text = '年轻人，做人还是诚实点好，如果没有，再去找就可以了。'}",
	[104796] = "{id = 104796, Speaker = 4791, Text = '这是真的么？你是怎么从火焰精灵弓箭手那里获得的？果然是把好弓。'}",
	[104797] = "{id = 104797, Speaker = 4791, Text = '样子虽然还是水晶球的样子，但是你看着美丽而又强大的火焰……'}",
	[104798] = "{id = 104798, Speaker = 4791, Text = '充满着力量的火焰和充满着信念的剑刃，这肯定是火光剑没错了。'}",
	[104799] = "{id = 104799, Speaker = 4791, Text = '火灵眼居然真的还存在于这个世界上！年轻人，辛苦了。'}",
	[104800] = "{id = 104800, Speaker = 4793, Text = '这卷轴是一个火焰精灵撰写的，可惜别的精灵不能理解它。但对我们而言这是个好东西，可以帮助维特更好的掌握火焰力量。你能帮忙找到它的页纸吗？10张就够了。', Option = '{上交,2},{没兴趣,3}'}",
	[104801] = "{id = 104801, Speaker = 4793, Text = '火焰宝石由一个火焰精灵制作，宝石内蕴含着非常强大的火焰力量，只要握在手里，就能让自己的法术变成火属性。你能把它的边角料找来给我吗？10个就可以了。', Option = '{上交,2},{没兴趣,3}'}",
	[104802] = "{id = 104802, Speaker = 4793, Text = '火焰鸟蛋是种富含火元素的食物，吃下去体内就会变得暖暖的。但是一般人类很难接近火焰鸟藏匿鸟蛋的地方，巧合的是有个火焰精灵有很多破损的鸟蛋，你去取10个给我吧。', Option = '{上交,2},{没兴趣,3}'}",
	[104803] = "{id = 104803, Speaker = 4793, Text = '这个项链其实是一颗远古的火种，是火焰力量的源泉，如果维特在它的加持下，能更好的将火焰力量融于体内。如果你能找到10个火种的话，请带给我。', Option = '{上交,2},{没兴趣,3}'}",
	[104804] = "{id = 104804, Speaker = 4793, Text = '如果能找到它们，对你也是很有帮助的。'}",
	[104805] = "{id = 104805, Speaker = 4793, Text = '我曾经有幸看到过它的边角料碎片，真是充满火焰力量的宝石啊！'}",
	[104806] = "{id = 104806, Speaker = 4793, Text = '实在不行，我得亲自去火焰鸟的洞穴一趟啊……'}",
	[104807] = "{id = 104807, Speaker = 4793, Text = '天神的火种，我们必须心存敬畏才有可能找到它……'}",
	[104808] = "{id = 104808, Speaker = 4793, Text = '年轻人，做人还是诚实点好，如果没有，再去找就可以了。'}",
	[104809] = "{id = 104809, Speaker = 4793, Text = '里面记载的法术果然精妙，年轻人你也看一看吧。'}",
	[104810] = "{id = 104810, Speaker = 4793, Text = '这可以说是一颗最完美的火焰宝石了，你也来感受一下它的力量。'}",
	[104811] = "{id = 104811, Speaker = 4793, Text = '如果能多拿几个，老朽也想尝尝它的美味……'}",
	[104812] = "{id = 104812, Speaker = 4793, Text = '这就是天神赐予的火种啊，我们要对它心存敬畏。'}",
	[104813] = "{id = 104813, Speaker = 1103, Text = '礼盒纸带来了吗？', Option = '带来了'}",
	[104814] = "{id = 104814, Speaker = 1103, Text = '这数量不够啊冒险者，要60个礼盒纸。'}",
	[104815] = "{id = 104815, Speaker = 1103, Text = '图纸呢？路上搞丢了吗？'}",
	[104816] = "{id = 104816, Speaker = 5687, Text = '谢谢你冒险者，朱诺的援兵马上就到，这里很危险，你快回去吧！', Option = '说明来意'}",
	[104817] = "{id = 104817, Speaker = 5687, Text = '原来如此，十分感谢！我正缺人手。'}",
	[104818] = "{id = 104818, Speaker = 5687, Text = '站在这不停击退暴躁的魔物也不是办法，我想在周围布一圈火墙，至少我就不用一直面对不停出现的魔物了。', Option = '没问题'}",
	[104819] = "{id = 104819, Speaker = 5687, Text = '大功告成，你可帮了我大忙！'}",
	[104820] = "{id = 104820, Speaker = 5687, Text = '不过最后还得请求你帮我个小忙。', Option = '尽管说吧'}",
	[104821] = "{id = 104821, Speaker = 5687, Text = '我挚爱的女孩康娜，她得知我这边的消息后十分担心。请你帮我给她报个平安，告诉她这边任务结束后我就回去找她。', Option = '没问题'}",
	[104822] = "{id = 104822, Speaker = 4778, Text = '是纽达叫你来的吗?', Option = '说明情况'}",
	[104823] = "{id = 104823, Speaker = 4778, Text = '噢，我的纽达…愿他平安无事。'}",
	[104824] = "{id = 104824, Speaker = 4778, Text = '谢谢你冒险者，我就在这等他回来！'}",
	[104825] = "{id = 104825, Speaker = 1101, Text = '很好！完成的很漂亮！'}",
	[104826] = "{id = 104826, Speaker = 1101, Text = '相信你已经完全兼备了符文骑士的精神！'}",
	[104827] = "{id = 104827, Speaker = 5579, Text = '啊，终于有人来了，救救我，冒险者！'}",
	[104828] = "{id = 104828, Speaker = 1103, Text = '艾因布洛克原野的魔物最近非常躁动，我想这与环境有关。'}",
	[104829] = "{id = 104829, Speaker = 1103, Text = '毕竟熔岩洞窟的温度已经影响到原野，再加上朱诺长期的燥热，使得那里的魔物们暴躁不安。'}",
	[104830] = "{id = 104830, Speaker = 1103, Text = '根据查理格的研究报告，冰之心可以暂时缓解熔岩洞窟的燥热。', Option = '我明白了'}",
	[104831] = "{id = 104831, Speaker = 1103, Text = '我需要60个冰之心，你收集好后再来找我。', Option = '没问题'}",
	[104832] = "{id = 104832, Speaker = 1103, Text = '辛苦你了。'}",
	[104833] = "{id = 104833, Speaker = 1103, Text = '你回来了？冰之心带来了么？', Option = '带来了'}",
	[104834] = "{id = 104834, Speaker = 1103, Text = '60个冰之心，你是不是记错了？'}",
	[104835] = "{id = 104835, Speaker = 1103, Text = '现在需要将这些冰之心拿给查理格融合才能使用。', Option = '好的'}",
	[104836] = "{id = 104836, Speaker = 1103, Text = '快去吧！'}",
	[104837] = "{id = 104837, Speaker = 4657, Text = '我已经听伊斯米儿说了，快将冰之心给我吧。', Option = '好的'}",
	[104838] = "{id = 104838, Speaker = 4657, Text = '没问题了，快将这些投进熔岩洞窟的岩浆里吧。'}",
	[104839] = "{id = 104839, Speaker = 0, Text = '岩浆突然剧烈翻腾一阵后恢复了平静。'}",
	[104840] = "{id = 104840, Speaker = 0, Text = '不知道是不是心里作用，感觉好像凉快了一些。'}",
	[104841] = "{id = 104841, Speaker = 1103, Text = '你完成的很出色，冒险者！'}",
	[104842] = "{id = 104842, Speaker = 1103, Text = '不过熔岩洞窟的环境还是应该长期维护，否则过不了多久魔物们又会开始躁动。'}",
	[104843] = "{id = 104843, Speaker = 4794, Text = '真相隐藏在沉默的岁月中。'}",
	[104844] = "{id = 104844, Speaker = 4795, Text = '任何蛛丝马迹都逃不过我卡戴珊的眼睛。'}",
	[104845] = "{id = 104845, Speaker = 4796, Text = '沉默的证人，才是最可靠的！'}",
	[104846] = "{id = 104846, Speaker = 4794, Text = '你愿意帮助我探查被岁月埋没的真相嘛？', Option = '{愿意,2},{不愿意,3}', Emoji = 7}",
	[104847] = "{id = 104847, Speaker = 4794, Text = '冷漠的家伙，我和你没什么好说的！'}",
	[104848] = "{id = 104848, Speaker = 4794, Text = '在25年前的艾因布洛克原野，某座村庄曾发生过一场致命的瘟疫。', Option = '瘟疫？'}",
	[104849] = "{id = 104849, Speaker = 4794, Text = '没错，但在一周之内，各种巧合拯救了被瘟疫感染的村民。', Option = '真神奇'}",
	[104850] = "{id = 104850, Speaker = 4794, Text = '同时，某个朱诺著名公司的部长带着机密文件离奇失踪。', Option = '真神秘'}",
	[104851] = "{id = 104851, Speaker = 4794, Text = '我的第六感告诉我，这两件事绝不是巧合！', Option = '的确可疑'}",
	[104852] = "{id = 104852, Speaker = 4794, Text = '原野的圣天使波利一族有收集人类旧物品的习性。'}",
	[104853] = "{id = 104853, Speaker = 4794, Text = '你愿意协助我的两位探员一起搜集证物，探查真相吗？', Option = '没问题'}",
	[104854] = "{id = 104854, Speaker = 4794, Text = '好，让我们一起探查被历史湮没的事件真相吧!'}",
	[104855] = "{id = 104855, Speaker = 4794, Text = '看来你已经找齐了线索。', Option = '没错'}",
	[104856] = "{id = 104856, Speaker = 4794, Text = '让我最后来考考你是否看透了真相吧。', Option = '好啊'}",
	[104857] = "{id = 104857, Speaker = 4794, Text = '你觉得这场瘟疫的源头是什么？', Option = '{邪恶诅咒,2},{工业污染,3},{超级病菌,2}'}",
	[104858] = "{id = 104858, Speaker = 4794, Text = '看来你对线索的掌握程度还不够噢，再试一次吧。'}",
	[104859] = "{id = 104859, Speaker = 4794, Text = '哈哈，这个问题果然难不倒你，准备好下一题了吗？', Option = '没问题'}",
	[104860] = "{id = 104860, Speaker = 4794, Text = '好，根据线索推理后，你认为是谁将解药放在了村庄中呢？', Option = '{村庄长老,4},{卡尔大总统,4},{艾恩·格林,5}'}",
	[104861] = "{id = 104861, Speaker = 4794, Text = '你和我想的不一样呢，再看看线索推理一下吧。'}",
	[104862] = "{id = 104862, Speaker = 4794, Text = '真厉害啊，我果然没有看错你，最后一题可是有难度的噢。', Option = '放马过来', Emoji = 5}",
	[104863] = "{id = 104863, Speaker = 4794, Text = '你能猜到艾恩·格林部长的女儿是谁嘛？', Option = '{安妮·格林,6},{卡戴珊,7},{赛尼亚,7}'}",
	[104864] = "{id = 104864, Speaker = 4794, Text = '答案或许远在天边近在眼前噢，结合线索再想想看吧。'}",
	[104865] = "{id = 104865, Speaker = 4794, Text = '果然被你看出来了，没错，我就是失踪部长的女儿。'}",
	[104866] = "{id = 104866, Speaker = 4794, Text = '谢谢你帮我调查关于我父亲往事的真相。', Option = '不客气'}",
	[104867] = "{id = 104867, Speaker = 4794, Text = '在所有人都觉得我父亲是畏罪潜逃时，我坚信他是为了完成必须完成的事而离开。'}",
	[104868] = "{id = 104868, Speaker = 4794, Text = '（手中握着发卡）因为父亲他，是个善良而执着的人啊。'}",
	[104869] = "{id = 104869, Speaker = 4795, Text = '如果你能帮忙找到某个感染者所写的日记，这一定能成为非常关键的线索，拜托你了！', Option = '{上交,2},{没见到,3}'}",
	[104870] = "{id = 104870, Speaker = 4795, Text = '遭受瘟疫的村庄有一位拄着拐杖的长老，你能帮我把长老的拐杖找来么？', Option = '{上交,2},{没见到,3}'}",
	[104871] = "{id = 104871, Speaker = 4795, Text = '要是你能把被感染者喝过的空瓶带给我，我们或许就能从中知道更多的线索，拜托了。', Option = '{上交,2},{没见到,3}'}",
	[104872] = "{id = 104872, Speaker = 4795, Text = '…你真的有认真帮我找吗？'}",
	[104873] = "{id = 104873, Speaker = 4795, Text = '这根拐杖是我们了解事件的钥匙，再努力找找看吧！'}",
	[104874] = "{id = 104874, Speaker = 4795, Text = '这个空瓶是解开瘟疫治愈之谜的关键线索，请务必找到！'}",
	[104875] = "{id = 104875, Speaker = 4795, Text = '追查线索可不是小孩子的侦探游戏，我最讨厌撒谎的人！'}",
	[104876] = "{id = 104876, Speaker = 4795, Text = '哎呀，终于找到这本日记了！'}",
	[104877] = "{id = 104877, Speaker = 4795, Text = '我将长老的回忆从拐杖中提取出来了，好好研究一下吧！'}",
	[104878] = "{id = 104878, Speaker = 4795, Text = '这个空瓶里有很强烈的情感呢，请好好调查一下！'}",
	[104879] = "{id = 104879, Speaker = 4796, Text = '曾经有个属于雷根贝勒公司的巨大机器矗立在村庄旁，你能帮我收集一份机器残片吗？', Option = '{上交,2},{没见到,3}'}",
	[104880] = "{id = 104880, Speaker = 4796, Text = '据说部长离开时带走了那家公司的机密文件，这么重要的证物你可得用心找找。', Option = '{上交,2},{没见到,3}'}",
	[104881] = "{id = 104881, Speaker = 4796, Text = '据说那个部长有一位小女儿，要是发现他女儿的发卡就马上带给我!', Option = '{上交,2},{没见到,3}'}",
	[104882] = "{id = 104882, Speaker = 4796, Text = '这个机器残片能为我们提供重要的证据，再努力找找吧！'}",
	[104883] = "{id = 104883, Speaker = 4796, Text = '这份机密文件是解开真相的钥匙，请务必找到它！'}",
	[104884] = "{id = 104884, Speaker = 4796, Text = '这个发卡对于我们了解事情的真相很重要，拜托你一定要找到它！'}",
	[104885] = "{id = 104885, Speaker = 4796, Text = '请你不要给我虚假的情报！'}",
	[104886] = "{id = 104886, Speaker = 4796, Text = '我们这下离真相更进一步了。'}",
	[104887] = "{id = 104887, Speaker = 4796, Text = '我们已经握有打开真相大门的钥匙了。'}",
	[104888] = "{id = 104888, Speaker = 4796, Text = '这个发卡上承载了很多的情感呢。'}",
	[104889] = "{id = 104889, Speaker = 1101, Text = '恭喜你冒险者，成为皇家卫士后的你看起来精神多了！', Option = '那是当然'}",
	[104890] = "{id = 104890, Speaker = 1101, Text = '现在你已经拥有资格驾驭狮鹫了，可是这两天个别狮鹫可不是很太平。', Option = '怎么了'}",
	[104891] = "{id = 104891, Speaker = 1101, Text = '正如你看到的那样，狮鹫是一种天生骄傲的物种，有一些狮鹫认为就算是皇家卫士，也没有资格驾驭它们。'}",
	[104892] = "{id = 104892, Speaker = 1101, Text = '于是个别顽固分子开始捣乱。', Option = '…'}",
	[104893] = "{id = 104893, Speaker = 1101, Text = '是不是觉得很搞笑，我可是很认真的。'}",
	[104894] = "{id = 104894, Speaker = 1101, Text = '狮鹫自身就拥有不俗的智慧，它们也有自己的想法。', Option = '好吧'}",
	[104895] = "{id = 104895, Speaker = 1101, Text = '刚刚转职成为皇家骑士的嘉特勒，转职后兴冲冲去租赁了一只狮鹫。'}",
	[104896] = "{id = 104896, Speaker = 1101, Text = '现在已经被折磨的开始自己徒步跑路了。', Option = '噗…', Emoji = 17}",
	[104897] = "{id = 104897, Speaker = 1101, Text = '是啊，我们也很无奈，所以你去帮忙看看吧。', Option = '没问题'}",
	[104898] = "{id = 104898, Speaker = 1101, Text = '嘉特勒在国界检查站，你去找找吧。'}",
	[104899] = "{id = 104899, Speaker = 5581, Text = '你就这么倔么？我把你租来不是给我找烦恼的好么？'}",
	[104900] = "{id = 104900, Speaker = 5582, Text = '咕咕咕…嗷…'}",
	[104901] = "{id = 104901, Speaker = 5581, Text = '你再这样，我可要把你送回去了！让你回到那又热又闷的狮鹫棚没有自由的呆着！'}",
	[104902] = "{id = 104902, Speaker = 5582, Text = '咕咕…'}",
	[104903] = "{id = 104903, Speaker = 5581, Text = '该死！那顽固的狮鹫怎么都不听我话，这次还直接跑了！', Emoji = 12}",
	[104904] = "{id = 104904, Speaker = 5581, Text = '我一定要找到它把它退回去，简直是花钱买罪受。费尽心思成为皇家卫士结果就给我这么个玩意儿。', Option = '劝说', Emoji = 12}",
	[104905] = "{id = 104905, Speaker = 5581, Text = '哎，就是说说气话，毕竟也带了它好几天了。你能帮我把它找回来吗？', Option = '没问题', Emoji = 15}",
	[104906] = "{id = 104906, Speaker = 5581, Text = '我刚才看见它往检查站桥那边跑了，可以先去那里看看。'}",
	[104907] = "{id = 104907, Speaker = 5582, Text = '咕…咕咕！'}",
	[104908] = "{id = 104908, Speaker = 5582, Text = '咕咕…'}",
	[104909] = "{id = 104909, Speaker = 5581, Text = '小心！'}",
	[104910] = "{id = 104910, Speaker = 5582, Text = '！！！！', Emoji = 7}",
	[104911] = "{id = 104911, Speaker = 5582, Text = '咕咕……咕！'}",
	[104912] = "{id = 104912, Speaker = 5582, Text = '嗷！'}",
	[104913] = "{id = 104913, Speaker = 5581, Text = '咳…还好，皇家卫士的铠甲帮助我抵挡下了那一下攻击。'}",
	[104914] = "{id = 104914, Speaker = 5582, Text = '咕咕……', Emoji = 24}",
	[104915] = "{id = 104915, Speaker = 5581, Text = '呵，怎么，你现在知道关心我了？'}",
	[104916] = "{id = 104916, Speaker = 5582, Text = '咕…', Emoji = 16}",
	[104917] = "{id = 104917, Speaker = 5581, Text = '我没事，现在，要跟我回去么？'}",
	[104918] = "{id = 104918, Speaker = 5582, Text = '咕……'}",
	[104919] = "{id = 104919, Speaker = 5581, Text = '以后听我话么？'}",
	[104920] = "{id = 104920, Speaker = 5582, Text = '咕…'}",
	[104921] = "{id = 104921, Speaker = 5581, Text = '我们走吧。', Emoji = 26}",
	[104922] = "{id = 104922, Speaker = 5583, Text = '感谢你，我和我的狮鹫重归于好了。', Option = '不客气'}",
	[104923] = "{id = 104923, Speaker = 5583, Text = '同为皇家卫士，我们要一起努力！', Option = '那是当然'}",
	[104924] = "{id = 104924, Speaker = 5583, Text = '我走了！再见！', Emoji = 20}",
	[104925] = "{id = 104925, Speaker = 1101, Text = '怎么样，嘉特勒的狮鹫还是不听话么？'}",
	[104926] = "{id = 104926, Speaker = 1101, Text = '真是戏剧性的发展啊，总得有一个契机让双方互相打开心扉。'}",
	[104927] = "{id = 104927, Speaker = 1101, Text = '作为皇家卫士的你，以后也要好好对待自己的狮鹫哦！', Emoji = 20}",
	[104928] = "{id = 104928, Speaker = 1105, Text = '谢天谢地，你总是在我最需要人的时候出现。', Option = '…', Emoji = 17}",
	[104929] = "{id = 104929, Speaker = 1105, Text = '那我就直接说了，朱诺艾因布洛克原野的魔物躁动，局势有些无法控制。'}",
	[104930] = "{id = 104930, Speaker = 1105, Text = '不少路过的居民被困，还有些负伤无法继续行动。我已命令大主教们前往支援。'}",
	[104931] = "{id = 104931, Speaker = 1105, Text = '你也去吧，那边依旧需要大量人手。', Option = '{没问题,3},{我有事,2}'}",
	[104932] = "{id = 104932, Speaker = 1105, Text = '负伤的居民在原野深处，你先去查理格那里领取药草。'}",
	[104933] = "{id = 104933, Speaker = 4657, Text = '是莱伊娜丝派你来的吗？', Option = '是的'}",
	[104934] = "{id = 104934, Speaker = 4657, Text = '你来的正是时候，大主教们刚刚给我送来一批药草。'}",
	[104935] = "{id = 104935, Speaker = 4657, Text = '我需要你拿些药草去原野给负伤的居民简单的处理一下，使他们能够正常的行动通过城门回到朱诺城。', Option = '没问题'}",
	[104936] = "{id = 104936, Speaker = 5584, Text = '我没事了！谢谢你冒险者，我这就回朱诺城！', Option = '去吧  ', Emoji = 17}",
	[104937] = "{id = 104937, Speaker = 5585, Text = '太好了，没有你我都不知道该怎么办了。', Option = '不客气', Emoji = 9}",
	[104938] = "{id = 104938, Speaker = 5586, Text = '谢谢你救了我，我愿一生陪着你来报答你！', Option = '{还是算了,4},{好啊好啊,5}', Emoji = 2}",
	[104939] = "{id = 104939, Speaker = 5584, Text = '我们被前面的魔物挡住了去路。'}",
	[104940] = "{id = 104940, Speaker = 5585, Text = '太好了，这下我们就畅通无阻了。谢谢你，我们走了！', Emoji = 17}",
	[104941] = "{id = 104941, Speaker = 4657, Text = '很好！不过局势依旧还没稳定。'}",
	[104942] = "{id = 104942, Speaker = 4657, Text = '防止未开始躁动的魔物受到影响，得先清除一些以免后患。'}",
	[104943] = "{id = 104943, Speaker = 4657, Text = '麻烦你再去清除一些土拨鼠，防止它们攻击平民。'}",
	[104944] = "{id = 104944, Speaker = 4657, Text = '干的漂亮冒险者！', Emoji = 20}",
	[104945] = "{id = 104945, Speaker = 4657, Text = '你歇会吧。我会继续关注原野的情况。还有情况的话希望你能继续伸出援助之手。', Option = '好的'}",
	[104946] = "{id = 104946, Speaker = 5586, Text = '天呐，我可不想死在这里！'}",
	[104947] = "{id = 104947, Speaker = 5585, Text = '这帮土拨鼠真是疯了!'}",
	[104948] = "{id = 104948, Speaker = 5584, Text = '可恶，我连土拨鼠都打不过了嘛…'}",
	[104949] = "{id = 104949, Speaker = 5581, Text = '摊上这只狮鹫，我真是倒了大霉…'}",
	[104950] = "{id = 104950, Speaker = 7369, Text = '下周的考试怎么办……'}",
	[104951] = "{id = 104951, Speaker = 7370, Text = '下周的考试怎么办……'}",
	[104952] = "{id = 104952, Speaker = 7371, Text = '……'}",
	[104953] = "{id = 104953, Speaker = 7372, Text = '……'}",
	[104954] = "{id = 104954, Speaker = 7373, Text = '……'}",
	[104955] = "{id = 104955, Speaker = 7374, Text = '……'}",
	[104956] = "{id = 104956, Speaker = 7375, Text = '……'}",
	[104957] = "{id = 104957, Speaker = 7376, Text = '下周的考试怎么办……'}",
	[104958] = "{id = 104958, Speaker = 7377, Text = '世界即将进入一个新的纪元，人类终会成为至高的主宰。'}",
