	[104979] = "{id = 104979, Speaker = 5999, Text = '完全不明白。'}",
	[104980] = "{id = 104980, Speaker = 5999, Text = '不过有一点很明确。'}",
	[104981] = "{id = 104981, Speaker = 5999, Text = '这本笔记里的内容是用一种古老的墨水书写的。'}",
	[104982] = "{id = 104982, Speaker = 5999, Text = '这种墨水印迹一旦干了，肉眼是看不到的。', Option = '原来如此'}",
