	[104984] = "{id = 104984, Speaker = 5999, Text = '真是没办法，我们这里最差的学生。'}",
	[104985] = "{id = 104985, Speaker = 5999, Text = '都知道这种药水如何制作。', Option = '请求'}",
	[104986] = "{id = 104986, Speaker = 5999, Text = '去找我的学生埃文，'}",
	[104987] = "{id = 104987, Speaker = 5999, Text = '我可不像你们这样空闲。'}",
	[104988] = "{id = 104988, Speaker = 7369, Text = '噢！我的天！不要突然站在我背后！', Option = '抱歉', Emoji = 1}",
	[104989] = "{id = 104989, Speaker = 7369, Text = '原来是塞壬的朋友…'}",
	[104990] = "{id = 104990, Speaker = 7369, Text = '塞壬还在学校的时候很照顾我，我愿意帮你。', Option = '谢谢', Emoji = 9}",
