	[104992] = "{id = 104992, Speaker = 7369, Text = '让我想想…'}",
	[104993] = "{id = 104993, Speaker = 7369, Text = '嗯…', Option = '是什么', Emoji = 14}",
	[104994] = "{id = 104994, Speaker = 7369, Text = '我忘记了。', Emoji = 19}",
	[104995] = "{id = 104995, Speaker = 7369, Text = '不过最里面的三排书架应该关于显影药水的书籍。'}",
	[104996] = "{id = 104996, Speaker = 0, Text = '《远古魔法》，这是一些古代学者对某些未知生物做的研究，但是用了未知的语言，你根本看不懂它，不过不要紧，这和显影药水无关。'}",
	[104997] = "{id = 104997, Speaker = 0, Text = '《亡灵之书》有关古代古神的记载与对未来的预言，又称“亡灵断章”看起来很可怕，但你不需要它。'}",
	[104998] = "{id = 104998, Speaker = 0, Text = '《灵药制作》第一章：显影药水，看来就是它了。'}",
	[104999] = "{id = 104999, Speaker = 7369, Text = '噢~对了，就是这本书。'}",
	[105000] = "{id = 105000, Speaker = 7369, Text = '对了对了！', Option = '什么'}",
	[105001] = "{id = 105001, Speaker = 7369, Text = '只要一些食人草再加一点电磁蘑菇！'}",
	[105002] = "{id = 105002, Speaker = 7369, Text = '和我前天记过的一样~我怎么给忘了，哈哈哈！', Option = '食人草？', Emoji = 9}",
	[105003] = "{id = 105003, Speaker = 7369, Text = '没错，噢对，你不是这里的人。'}",
