	[105007] = "{id = 105007, Speaker = 7369, Text = '跟我来！'}",
	[105008] = "{id = 105008, Speaker = 7369, Text = '就是这些，冒险者，就是这些！', Option = '该怎么做'}",
	[105009] = "{id = 105009, Speaker = 7369, Text = '嗯...要小心，这些草可以十分危险。', Option = '危险？'}",
	[105010] = "{id = 105010, Speaker = 7369, Text = '我们得先把叶子打下来，我们不需要它的叶子。'}",
