	[105124] = "{id = 105124, Speaker = 500260, Text = '让我拉一曲离别的悲伤小调'}",
	[105125] = "{id = 105125, Speaker = 500260, Text = '噢，多么富丽堂皇的地方，让我来献上一曲！'}",
	[105126] = "{id = 105126, Speaker = 500260, Text = '我要为这美妙的时刻拉一曲欢乐颂！'}",
	[105127] = "{id = 105127, Speaker = 500260, Text = '这时候，就该默默地拉一曲破碎之梦！'}",
	[105128] = "{id = 105128, Speaker = 500260, Text = '这是我用练琴的休息时间，为主人准备的惊喜唷！'}",
	[105129] = "{id = 105129, Speaker = 500260, Text = '啦啦啦~拉一曲升级的颂歌！'}",
	[105130] = "{id = 105130, Speaker = 500260, Text = '我的旋律停止了...'}",
	[105131] = "{id = 105131, Speaker = 500260, Text = '主人的爱，是我艺术灵感的来源！'}",
	[105132] = "{id = 105132, Speaker = 500260, Text = '吃饱了才有力气演奏！'}",
	[105133] = "{id = 105133, Speaker = 500260, Text = '再吃下去，我的肚子就要把小提琴顶走了。'}",
	[105134] = "{id = 105134, Speaker = 500260, Text = '为了迎接主人归来，特此献上一曲！'}",
	[105135] = "{id = 105135, Speaker = 500260, Text = '进入工会！收腹、挺胸、抬头、目视前方，两肩向后张！'}",
	[105136] = "{id = 105136, Speaker = 500260, Text = '努力付出终归会有应得的回报！'}",
	[105137] = "{id = 105137, Speaker = 500260, Text = '为mvp拉上一首终焉之曲'}",
	[105138] = "{id = 105138, Speaker = 500260, Text = '我这里可以随时点播聊天的背景音乐噢'}",
	[105139] = "{id = 105139, Speaker = 500260, Text = '主人升级后更具气质与美了呢！'}",
	[105140] = "{id = 105140, Speaker = 500260, Text = '主人，我会为你拉一曲挽歌...'}",
	[105141] = "{id = 105141, Speaker = 500260, Text = '抱着侥幸心理可不行啊，主人！'}",
	[105142] = "{id = 105142, Speaker = 500260, Text = '主人，你可要坚持住！我会用音乐给你打气的！'}",
	[105143] = "{id = 105143, Speaker = 500260, Text = '这么优秀的品色，谢谢主人的厚爱呢'}",
	[105144] = "{id = 105144, Speaker = 500260, Text = '啊~哆瑞咪发梭拉西哆，练习不能停！'}",
	[105145] = "{id = 105145, Speaker = 500260, Text = '音乐家也需要主人的抚摸作为奖励啊~'}",
	[105146] = "{id = 105146, Speaker = 500260, Text = '饿着肚子，音都拉不准了呢。'}",
	[105147] = "{id = 105147, Speaker = 500260, Text = '与主人的美妙气质很相符。'}",
	[105148] = "{id = 105148, Speaker = 500260, Text = '主人真是体贴又细腻的人啊！我来献上一首感谢之曲吧'}",
	[105149] = "{id = 105149, Speaker = 500260, Text = '你可征服不了我高雅的品味！'}",
	[105150] = "{id = 105150, Speaker = 500260, Text = '可惜啊，你与美妙的艺术擦肩而过~'}",
	[105151] = "{id = 105151, Speaker = 500260, Text = '啊，还有音乐会在等着我，拜拜咯~'}",
	[105152] = "{id = 105152, Speaker = 500260, Text = '啊，我相信你一定会爱上我的音乐天赋！'}",
	[105153] = "{id = 105153, Speaker = 500260, Text = '音乐就是我生命的全部！'}",
	[105154] = "{id = 105154, Speaker = 500270, Text = '这个就是毒魔菇的名字嘛？真不错！'}",
	[105155] = "{id = 105155, Speaker = 500270, Text = '啊，让我来打个冒着毒气的哈欠~'}",
	[105156] = "{id = 105156, Speaker = 500270, Text = '我和主人的感情越来越浓郁啦。'}",
	[105157] = "{id = 105157, Speaker = 500270, Text = '主人我不放毒啦！别把我塞回去嘛'}",
	[105158] = "{id = 105158, Speaker = 500270, Text = '这里好热闹啊，我会乖乖的不漏出毒气哒。'}",
	[105159] = "{id = 105159, Speaker = 500270, Text = '哟呼，我们今晚好好庆祝一下吧！'}",
	[105160] = "{id = 105160, Speaker = 500270, Text = '哎呀，主人你就是中了精炼的毒！'}",
	[105161] = "{id = 105161, Speaker = 500270, Text = '主人放心收下吧，我的礼物里可没下毒！'}",
	[105162] = "{id = 105162, Speaker = 500270, Text = '哼哼，我的剧毒又变得浓郁了呢~'}",
	[105163] = "{id = 105163, Speaker = 500270, Text = '主人，不要倒下啊，我不想做孤单的毒蘑菇！'}",
	[105164] = "{id = 105164, Speaker = 500270, Text = '唔哇，被主人疼爱的时候，世界都变得温暖啦。'}",
	[105165] = "{id = 105165, Speaker = 500270, Text = '啊，这毒草的口感，真是停不下嘴的美味啊！'}",
	[105166] = "{id = 105166, Speaker = 500270, Text = '吃饱了就不想动呢，只想做一只安静的美魔菇。'}",
	[105167] = "{id = 105167, Speaker = 500270, Text = '主人回来啦~等的我的伞盖都快长小蘑菇了。'}",
	[105168] = "{id = 105168, Speaker = 500270, Text = '尝尝我的毒气头槌!'}",
	[105169] = "{id = 105169, Speaker = 500270, Text = '哼，讨厌的家伙就倒在我的毒气里吧！'}",
	[105170] = "{id = 105170, Speaker = 500270, Text = '主人，我的剧毒也帮了你不小的忙吧~'}",
	[105171] = "{id = 105171, Speaker = 500270, Text = '主人只顾自己聊天！我要蹲在你们身边偷听!'}",
	[105172] = "{id = 105172, Speaker = 500270, Text = '主人的成长，小毒魔菇全都看在眼里呢~'}",
	[105173] = "{id = 105173, Speaker = 500270, Text = '主人，别睡在这么脏的地方啊，快点起来好不好？'}",
	[105174] = "{id = 105174, Speaker = 500270, Text = '主人得抚摸好帮呀~放心，我不会漏出毒气的~'}",
	[105175] = "{id = 105175, Speaker = 500270, Text = '主人，快给自己治疗呀，我来掩护你撤退。'}",
	[105176] = "{id = 105176, Speaker = 500270, Text = '这个装备，我穿上去一定会帅得发光！'}",
	[105177] = "{id = 105177, Speaker = 500270, Text = '啊，不小心让毒气跑出来了，又熏倒了一只盗虫~'}",
	[105178] = "{id = 105178, Speaker = 500270, Text = '快来摸摸毒蘑菇的小伞盖！'}",
	[105179] = "{id = 105179, Speaker = 500270, Text = '我的毒草呢！主人喂我吃鲜嫩的毒草好不好？'}",
	[105180] = "{id = 105180, Speaker = 500270, Text = '主人啥时候做一个魔菇样子的头饰，我们一起蹲角落！'}",
	[105181] = "{id = 105181, Speaker = 500270, Text = '主人是这个世界上对毒魔菇最好的人啦~'}",
	[105182] = "{id = 105182, Speaker = 500270, Text = '哼，帅气的毒蘑菇哪有那么好到手啊，略略略。'}",
	[105183] = "{id = 105183, Speaker = 500270, Text = '哎呀，就差一步就能得到一只可爱的毒魔菇了哟。'}",
	[105184] = "{id = 105184, Speaker = 500270, Text = '毒气烟雾弹！溜了溜了'}",
	[105185] = "{id = 105185, Speaker = 500270, Text = '我的毒气随时听主人吩咐。'}",
	[105186] = "{id = 105186, Speaker = 500270, Text = '我打喷嚏的时候，不要过来噢，会中毒的。'}",
	[105187] = "{id = 105187, Speaker = 500280, Text = '嘤嘤嘤，我终于有名字了！'}",
	[105188] = "{id = 105188, Speaker = 500280, Text = '今天也要呼呦呼呦的飘在主人身边~'}",
	[105189] = "{id = 105189, Speaker = 500280, Text = '主人与我的羁绊变得更深了呢！'}",
	[105190] = "{id = 105190, Speaker = 500280, Text = '我是一条白床单，主人叠一叠再放回去……'}",
	[105191] = "{id = 105191, Speaker = 500280, Text = '哦~以前我可从来没来过这么厉害的地方~'}",
	[105192] = "{id = 105192, Speaker = 500280, Text = '真是太好了！我真是太开心了！可惜我做不出表情……'}",
	[105193] = "{id = 105193, Speaker = 500280, Text = '嘤嘤嘤……我的心情现在就如同我的表情一样……'}",
	[105194] = "{id = 105194, Speaker = 500280, Text = '小幽灵的心意，主人别嫌弃哟！'}",
	[105195] = "{id = 105195, Speaker = 500280, Text = '唔哇，我感觉自己的存在感变强了!'}",
	[105196] = "{id = 105196, Speaker = 500280, Text = '这不科学！明明我已经死了...'}",
	[105197] = "{id = 105197, Speaker = 500280, Text = '你知道吗，你是我哦存在世上最重要的羁绊！'}",
	[105198] = "{id = 105198, Speaker = 500280, Text = '快给我点好吃的~我都饿得变透明啦！'}",
	[105199] = "{id = 105199, Speaker = 500280, Text = '主人，再吃下去，我就要变成圆鼓鼓幽灵了...'}",
	[105200] = "{id = 105200, Speaker = 500280, Text = '主人，你一回来我就感知到啦！'}",
	[105201] = "{id = 105201, Speaker = 500280, Text = '嘻嘻，让我来把主人的敌人拉向冥界！'}",
	[105202] = "{id = 105202, Speaker = 500280, Text = '主人把它变成我的同类啦！'}",
	[105203] = "{id = 105203, Speaker = 500280, Text = '哈哈哈，不知道MVP的幽灵长啥样。'}",
	[105204] = "{id = 105204, Speaker = 500280, Text = '呜呜呜，再不理我，我就要飘走啦！'}",
	[105205] = "{id = 105205, Speaker = 500280, Text = '我可以明显的感觉主人的存在变得更强了！'}",
	[105206] = "{id = 105206, Speaker = 500280, Text = '唔，主人，就算变成我的同类我还是会喜欢你的。'}",
	[105207] = "{id = 105207, Speaker = 500280, Text = '嘻嘻，被主人抚摸能让我忘记悲伤。'}",
	[105208] = "{id = 105208, Speaker = 500280, Text = '主人，坚持住！别变成我的同类啊~'}",
	[105209] = "{id = 105209, Speaker = 500280, Text = '我会努力试着穿上他的...'}",
	[105210] = "{id = 105210, Speaker = 500280, Text = '飘来飘去才是幽灵的本职工作~'}",
	[105211] = "{id = 105211, Speaker = 500280, Text = '主人我飘过来啦，快摸摸我~'}",
	[105212] = "{id = 105212, Speaker = 500280, Text = '谁说幽灵不会饿肚子的？没看到我的肚子空空的么？'}",
	[105213] = "{id = 105213, Speaker = 500280, Text = '好羡慕，我也想试试手工，可惜我没有手...'}",
	[105214] = "{id = 105214, Speaker = 500280, Text = '没想到我也会收到礼物，主人最棒了！'}",
	[105215] = "{id = 105215, Speaker = 500280, Text = '想抓到幽灵真是白日做梦！'}",
	[105216] = "{id = 105216, Speaker = 500280, Text = '怎么，知道而退了嘛？我就在你面前哟'}",
	[105217] = "{id = 105217, Speaker = 500280, Text = '我要去参加幽灵派对咯，拜拜~'}",
	[105218] = "{id = 105218, Speaker = 500280, Text = '拥有一只可爱的幽灵可是人类少有的荣信哟'}",
	[105219] = "{id = 105219, Speaker = 500280, Text = '呜呜呜呜~你有被吓到嘛？'}",
	[105220] = "{id = 105220, Speaker = 500290, Text = '咕咕！主人取的名字，我喜欢！'}",
	[105221] = "{id = 105221, Speaker = 500290, Text = '咕！今天也想和主人一起尽情奔跑呢！'}",
	[105222] = "{id = 105222, Speaker = 500290, Text = '咕~越来越喜欢呆在主人身边啦！'}",
	[105223] = "{id = 105223, Speaker = 500290, Text = '咕咕，回到蛋里的感觉真让我怀念'}",
	[105224] = "{id = 105224, Speaker = 500290, Text = '咕咕，这里就是主人的窝嘛？'}",
	[105225] = "{id = 105225, Speaker = 500290, Text = '咕~太好啦主人，好运临头！'}",
	[105226] = "{id = 105226, Speaker = 500290, Text = '不要气馁，主人~咕咕'}",
	[105227] = "{id = 105227, Speaker = 500290, Text = '咕咕，我为主人准备的礼物，主人喜欢嘛？'}",
	[105228] = "{id = 105228, Speaker = 500290, Text = '我的变得更加强壮有力了呢，可以帮到主人啦！'}",
	[105229] = "{id = 105229, Speaker = 500290, Text = '主人，我太累了，先睡一会儿...'}",
	[105230] = "{id = 105230, Speaker = 500290, Text = '主人的抚摸，是对我最好的奖赏！'}",
	[105231] = "{id = 105231, Speaker = 500290, Text = '呜呜呜，肥美的蚯蚓在哪里？大嘴鸟馋死了~'}",
	[105232] = "{id = 105232, Speaker = 500290, Text = '吃不下了~肚子涨得跑不动了！'}",
	[105233] = "{id = 105233, Speaker = 500290, Text = '听到主人回来，我马上乖乖的跑过来啦！'}",
	[105234] = "{id = 105234, Speaker = 500290, Text = '我要把敌人啄得满头大包！'}",
	[105235] = "{id = 105235, Speaker = 500290, Text = '这样的敌人，对我和主人来说就是小菜一碟！'}",
	[105236] = "{id = 105236, Speaker = 500290, Text = '主人，主人，看到我用嘴巴啄它的英姿了嘛？'}",
	[105237] = "{id = 105237, Speaker = 500290, Text = '咕咕咕？你们在聊些什么话题呀？也告诉我嘛~'}",
	[105238] = "{id = 105238, Speaker = 500290, Text = '主人变得更强壮了呢~什么时候我们再赛跑一次呢？'}",
	[105239] = "{id = 105239, Speaker = 500290, Text = '主人，不要闭上眼睛！我们不是约好了要一起奔跑的嘛？'}",
	[105240] = "{id = 105240, Speaker = 500290, Text = '主人的抚摸像沙漠中吹来的凉风一样舒服~'}",
	[105241] = "{id = 105241, Speaker = 500290, Text = '主人！你受重伤啦，赶快溜~'}",
	[105242] = "{id = 105242, Speaker = 500290, Text = '这个就是我的新装备嘛！一定能让我跑得更快！'}",
	[105243] = "{id = 105243, Speaker = 500290, Text = '我喜欢像风一样自由的奔跑！'}",
	[105244] = "{id = 105244, Speaker = 500290, Text = '帮我梳理一下羽毛吧，主人！'}",
	[105245] = "{id = 105245, Speaker = 500290, Text = '肚子饿啦~腿都迈不开了~'}",
	[105246] = "{id = 105246, Speaker = 500290, Text = '如果我戴上这个头饰跑步，肯定超拉风！'}",
	[105247] = "{id = 105247, Speaker = 500290, Text = '恩，真是好东西，我就收下啦！'}",
	[105248] = "{id = 105248, Speaker = 500290, Text = '哼，给点小恩小惠就想驯服我？不存在的！'}",
	[105249] = "{id = 105249, Speaker = 500290, Text = '这样就放弃了么？咕咕咕'}",
	[105250] = "{id = 105250, Speaker = 500290, Text = '你可追不上我的速度！望尘莫及吧~'}",
	[105251] = "{id = 105251, Speaker = 500290, Text = '从此以后，我就是你忠诚的大嘴鸟啦！'}",
	[105252] = "{id = 105252, Speaker = 500290, Text = '一望无际的沙漠，最最适合撒开腿奔跑！'}",
	[105253] = "{id = 105253, Speaker = 500300, Text = '啊，这个名字有种似曾相识的感觉。'}",
	[105254] = "{id = 105254, Speaker = 500300, Text = '终于能行走在阳光下了！'}",
	[105255] = "{id = 105255, Speaker = 500300, Text = '主人，我们的羁绊之线越来越紧密了呢。'}",
	[105256] = "{id = 105256, Speaker = 500300, Text = '主人，要记得唤醒我噢。'}",
	[105257] = "{id = 105257, Speaker = 500300, Text = '好重的阳气啊，主人的公会真是活力十足呢！'}",
	[105258] = "{id = 105258, Speaker = 500300, Text = '必然的成功，冥冥之中自有安排！'}",
	[105259] = "{id = 105259, Speaker = 500300, Text = '主人，成败自有定数，再接再厉！'}",
	[105260] = "{id = 105260, Speaker = 500300, Text = '主人，收下礼物吧，这是我的报恩~'}",
	[105261] = "{id = 105261, Speaker = 500300, Text = '我的力量变得更强了，一定能帮上主人的忙！'}",
	[105262] = "{id = 105262, Speaker = 500300, Text = '啊，没想到我又要再度死去了。'}",
	[105263] = "{id = 105263, Speaker = 500300, Text = '嘻嘻，主人不嫌弃我的恩情，没齿难忘！'}",
	[105264] = "{id = 105264, Speaker = 500300, Text = '我生前从来没吃过这么好吃得东西！'}",
	[105265] = "{id = 105265, Speaker = 500300, Text = '吃得太饱我可跳不动哦！'}",
	[105266] = "{id = 105266, Speaker = 500300, Text = '与你几天不见，就有一种恍如隔世的感觉呢'}",
	[105267] = "{id = 105267, Speaker = 500300, Text = '我要让敌人尝尝深陷轮回的怨恨！'}",
	[105268] = "{id = 105268, Speaker = 500300, Text = '这样的家伙，不值得活在世界上。'}",
	[105269] = "{id = 105269, Speaker = 500300, Text = '最好的做法，就是让它毫无痛苦的死去。'}",
	[105270] = "{id = 105270, Speaker = 500300, Text = '你们聊得什么内容呀？让我跳过来听听。'}",
	[105271] = "{id = 105271, Speaker = 500300, Text = '主人的阳气更加强大了呢~'}",
	[105272] = "{id = 105272, Speaker = 500300, Text = '不要害怕，死亡是必经的过程~'}",
	[105273] = "{id = 105273, Speaker = 500300, Text = '主人，你的手热热的，好温暖啊。'}",
	[105274] = "{id = 105274, Speaker = 500300, Text = '主人，你的脸怎么白得像我一样，赶紧治疗啊。'}",
	[105275] = "{id = 105275, Speaker = 500300, Text = '没想到这个装备和我的古风很相配呢~'}",
	[105276] = "{id = 105276, Speaker = 500300, Text = '啊，额头上的道符褪色了呢，要不去换个新的？'}",
	[105277] = "{id = 105277, Speaker = 500300, Text = '恩，据说摸摸僵尸的后脑勺能带来好运，你要试试么？'}",
	[105278] = "{id = 105278, Speaker = 500300, Text = '吃饱了我就不会做坏事啦！快给我吃的吧。'}",
	[105279] = "{id = 105279, Speaker = 500300, Text = '这就是你们现代人的品味么？我总觉得怪怪的...'}",
	[105280] = "{id = 105280, Speaker = 500300, Text = '啊，好久没收到礼物，还以为你忘记我了呢。'}",
	[105281] = "{id = 105281, Speaker = 500300, Text = '看来我们前世的因缘还没到呢，下次再试试吧'}",
	[105282] = "{id = 105282, Speaker = 500300, Text = '你真的想得到我的力量么？再努力一下试试吧。'}",
	[105283] = "{id = 105283, Speaker = 500300, Text = '我可要和鬼女约会去了，你好自为之吧~'}",
	[105284] = "{id = 105284, Speaker = 500300, Text = '或许，你能带我见识一下洞窟外得世界。'}",
	[105285] = "{id = 105285, Speaker = 500300, Text = '我的前世究竟是个怎么样的人呢？真是好奇啊~'}",
	[105286] = "{id = 105286, Speaker = 500310, Text = '我也是块有名有姓的石头啦！'}",
	[105287] = "{id = 105287, Speaker = 500310, Text = '我怎么不是从石头里蹦出来的？？'}",
	[105288] = "{id = 105288, Speaker = 500310, Text = '我可是块有感情的石头，'}",
	[105289] = "{id = 105289, Speaker = 500310, Text = '宠物蛋压缩石头，真憋屈！'}",
	[105290] = "{id = 105290, Speaker = 500310, Text = '啊，神力水晶和我哪个更硬呢？好想试试！'}",
	[105291] = "{id = 105291, Speaker = 500310, Text = '太棒了，我们可以听一段摇滚音乐庆祝一下！'}",
	[105292] = "{id = 105292, Speaker = 500310, Text = '我这样的石头脑袋没办法说漂亮话，但还是希望主人重新振作起来！'}",
	[105293] = "{id = 105293, Speaker = 500310, Text = '这是我的一点心意，不嫌弃的话，就收下吧~'}",
	[105294] = "{id = 105294, Speaker = 500310, Text = '我感觉我的身体变得更加坚硬了~'}",
	[105295] = "{id = 105295, Speaker = 500310, Text = '唔啊啊啊，石块崩塌碎裂了...'}",
	[105296] = "{id = 105296, Speaker = 500310, Text = '被你关心的感觉，让我这块石头感觉到了心的温度。'}",
	[105297] = "{id = 105297, Speaker = 500310, Text = '魔力石板真好吃啊，一口一个嘎嘣脆！'}",
	[105298] = "{id = 105298, Speaker = 500310, Text = '再吃下去，我就要重得陷进泥土里啦。'}",
	[105299] = "{id = 105299, Speaker = 500310, Text = '我一直在等着主人回来呢，身上都落满了尘土。'}",
	[105300] = "{id = 105300, Speaker = 500310, Text = '我要让他们尝尝滚石般隆隆席卷的愤怒~'}",
	[105301] = "{id = 105301, Speaker = 500310, Text = '它的攻击打在我身上就像挠痒痒一样~'}",
	[105302] = "{id = 105302, Speaker = 500310, Text = '哈哈哈，再强大的敌人也会在我们的攻势下土崩瓦解！'}",
	[105303] = "{id = 105303, Speaker = 500310, Text = '你们聊天的时候，我就伪装成路边的一块石头好了...'}",
	[105304] = "{id = 105304, Speaker = 500310, Text = '主人已经变的更强了，我也要变得更坚硬！不能拖主人的后腿！'}",
	[105305] = "{id = 105305, Speaker = 500310, Text = '主人，我会化作昙石就这样守护着你的。'}",
	[105306] = "{id = 105306, Speaker = 500310, Text = '主人，我摸上去是不是很粗糙啊？你喜欢么？'}",
	[105307] = "{id = 105307, Speaker = 500310, Text = '主人，注意自己的生命力！躲到我身后去。'}",
	[105308] = "{id = 105308, Speaker = 500310, Text = '哈哈，有了它，我会变得更加坚不可摧呢！'}",
	[105309] = "{id = 105309, Speaker = 500310, Text = '啊，继续发呆的话，我的身上快长出青苔和小花了。'}",
	[105310] = "{id = 105310, Speaker = 500310, Text = '主人，来摸摸我刚打理过的大理石身躯吧~'}",
	[105311] = "{id = 105311, Speaker = 500310, Text = '石头不能吃东西，谁规定的？没听见我的肚子饿得咔咔作响嘛？'}",
	[105312] = "{id = 105312, Speaker = 500310, Text = '要是我带上这个的话，会不会像一座假山？'}",
	[105313] = "{id = 105313, Speaker = 500310, Text = '我虽是石头变得，收到主人的礼物还是会兴奋不已啊'}",
	[105314] = "{id = 105314, Speaker = 500310, Text = '就凭你也想撼动顽石嘛？'}",
	[105315] = "{id = 105315, Speaker = 500310, Text = '哼，人类就是没有石头般坚韧的毅力。'}",
	[105316] = "{id = 105316, Speaker = 500310, Text = '滚石不生苔！'}",
	[105317] = "{id = 105317, Speaker = 500310, Text = '把石头当宠物，你可真是特立独行！'}",
	[105318] = "{id = 105318, Speaker = 500310, Text = '巨石怪？大家都五指山。'}",
	[105319] = "{id = 105319, Speaker = 500320, Text = '这个名字听起来就很威风，嘻嘻！'}",
	[105320] = "{id = 105320, Speaker = 500320, Text = '老大，我的榔头已经跃跃欲试了！'}",
	[105321] = "{id = 105321, Speaker = 500320, Text = '嘻嘻，我们的感情连榔头都砸不坏！'}",
	[105322] = "{id = 105322, Speaker = 500320, Text = '老大~~，我还没锤够呢！'}",
	[105323] = "{id = 105323, Speaker = 500320, Text = '如果把这个水晶砸掉会发生什么呢？好想试试啊~'}",
	[105324] = "{id = 105324, Speaker = 500320, Text = '真是一锤定音呢，主人！'}",
	[105325] = "{id = 105325, Speaker = 500320, Text = '哼，忽克连的锤子还没我的榔头好呢~'}",
	[105326] = "{id = 105326, Speaker = 500320, Text = '主人，小弟的一点心意快快收下吧!'}",
	[105327] = "{id = 105327, Speaker = 500320, Text = '哼，千锤百炼的力量，谁敢小看我？'}",
	[105328] = "{id = 105328, Speaker = 500320, Text = '额啊，我的榔头竟然输了...'}",
	[105329] = "{id = 105329, Speaker = 500320, Text = '在主人身边，总能吃香的喝辣的~'}",
	[105330] = "{id = 105330, Speaker = 500320, Text = '啊呜，这可比哥布林森林的野果子好吃多啦~'}",
	[105331] = "{id = 105331, Speaker = 500320, Text = '不能再吃啦，吃的太撑就没办法捣蛋了！'}",
	[105332] = "{id = 105332, Speaker = 500320, Text = '老大，今天带我去哪里捣蛋嘛？'}",
	[105333] = "{id = 105333, Speaker = 500320, Text = '让你们尝尝我榔头的厉害！'}",
	[105334] = "{id = 105334, Speaker = 500320, Text = '再强大的怪物，也受不了我榔头的一击。'}",
	[105335] = "{id = 105335, Speaker = 500320, Text = '管他什么三七二十一，先来一榔头总是没错的！'}",
	[105336] = "{id = 105336, Speaker = 500320, Text = '喂喂喂，主人，聊天比用榔头锤魔物的头还好玩么？'}",
	[105337] = "{id = 105337, Speaker = 500320, Text = '嘻嘻，老大的破坏力激增！我们一起去搞点大动静吧！'}",
	[105338] = "{id = 105338, Speaker = 500320, Text = '主人，我会用榔头为你报仇的！'}",
	[105339] = "{id = 105339, Speaker = 500320, Text = '啊哈哈，主人得表彰，让我动力十足！'}",
	[105340] = "{id = 105340, Speaker = 500320, Text = '老大，可别战斗得太忘我，小命要紧啊！'}",
	[105341] = "{id = 105341, Speaker = 500320, Text = '让我来掂量掂量我的新家伙~'}",
	[105342] = "{id = 105342, Speaker = 500320, Text = '让我来看看哪里可以让我尽情敲！敲！敲！'}",
	[105343] = "{id = 105343, Speaker = 500320, Text = '来摸摸我的头吧，我马上会变得充满活力！'}",
	[105344] = "{id = 105344, Speaker = 500320, Text = '快给我点吃的吧，没力气可没法挥舞我心爱的榔头！'}",
	[105345] = "{id = 105345, Speaker = 500320, Text = '这个头饰用榔头砸的坏嘛？我很好奇。'}",
	[105346] = "{id = 105346, Speaker = 500320, Text = '嘻嘻，老大对我真好啊！'}",
	[105347] = "{id = 105347, Speaker = 500320, Text = '你的实力还不能进我的法眼！'}",
	[105348] = "{id = 105348, Speaker = 500320, Text = '中途放弃还不如从未做过。'}",
	[105349] = "{id = 105349, Speaker = 500320, Text = '榔头哥布林可不是好欺负的主！'}",
	[105350] = "{id = 105350, Speaker = 500320, Text = '今天起，你就是我榔头哥布林的老大了！'}",
	[105351] = "{id = 105351, Speaker = 500320, Text = '啊，停下手中得榔头，我就浑身难受！'}",
	[105352] = "{id = 105352, Speaker = 500330, Text = '嗬，听起来就是个强壮的名字！'}",
	[105353] = "{id = 105353, Speaker = 500330, Text = '嗬，终于可以活动一下我的筋骨啦。'}",
	[105354] = "{id = 105354, Speaker = 500330, Text = '主人与我的情感在战斗中愈发深厚！'}",
	[105355] = "{id = 105355, Speaker = 500330, Text = '留得青山在，不怕没人劈！'}",
	[105356] = "{id = 105356, Speaker = 500330, Text = '这就是主人的公会嘛，可比我们的村落帅气多了！'}",
	[105357] = "{id = 105357, Speaker = 500330, Text = '好运果然是站在强者一边的呢！'}",
	[105358] = "{id = 105358, Speaker = 500330, Text = '别被失败击倒，主人！再度爬起来，一切照旧。'}",
	[105359] = "{id = 105359, Speaker = 500330, Text = '我嘴笨，不会说话，感情全在礼物里！'}",
	[105360] = "{id = 105360, Speaker = 500330, Text = '的力量增长了，果然与主人这样的强者一起，进步得就是快！'}",
	[105361] = "{id = 105361, Speaker = 500330, Text = '真不甘心啊，难道就在这里倒下了吗...'}",
	[105362] = "{id = 105362, Speaker = 500330, Text = '哼哼，你也沉醉在我壮硕肌肉的美感中了吧？'}",
	[105363] = "{id = 105363, Speaker = 500330, Text = '啊，这样大块朵颐才像战士的作风！'}",
	[105364] = "{id = 105364, Speaker = 500330, Text = '作为一个战士，吃太多是作战的大忌！'}",
	[105365] = "{id = 105365, Speaker = 500330, Text = '时间不等人！让我们赶紧奔赴战场吧！'}",
	[105366] = "{id = 105366, Speaker = 500330, Text = '敌人在我的眼中就是荣耀的勋章！'}",
	[105367] = "{id = 105367, Speaker = 500330, Text = '哼，不值一提的对手！'}",
	[105368] = "{id = 105368, Speaker = 500330, Text = '嗬，真是个值得一战的对手。'}",
	[105369] = "{id = 105369, Speaker = 500330, Text = '有时间再这里磨磨唧唧还不如奔向战场杀敌夺旗！'}",
	[105370] = "{id = 105370, Speaker = 500330, Text = '不知不觉主人已经成为更强大的战士了呢，我由衷的倾佩！'}",
	[105371] = "{id = 105371, Speaker = 500330, Text = '这个世界就是适者生存，弱肉强食这么残酷。'}",
	[105372] = "{id = 105372, Speaker = 500330, Text = '怎么样，被我完美的身材镇住了吧！'}",
	[105373] = "{id = 105373, Speaker = 500330, Text = '哼，对于不能在战场上照顾好自己的战士，死亡是唯一的结局！'}",
	[105374] = "{id = 105374, Speaker = 500330, Text = '真是精良的装备，正适合我这样强大的战士！'}",
	[105375] = "{id = 105375, Speaker = 500330, Text = '恩，美好的肌肉要靠坚持不懈的锻炼与钢铁的意志！'}",
	[105376] = "{id = 105376, Speaker = 500330, Text = '看看我漂亮的肌肉！你难道不想摸摸看嘛？'}",
	[105377] = "{id = 105377, Speaker = 500330, Text = '体力不足，向主人申请作战物资！'}",
	[105378] = "{id = 105378, Speaker = 500330, Text = '装饰品对于战士来说毫无意义，除非它够帅！'}",
	[105379] = "{id = 105379, Speaker = 500330, Text = '没想到你也是个有心人啊，我就不客气的收下啦。'}",
	[105380] = "{id = 105380, Speaker = 500330, Text = '就凭你的本事，根本没资格让我正眼看你！'}",
	[105381] = "{id = 105381, Speaker = 500330, Text = '你难道只有这点毅力吗？'}",
	[105382] = "{id = 105382, Speaker = 500330, Text = '太无聊了，我还是回到村子里去喝上一杯吧。'}",
	[105383] = "{id = 105383, Speaker = 500330, Text = '没想到你还有这一手，我认可你了，人类。'}",
	[105384] = "{id = 105384, Speaker = 500330, Text = '坚持锻炼！这就是我炼出完美肌肉的秘诀~'}",
	[105385] = "{id = 105385, Speaker = 500340, Text = '谢谢，主人，给我，名字!'}",
	[105386] = "{id = 105386, Speaker = 500340, Text = '主人，我们，今天，要去，哪里？'}",
	[105387] = "{id = 105387, Speaker = 500340, Text = '土人，最喜欢，主人了。'}",
	[105388] = "{id = 105388, Speaker = 500340, Text = '土人，先回去，休息了。'}",
	[105389] = "{id = 105389, Speaker = 500340, Text = '土人，喜欢这里，华丽的，感觉。'}",
	[105390] = "{id = 105390, Speaker = 500340, Text = '主人，你的好运，真是神奇！'}",
	[105391] = "{id = 105391, Speaker = 500340, Text = '别灰心，主人，钱总会赚回来的！'}",
	[105392] = "{id = 105392, Speaker = 500340, Text = '土人的礼物，主人，收下吧。'}",
	[105393] = "{id = 105393, Speaker = 500340, Text = '土人，感觉变强了，真高兴！'}",
	[105394] = "{id = 105394, Speaker = 500340, Text = '土人，倒下了，主人，再见了...'}",
	[105395] = "{id = 105395, Speaker = 500340, Text = '没想到，人类的体温，这么温暖。'}",
	[105396] = "{id = 105396, Speaker = 500340, Text = '土人，心满意足！'}",
	[105397] = "{id = 105397, Speaker = 500340, Text = '土人，不想，吃成一个，球球。'}",
	[105398] = "{id = 105398, Speaker = 500340, Text = '主人回来了，土人好想你啊！'}",
	[105399] = "{id = 105399, Speaker = 500340, Text = '尝尝，土人的，狼牙棒！'}",
	[105400] = "{id = 105400, Speaker = 500340, Text = '主人的力量，真是让人吃惊！'}",
	[105401] = "{id = 105401, Speaker = 500340, Text = '主人的实力，已经如此强大了吗？'}",
	[105402] = "{id = 105402, Speaker = 500340, Text = '主人，不要聊天啦，陪我玩一会儿做迷藏吧。'}",
	[105403] = "{id = 105403, Speaker = 500340, Text = '主人，变强了，土人，好羡慕！'}",
	[105404] = "{id = 105404, Speaker = 500340, Text = '主人，我会把你，好好埋葬的。'}",
	[105405] = "{id = 105405, Speaker = 500340, Text = '土人，最喜欢，主人的疼爱~'}",
	[105406] = "{id = 105406, Speaker = 500340, Text = '主人！不要节省血瓶啦，保住性命要紧！'}",
	[105407] = "{id = 105407, Speaker = 500340, Text = '这套装备，看起来价格不菲呢，谢谢主人！'}",
	[105408] = "{id = 105408, Speaker = 500340, Text = '啊，每天存一个秘银，一年以后就可以...'}",
	[105409] = "{id = 105409, Speaker = 500340, Text = '主人，你的抚摸比金山银山更让我开心！'}",
	[105410] = "{id = 105410, Speaker = 500340, Text = '肚子饿了！主人，我的食物呢？'}",
	[105411] = "{id = 105411, Speaker = 500340, Text = '这个头饰，可以送给，土人吗？'}",
	[105412] = "{id = 105412, Speaker = 500340, Text = '土人，真的，可以收下吗？好高兴！'}",
	[105413] = "{id = 105413, Speaker = 500340, Text = '土人，可没那么笨，人类。'}",
	[105414] = "{id = 105414, Speaker = 500340, Text = '你难道不想，把我带回去么？'}",
	[105415] = "{id = 105415, Speaker = 500340, Text = '哼哼，捉到我，你还早了100年呢。'}",
	[105416] = "{id = 105416, Speaker = 500340, Text = '土人，愿意追随你，人类。'}",
	[105417] = "{id = 105417, Speaker = 500340, Text = '土人，喜欢，亮晶晶的，东西~'}",
	[105418] = "{id = 105418, Speaker = 500350, Text = '能得到主人赏赐的称谓，在下不胜荣信！'}",
	[105419] = "{id = 105419, Speaker = 500350, Text = '主人，今天也请尽情吩咐我。'}",
	[105420] = "{id = 105420, Speaker = 500350, Text = '对于我伟大的主人，唯有臣服之道。'}",
	[105421] = "{id = 105421, Speaker = 500350, Text = '既然是主人的命令，在下就先行告退了。'}",
	[105422] = "{id = 105422, Speaker = 500350, Text = '这里的人都是主人的同伴么？在下会收敛自己的傲慢的。'}",
	[105423] = "{id = 105423, Speaker = 500350, Text = '啊，我的主人，成功是你必然得到的结果~'}",
	[105424] = "{id = 105424, Speaker = 500350, Text = '别被无聊的小挫折绊住了前进的脚步，主人'}",
	[105425] = "{id = 105425, Speaker = 500350, Text = '这是我作为侍者为主人献上的礼物，请笑纳。'}",
	[105426] = "{id = 105426, Speaker = 500350, Text = '啊，每次变强我都感觉自己变得更加帅气逼人~'}",
	[105427] = "{id = 105427, Speaker = 500350, Text = '在下对主人的侍奉只能到此为止了么？'}",
	[105428] = "{id = 105428, Speaker = 500350, Text = '啊~能得到主人如此宠幸，在下深感荣幸。'}",
	[105429] = "{id = 105429, Speaker = 500350, Text = '美妙的食物总是让人心情愉悦！'}",
	[105430] = "{id = 105430, Speaker = 500350, Text = '侍者可不能吃得比主人都多呢~'}",
	[105431] = "{id = 105431, Speaker = 500350, Text = '欢迎归来，我尊贵的主人！在下恭候多时了。'}",
	[105432] = "{id = 105432, Speaker = 500350, Text = '让你们见识一下恶魔真正的力量！'}",
	[105433] = "{id = 105433, Speaker = 500350, Text = '可笑，这样的敌人，不足为惧！'}",
	[105434] = "{id = 105434, Speaker = 500350, Text = '所谓的强敌，在我和主人的面前也不过如此嘛...'}",
	[105435] = "{id = 105435, Speaker = 500350, Text = '恶魔侍者在您身边，随时待命。'}",
	[105436] = "{id = 105436, Speaker = 500350, Text = '恩~主人变强后散发着危险的魅力呢~'}",
	[105437] = "{id = 105437, Speaker = 500350, Text = '无论生死，在下都会永远侍奉您...'}",
	[105438] = "{id = 105438, Speaker = 500350, Text = '主人亲昵的恩赐让在下受宠若惊~'}",
	[105439] = "{id = 105439, Speaker = 500350, Text = '主人，不可大意！记得要及时治愈自己地1伤势。'}",
	[105440] = "{id = 105440, Speaker = 500350, Text = '在下英俊的容颜果然穿什么都那么帅气呢~'}",
	[105441] = "{id = 105441, Speaker = 500350, Text = '啊，这么多的情书，我都不知道哪一封是谁的了...'}",
	[105442] = "{id = 105442, Speaker = 500350, Text = '主人，能赐予在下亲近的抚摸吗？'}",
	[105443] = "{id = 105443, Speaker = 500350, Text = '嗯，让在下饿肚子的话，可不能发挥出全部的实力。'}",
	[105444] = "{id = 105444, Speaker = 500350, Text = '嗯，要是再加一点邪力，这件头饰会更完美~'}",
	[105445] = "{id = 105445, Speaker = 500350, Text = '主人考虑的就是周道，收到主人的礼物，在下喜不胜收。'}",
	[105446] = "{id = 105446, Speaker = 500350, Text = '我可不想和你这样无趣的人类产生任何交集。'}",
	[105447] = "{id = 105447, Speaker = 500350, Text = '难道的魅力只到这个程度么？这可说服不了我。'}",
	[105448] = "{id = 105448, Speaker = 500350, Text = '真是浪费我的时间，在下去见女粉丝了~'}",
	[105449] = "{id = 105449, Speaker = 500350, Text = '像您这样的强者，完全有资格做在下的主人。'}",
	[105450] = "{id = 105450, Speaker = 500350, Text = '被太多异性爱慕，有时候也是一种甜蜜的苦恼呢~'}",
	[105451] = "{id = 105451, Speaker = 500360, Text = '啊哈，这个名字真好听，主人可真懂人家的心呢~'}",
	[105452] = "{id = 105452, Speaker = 500360, Text = '人家都快无聊死了，主人，让我们来找点乐子吧！'}",
	[105453] = "{id = 105453, Speaker = 500360, Text = '啊，越来越离不开主人了呢。'}",
	[105454] = "{id = 105454, Speaker = 500360, Text = '主人，你真的舍得我回去嘛？'}",
	[105455] = "{id = 105455, Speaker = 500360, Text = '哇，主人的公会真是大呢~我可以用魔力来大扫除嘛？'}",
	[105456] = "{id = 105456, Speaker = 500360, Text = '呀哈，主人的好运真是强得离谱呢~'}",
	[105457] = "{id = 105457, Speaker = 500360, Text = '哎~别灰心了主人~扑到我怀里让我抱抱你吧~'}",
	[105458] = "{id = 105458, Speaker = 500360, Text = '这是我专门为主人准备的礼物哦，快打开看看吧~'}",
	[105459] = "{id = 105459, Speaker = 500360, Text = '哇，又变强了，主人觉得我有变得更可爱了嘛？'}",
	[105460] = "{id = 105460, Speaker = 500360, Text = '啊啊啊，我的恶魔之力就这样消散了！'}",
	[105461] = "{id = 105461, Speaker = 500360, Text = '主人的抚摸舒服极了~忍不住舔嘴唇呢~'}",
	[105462] = "{id = 105462, Speaker = 500360, Text = '好吃好吃，主人果然最宠我~'}",
	[105463] = "{id = 105463, Speaker = 500360, Text = '不吃啦，保持好身材也是女仆的任务~'}",
	[105464] = "{id = 105464, Speaker = 500360, Text = '欢迎回来~主人，我刚刚还在想念你呢~'}",
	[105465] = "{id = 105465, Speaker = 500360, Text = '让你们见识一下女仆的愤怒！'}",
	[105466] = "{id = 105466, Speaker = 500360, Text = '我们的力量让地热毫无还手之力！'}",
	[105467] = "{id = 105467, Speaker = 500360, Text = '哼，就这样还敢被称作MVP？'}",
	[105468] = "{id = 105468, Speaker = 500360, Text = '主人有了我一个人还不够么？'}",
	[105469] = "{id = 105469, Speaker = 500360, Text = '呀啊~主人散发力量的样子真是迷死人了！'}",
	[105470] = "{id = 105470, Speaker = 500360, Text = '无论生死，我永远都是您的女仆...'}",
	[105471] = "{id = 105471, Speaker = 500360, Text = '啊，主人特有的魅力让我不可控制的沉醉其中呢。'}",
	[105472] = "{id = 105472, Speaker = 500360, Text = '主人！你受了好重的伤，先应急处理一下！回去让我给你包扎！'}",
	[105473] = "{id = 105473, Speaker = 500360, Text = '嗯？难道主人已经看腻了我的女仆装吗？'}",
	[105474] = "{id = 105474, Speaker = 500360, Text = '主人想先吃饭，还是先洗澡，还是先享用...暖心料理呢？'}",
	[105475] = "{id = 105475, Speaker = 500360, Text = '主人，偶尔我也想撒娇，让你来好好抚摸我呢~'}",
	[105476] = "{id = 105476, Speaker = 500360, Text = '主人，饿肚子的女仆就不可爱啦，快喂饱我吧~'}",
	[105477] = "{id = 105477, Speaker = 500360, Text = '制作头饰？主人也有不为人知的可爱一面呢~'}",
	[105478] = "{id = 105478, Speaker = 500360, Text = '啊，送我的嘛~主人可真是个偷心小恶魔啊~'}",
	[105479] = "{id = 105479, Speaker = 500360, Text = '就凭你还想得到我？痴人说梦！'}",
	[105480] = "{id = 105480, Speaker = 500360, Text = '我可不会跟随意志力不坚定的人。'}",
	[105481] = "{id = 105481, Speaker = 500360, Text = '我还有一堆活要做呢，没工夫陪你胡闹了。'}",
	[105482] = "{id = 105482, Speaker = 500360, Text = '看到你的第一眼，我就觉得你有做我主人的资格呢~'}",
	[105483] = "{id = 105483, Speaker = 500360, Text = '恶魔女仆才是米德加尔特最棒的女仆~'}",
	[105484] = "{id = 105484, Speaker = 500370, Text = '有了你取的名字，我就是这个世界上独一无二的土拨鼠啦！'}",
	[105485] = "{id = 105485, Speaker = 500370, Text = '唔噜噜，阳光好刺眼呢！'}",
	[105486] = "{id = 105486, Speaker = 500370, Text = '土拨鼠越来越崇拜主人啦~'}",
	[105487] = "{id = 105487, Speaker = 500370, Text = '唔，又要钻到黑咕隆咚的地方了...'}",
	[105488] = "{id = 105488, Speaker = 500370, Text = '这里有好多伙伴呢，真有家的感觉！'}",
	[105489] = "{id = 105489, Speaker = 500370, Text = '主人，我的鼓励也是你成功的要素之一哟！'}",
	[105490] = "{id = 105490, Speaker = 500370, Text = '哎呀呀，吃个快乐坚果忘记悲伤吧。'}",
	[105491] = "{id = 105491, Speaker = 500370, Text = '这可是我囤了好久，压箱底的宝贝！主人快收下。'}",
	[105492] = "{id = 105492, Speaker = 500370, Text = '我感觉我更加有力，灵敏，可爱啦！'}",
	[105493] = "{id = 105493, Speaker = 500370, Text = '唔，失算啦！脑袋被打得晕晕的。'}",
	[105494] = "{id = 105494, Speaker = 500370, Text = '啊，主人的臂弯总是能给人安心感！'}",
	[105495] = "{id = 105495, Speaker = 500370, Text = '呀咪呀咪~把吃的塞进嘴巴的瞬间，是最满足的时刻啊！'}",
	[105496] = "{id = 105496, Speaker = 500370, Text = '呜呜呜，我的小肚子已经再也塞不下了！'}",
	[105497] = "{id = 105497, Speaker = 500370, Text = '我一听到主人的脚步声就马上钻过来啦~'}",
	[105498] = "{id = 105498, Speaker = 500370, Text = '哼，别看我个子小，我为了主人，我会把你打的嗷嗷叫！'}",
	[105499] = "{id = 105499, Speaker = 500370, Text = '只要主人和我联手，我们就能上天入地无所不能！'}",
	[105500] = "{id = 105500, Speaker = 500370, Text = '主人，看到我刚刚努力战斗的样子了嘛？'}",
	[105501] = "{id = 105501, Speaker = 500370, Text = '主人，你聊得真欢！都把我撂在一边不闻不问！'}",
	[105502] = "{id = 105502, Speaker = 500370, Text = '主人又变强了？能教教我变强的方法嘛？'}",
	[105503] = "{id = 105503, Speaker = 500370, Text = '主人！快起来，在地上睡觉会着凉的！'}",
	[105504] = "{id = 105504, Speaker = 500370, Text = '嘻嘻嘻，每次被主人摸头的时候，心里面都会变得暖洋洋的呢~'}",
	[105505] = "{id = 105505, Speaker = 500370, Text = '主人，主人！你的衣服都染红了，好严重的伤口，我都吓到了！'}",
	[105506] = "{id = 105506, Speaker = 500370, Text = '啊哈哈，这件装备正好能和我的护目镜凑成一套！'}",
	[105507] = "{id = 105507, Speaker = 500370, Text = '高原佩瑞丝姐姐真可爱啊，等我长高了就向她表白！'}",
	[105508] = "{id = 105508, Speaker = 500370, Text = '主人，摸摸我的头，让我快点长高！'}",
	[105509] = "{id = 105509, Speaker = 500370, Text = '主人，我继续饿肚子的话，就更加长不大了...'}",
	[105510] = "{id = 105510, Speaker = 500370, Text = '什么头饰？有我的魔物护目镜帅气么？'}",
	[105511] = "{id = 105511, Speaker = 500370, Text = '哇！我会把主人送的每一件礼物都好好保存起来！'}",
	[105512] = "{id = 105512, Speaker = 500370, Text = '我可不会乖乖束手就擒！'}",
	[105513] = "{id = 105513, Speaker = 500370, Text = '嗯？我可不喜欢半途而废的家伙！'}",
	[105514] = "{id = 105514, Speaker = 500370, Text = '我要去找高原佩瑞斯玩捉迷藏了，拜拜！'}",
	[105515] = "{id = 105515, Speaker = 500370, Text = '我选择跟着你的话，你要好好保护我哦。'}",
	[105516] = "{id = 105516, Speaker = 500370, Text = '要是土拨鼠能开飞机和坦克，那该多帅啊~'}",
	[105517] = "{id = 105517, Speaker = 500380, Text = '真是动听的名字，请主人每天都用它呼唤我。'}",
	[105518] = "{id = 105518, Speaker = 500380, Text = '只要能陪伴主人，我愿让这尾巴化作双脚。'}",
	[105519] = "{id = 105519, Speaker = 500380, Text = '主人，陪伴在你身边的幸福感越来越浓了呢。'}",
	[105520] = "{id = 105520, Speaker = 500380, Text = '没关系，只要在主人身边，我都会安心。'}",
	[105521] = "{id = 105521, Speaker = 500380, Text = '来到主人的公会有点害羞呢，可以躲到你的背后嘛？'}",
	[105522] = "{id = 105522, Speaker = 500380, Text = '主人，果然向海神大人的祈祷有用了~'}",
	[105523] = "{id = 105523, Speaker = 500380, Text = '别丧气，我们去海边散散心吧'}",
	[105524] = "{id = 105524, Speaker = 500380, Text = '这是小人鱼的心意，主人请收下吧~'}",
	[105525] = "{id = 105525, Speaker = 500380, Text = '我能感觉到潮汐之力在我体内增长~'}",
	[105526] = "{id = 105526, Speaker = 500380, Text = '啊，我的灵魂，回到大海了...'}",
	[105527] = "{id = 105527, Speaker = 500380, Text = '主人的爱与温柔，就像宽广的大海母亲~'}",
	[105528] = "{id = 105528, Speaker = 500380, Text = '唔，吃的好满足呢~'}",
	[105529] = "{id = 105529, Speaker = 500380, Text = '再吃下去，我就要变成河豚鱼了..'}",
	[105530] = "{id = 105530, Speaker = 500380, Text = '你终于回来了，我们今天要去哪里冒险呢？'}",
	[105531] = "{id = 105531, Speaker = 500380, Text = '把人鱼惹恼了，可是要付出代价的！'}",
	[105532] = "{id = 105532, Speaker = 500380, Text = '这样的敌人，海底我见多了。'}",
	[105533] = "{id = 105533, Speaker = 500380, Text = '主人与我的力量如同呼啸的巨浪，不可阻挡！'}",
	[105534] = "{id = 105534, Speaker = 500380, Text = '主人比我想象中，更活泼呢...'}",
	[105535] = "{id = 105535, Speaker = 500380, Text = '主人的力量像浪潮一样涌动，恭喜主人变得更强！'}",
	[105536] = "{id = 105536, Speaker = 500380, Text = '在我的怀抱中安睡吧，主人。'}",
	[105537] = "{id = 105537, Speaker = 500380, Text = '总是那么温柔，这就是我愿意追随你的原因吧。'}",
	[105538] = "{id = 105538, Speaker = 500380, Text = '主人，赶快治疗，伤在你身，痛在我心啊！'}",
	[105539] = "{id = 105539, Speaker = 500380, Text = '嗯，意外的合身呢，主人真是细心又温柔。'}",
	[105540] = "{id = 105540, Speaker = 500380, Text = '偶尔，我也会怀念我那浪潮深处的故乡...'}",
	[105541] = "{id = 105541, Speaker = 500380, Text = '不想摸摸我漂亮的鳞片嘛，主人？'}",
	[105542] = "{id = 105542, Speaker = 500380, Text = '主人，我想吃点好吃的补充魔力~'}",
	[105543] = "{id = 105543, Speaker = 500380, Text = '好可爱的头饰！主人可以为我做一件吗？'}",
	[105544] = "{id = 105544, Speaker = 500380, Text = '给我的嘛？真是劳烦你了，我很喜欢~'}",
	[105545] = "{id = 105545, Speaker = 500390, Text = '有了正式的名字，我们的契约就完全成立了。'}",
	[105546] = "{id = 105546, Speaker = 500390, Text = '是时候让贫道来为主人呼风唤雨了！'}",
	[105547] = "{id = 105547, Speaker = 500390, Text = '主人与贫道的羁绊日渐深邃呢。'}",
	[105548] = "{id = 105548, Speaker = 500390, Text = '哼，每次回去都有种被封印的感觉，真不爽！'}",
	[105549] = "{id = 105549, Speaker = 500390, Text = '贫道掐指算了算，主人的公会武运昌隆！'}",
	[105550] = "{id = 105550, Speaker = 500390, Text = '啊，这一卦贫道早就算准了!'}",
	[105551] = "{id = 105551, Speaker = 500390, Text = '这是命中注定之劫，别灰心，主人~'}",
	[105552] = "{id = 105552, Speaker = 500390, Text = '贫道集日月精华炼出的礼物，主人可要好好收下~'}",
	[105553] = "{id = 105553, Speaker = 500390, Text = '贫道的修为又上了一层阶梯！'}",
	[105554] = "{id = 105554, Speaker = 500390, Text = '我的千年道行只能止步于此了吗...'}",
	[105555] = "{id = 105555, Speaker = 500390, Text = '主人的爱总能给我力量呢，人类的情感真神奇'}",
	[105556] = "{id = 105556, Speaker = 500390, Text = '享受美妙的食物也是修行的一部分呢~'}",
	[105557] = "{id = 105557, Speaker = 500390, Text = '吃的太多，我就没办法顺利施展法术！'}",
	[105558] = "{id = 105558, Speaker = 500390, Text = '道法自然！今天我们去哪里修行呢？'}",
	[105559] = "{id = 105559, Speaker = 500390, Text = '在贫道的法术面前颤抖吧！'}",
	[105560] = "{id = 105560, Speaker = 500390, Text = '真是一个不错的实验对象...'}",
	[105561] = "{id = 105561, Speaker = 500390, Text = '要是能把MVP做成僵尸，那该多好啊...'}",
	[105562] = "{id = 105562, Speaker = 500390, Text = '难道聊天要比研究道法还有趣吗？'}",
	[105563] = "{id = 105563, Speaker = 500390, Text = '主人的修为随着战斗的磨砺成长了呢~'}",
	[105564] = "{id = 105564, Speaker = 500390, Text = '主人，你介意我把你做成僵尸么？'}",
	[105565] = "{id = 105565, Speaker = 500390, Text = '偶尔的亲近有百利而无一害~'}",
	[105566] = "{id = 105566, Speaker = 500390, Text = '永远不要小看你的对手，主人，马上治疗自己吧！'}",
	[105567] = "{id = 105567, Speaker = 500390, Text = '这件出色的法器，真是让贫道爱不释手。'}",
	[105568] = "{id = 105568, Speaker = 500390, Text = '不知道这张道符还有没有用，要不贴主人额头上试试看...'}",
	[105569] = "{id = 105569, Speaker = 500390, Text = '主人帮我调整一下道符吧，顺便摸摸我的头~'}",
	[105570] = "{id = 105570, Speaker = 500390, Text = '吃饱了，修行才能顺利。'}",
	[105571] = "{id = 105571, Speaker = 500390, Text = '小小头饰蕴藏了强大的力量呢，能借贫道研究一下么？'}",
	[105572] = "{id = 105572, Speaker = 500390, Text = '主人的好意，贫道无法推辞呢~'}",
	[105573] = "{id = 105573, Speaker = 500400, Text = '哇哈哈哈，今天的圣诞礼物是名字！'}",
	[105574] = "{id = 105574, Speaker = 500400, Text = '对于我来说，每天都是圣诞节！'}",
	[105575] = "{id = 105575, Speaker = 500400, Text = '有了主人，我的圣诞节才完整！'}",
	[105576] = "{id = 105576, Speaker = 500400, Text = '什么？！今天的圣诞节已经结束了嘛？'}",
	[105577] = "{id = 105577, Speaker = 500400, Text = '噢，这里还缺了一点圣诞节的气氛。'}",
	[105578] = "{id = 105578, Speaker = 500400, Text = '哇哈哈哈，今天的圣诞礼物是好运！'}",
	[105579] = "{id = 105579, Speaker = 500400, Text = '偶尔，圣诞节也会发生一些小小的不幸...'}",
	[105580] = "{id = 105580, Speaker = 500400, Text = '啊，这可是我诚心诚意为主人准备的圣诞礼物哟！'}",
	[105581] = "{id = 105581, Speaker = 500400, Text = '哦，我浑身充满了圣诞之力！'}",
	[105582] = "{id = 105582, Speaker = 500400, Text = '呃啊啊啊，这次是我经历过最糟糕的圣诞节！'}",
	[105583] = "{id = 105583, Speaker = 500400, Text = '阿，你的抚摸真是今年的最佳圣诞礼物。'}",
	[105584] = "{id = 105584, Speaker = 500400, Text = '好吧，虽然比不上圣诞大餐，但是吃起来真不错！'}",
	[105585] = "{id = 105585, Speaker = 500400, Text = '等等！我还要把肚皮留给一会儿的圣诞大餐呢！'}",
	[105586] = "{id = 105586, Speaker = 500400, Text = '呀呼，我已经等不及和你开始一场圣诞狂欢了！主人！'}",
	[105587] = "{id = 105587, Speaker = 500400, Text = '尝尝为你特制的圣诞节惊喜~地狱入场券！！'}",
	[105588] = "{id = 105588, Speaker = 500400, Text = '这个小家伙，真是完美的圣诞节礼物！'}",
	[105589] = "{id = 105589, Speaker = 500400, Text = '恩~看来它的圣诞回忆里都是眼泪。'}",
	[105590] = "{id = 105590, Speaker = 500400, Text = '啊，聊天搭讪可不是圣诞节的传统风俗！'}",
	[105591] = "{id = 105591, Speaker = 500400, Text = '哦，圣诞节果然会让人变得更强！'}",
	[105592] = "{id = 105592, Speaker = 500400, Text = '唔，今天真是个黑色的圣诞节。'}",
	[105593] = "{id = 105593, Speaker = 500400, Text = '唔呼呼，舒服得就像圣诞节一样！哇哈哈哈哈'}",
	[105594] = "{id = 105594, Speaker = 500400, Text = '主人，可不要在圣诞节丢了小命，我们还没挂圣诞袜呐！'}",
	[105595] = "{id = 105595, Speaker = 500400, Text = '没想到你为我准备了圣诞礼物。'}",
	[105596] = "{id = 105596, Speaker = 500400, Text = '过圣诞节难道不应该出去找点乐子嘛？'}",
	[105597] = "{id = 105597, Speaker = 500400, Text = '噢，我已经记不清你上次抚摸我是什么时候了。'}",
	[105598] = "{id = 105598, Speaker = 500400, Text = '美妙的圣诞节可不能饿着肚子过！'}",
	[105599] = "{id = 105599, Speaker = 500400, Text = '噢，主人做的圣诞节饰品可真时髦啊！'}",
	[105600] = "{id = 105600, Speaker = 500400, Text = '啊啊啊，这正是我想要的圣诞礼物！主人万岁！'}",
	[105601] = "{id = 105601, Speaker = 500410, Text = '得到主人取的名字后，我就不再是孤单的傀儡娃娃了。'}",
	[105602] = "{id = 105602, Speaker = 500410, Text = '主人，不要离开我的视线，好么？'}",
	[105603] = "{id = 105603, Speaker = 500410, Text = '我对主人的爱日复一日，如同病入膏肓，无药可救。'}",
	[105604] = "{id = 105604, Speaker = 500410, Text = '主人，求求你，让我在你身边继续陪伴你，哪怕一秒！'}",
	[105605] = "{id = 105605, Speaker = 500410, Text = '哼，这些人都比不上我最爱的主人。'}",
	[105606] = "{id = 105606, Speaker = 500410, Text = '不愧是我深爱的主人！成功是必然的！'}",
	[105607] = "{id = 105607, Speaker = 500410, Text = '狡猾的忽克连肯定做了手脚，要不要我帮你复仇，主人？'}",
	[105608] = "{id = 105608, Speaker = 500410, Text = '这是我对主人浓浓爱意的具象化表现~'}",
	[105609] = "{id = 105609, Speaker = 500410, Text = '啊，足以守护主人的强大力量~'}",
	[105610] = "{id = 105610, Speaker = 500410, Text = '我不想离开主人独自死去..'}",
	[105611] = "{id = 105611, Speaker = 500410, Text = '主人，我想要更多更多你的爱！'}",
	[105612] = "{id = 105612, Speaker = 500410, Text = '主人可以亲手喂我嘛？啊~~'}",
	[105613] = "{id = 105613, Speaker = 500410, Text = '好撑~我的胃可装不下太多食物。'}",
	[105614] = "{id = 105614, Speaker = 500410, Text = '主人终于回来了，我对你的思念快让我疯狂了~'}",
	[105615] = "{id = 105615, Speaker = 500410, Text = '妨碍主人的家伙就用付出性命的代价吧！'}",
	[105616] = "{id = 105616, Speaker = 500410, Text = '哼，你的力量和我对主人的爱比起来，根本不值一提！'}",
	[105617] = "{id = 105617, Speaker = 500410, Text = '妨碍主人的家伙，品尝我的诅咒吧！'}",
	[105618] = "{id = 105618, Speaker = 500410, Text = '我只想你属于我一个人...你是不是不要我了？'}",
	[105619] = "{id = 105619, Speaker = 500410, Text = '我的主人果然是米德加尔特第一帅气的勇士~'}",
	[105620] = "{id = 105620, Speaker = 500410, Text = '我会永远陪伴着你，无论生死。'}",
	[105621] = "{id = 105621, Speaker = 500410, Text = '主人的轻抚让我内心的创伤得到饿了治愈~'}",
	[105622] = "{id = 105622, Speaker = 500410, Text = '主人，你流血了!赶快包扎，我会让敌人付出代价的！'}",
	[105623] = "{id = 105623, Speaker = 500410, Text = '只要是你送我的，我都喜欢~'}",
	[105624] = "{id = 105624, Speaker = 500410, Text = '虽然我只是一个人偶，但我却对主人交出了真心。'}",
	[105625] = "{id = 105625, Speaker = 500410, Text = '快来摸摸你专属的傀儡娃娃~只有你的安抚能让我平静。'}",
	[105626] = "{id = 105626, Speaker = 500410, Text = '原来这种感觉就是肚子饿啊，能给我一些好吃的么？~~'}",
	[105627] = "{id = 105627, Speaker = 500410, Text = '真希望有一天主人会为我做一件好看的头饰。'}",
	[105628] = "{id = 105628, Speaker = 500410, Text = '啊~主人送我的这件礼物我会好好珍藏的。'}",
	[105629] = "{id = 105629, Speaker = 500420, Text = '有了主人给我的名字，我可以不用再伪装躲藏了！'}",
	[105630] = "{id = 105630, Speaker = 500420, Text = '好怕怕，主人你要挡在我身前保护我唷！'}",
	[105631] = "{id = 105631, Speaker = 500420, Text = '主人与我的感情越深，我的安全感越强！'}",
	[105632] = "{id = 105632, Speaker = 500420, Text = '遵命，我现在就藏到蛋里去~~'}",
	[105633] = "{id = 105633, Speaker = 500420, Text = '嘻嘻，这里真是个适合捉迷藏的好地方~'}",
	[105634] = "{id = 105634, Speaker = 500420, Text = '哇，主人的好运让我也来蹭蹭呗~'}",
	[105635] = "{id = 105635, Speaker = 500420, Text = '唔，主人别哭，我变个小丑逗你笑~'}",
	[105636] = "{id = 105636, Speaker = 500420, Text = '主人，我从山野林间收集来的礼物，你还喜欢嘛？'}",
	[105637] = "{id = 105637, Speaker = 500420, Text = '啊，没想到我也跟着主人变强了~'}",
	[105638] = "{id = 105638, Speaker = 500420, Text = '呜呜，我的精妙伪装竟然被识破了...'}",
	[105639] = "{id = 105639, Speaker = 500420, Text = '主人的怀抱就是为我遮风避雨的港湾！'}",
	[105640] = "{id = 105640, Speaker = 500420, Text = '饿得我都快变成一张纸啦，主人给我点好吃得吧~'}",
	[105641] = "{id = 105641, Speaker = 500420, Text = '吃不下啦！主人是想把我变成胖狸猫嘛？'}",
	[105642] = "{id = 105642, Speaker = 500420, Text = '嘻嘻，我最喜欢的主人回来啦，今天也带我去冒险，好不好？'}",
	[105643] = "{id = 105643, Speaker = 500420, Text = '哼，别小看了我百变狸猫的威力！'}",
	[105644] = "{id = 105644, Speaker = 500420, Text = '没想到有一天能和主人一起战胜这么强大的敌人~'}",
	[105645] = "{id = 105645, Speaker = 500420, Text = '主人太强啦~看主人的眼神都变成星星眼啦！'}",
	[105646] = "{id = 105646, Speaker = 500420, Text = '哼，下次我就变成MVP的样子，在你们聊天的时候捣蛋！'}",
	[105647] = "{id = 105647, Speaker = 500420, Text = '我家的主人变得越来越可靠了呢~'}",
	[105648] = "{id = 105648, Speaker = 500420, Text = '主人，不要再装死啦,快睁开眼睛吧！'}",
	[105649] = "{id = 105649, Speaker = 500420, Text = '嘻嘻，主人得抚摸就像穿过林间得清风般舒服~'}",
	[105650] = "{id = 105650, Speaker = 500420, Text = '主人，赶快做治疗措施，你的处境很危险！'}",
	[105651] = "{id = 105651, Speaker = 500420, Text = '有了主人给我的礼物，遇到再可怕的怪物我也不会退缩啦！'}",
	[105652] = "{id = 105652, Speaker = 500420, Text = '今天变个什么MVP来吓唬主人呢？'}",
	[105653] = "{id = 105653, Speaker = 500420, Text = '来摸摸我吧，我想对主人撒个娇~'}",
	[105654] = "{id = 105654, Speaker = 500420, Text = '主人！狸猫的肚子都饿扁了！'}",
	[105655] = "{id = 105655, Speaker = 500420, Text = '这个头饰能借我戴戴么？绝对会还给你哒。'}",
	[105656] = "{id = 105656, Speaker = 500420, Text = '哇，送给我的礼物嘛？真让我开心得真打滚~'}",
	[105657] = "{id = 105657, Speaker = 500430, Text = '啊，这美妙的名字和我的歌声多动听啊。'}",
	[105658] = "{id = 105658, Speaker = 500430, Text = '从蛋里孵化出来的感觉真让我怀念~'}",
	[105659] = "{id = 105659, Speaker = 500430, Text = '主人的温柔在任何时候，都让我缱绻无限呢。'}",
	[105660] = "{id = 105660, Speaker = 500430, Text = '啊，回到蛋里的感觉很温馨啊~'}",
	[105661] = "{id = 105661, Speaker = 500430, Text = '啊，主人的巢穴就是漂亮呢，好羡慕呀~'}",
	[105662] = "{id = 105662, Speaker = 500430, Text = '嘻嘻，我们载歌载舞好好庆祝一下吧~'}",
	[105663] = "{id = 105663, Speaker = 500430, Text = '别难过主人，听我唱一支欢快的小曲，转换一下心情吧~'}",
	[105664] = "{id = 105664, Speaker = 500430, Text = '哈比的一点小心意，主人可别嫌弃啊~'}",
	[105665] = "{id = 105665, Speaker = 500430, Text = '我感受到力量之风围绕在我的羽翼上~'}",
	[105666] = "{id = 105666, Speaker = 500430, Text = '啊，我从自由的天空坠落了...'}",
	[105667] = "{id = 105667, Speaker = 500430, Text = '嘻嘻，主人可不要迷上我的美貌哟！'}",
	[105668] = "{id = 105668, Speaker = 500430, Text = '主人，我饿得翅膀都挥不动了~'}",
	[105669] = "{id = 105669, Speaker = 500430, Text = '再吃下去，我可要飞不起来啦~'}",
	[105670] = "{id = 105670, Speaker = 500430, Text = '哟呼，主人今天我们要飞向何方呢？'}",
	[105671] = "{id = 105671, Speaker = 500430, Text = '尝尝我的锐羽与利爪吧！'}",
	[105672] = "{id = 105672, Speaker = 500430, Text = '谁也挡不住我与主人掀起的风暴！'}",
	[105673] = "{id = 105673, Speaker = 500430, Text = '在主人的力量面前随风飘散吧！'}",
	[105674] = "{id = 105674, Speaker = 500430, Text = '哼，主人和别人聊天的时候对我好冷淡，正无聊！'}",
	[105675] = "{id = 105675, Speaker = 500430, Text = '看到主人变得越来越强，我的心里就像吃了蜜一样甜~'}",
	[105676] = "{id = 105676, Speaker = 500430, Text = '主人别怕！我会用歌声将你唤醒的！'}",
	[105677] = "{id = 105677, Speaker = 500430, Text = '嘻嘻，主人的抚摸总是那么温柔呢~'}",
	[105678] = "{id = 105678, Speaker = 500430, Text = '主人，你的伤口好深，哈比看了好心疼啊！'}",
	[105679] = "{id = 105679, Speaker = 500430, Text = '这件装备一定能让我飞的更高更远！'}",
	[105680] = "{id = 105680, Speaker = 500430, Text = '我也不知道为什么他们那么想要我的翎羽...'}",
	[105681] = "{id = 105681, Speaker = 500430, Text = '主人，哈比想要被你温柔的抚摸~~'}",
	[105682] = "{id = 105682, Speaker = 500430, Text = '主人，主人~今天要给我喂什么美味呀？'}",
	[105683] = "{id = 105683, Speaker = 500430, Text = '哇，主人也能给我做一个好看的头饰吗？'}",
	[105684] = "{id = 105684, Speaker = 500430, Text = '唔芙芙，你可真是个贴心的主人呀~'}",
	[105685] = "{id = 105685, Speaker = 500440, Text = '主人取的名字，听上去真是让人兴奋不已。'}",
	[105686] = "{id = 105686, Speaker = 500440, Text = '带刺的玫瑰再次绽放。'}",
	[105687] = "{id = 105687, Speaker = 500440, Text = '我真是对你，越来越中意了。'}",
	[105688] = "{id = 105688, Speaker = 500440, Text = '这就让我回去了？可别后悔噢。'}",
	[105689] = "{id = 105689, Speaker = 500440, Text = '这就是你的公会？马马虎虎，还不错~'}",
	[105690] = "{id = 105690, Speaker = 500440, Text = '唷，居然成功了，不错嘛。'}",
	[105691] = "{id = 105691, Speaker = 500440, Text = '这副神情是干什么，振作一点。'}",
	[105692] = "{id = 105692, Speaker = 500440, Text = '我给的礼物你可要好好珍惜。'}",
	[105693] = "{id = 105693, Speaker = 500440, Text = '要不要来感受一下我登峰造极的鞭法？'}",
	[105694] = "{id = 105694, Speaker = 500440, Text = '唔，自古红颜多薄命啊...'}",
	[105695] = "{id = 105695, Speaker = 500440, Text = '偶尔被别人疼爱的感觉，也不错呢。'}",
	[105696] = "{id = 105696, Speaker = 500440, Text = '只有吃饱了才有离奇让我的鞭子跳舞！'}",
	[105697] = "{id = 105697, Speaker = 500440, Text = '苗条的身材是我的武器之一！'}",
	[105698] = "{id = 105698, Speaker = 500440, Text = '等不及回来让我烙下爱痕了嘛？'}",
	[105699] = "{id = 105699, Speaker = 500440, Text = '呵，就没有能不臣服于我鞭子下的生物。'}",
	[105700] = "{id = 105700, Speaker = 500440, Text = '哼哼，可别怪我下手太重。'}",
	[105701] = "{id = 105701, Speaker = 500440, Text = '我的疼爱可是致命的甜度。'}",
	[105702] = "{id = 105702, Speaker = 500440, Text = '敢对我三心二意的话，可是会吃鞭子的唷。'}",
	[105703] = "{id = 105703, Speaker = 500440, Text = '人果然在疼痛中才会成长呢'}",
	[105704] = "{id = 105704, Speaker = 500440, Text = '要是你就这样倒下，我可不会原谅你噢！'}",
	[105705] = "{id = 105705, Speaker = 500440, Text = '不要趁我陶醉乱摸奇怪的地方唷，主人。'}",
	[105706] = "{id = 105706, Speaker = 500440, Text = '保护好自己，你的伤口只能是我留下的，别人，呵……'}",
	[105707] = "{id = 105707, Speaker = 500440, Text = '这就是你的品味么，我就勉为其难的穿上它满足一下你吧。'}",
	[105708] = "{id = 105708, Speaker = 500440, Text = '舔嘴唇是因为我想到了有趣的玩法，想试试么？'}",
	[105709] = "{id = 105709, Speaker = 500440, Text = '带刺的玫瑰也需要轻抚，你还在等什么？'}",
	[105710] = "{id = 105710, Speaker = 500440, Text = '先填饱肚子，才有力气玩刺激的把戏。'}",
	[105711] = "{id = 105711, Speaker = 500440, Text = '增加魅力的小道具？没想到你还蛮有情趣的嘛。'}",
	[105712] = "{id = 105712, Speaker = 500440, Text = '恩，看来你很清楚我的小癖好。'}",
	[105713] = "{id = 105713, Speaker = 500450, Text = '汝取的这个名字，还算衬得上咱家的品味。'}",
	[105714] = "{id = 105714, Speaker = 500450, Text = '咱家终于可以出来呼吸新鲜空气了~'}",
	[105715] = "{id = 105715, Speaker = 500450, Text = '啊，不知不觉，咱家对汝的依赖已经到如此地步了呢~'}",
	[105716] = "{id = 105716, Speaker = 500450, Text = '咱家可不喜欢被关在蛋里做一只笼中雀。'}",
	[105717] = "{id = 105717, Speaker = 500450, Text = '哼，那些人盯着咱家的美貌就走不动路了呢。'}",
	[105718] = "{id = 105718, Speaker = 500450, Text = '哎呀，汝可真是福星高照呢，恭喜恭喜！'}",
	[105719] = "{id = 105719, Speaker = 500450, Text = '不要为了这点小挫折难过嘛，咱家会为你打气的！加油哟！'}",
	[105720] = "{id = 105720, Speaker = 500450, Text = '这是咱家的一点心意，汝还不快收下？'}",
	[105721] = "{id = 105721, Speaker = 500450, Text = '咱家的气息变得更强了！'}",
	[105722] = "{id = 105722, Speaker = 500450, Text = '啊，再美的樱花也有凋零的一天啊。'}",
	[105723] = "{id = 105723, Speaker = 500450, Text = '再咱家心里，汝温柔的样子最棒了'}",
	[105724] = "{id = 105724, Speaker = 500450, Text = '唔，咱家肚子饿了你都不关心嘛？'}",
	[105725] = "{id = 105725, Speaker = 500450, Text = '再吃下去，咱家的曼妙身材都要走样了~'}",
	[105726] = "{id = 105726, Speaker = 500450, Text = '哼，果然还是太过思念咱家所以才回来了吧？'}",
	[105727] = "{id = 105727, Speaker = 500450, Text = '哼，弱者就从咱家的眼前消失吧!'}",
	[105728] = "{id = 105728, Speaker = 500450, Text = '我们的力量将它送葬绰绰有余~'}",
	[105729] = "{id = 105729, Speaker = 500450, Text = '强敌也会拜倒再主人的力量与咱家的裙下~'}",
	[105730] = "{id = 105730, Speaker = 500450, Text = '哼，每天和咱家聊天你还不满足么？'}",
	[105731] = "{id = 105731, Speaker = 500450, Text = '汝真是越来越强大了，咱家也可以安心依靠汝了吧~'}",
	[105732] = "{id = 105732, Speaker = 500450, Text = '你怎么忍心丢下咱家一个人啊，笨蛋！'}",
	[105733] = "{id = 105733, Speaker = 500450, Text = '沉溺在汝的温柔里，真是一种享受呢~'}",
	[105734] = "{id = 105734, Speaker = 500450, Text = '汝可别在战斗中大意啊！要是受伤的花，咱家会为你担忧心碎的。'}",
	[105735] = "{id = 105735, Speaker = 500450, Text = '快来看看，咱家穿着好看么？'}",
	[105736] = "{id = 105736, Speaker = 500450, Text = '最美的，果然还是天津町的樱花雨啊！'}",
	[105737] = "{id = 105737, Speaker = 500450, Text = '咱家想念汝的温柔了呢~'}",
	[105738] = "{id = 105738, Speaker = 500450, Text = '肚子饿了这种话，女孩子说出来好失礼！食物还没准备好嘛？'}",
	[105739] = "{id = 105739, Speaker = 500450, Text = '没想到汝的手艺竟然如此精致，下次也为咱家做一个呗~'}",
	[105740] = "{id = 105740, Speaker = 500450, Text = '哦，汝送的礼物真是深得咱家的心啊'}",
	[105741] = "{id = 105741, Speaker = 500460, Text = '名字是最重要的言灵，我很高兴我和你之间有了这份羁绊。'}",
	[105742] = "{id = 105742, Speaker = 500460, Text = '要我为你跳一支舞吗？我的主人~'}",
	[105743] = "{id = 105743, Speaker = 500460, Text = '我越来越想守护你，我的一切都为了你而存在。'}",
	[105744] = "{id = 105744, Speaker = 500460, Text = '我要离开了嘛……我会在宠物蛋里等着你的召唤……'}",
	[105745] = "{id = 105745, Speaker = 500460, Text = '这里好棒呀！能种棵樱花树在这里的话就更好了~'}",
	[105746] = "{id = 105746, Speaker = 500460, Text = '太棒了呢~今天的主人的运势一定是大吉~'}",
	[105747] = "{id = 105747, Speaker = 500460, Text = '给主人加了仙女的祝福~下一次一定会成功哒~'}",
	[105748] = "{id = 105748, Speaker = 500460, Text = '这是…是我给主人的礼物~主人喜欢吗？'}",
	[105749] = "{id = 105749, Speaker = 500460, Text = '我觉得我的舞跳得越来越好了~是不是呀~'}",
	[105750] = "{id = 105750, Speaker = 500460, Text = '我唯一的愿望是能再次回到你的身边，我的主人……'}",
	[105751] = "{id = 105751, Speaker = 500460, Text = '被主人疼爱的时候，世界都变成粉红色啦！'}",
	[105752] = "{id = 105752, Speaker = 500460, Text = '果然还是人类的食物好吃，露水太淡了。'}",
	[105753] = "{id = 105753, Speaker = 500460, Text = '小仙女只喝露水，主人你不知道嘛？'}",
	[105754] = "{id = 105754, Speaker = 500460, Text = '我一直在等你~樱花开了~你也终于来了~'}",
	[105755] = "{id = 105755, Speaker = 500460, Text = '你的心愿我都会帮你达成。'}",
	[105756] = "{id = 105756, Speaker = 500460, Text = '至少他死前目睹了仙女的芳容~'}",
	[105757] = "{id = 105757, Speaker = 500460, Text = '我果然没有看错，你是这世上最出色的人。'}",
	[105758] = "{id = 105758, Speaker = 500460, Text = '你们在聊什么，让我一起听听好不好~'}",
	[105759] = "{id = 105759, Speaker = 500460, Text = '祝你武运昌盛，我的主人。'}",
	[105760] = "{id = 105760, Speaker = 500460, Text = '我的思念能唤回你在我身边嘛……'}",
	[105761] = "{id = 105761, Speaker = 500460, Text = '别人我不会轻易给他摸摸的，但是主人你可以哦~'}",
	[105762] = "{id = 105762, Speaker = 500460, Text = '快来我这接受治疗！我不可以失去你。'}",
	[105763] = "{id = 105763, Speaker = 500460, Text = '这是给我戴的嘛？真好看~主人你觉得怎么样？'}",
	[105764] = "{id = 105764, Speaker = 500460, Text = '主人想不想看我跳舞呢~'}",
	[105765] = "{id = 105765, Speaker = 500460, Text = '人与人的肢体接触有时候会成为人们的力量~'}",
	[105766] = "{id = 105766, Speaker = 500460, Text = '这个东西看上去很好吃，要不要休息一下，一起品尝？'}",
	[105767] = "{id = 105767, Speaker = 500460, Text = '这个饰品主人戴一定很适合~'}",
	[105768] = "{id = 105768, Speaker = 500460, Text = '我从这个礼物上感受到了主人的心意~我会好好珍惜的~'}",
	[105769] = "{id = 105769, Speaker = 500470, Text = '主，主人亲自取的名字么？我太喜欢了。'}",
	[105770] = "{id = 105770, Speaker = 500470, Text = '你的专属女仆随时待命~'}",
	[105771] = "{id = 105771, Speaker = 500470, Text = '我对主人的倾慕与忠诚与日俱增~'}",
	[105772] = "{id = 105772, Speaker = 500470, Text = '先不打扰主人了~有事记得叫我哦~'}",
	[105773] = "{id = 105773, Speaker = 500470, Text = '主人的公会我一定打扫的干干净净！'}",
	[105774] = "{id = 105774, Speaker = 500470, Text = '太好了~成功了呢！主人真是厉害！'}",
	[105775] = "{id = 105775, Speaker = 500470, Text = '别难过~我会一直陪着主人的~'}",
	[105776] = "{id = 105776, Speaker = 500470, Text = '这是我给主人准备的礼物，不知道主人喜不喜欢~'}",
	[105777] = "{id = 105777, Speaker = 500470, Text = '我要变得更强，才能更好的侍奉主人！'}",
	[105778] = "{id = 105778, Speaker = 500470, Text = '呜呜呜，主人你醒一醒，呜呜呜呜……'}",
	[105779] = "{id = 105779, Speaker = 500470, Text = '我愿用尽我的一生，永远陪伴主人。'}",
	[105780] = "{id = 105780, Speaker = 500470, Text = '主人给我的吃的太美味啦，力气一下就涌上来了！'}",
	[105781] = "{id = 105781, Speaker = 500470, Text = '吃的太多可没办法帮主人好好打扫呢。'}",
	[105782] = "{id = 105782, Speaker = 500470, Text = '欢迎回家~我的主人~'}",
	[105783] = "{id = 105783, Speaker = 500470, Text = '主人的敌人，从世界上彻底消失吧！'}",
	[105784] = "{id = 105784, Speaker = 500470, Text = '敌人在主人的力量面前，不值一提呢！'}",
	[105785] = "{id = 105785, Speaker = 500470, Text = '主人战斗的时候，我也没拖后腿哟！'}",
	[105786] = "{id = 105786, Speaker = 500470, Text = '唔，好在意主人的聊天内容啊，主人的全部都想知道！'}",
	[105787] = "{id = 105787, Speaker = 500470, Text = '主人似乎变得更强了，我也不能输给主人呢！'}",
	[105788] = "{id = 105788, Speaker = 500470, Text = '主人！不要！没了你，我的世界便失去了光！'}",
	[105789] = "{id = 105789, Speaker = 500470, Text = '嘿嘿嘿，主人真的好温柔，总是让我面红心跳呢。'}",
	[105790] = "{id = 105790, Speaker = 500470, Text = '坚持住，主人，我会为你包扎伤口的！'}",
	[105791] = "{id = 105791, Speaker = 500470, Text = '哇，真的是给我的么？主人真的太宠我了啦！'}",
	[105792] = "{id = 105792, Speaker = 500470, Text = '我的目标是成为主人专属的最强女仆！'}",
	[105793] = "{id = 105793, Speaker = 500470, Text = '唔，今天我也很努力的侍奉着主人呢，给我一点小小的奖励吧！'}",
	[105794] = "{id = 105794, Speaker = 500470, Text = '肚子饿的咕咕叫了，真是丢人啊。'}",
	[105795] = "{id = 105795, Speaker = 500470, Text = '没想到我家的主人还有这样心灵手巧的一面呢，真是刮目相看！'}",
	[105796] = "{id = 105796, Speaker = 500470, Text = '哇，真的可以收下么？主人对我这么温柔，我快幸福得晕倒了！'}",
	[105797] = "{id = 105797, Speaker = 4797, Text = '探究魔法与自然的联系是我毕生追求的事业。'}",
	[105798] = "{id = 105798, Speaker = 4798, Text = '我一点都不害怕，这里是天神赐予我们的宝藏……我了解它，它会保护我！'}",
	[105799] = "{id = 105799, Speaker = 4799, Text = '我要努力工作赚钱！妈妈还在等着我……等着我回家……'}",
	[105800] = "{id = 105800, Speaker = 4800, Text = '我很害怕……但是又不得不来挖矿……这种日子什么时候才是尽头……'}",
	[105801] = "{id = 105801, Speaker = 4749, Text = '是否开启[c][ffff00]火焰之地[-][/c]的冒险之旅（[c][ffff00]需要组队[-][/c]）？', Option = '{是,0},{以后再说,1}'}",
	[105802] = "{id = 105802, Speaker = 4749, Text = '[c][ffff00]需要组队[-][/c]后才能进入[c][ffff00]火焰之地[-][/c]哦！'}",
	[105803] = "{id = 105803, Speaker = 4797, Text = '你也是来探寻火焰领主卡浩的故事的吗？', Option = '{是的,2},{不是,3}'}",
	[105804] = "{id = 105804, Speaker = 4797, Text = '熔岩洞窟是个富产矿石的地方，而且这里的矿石很特别，这是一种天然自带火焰力量的矿石，名为火焰矿石。'}",
	[105805] = "{id = 105805, Speaker = 4797, Text = '这种矿石可以说是完全因为生活在熔岩洞窟的炎之领主的力量才会诞生，售卖价值很高，人类曾有很长的一段时间一直在这边挖掘这种矿石。'}",
	[105806] = "{id = 105806, Speaker = 4797, Text = '后来，这里出了一场大事故，火焰领主非常愤怒，用强大的力量将所有人类矿工赶出了洞窟。'}",
	[105807] = "{id = 105807, Speaker = 4797, Text = '从此之后，火焰矿石不再有固定的出产，成为了稀有之物。'}",
	[105808] = "{id = 105808, Speaker = 4797, Text = '而当时的这场大事故，使无数的矿工丧命于此，其中有三个矿工似乎对这里还留有什么执念，而他们的灵魂现在正被这份执念禁锢于此。'}",
	[105809] = "{id = 105809, Speaker = 4797, Text = '帮助他们找到他们的执念之物，解放他们的灵魂，你再回来找我。相信我，你的付出一定会有回报。'}",
	[105810] = "{id = 105810, Speaker = 4797, Text = '你已经帮助他们了吗？让我先问你几个问题。'}",
	[105811] = "{id = 105811, Speaker = 4797, Text = '矿工欧尼斯特是因为什么选择矿工这个职业？', Option = '{因为爱好,2},{因为金钱,3},{因为爱情,3}'}",
	[105812] = "{id = 105812, Speaker = 4797, Text = '看来你还是不够了解这些矿工们啊……'}",
	[105813] = "{id = 105813, Speaker = 4797, Text = '矿工莱尼尔为了谁来矿洞里工作赚钱？', Option = '{为了父亲,4},{为了母亲,5},{为了姐姐,4}'}",
	[105814] = "{id = 105814, Speaker = 4797, Text = '矿工瓦尔特为什么会受到别的矿工的排挤？', Option = '{因为胆小,6},{因为贪婪,7},{因为自大,7}'}",
	[105815] = "{id = 105815, Speaker = 4797, Text = '看来你已经很清楚这些矿工们的事情了……'}",
	[105816] = "{id = 105816, Speaker = 4797, Text = '其实这个事故也不是矿工们的错误，他们也是这个时代的受害者，他们不知道自然是不会无尽的回应他们的索取……'}",
	[105817] = "{id = 105817, Speaker = 4797, Text = '在那件事情之后人类也并没有停止过度开发自然的脚步，如今修发兹发德共和国的国土已经有很大一部分变成废土与沙漠。'}",
	[105818] = "{id = 105818, Speaker = 4797, Text = '真心希望这些死去的矿工灵魂能得到真正的解放……'}",
	[105819] = "{id = 105819, Speaker = 4797, Text = '冒险者，这些火焰矿石就作为回报送给你吧，这是我从熔岩洞窟里捡到的，希望它们能警醒你，对自然保有敬畏。', Option = '我会的'}",
	[105820] = "{id = 105820, Speaker = 4798, Text = '我怎么找不到我的铁镐了，去哪里了呢？找不到就没法干活了……不干活就没有工钱了……你可以帮我一起找找铁镐吗？', Option = '{找到了,2},{没见到,3}'}",
	[105821] = "{id = 105821, Speaker = 4799, Text = '你知道我的推车在哪里吗？找不到它会被工头骂的……说不定还要赶我回家……好不容易能赚钱……你能帮我找一找吗？', Option = '{找到了,2},{没见到,3}'}",
	[105822] = "{id = 105822, Speaker = 4800, Text = '我的安全帽不见了！得赶紧找到它啊！这里的随时都会有石头砸下来，万一砸到头怎么办……你也去帮我找一下它！', Option = '{找到了,2},{没见到,3}'}",
	[105823] = "{id = 105823, Speaker = 4798, Text = '你再找找，一定就在这附近。'}",
	[105824] = "{id = 105824, Speaker = 4799, Text = '呜呜……真的找不到了吗？呜呜……'}",
	[105825] = "{id = 105825, Speaker = 4800, Text = '石头……石头要砸下来了！你再去找一下！'}",
	[105826] = "{id = 105826, Speaker = 4798, Text = '这对我很重要，你不要欺骗我！'}",
	[105827] = "{id = 105827, Speaker = 4799, Text = '妈妈说，说谎的孩子是会遭到惩罚的……'}",
	[105828] = "{id = 105828, Speaker = 4800, Text = '你骗我！你是想害死我是不是？'}",
	[105829] = "{id = 105829, Speaker = 4798, Text = '我的铁镐！终于找到了……可以继续干活了……谢谢你。'}",
	[105830] = "{id = 105830, Speaker = 4799, Text = '怎么有点破破烂烂的……算了能用就好！你人真好。'}",
	[105831] = "{id = 105831, Speaker = 4800, Text = '快给我！我得赶紧戴上！不然会有危险！'}",
	[105832] = "{id = 105832, Speaker = 4797, Text = '熔岩洞窟是个富产矿石的地方，而且这里的矿石很特别，这是一种天然自带火焰力量的矿石，名为火焰矿石。'}",
	[105833] = "{id = 105833, Speaker = 4797, Text = '这种矿石可以说是完全因为生活在熔岩洞窟的炎之领主的力量才会诞生，售卖价值很高，人类曾有很长的一段时间一直在这边挖掘这种矿石。'}",
	[105834] = "{id = 105834, Speaker = 4797, Text = '后来，这里出了一场大事故，火焰领主非常愤怒，用强大的力量将所有人类矿工赶出了洞窟。'}",
	[105835] = "{id = 105835, Speaker = 4797, Text = '从此之后，火焰矿石不再有固定的出产，成为了稀有之物。'}",
	[105836] = "{id = 105836, Speaker = 4797, Text = '其实这个事故也不是矿工们的错误，他们也是这个时代的受害者，他们不知道自然是不会无尽的回应他们的索取……'}",
	[105837] = "{id = 105837, Speaker = 4797, Text = '在那件事情之后人类也并没有停止过度开发自然的脚步，如今修发兹发德共和国的国土已经有很大一部分变成废土与沙漠。'}",
	[105838] = "{id = 105838, Speaker = 4797, Text = '真心希望这些死去的矿工灵魂能得到真正的解放……'}",
	[105839] = "{id = 105839, Speaker = 4794, Text = '25年前，艾因布洛克的某个村庄突然爆发了一场致命瘟疫。'}",
	[105840] = "{id = 105840, Speaker = 4794, Text = '与此同时，雷根贝勒公司的执行部长艾恩·格林带着一份机密文件离奇失踪。'}",
	[105841] = "{id = 105841, Speaker = 4794, Text = '令人啧啧称奇的是，一瓶解药与神秘字条突然出现在村庄的神像旁，解药让疫情在短时间内得到了控制。'}",
	[105842] = "{id = 105842, Speaker = 4794, Text = '此后疑似瘟疫污染源的机器也由雷根贝勒公司宣布永久损坏。'}",
	[105843] = "{id = 105843, Speaker = 4794, Text = '至于杳无音信的艾恩部长，有人说他带着充斥罪证的机密文件畏罪潜逃到了异国他乡；有人说见到他感染了瘟疫，死在了原野的林间，恶有恶报。'}",
	[105844] = "{id = 105844, Speaker = 4794, Text = '但是父亲怎么会做这样的事情呢！'}",
	[105845] = "{id = 105845, Speaker = 4794, Text = '在所有人都觉得父亲是畏罪潜逃时，我坚信他是为了完成必须做的事而离开。'}",
	[105846] = "{id = 105846, Speaker = 4794, Text = '因为父亲他，是个善良而执着的人啊。'}",
	[105847] = "{id = 105847, Speaker = 4788, Text = '几千年前，在这片原野上曾有一个以打猎和采集为生的部落。'}",
	[105848] = "{id = 105848, Speaker = 4788, Text = '那时候的人类可依仗的东西并不多，没有科技也没有法术，甚至连对事物足够的认知都没有。他们可以依靠的只有自己的双手。'}",
	[105849] = "{id = 105849, Speaker = 4788, Text = '不知从何时开始，鸟人一族在村落外面扎了根。为了原野上的资源，外出的勇士们经常与鸟人发生冲突，死伤甚多。'}",
	[105850] = "{id = 105850, Speaker = 4788, Text = '勇士们渐渐的不敢轻易外出猎物，村落也渐渐失去了往日的欣荣。'}",
	[105851] = "{id = 105851, Speaker = 4788, Text = '所有人都惶惶不安，祭司们不分昼夜的在祭台上祈求天神为村子指明一条生存的道路。'}",
	[105852] = "{id = 105852, Speaker = 4788, Text = '大祭司有一晚做了一个梦。在梦里，一个逆着光看不清面容的青年人举着一颗发光的金色鸟蛋，鸟蛋的光芒笼罩着村落，村民的脸上露出幸福的神情。'}",
	[105853] = "{id = 105853, Speaker = 4788, Text = '大祭司认为这个梦是天神给他们的指引，他连忙将梦境告诉了大家。'}",
	[105854] = "{id = 105854, Speaker = 4788, Text = '在村长的提议下，村落将举办一个大型祭祀，为愿意外出寻找黄金鸟蛋的勇士们祈福送行，而找到鸟蛋的勇士就是天选勇士，他将带领村落走出困境。'}",
	[105855] = "{id = 105855, Speaker = 4788, Text = '愿意外出寻找黄金鸟蛋的勇士大约有二十个，他们都是刚毅勇敢的青年。'}",
	[105856] = "{id = 105856, Speaker = 4788, Text = '他们不在乎自己是不是那个天选勇士，他们只想找到那颗可以守护村子的鸟蛋。'}",
	[105857] = "{id = 105857, Speaker = 4788, Text = '最后黄金鸟蛋被找到了，但是回来的勇士却只有五个……', Option = '后来如何'}",
	[105858] = "{id = 105858, Speaker = 4788, Text = '这颗鸟蛋的确具有一定的魔力！'}",
	[105859] = "{id = 105859, Speaker = 4788, Text = '它日复一日年复一年的吸取鸟人王哈利的力量，村落外面的鸟人感受到了这股熟悉的力量，也不再与村落的人起冲突。'}",
	[105860] = "{id = 105860, Speaker = 4788, Text = '天选勇士带领着村民开荒拓土，将野外采集来的种子种植在土地里，通过不断的摸索，转变成了一个农耕村落，自然村民也都过上了安稳的生活。'}",
	[105861] = "{id = 105861, Speaker = 4782, Text = '在许多年前，熔岩洞窟曾发生过一次魔物大暴动，灾厄之火一度逼近朱诺城。'}",
	[105862] = "{id = 105862, Speaker = 4782, Text = '四位来自各地的勇士自发组成了一支特别行动小队，他们一路披荆斩棘，将狂化的魔物群压制回了熔岩洞窟。'}",
	[105863] = "{id = 105863, Speaker = 4782, Text = '最后精疲力竭的勇士们牺牲自己终结了灾难，但随着时间的流逝，众人遗忘了英雄们的事迹。'}",
	[105864] = "{id = 105864, Speaker = 4782, Text = '今天我与两位历史学家想让英雄们的事迹重见天日，同时也想警示世人。'}",
	[105865] = "{id = 105865, Speaker = 4782, Text = '英雄虽然已经牺牲，但他们的遗物会时刻提醒着我们。'}",
	[105866] = "{id = 105866, Speaker = 4782, Text = '当你拥有了强大的能力，将这力量为自己或是为他人使用。'}",
	[105867] = "{id = 105867, Speaker = 4782, Text = '这是一道永远没有对错的选择题。'}",
	[105868] = "{id = 105868, Speaker = 4782, Text = '但对于那四位为了朱诺，毅然决然选择牺牲自己的勇士…'}",
	[105869] = "{id = 105869, Speaker = 4782, Text = '我们应永远献上最高的敬意与缅怀。'}",
	[105870] = "{id = 105870, Speaker = 4782, Text = '诺法、桃丽、瓦西里、斯曼玲·黎风，朱诺会永远铭记这四个名字。'}",
	[105871] = "{id = 105871, Speaker = 4792, Text = '你相信命运吗？我以前一直想不明白，为什么我只是一只小恶魔……'}",
	[105872] = "{id = 105872, Speaker = 4792, Text = '我有一个远方亲戚家的哥哥，是一只特别帅气的爆炎小恶魔！'}",
	[105873] = "{id = 105873, Speaker = 4792, Text = '我真是好羡慕它啊！'}",
	[105874] = "{id = 105874, Speaker = 4792, Text = '于是我就央求长老带我来熔岩洞窟，我也要变成一只帅气的爆炎小恶魔！'}",
	[105875] = "{id = 105875, Speaker = 4792, Text = '但长老说即使我现在一直呆在熔岩洞窟，我也是不能变成爆炎小恶魔的……'}",
	[105876] = "{id = 105876, Speaker = 4792, Text = '但是，如果将火焰之力直接注入我的体内！说不定就会成功了！'}",
	[105877] = "{id = 105877, Speaker = 4792, Text = '冒险者，你看，现在我真的梦想成真了！'}",
	[105878] = "{id = 105878, Speaker = 4792, Text = '有时候，生活就是这样，如果你想飞翔，那就用力去飞吧。'}",
	[105879] = "{id = 105879, Speaker = 4368, Text = '你好啊冒险者！', Option = '你好'}",
	[105880] = "{id = 105880, Speaker = 4368, Text = '本喵就是佣兵猫阿宝，如果需要雇佣佣兵猫的话，可以先考虑本喵哦喵！', Option = '阿宝？', Emoji = 17}",
	[105881] = "{id = 105881, Speaker = 4368, Text = '对哒！看来你还对本喵不是很了解呢。'}",
	[105882] = "{id = 105882, Speaker = 4368, Text = '本喵可以被雇佣，雇佣后会跟随在你身后，随时帮助雇主战斗哦喵！', Option = '原来如此'}",
	[105883] = "{id = 105883, Speaker = 4368, Text = '而本喵就是佣兵猫团队中的物理输出担当！想不想瞧瞧本喵的猫波气功？', Emoji = 5}",
	[105884] = "{id = 105884, Speaker = 4368, Text = '好了，现在开始你可以雇佣本喵了，不过可不是免费的哦喵！', Option = '好的'}",
	[105885] = "{id = 105885, Speaker = 4556, Text = '你看起来有些瘦弱啊冒险者！', Option = '…'}",
	[105886] = "{id = 105886, Speaker = 4556, Text = '所以你需要一个坚实的肉盾，那就是在下——山葵！', Option = '雇佣兵？'}",
	[105887] = "{id = 105887, Speaker = 4556, Text = '没错！有了在下，可以帮你分担百分之九十九点九的伤害！', Option = '真的吗？'}",
	[105888] = "{id = 105888, Speaker = 4556, Text = '你居然质疑在下！？我可是雇佣兵中最坚实的肉盾！', Option = '好的'}",
	[105889] = "{id = 105889, Speaker = 4556, Text = '现在你可以雇佣在下了，你只需要站在下身后就好！'}",
	[105890] = "{id = 105890, Speaker = 4558, Text = '啊…尊贵的客人…您是想要雇佣妾身嘛？', Option = '什么东西'}",
	[105891] = "{id = 105891, Speaker = 4558, Text = '咦？尊贵的客人，您是第一次雇佣我吗？', Option = '…'}",
	[105892] = "{id = 105892, Speaker = 4558, Text = '好吧，那妾身先介绍一下。'}",
	[105893] = "{id = 105893, Speaker = 4558, Text = '妾身是美莎，美丽的美，美莎的莎。', Option = '…'}",
	[105894] = "{id = 105894, Speaker = 4558, Text = '妾身可是佣兵猫中的颜值担当！', Option = '只有颜值吗'}",
	[105895] = "{id = 105895, Speaker = 4558, Text = '其次，妾身也是佣兵猫中的法术担当！', Option = '…'}",
	[105896] = "{id = 105896, Speaker = 4558, Text = '难道应该把法术放在前面介绍吗？', Option = '不然嘞'}",
	[105897] = "{id = 105897, Speaker = 4558, Text = '所以，雇佣了妾身，等于雇佣了佣兵猫中的两大担当！', Option = '…'}",
	[105898] = "{id = 105898, Speaker = 4558, Text = '是不是很划算呢…记得妾身叫美莎哦！'}",
	[105899] = "{id = 105899, Speaker = 4302, Text = '我是佣兵喵中人气最高的—五！郎！', Option = '五郎？'}",
	[105900] = "{id = 105900, Speaker = 4302, Text = '对哒！我有很多寿司，冒险途中，可以给你投食哦！', Option = '…'}",
	[105901] = "{id = 105901, Speaker = 4302, Text = '不要叫我奶妈，也不太要叫我牧师！我就是我，和外面那些佣兵猫可不一样！'}",
	[105902] = "{id = 105902, Speaker = 4556, Text = '喂！你在那胡说八道什么！？公平竞争好嘛！', Emoji = 12}",
	[105903] = "{id = 105903, Speaker = 4302, Text = '糟糕！（小声的说）让那个胸大无脑的山葵听到了！', Option = '…', Emoji = 1}",
	[105904] = "{id = 105904, Speaker = 4302, Text = '咳咳…总之，带上我，每天省好几万zeny的药钱呢！'}",
	[105905] = "{id = 105905, Speaker = 4302, Text = '而且，这些寿司可都是我亲手做的！很好吃哦！', Emoji = 5}",
	[105906] = "{id = 105906, Speaker = 4302, Text = '我是五郎，不是二郎！别记错了哟！'}",
	[105907] = "{id = 105907, Speaker = 4368, Text = '根据我们佣兵猫的规定，每只佣兵猫都可以对雇佣者进行考核。'}",
	[105908] = "{id = 105908, Speaker = 4368, Text = '通过考核的冒险者可以获得免费雇佣我们的机会哦！', Option = '真的吗？'}",
	[105909] = "{id = 105909, Speaker = 4368, Text = '当然了，对于本喵来说，最好的考核当然是比武了！'}",
	[105910] = "{id = 105910, Speaker = 4368, Text = '可惜这是普隆德拉，不允许动手打斗。'}",
	[105911] = "{id = 105911, Speaker = 4368, Text = '所以，能让本喵感到佩服的，只有那些勇敢的冒险者。'}",
	[105912] = "{id = 105912, Speaker = 4368, Text = '从那些凶恶的树精身上取得一些树皮，本喵想用这些树皮制作一个人偶。'}",
	[105913] = "{id = 105913, Speaker = 4368, Text = '这样本喵就可以天天用人偶来练手啦。'}",
	[105914] = "{id = 105914, Speaker = 4368, Text = '完成这个本喵才会承认你，你也就能通过本喵的考核了。', Option = '没问题'}",
	[105915] = "{id = 105915, Speaker = 4368, Text = '嘿，动作不慢嘛老板！算你通过考核啦！'}",
	[105916] = "{id = 105916, Speaker = 4556, Text = '你好，在下山葵！', Option = '…'}",
	[105917] = "{id = 105917, Speaker = 4556, Text = '嗯，因为怕你忘记在下的名字，所以只好重复一次。', Option = '我知道啦'}",
	[105918] = "{id = 105918, Speaker = 4556, Text = '在下有事需要你的帮助。', Option = '说吧'}",
	[105919] = "{id = 105919, Speaker = 4556, Text = '嗯…是这样，昨晚，五郎给在下送了一包寿司。'}",
	[105920] = "{id = 105920, Speaker = 4556, Text = '结果，在下吃完，今天就开始闹肚子。'}",
	[105921] = "{id = 105921, Speaker = 4556, Text = '五郎那小子，怪不得突然这么好心。', Option = '…'}",
	[105922] = "{id = 105922, Speaker = 4556, Text = '现在实在行动不便，麻烦你帮我去带一份暖身料理回来，必有重谢！', Option = '没问题'}",
	[105923] = "{id = 105923, Speaker = 4556, Text = '速度真快！谢谢你冒险者！在下现在就要去找五郎算账！', Option = '…'}",
	[105924] = "{id = 105924, Speaker = 4556, Text = '哦对了，你帮在下这么大忙，就算是佣兵猫的考核吧！'}",
	[105925] = "{id = 105925, Speaker = 4556, Text = '暖身料理啊冒险者，快点，在下肚子实在太难受了！'}",
	[105926] = "{id = 105926, Speaker = 4556, Text = '这是奖励，请你收下。可以用来立刻雇佣在下！不过先等在下找完五郎再说！'}",
	[105927] = "{id = 105927, Speaker = 4558, Text = '您想免费雇佣妾身吗？机会难得哦！', Option = '当然'}",
