	[105931] = "{id = 105931, Speaker = 3043, Text = '唔，就是“我中意你”，不过因为美莎很喜欢这个，所以她对这件物品的需求可是非常大的。'}",
	[105932] = "{id = 105932, Speaker = 3043, Text = '你可要多买一点哟。', Option = '好的'}",
