	[106053] = "{id = 106053, Speaker = 7392, Text = '唔吼…'}",
	[106054] = "{id = 106054, Speaker = 7392, Text = '我...我不想。'}",
	[106055] = "{id = 106055, Speaker = 7392, Text = '不想死…'}",
	[106056] = "{id = 106056, Speaker = 7392, Text = '救…救救我…'}",
	[106057] = "{id = 106057, Speaker = 7392, Text = '地下…'}",
	[106058] = "{id = 106058, Speaker = 7392, Text = '危险…快跑…'}",
	[106059] = "{id = 106059, Speaker = 7348, Text = '可恶的宝伏，简直惨无人道。'}",
	[106060] = "{id = 106060, Speaker = 7348, Text = '这一定又是一个他罪恶研究的牺牲品。', Option = '是啊'}",
	[106061] = "{id = 106061, Speaker = 7348, Text = '我们现在不能鲁莽，最好是召集大家一起行动。'}",
	[106062] = "{id = 106062, Speaker = 7347, Text = '塞壬说的对。'}",
	[106063] = "{id = 106063, Speaker = 7358, Text = '是时候让宝伏看看我们秘密之翼的厉害了！！'}",
	[106064] = "{id = 106064, Speaker = 7348, Text = '此次行动的危险性将无法估计！'}",
	[106065] = "{id = 106065, Speaker = 7348, Text = '但我对大家有信心！！'}",
	[106066] = "{id = 106066, Speaker = 7348, Text = '冒险者，你准备好了吗？', Option = '{准备好了,3},{还没有,4}'}",
	[106067] = "{id = 106067, Speaker = 7348, Text = '我们要争分夺秒！'}",
	[106068] = "{id = 106068, Speaker = 7402, Text = '太惨烈了…这里到处弥漫着不详的气息…'}",
	[106069] = "{id = 106069, Speaker = 7402, Text = '宝伏就在前面！'}",
	[106070] = "{id = 106070, Speaker = 7403, Text = '宝伏，你跑不掉了！'}",
	[106071] = "{id = 106071, Speaker = 7412, Text = '嘿嘿嘿嘿~你们可真是群天真的小可爱。'}",
	[106072] = "{id = 106072, Speaker = 7412, Text = '出来吧~我的宝贝们~'}",
	[106073] = "{id = 106073, Speaker = 7411, Text = '什么！！', Emoji = 7}",
	[106074] = "{id = 106074, Speaker = 7410, Text = '塞壬，你看清楚，那不是你的父亲！'}",
	[106075] = "{id = 106075, Speaker = 7410, Text = '那只是宝伏的变异魔物！'}",
	[106076] = "{id = 106076, Speaker = 7411, Text = '我不会再让阴谋与邪恶蒙蔽我的双眼！！！'}",
	[106077] = "{id = 106077, Speaker = 7410, Text = '干的好！接下来的交给我们！'}",
	[106078] = "{id = 106078, Speaker = 7412, Text = '可恶！！'}",
	[106079] = "{id = 106079, Speaker = 7411, Text = '冒险者！别让他跑了！'}",
	[106080] = "{id = 106080, Speaker = 7410, Text = '这家伙没有想象中的厉害嘛。'}",
	[106081] = "{id = 106081, Speaker = 7418, Text = '渺小的人类，嘿嘿嘿嘿。'}",
	[106082] = "{id = 106082, Speaker = 7418, Text = '你们就是不懂进化的艺术！'}",
	[106083] = "{id = 106083, Speaker = 7418, Text = '啊…无知的你们啊，为什么不跟着我踏进新世界的大门？'}",
	[106084] = "{id = 106084, Speaker = 7411, Text = '如果你乖乖跟我们走，我们可以不杀你。'}",
	[106085] = "{id = 106085, Speaker = 7418, Text = '嘿嘿嘿嘿，没用的，你们抹去的无非只是飘渺的幻想。'}",
	[106086] = "{id = 106086, Speaker = 7410, Text = '他到底在说什么，是疯了吗？'}",
	[106087] = "{id = 106087, Speaker = 7418, Text = '嘿嘿嘿嘿…'}",
	[106088] = "{id = 106088, Speaker = 7410, Text = '什么！', Emoji = 7}",
	[106089] = "{id = 106089, Speaker = 7418, Text = '我的艺术品们啊！'}",
	[106090] = "{id = 106090, Speaker = 7418, Text = '听从我的指挥，摧毁海姆达水晶！！！'}",
	[106091] = "{id = 106091, Speaker = 7410, Text = '我的天呐！！！'}",
	[106092] = "{id = 106092, Speaker = 0, Text = '宝伏说完最后一句话，便灰飞烟灭。'}",
	[106093] = "{id = 106093, Speaker = 7414, Text = '吼！！！！！！！！！！！！！！！！！！！！！！'}",
	[106094] = "{id = 106094, Speaker = 0, Text = '怪物们发出震天动地的吼声。'}",
	[106095] = "{id = 106095, Speaker = 7410, Text = '我们！敌不过这些怪物！'}",
	[106096] = "{id = 106096, Speaker = 7411, Text = '老师！！'}",
	[106097] = "{id = 106097, Speaker = 7410, Text = '西芙先生来救我们了！'}",
	[106098] = "{id = 106098, Speaker = 7422, Text = '先离开这里！！'}",
	[106099] = "{id = 106099, Speaker = 7421, Text = '我知道，你们已经尽力了。'}",
	[106114] = "{id = 106114, Speaker = 7423, Text = '快点打扫吧~冒险者！'}",
	[106115] = "{id = 106115, Speaker = 4801, Text = '修发兹发德共和国的环境保护正面临着严峻的挑战，我们急切需要必要的治理措施。'}",
	[106116] = "{id = 106116, Speaker = 4801, Text = '天空之城静静地漂浮在云巅，行走在那充满诗意的街道上，似乎踏入了一片人间仙境。'}",
	[106117] = "{id = 106117, Speaker = 4801, Text = '有时候我也会感叹人类的独具匠心，这是需要怎样的创造力才能实现的奇迹啊！'}",
	[106118] = "{id = 106118, Speaker = 4801, Text = '但是，在更多时候，我却不得不面对一个问题，正是由于那些无止境的开发，共和国的环境已经遭到了毁灭性的破坏。'}",
	[106119] = "{id = 106119, Speaker = 4801, Text = '在这里，由于过度的矿业开采和地面污染物的排放泄露，已经引起了熔岩洞穴生态环境的恶性循环。'}",
	[106120] = "{id = 106120, Speaker = 4801, Text = '更有甚者，有些温顺的魔物因为污染变得暴躁易怒，因而治理已是刻不容缓！'}",
	[106121] = "{id = 106121, Speaker = 4801, Text = '冒险者，你愿意帮助我吗？', Option = '{我愿意,2},{我不愿意,3}'}",
	[106122] = "{id = 106122, Speaker = 4801, Text = '你的选择真是令人遗憾，看来你并没有意识到问题的严重。'}",
	[106123] = "{id = 106123, Speaker = 4801, Text = '太好了，我很期待你能带来改变。'}",
	[106124] = "{id = 106124, Speaker = 4801, Text = '那么请你听好了，为了提供治理所需的材料，我们急需[c][ffff00]30个[-][/c]火焰结晶、炙热的毛或者燃烧的心脏，那么你会选择……', Option = '{火焰结晶,4},{炙热的毛,5},{燃烧的心脏,6}'}",
