	[106164] = "{id = 106164, Speaker = 4801, Text = '你身上并没有足够的材料，等你收集齐了再来找我吧。'}",
	[106165] = "{id = 106165, Speaker = 4801, Text = '冒险者，从你的行动中我感受到你无私的热情，接下来的治理行动我仍希望你能全心投入。'}",
	[106166] = "{id = 106166, Speaker = 4801, Text = '根据情报，熔岩洞穴里出现了一批危险的魔物，它们因为赖以生存的环境被破坏而变得狂躁暴怒，如果任由它们活动，恐怕会伤害洞穴里无辜的生灵。'}",
	[106167] = "{id = 106167, Speaker = 4801, Text = '冒险者，请去寻找并消灭这些异变的魔物，请千万小心。'}",
	[106168] = "{id = 106168, Speaker = 4801, Text = '我看到了你的努力，生态治理需要持之以恒，也需要我们所有人携手共进。'}",
	[106180] = "{id = 106180, Speaker = 9538, Text = '你好呀冒险者~你知道偶像音乐剧新星伽鲁就要来普隆德拉演出新剧了呢！'}",
	[106181] = "{id = 106181, Speaker = 9538, Text = '我们宠物协会的会长也是他的粉丝呢！', Emoji = 2}",
	[106182] = "{id = 106182, Speaker = 9538, Text = '我们协会举办了一个帮助伽鲁的幸运粉丝活动~有没有兴趣来参加呀？', Option = '{有兴趣,3},{没兴趣,2}'}",
	[106183] = "{id = 106183, Speaker = 9538, Text = '太好了！有你的帮助~这位幸运粉丝一定会实现心愿的！', Option = '过奖', Emoji = 20}",
	[106184] = "{id = 106184, Speaker = 9538, Text = '幸运粉丝已经在之前的报名中选出来了，嗯，我看看名单……'}",
	[106185] = "{id = 106185, Speaker = 9538, Text = '啊！就这孩子吧！他名叫吉米，现在住在依斯鲁得岛。'}",
	[106186] = "{id = 106186, Speaker = 9538, Text = '他可是个十分热爱音乐的孩子哦！'}",
	[106187] = "{id = 106187, Speaker = 9538, Text = '你带着我的认证卷轴去找他吧~帮助他完成心愿，会有惊喜等着你哦！', Option = '好的'}",
	[106188] = "{id = 106188, Speaker = 9540, Text = '找我有什么事？', Option = '递交卷轴', Emoji = 8}",
	[106189] = "{id = 106189, Speaker = 9540, Text = '原来是宠物协会派来帮助我的冒险者！等你很久啦！', Emoji = 17}",
	[106190] = "{id = 106190, Speaker = 9540, Text = '你也是伽鲁的粉丝吗？', Option = '{是啊是啊,6},{不是的呢,5}'}",
	[106191] = "{id = 106191, Speaker = 9540, Text = '哇！你真是太有眼光了！'}",
	[106192] = "{id = 106192, Speaker = 9540, Text = '你肯定是没听过他的歌！不然一定会爱上他的！', Emoji = 12}",
	[106193] = "{id = 106193, Speaker = 9540, Text = '我以前是个很内向的人，一直被小伙伴排挤。'}",
	[106194] = "{id = 106194, Speaker = 9540, Text = '直到我听到了伽鲁的歌！'}",
	[106195] = "{id = 106195, Speaker = 9540, Text = '他的歌充满了希望和力量！让我慢慢敞开的心扉！'}",
	[106196] = "{id = 106196, Speaker = 9540, Text = '你看我现在是不是超级开朗的！这都是伽鲁的功劳！', Option = '你的心愿', Emoji = 26}",
	[106197] = "{id = 106197, Speaker = 9540, Text = '你不提我都快忘了！我就抱着试试的态度没想到真的被选中了！'}",
	[106198] = "{id = 106198, Speaker = 9540, Text = '我想要！和伽鲁合唱一首歌！这是我最大的梦想了！'}",
	[106199] = "{id = 106199, Speaker = 9540, Text = '你一定能帮我实现这个梦想的吧！', Option = '伽鲁在哪', Emoji = 26}",
	[106200] = "{id = 106200, Speaker = 9540, Text = '这我怎么知道！我要是知道！就自己去找他啦！'}",
	[106201] = "{id = 106201, Speaker = 9540, Text = '或许你可以去问问宠物协会的工作人员，毕竟是他们举办的活动！应该知道伽鲁在哪。'}",
	[106202] = "{id = 106202, Speaker = 9540, Text = '等你的好消息哦！', Emoji = 26}",
	[106203] = "{id = 106203, Speaker = 6575, Text = '需要什么帮助吗？冒险者？', Option = '说明缘由'}",
	[106204] = "{id = 106204, Speaker = 6575, Text = '伽鲁正好在依斯鲁得岛度假，他最喜欢那边码头边的小花园了。'}",
	[106205] = "{id = 106205, Speaker = 6575, Text = '但是他可不会轻易和不认识的人说话，你得带点他感兴趣的东西过去才行……'}",
	[106206] = "{id = 106206, Speaker = 6575, Text = '记得一个人去哦！他不喜欢被打扰。', Option = '好的'}",
	[106207] = "{id = 106207, Speaker = 9540, Text = '怎么样怎么样？', Option = '说明情况', Emoji = 21}",
	[106208] = "{id = 106208, Speaker = 9540, Text = '伽鲁喜欢吉他，喜欢唱歌……还有什么呢……让我想想……'}",
	[106209] = "{id = 106209, Speaker = 9540, Text = '我想到了！是营养剂！', Emoji = 11}",
	[106210] = "{id = 106210, Speaker = 9540, Text = '营养剂是伽鲁最喜欢的食物呢！'}",
	[106211] = "{id = 106211, Speaker = 9540, Text = '我为了他还特意学了如何制作营养剂呢！'}",
	[106212] = "{id = 106212, Speaker = 9540, Text = '不过缺点材料~嘿嘿~麻烦你去取一些绿棉虫的汁液过来啦！'}",
	[106213] = "{id = 106213, Speaker = 9540, Text = '哇！你的速度真快！来来来，看我的了！'}",
	[106214] = "{id = 106214, Speaker = 9540, Text = '锵锵锵…吉米特制营养剂完成啦！', Emoji = 20}",
	[106215] = "{id = 106215, Speaker = 9540, Text = '你快拿着去找伽鲁吧！我都迫不及待了！'}",
	[106216] = "{id = 106216, Speaker = 9539, Text = '……', Option = '拿出营养剂', Emoji = 14}",
	[106217] = "{id = 106217, Speaker = 9539, Text = '……', Emoji = 14}",
	[106218] = "{id = 106218, Speaker = 9539, Text = '这是给我的吗？', Option = '是的', Emoji = 28}",
	[106219] = "{id = 106219, Speaker = 9539, Text = '谢谢你的礼物。你是我的粉丝吧！我可以给你签名。', Option = '说明由来'}",
	[106220] = "{id = 106220, Speaker = 9539, Text = '是宠物协会办的那个活动啊！我知道我知道……但是要和我一起合唱，我要求可是很高的！'}",
	[106221] = "{id = 106221, Speaker = 9539, Text = '看到这么好吃的营养剂的份上，这样吧，要是他的歌声能获得音乐精灵艾莉儿的认可！你再来找我！'}",
	[106222] = "{id = 106222, Speaker = 9539, Text = '艾莉儿的乐感可是很好的哦！加油吧！我的粉丝们！', Option = '好的'}",
	[106223] = "{id = 106223, Speaker = 9540, Text = '你居然见到他本人了！？天哪！你实在是太幸运了吧！？'}",
	[106224] = "{id = 106224, Speaker = 9540, Text = '早知道我就跟过去了！', Emoji = 24}",
	[106225] = "{id = 106225, Speaker = 9540, Text = '他怎么说他怎么说？', Option = '告知情况'}",
	[106226] = "{id = 106226, Speaker = 9540, Text = '没问题！快带我去吧！我对自己的歌声可是很自信的！'}",
	[106227] = "{id = 106227, Speaker = 1058, Text = '有什么事吗？冒险者。', Option = '伽鲁的考验'}",
	[106228] = "{id = 106228, Speaker = 1058, Text = '是这样啊~那好吧~来吧！给我听听你的歌声~对音乐我可是很公正的。'}",
	[106229] = "{id = 106229, Speaker = 9540, Text = '啊~啦啦啦~~~啦啦啦啦~~~'}",
	[106230] = "{id = 106230, Speaker = 9540, Text = '啦~~啦~~~啦啦~~~~啦~~~~~~~~'}",
	[106231] = "{id = 106231, Speaker = 1058, Text = '嗯，还挺不错的！你们告诉伽鲁我认可这个少年了！'}",
	[106232] = "{id = 106232, Speaker = 9540, Text = '太好了~你快去和伽鲁汇报！我先回去等你的好消息！', Emoji = 17}",
	[106233] = "{id = 106233, Speaker = 9539, Text = '是嘛，居然得到了艾莉儿的认可。'}",
	[106234] = "{id = 106234, Speaker = 9539, Text = '不过别以为这样就结束了！我可是很严格的。'}",
	[106235] = "{id = 106235, Speaker = 9539, Text = '今天我要回去休息了，明天再来这里找我。'}",
	[106236] = "{id = 106236, Speaker = 9540, Text = '你回来啦冒险者！', Option = '告知情况'}",
	[106237] = "{id = 106237, Speaker = 9540, Text = '好的好的，辛苦你了冒险者！好期待明天的到来呀！', Emoji = 21}",
	[106238] = "{id = 106238, Speaker = 9540, Text = '你终于来啦！等你好久了。'}",
	[106239] = "{id = 106239, Speaker = 9540, Text = '这是今天的营养剂！帮我带给伽鲁吧~嘻嘻~是我的小小心意！', Emoji = 2}",
	[106240] = "{id = 106240, Speaker = 9540, Text = '我真想偷偷跟你过去见见他呀！不过我要忍耐！不能表现的没有礼貌！'}",
	[106241] = "{id = 106241, Speaker = 9539, Text = '你来啦，依斯鲁得岛真是寻找音乐灵感的好地方。', Option = '你的礼物'}",
	[106242] = "{id = 106242, Speaker = 9539, Text = '这个营养剂真是太好吃了！哪里买的？', Option = '吉米做的', Emoji = 28}",
	[106243] = "{id = 106243, Speaker = 9539, Text = '嗯，这孩子很有前途。'}",
	[106244] = "{id = 106244, Speaker = 9539, Text = '我昨天听艾莉儿说了，他唱的很不错啊！其实我这次的演出正好有个段落需要一个粉丝和我合唱一首歌。', Option = '那太好了'}",
	[106245] = "{id = 106245, Speaker = 9539, Text = '别高兴的太早了，想要这个机会的人可多着呢。'}",
	[106246] = "{id = 106246, Speaker = 9539, Text = '不过看在这么好吃的营养剂的份上，我给他个机会。'}",
	[106247] = "{id = 106247, Speaker = 9539, Text = '首先，和我同台演出最重要的是！要会跳舞！', Option = '…'}",
	[106248] = "{id = 106248, Speaker = 9539, Text = '怎么！舞蹈可是音乐剧的灵魂！不许小看它了！'}",
	[106249] = "{id = 106249, Speaker = 9539, Text = '我认识个朋友跳舞跳的很有灵性，你们去和他学习舞蹈吧！他要是认可你们了，就算你们通过。'}",
	[106250] = "{id = 106250, Speaker = 9539, Text = '他叫摩根，现在应该在天水之国磨砺他的舞艺。', Option = '……'}",
	[106251] = "{id = 106251, Speaker = 9540, Text = '你可回来了~伽鲁怎么说？', Option = '告知情况'}",
	[106252] = "{id = 106252, Speaker = 9540, Text = '我确实不太会跳舞……'}",
	[106253] = "{id = 106253, Speaker = 9540, Text = '那走吧走吧！带我去找那个舞蹈家摩根学跳舞。', Option = '…'}",
	[106254] = "{id = 106254, Speaker = 9541, Text = '怎么又是你！？', Option = '…', Emoji = 1}",
	[106255] = "{id = 106255, Speaker = 9541, Text = '你又要来打扰我和小姐姐们玩耍了吗？！', Option = '说明情况'}",
	[106256] = "{id = 106256, Speaker = 9540, Text = '拜托你了！摩根老师！'}",
	[106257] = "{id = 106257, Speaker = 9541, Text = '原来是伽鲁，他就知道给我找事情做！下次碰到他得让他好好的请我喝酒。'}",
	[106258] = "{id = 106258, Speaker = 9541, Text = '想让我教他跳舞可以，你可得帮我做点事啊！', Option = '什么事', Emoji = 5}",
	[106259] = "{id = 106259, Speaker = 9541, Text = '我的樱花佳酿喝完了，酿制还缺少樱花花瓣，你帮我取点来呗。'}",
	[106260] = "{id = 106260, Speaker = 9541, Text = '记住，要靠近神社的那些樱花树的花瓣哦~那边的樱花花瓣最棒了！'}",
	[106261] = "{id = 106261, Speaker = 9541, Text = '吉米，你就留下陪我在这聊聊天吧。'}",
	[106262] = "{id = 106262, Speaker = 9541, Text = '动作挺快的嘛~嗯，真好真好，又能有好喝的樱花佳酿了。'}",
	[106263] = "{id = 106263, Speaker = 9541, Text = '来吧少年跟我一起跳舞吧！'}",
	[106264] = "{id = 106264, Speaker = 9540, Text = '是！摩根老师！'}",
	[106265] = "{id = 106265, Speaker = 9540, Text = '怎么样怎么样，我学的像不像？', Option = '…'}",
	[106266] = "{id = 106266, Speaker = 9540, Text = '我感觉自己找到了跳舞的感觉！'}",
	[106267] = "{id = 106267, Speaker = 9540, Text = '摩根老师教的真好！', Option = '…'}",
	[106268] = "{id = 106268, Speaker = 9540, Text = '（边跳边唱）啦…啦啦啦…啦啦啦啦啦啦……', Emoji = 9}",
	[106269] = "{id = 106269, Speaker = 9541, Text = '（边跳边唱）啦…啦啦啦…啦啦啦啦啦啦……', Emoji = 9}",
	[106270] = "{id = 106270, Speaker = 9541, Text = '少年可以了，你的舞已经跳的很好了！应付伽鲁的演出足够了。'}",
	[106271] = "{id = 106271, Speaker = 9540, Text = '谢谢摩根老师的教导！', Emoji = 20}",
	[106272] = "{id = 106272, Speaker = 9540, Text = '冒险者，我先回去等你了！你快去和伽鲁汇报情况！'}",
	[106273] = "{id = 106273, Speaker = 9539, Text = '不错嘛，这个孩子居然能得到摩根的认可。看来在舞蹈方面很有灵性啊！'}",
	[106274] = "{id = 106274, Speaker = 9539, Text = '今天就到此为止吧！让他好好休息！'}",
	[106275] = "{id = 106275, Speaker = 9539, Text = '明天再来这里找我，还有别的考验哦！', Emoji = 5}",
	[106276] = "{id = 106276, Speaker = 9540, Text = '你回来啦！', Option = '告知情况'}",
	[106277] = "{id = 106277, Speaker = 9540, Text = '明天，快点到来吧！', Emoji = 21}",
	[106278] = "{id = 106278, Speaker = 9540, Text = '嘿！看见你真高兴！'}",
	[106279] = "{id = 106279, Speaker = 9540, Text = '这是今日份的营养剂，不知道今天伽鲁会让我做些什么呢？', Option = '我去问问'}",
	[106280] = "{id = 106280, Speaker = 9539, Text = '嘿，我的粉丝，今天又是活力满满的一天！', Option = '你的营养剂'}",
	[106281] = "{id = 106281, Speaker = 9539, Text = '嗯，吉米这孩子，我很喜欢。'}",
	[106282] = "{id = 106282, Speaker = 9539, Text = '既然他唱歌跳舞都没问题了，那么就该准备一些舞台上需要用的东西了。', Option = '好的'}",
	[106283] = "{id = 106283, Speaker = 9539, Text = '我来问问你，你认为作为一个音乐剧的表演者，在舞台上的时候最重要的工具是什么？', Option = '{照相机,3},{麦克风,4},{八音盒,3}'}",
	[106284] = "{id = 106284, Speaker = 9539, Text = '不对哦，你再仔细想一想。'}",
	[106285] = "{id = 106285, Speaker = 9539, Text = '对！就是麦克风！'}",
	[106286] = "{id = 106286, Speaker = 9539, Text = '一个好的麦克风对演出的影响可是很大的！'}",
	[106287] = "{id = 106287, Speaker = 9539, Text = '麦克风要音质够好！外表够炫！', Emoji = 5}",
	[106288] = "{id = 106288, Speaker = 9539, Text = '演出就在几天之后，我们的工作人员都忙着在准备，吉米的麦克风要他自己去想办法了！'}",
	[106289] = "{id = 106289, Speaker = 9539, Text = '准备好的麦克风记得拿给我看一下哦！', Option = '好的'}",
	[106290] = "{id = 106290, Speaker = 9540, Text = '怎么说怎么说？', Option = '麦克风'}",
	[106291] = "{id = 106291, Speaker = 9540, Text = '唔……我没有麦克风呢……这可是个稀罕的东西……'}",
	[106292] = "{id = 106292, Speaker = 9540, Text = '听说只有吉芬的一个魔法师能做出麦克风……'}",
	[106293] = "{id = 106293, Speaker = 9540, Text = '你能帮我去吉芬找到这个魔法师，请求他帮我做一个麦克风吗？', Option = '没问题'}",
	[106294] = "{id = 106294, Speaker = 1321, Text = '好久不见，[c][ffff00][PlayerName][-][/c]，找我什么事？', Option = '说明情况'}",
	[106295] = "{id = 106295, Speaker = 1321, Text = '原来是这样。'}",
	[106296] = "{id = 106296, Speaker = 1321, Text = '制作麦克风的那个魔法师是个发明天才，名字叫哈尔。'}",
	[106297] = "{id = 106297, Speaker = 1321, Text = '他就住在吉芬的北边。不过发明家的脾气都有点古怪，你要做好准备。'}",
	[106298] = "{id = 106298, Speaker = 9542, Text = '你站在这影响到我的思考了。', Option = '说明来意'}",
	[106299] = "{id = 106299, Speaker = 9542, Text = '想要我做的麦克风？那可是抢手货，凭什么给你？'}",
	[106300] = "{id = 106300, Speaker = 9542, Text = '一个两个都这样，最近连哥布灵都要来抢我的麦克风……', Emoji = 12}",
	[106301] = "{id = 106301, Speaker = 9542, Text = '你看，说到它们，它们就来了。'}",
	[106302] = "{id = 106302, Speaker = 9542, Text = '你不是想要麦克风吗？帮我击退它们我就考虑一下。'}",
	[106303] = "{id = 106303, Speaker = 9542, Text = '哼，还不算太弱。'}",
	[106304] = "{id = 106304, Speaker = 9542, Text = '想要麦克风是吧？那你得拿东西来换。', Option = '要什么'}",
	[106305] = "{id = 106305, Speaker = 9542, Text = '制作麦克风需要大量的优质木头，我一个人去收集太累了。'}",
	[106306] = "{id = 106306, Speaker = 9542, Text = '普隆德拉西门的树精身上的木材是最适合的了。'}",
	[106307] = "{id = 106307, Speaker = 9542, Text = '你懂我的意思了吧？', Option = '知道了'}",
	[106308] = "{id = 106308, Speaker = 9542, Text = '嗯，这够我再做好几支麦克风了。'}",
	[106309] = "{id = 106309, Speaker = 9542, Text = '这支成品就给你吧……'}",
	[106310] = "{id = 106310, Speaker = 9540, Text = '哇！是麦克风！好棒啊~现在就想用起来了！', Emoji = 21}",
	[106311] = "{id = 106311, Speaker = 9540, Text = '但这个是不是朴素了点……伽鲁好像说要外表够炫？'}",
	[106312] = "{id = 106312, Speaker = 9540, Text = '对了，依斯鲁得岛的装备制造商鲁瑞克最擅长制作了，你拿去问问他吧！'}",
	[106313] = "{id = 106313, Speaker = 2621, Text = '是需要制作什么装备吗？冒险者。', Option = '说明缘由'}",
	[106314] = "{id = 106314, Speaker = 2621, Text = '这点小事，你就找我的助手吧！她就在旁边。'}",
	[106315] = "{id = 106315, Speaker = 3342, Text = '是把这个麦克风加工的更好看是把！我最喜欢做这个了！'}",
	[106316] = "{id = 106316, Speaker = 3342, Text = '鲁瑞克做的装备都是我加工美化的~是不是很好看！'}",
	[106317] = "{id = 106317, Speaker = 3342, Text = '你想要什么样的风格呀！？', Option = '{可爱,6},{华丽,6},{酷炫,7}', Emoji = 21}",
	[106318] = "{id = 106318, Speaker = 3342, Text = '你确定吗？开工了就没办法反悔了哟！'}",
	[106319] = "{id = 106319, Speaker = 3342, Text = '好咧！看我的吧!'}",
	[106320] = "{id = 106320, Speaker = 3342, Text = '好咧完成了!', Option = '真棒'}",
	[106321] = "{id = 106321, Speaker = 3342, Text = '嘿嘿~其实我也是伽鲁的粉丝呢！这次的演出我也买了门票~我到时候会去看的！你让吉米加油哦!', Emoji = 27}",
	[106322] = "{id = 106322, Speaker = 9539, Text = '酷！这个麦克风真棒！'}",
	[106323] = "{id = 106323, Speaker = 9539, Text = '不错不错，拿着这个麦克风和我同台才够资格嘛！'}",
	[106324] = "{id = 106324, Speaker = 9539, Text = '别高兴的太早了！还有东西要准备呢！', Emoji = 5}",
	[106325] = "{id = 106325, Speaker = 9539, Text = '我的假期结束了，要回普隆德拉准备我的演出了！'}",
	[106326] = "{id = 106326, Speaker = 9539, Text = '明天找我记得来普隆德拉哦！我会在喷泉旁边等你。'}",
	[106327] = "{id = 106327, Speaker = 9540, Text = '哇！这个麦克风真的太酷了!', Option = '告知情况', Emoji = 9}",
	[106328] = "{id = 106328, Speaker = 9540, Text = '好兴奋！演出一天比一天近了呢！', Emoji = 9}",
	[106329] = "{id = 106329, Speaker = 9540, Text = '你来啦，我的朋友！', Emoji = 4}",
	[106330] = "{id = 106330, Speaker = 9540, Text = '嘿嘿，惯例营养剂！', Option = '好的'}",
	[106331] = "{id = 106331, Speaker = 9540, Text = '这几天真是辛苦你啦！'}",
	[106332] = "{id = 106332, Speaker = 9539, Text = '远远的我就闻到了营养剂的香味！', Option = '给你', Emoji = 28}",
	[106333] = "{id = 106333, Speaker = 9539, Text = '坚持不懈是成功的必要品质。'}",
	[106334] = "{id = 106334, Speaker = 9539, Text = '麦克风的问题解决了，就该帮吉米准备演出服啦！'}",
	[106335] = "{id = 106335, Speaker = 9539, Text = '要不要介绍我的御用服装设计师给你们？', Option = '要！'}",
	[106336] = "{id = 106336, Speaker = 9539, Text = '明智的选择。你看看我这身~是不是超赞的？', Emoji = 9}",
	[106337] = "{id = 106337, Speaker = 9539, Text = '她叫卡娜赫拉，在艾尔帕兰开着一家高级时装定制店。'}",
	[106338] = "{id = 106338, Speaker = 9539, Text = '她可是很忙的，档期排的满满的。'}",
	[106339] = "{id = 106339, Speaker = 9539, Text = '但是有我的介绍信，相信她会给你们优先制作的。'}",
	[106340] = "{id = 106340, Speaker = 9539, Text = '对了！我晚点要排练，今天你就不用来找我了，明天直接把吉米带过来吧！'}",
	[106341] = "{id = 106341, Speaker = 9543, Text = '有什么可以帮助你的吗？', Option = '递交介绍信'}",
	[106342] = "{id = 106342, Speaker = 9543, Text = '哎呀呀，原来是小伽鲁介绍来的客人。'}",
	[106343] = "{id = 106343, Speaker = 9543, Text = '唔……今天之内要做好一套衣服呀……'}",
	[106344] = "{id = 106344, Speaker = 9543, Text = '小伽鲁的忙我还是要帮的！', Emoji = 21}",
	[106345] = "{id = 106345, Speaker = 9543, Text = '但是时间有点紧，需要你的协助呢！', Option = '没问题'}",
	[106346] = "{id = 106346, Speaker = 9543, Text = '我看了小伽鲁的信，我觉得给小吉米做一套星星的礼服比较合适呢！'}",
	[106347] = "{id = 106347, Speaker = 9543, Text = '就拜托你在艾尔帕兰找商人们购买一些材料吧！'}",
	[106348] = "{id = 106348, Speaker = 9543, Text = '分别是白色布料、黄色染料和金色纽扣。'}",
	[106349] = "{id = 106349, Speaker = 9543, Text = '快去吧，不要耽误时间了…'}",
	[106350] = "{id = 106350, Speaker = 9544, Text = '需要点什么呢？我们这边什么颜色的布料都有哦！', Option = '{黄色布料,3},{白色布料,4},{金色布料,3}'}",
	[106351] = "{id = 106351, Speaker = 9544, Text = '确定吗？我们家不能退货的哦！'}",
	[106352] = "{id = 106352, Speaker = 9544, Text = '您的布料，欢迎下次再来！'}",
	[106353] = "{id = 106353, Speaker = 9545, Text = '想要选购点什么？我这边的颜色可以说是全艾尔帕兰最齐全的了。', Option = '{白色染料,6},{金色染料,6},{黄色染料,7}'}",
	[106354] = "{id = 106354, Speaker = 9545, Text = '确定买这个吗？售出不退哦！'}",
	[106355] = "{id = 106355, Speaker = 9545, Text = '谢谢选购！'}",
	[106356] = "{id = 106356, Speaker = 9546, Text = '我这边应有尽有，你想要点什么？', Option = '{金色纽扣,10},{金色领带,9},{金色别针,9}'}",
	[106357] = "{id = 106357, Speaker = 9546, Text = '东西比较多，容易看花眼，请仔细挑选。'}",
	[106358] = "{id = 106358, Speaker = 9546, Text = '祝你生活愉快，再见！'}",
	[106359] = "{id = 106359, Speaker = 9543, Text = '哎呀呀，帮大忙了！'}",
	[106360] = "{id = 106360, Speaker = 9543, Text = '我设计图在刚刚已经画好啦，现在就开工！'}",
	[106361] = "{id = 106361, Speaker = 9543, Text = '你若是没事，就去把小吉米带过来吧，衣服还是要试穿才好。'}",
	[106362] = "{id = 106362, Speaker = 9540, Text = '你这么快就回来啦？', Option = '说明情况'}",
	[106363] = "{id = 106363, Speaker = 9540, Text = '正好，我手上也没什么事，我这就跟你去！'}",
	[106364] = "{id = 106364, Speaker = 9540, Text = '好期待啊！第一次有衣服穿呢！', Emoji = 21}",
	[106365] = "{id = 106365, Speaker = 9543, Text = '呀，这就是小吉米呀！'}",
	[106366] = "{id = 106366, Speaker = 9540, Text = '你好。'}",
	[106367] = "{id = 106367, Speaker = 9543, Text = '来的正好呢，就在刚才，衣服完工了呢！'}",
	[106368] = "{id = 106368, Speaker = 9543, Text = '快来试试，合不合适。'}",
	[106369] = "{id = 106369, Speaker = 9540, Text = '好的。'}",
	[106370] = "{id = 106370, Speaker = 9543, Text = '真合身，我真佩服我自己！', Emoji = 27}",
	[106371] = "{id = 106371, Speaker = 9543, Text = '真的太棒了！', Emoji = 20}",
	[106372] = "{id = 106372, Speaker = 9540, Text = '我这身怎么样！是不是超赞的！'}",
	[106373] = "{id = 106373, Speaker = 9540, Text = '真的太感谢你了！', Option = '明天见伽鲁'}",
	[106374] = "{id = 106374, Speaker = 9540, Text = '真的嘛！我突然好紧张啊！我要见到我的偶像了！'}",
	[106375] = "{id = 106375, Speaker = 9540, Text = '不行不行，我得赶紧回去准备准备，再多做点营养剂。'}",
	[106376] = "{id = 106376, Speaker = 9540, Text = '那就明天见啦！'}",
	[106377] = "{id = 106377, Speaker = 9540, Text = '你总算来啦！我等不及了！', Option = '准备好了吗', Emoji = 21}",
	[106378] = "{id = 106378, Speaker = 9540, Text = '准备好啦！这次我要亲手把营养剂交给伽鲁！'}",
	[106379] = "{id = 106379, Speaker = 9539, Text = '我的粉丝们，你们好呀。你就是吉米吧！'}",
	[106380] = "{id = 106380, Speaker = 9540, Text = '你你你你好！'}",
	[106381] = "{id = 106381, Speaker = 9539, Text = '别紧张，这么紧张之后演出怎么办。'}",
	[106382] = "{id = 106382, Speaker = 9539, Text = '本来今天叫你过来是想排练一下，但是没想到我的伴奏团首席小提琴家舒尔特突然受伤了！', Emoji = 15}",
	[106383] = "{id = 106383, Speaker = 9539, Text = '不过好在舒尔特的家族里有好多擅长小提琴的演奏家。'}",
	[106384] = "{id = 106384, Speaker = 9539, Text = '冒险者，你能帮我们去邀请一下吗？', Option = '好吧'}",
	[106385] = "{id = 106385, Speaker = 9539, Text = '感谢你对我的支持，舒尔特就在旁边，你去问问具体情况吧。'}",
	[106386] = "{id = 106386, Speaker = 9547, Text = '嗯？ 你就是伽鲁说的来帮忙的粉丝？'}",
	[106387] = "{id = 106387, Speaker = 9547, Text = '哎……好不容易快到演出日了，昨天却摔了一跤，现在我的手折了，没法弹奏了。', Emoji = 24}",
	[106388] = "{id = 106388, Speaker = 9547, Text = '现在不是哀叹我的手的时候了！得赶紧找人顶替我，不要影响伽鲁的演出。'}",
	[106389] = "{id = 106389, Speaker = 9547, Text = '我有个叔叔叫莫菲特，他小提琴演奏的特别好，你去帮忙把他请过来吧！'}",
	[106390] = "{id = 106390, Speaker = 9547, Text = '他平时就喜欢听音乐，在南门的波利留声机旁边应该能找到他。'}",
	[106391] = "{id = 106391, Speaker = 9548, Text = '干什么干什么？', Option = '说明情况'}",
	[106392] = "{id = 106392, Speaker = 9548, Text = '舒尔特这臭小孩的事情我可不管！'}",
	[106393] = "{id = 106393, Speaker = 9548, Text = '他整天在外面用音乐赚点臭钱，我可不稀罕，去去去，别打扰我听音乐。', Emoji = 25}",
	[106394] = "{id = 106394, Speaker = 0, Text = '莫菲特似乎不想理你了'}",
	[106395] = "{id = 106395, Speaker = 9547, Text = '你回来啦！', Option = '说明情况'}",
	[106396] = "{id = 106396, Speaker = 9547, Text = '是这样啊……'}",
	[106397] = "{id = 106397, Speaker = 9547, Text = '哎……我这叔叔什么都好，就是一根筋，觉得音乐就是自己一个人关起门来的事情。'}",
	[106398] = "{id = 106398, Speaker = 9547, Text = '这样吧，我这里有一份绝版的曲谱，他寻找很久了，你拿这个去请他过来吧。'}",
	[106399] = "{id = 106399, Speaker = 9548, Text = '怎么又是你？走开走开……', Option = '拿出曲谱'}",
	[106400] = "{id = 106400, Speaker = 9548, Text = '你怎么会有这个！给我看看！', Option = '帮助舒尔特'}",
	[106401] = "{id = 106401, Speaker = 9548, Text = '唔……好吧好吧，我就帮他一回吧。'}",
	[106402] = "{id = 106402, Speaker = 9548, Text = '我一会去找他，你把曲谱给我！', Option = '交出曲谱'}",
	[106403] = "{id = 106403, Speaker = 9548, Text = '让我看看这绝妙的曲谱……', Emoji = 9}",
	[106404] = "{id = 106404, Speaker = 9547, Text = '他是不是答应了？', Option = '是的'}",
	[106405] = "{id = 106405, Speaker = 9547, Text = '我就知道！好啦好啦，也算解决了一桩事情，你快去告诉伽鲁，让他安心排练吧。'}",
	[106406] = "{id = 106406, Speaker = 9539, Text = '怎么样冒险者，请到演奏者了么？', Option = '说明情况'}",
	[106407] = "{id = 106407, Speaker = 9539, Text = '嗯，我就知道你可以圆满解决这件事情。'}",
	[106408] = "{id = 106408, Speaker = 9539, Text = '我准备带吉米去排练去啦，演出你一定会来观看的吧！', Emoji = 21}",
	[106409] = "{id = 106409, Speaker = 9540, Text = '唔…要和你分别了吗我的朋友……', Emoji = 24}",
	[106410] = "{id = 106410, Speaker = 9540, Text = '那最后，我们一起拍一张照吧，我和伽鲁，还有你。'}",
	[106411] = "{id = 106411, Speaker = 9540, Text = '照片的背面我再让伽鲁给我签个名。'}",
	[106412] = "{id = 106412, Speaker = 9540, Text = '这个主意好极了。快来拍照！', Emoji = 27}",
	[106413] = "{id = 106413, Speaker = 9540, Text = '这段回忆我永远不会忘记！感谢你，我的朋友！', Emoji = 2}",
	[106414] = "{id = 106414, Speaker = 9540, Text = '谢谢你完成了我的心愿，演出一定要来看哦！'}",
	[106415] = "{id = 106415, Speaker = 9540, Text = '再见了，我的朋友！'}",
	[106416] = "{id = 106416, Speaker = 9538, Text = '还有，冒险者！同账号下只有一个角色可以帮助我们的幸运粉丝哦！你确定使用该角色吗？', Option = '{确定,99},{我再想想,98}'}",
	[106417] = "{id = 106417, Speaker = 4807, Text = '别靠近我，我不想和你说话。'}",
	[106418] = "{id = 106418, Speaker = 7045, Text = '冒险者，你知道月神曼尼的故事么？', Option = '{我想听,2},{不感兴趣,3}'}",
	[106419] = "{id = 106419, Speaker = 7045, Text = '传说千年前，有一位神族与巨人族混血的女神，名叫曼尼。'}",
	[106420] = "{id = 106420, Speaker = 7045, Text = '每当夜晚，她会弹奏着鲁特琴，在天空中望着沉睡的人们。', Option = '鲁特琴？'}",
	[106421] = "{id = 106421, Speaker = 7045, Text = '是的，那把琴名为月神的呢喃，据说拥有抚慰人心，驱散噩梦的神奇魔力。', Option = '哇！'}",
	[106422] = "{id = 106422, Speaker = 7045, Text = '但是几百年前，那把琴遗失了……月神曼尼也从此消失了。', Option = '好可惜啊'}",
	[106423] = "{id = 106423, Speaker = 7045, Text = '好消息是，我最近感应到了月神的魔力。'}",
	[106424] = "{id = 106424, Speaker = 7045, Text = '它似乎回荡在大陆的东南方，嗯…你觉得具体是哪？', Option = '{朱诺,4},{吉芬,4},{斐扬,5}'}",
	[106425] = "{id = 106425, Speaker = 7045, Text = '不对不对，我是说在东南方的位置呀，再想想吧？'}",
	[106426] = "{id = 106426, Speaker = 7045, Text = '没错，月神的魔力一定是在斐扬附近。', Option = '嗯嗯'}",
	[106427] = "{id = 106427, Speaker = 7045, Text = '你去斐扬调查下线索吧，一定会有收获的。', Option = '好的'}",
	[106428] = "{id = 106428, Speaker = 4807, Text = '你也是被月神的魔力吸引来的？快住手，月神只能是我们的！'}",
	[106429] = "{id = 106429, Speaker = 0, Text = '神秘的人影逃走了，但身上似乎有东西落了出来。'}",
	[106430] = "{id = 106430, Speaker = 0, Text = '一条绣着月亮图案的手帕，带上它去请教见多识广的斐扬统帅吧。'}",
	[106431] = "{id = 106431, Speaker = 2527, Text = '噢，发生什么事了么？', Option = '说明情况'}",
	[106432] = "{id = 106432, Speaker = 2527, Text = '这…这手帕是月神族部落的随身物件。'}",
	[106433] = "{id = 106433, Speaker = 2527, Text = '可是她们数十年前就应该都失踪了啊！', Option = '噢？'}",
	[106434] = "{id = 106434, Speaker = 2527, Text = '想必她们和你一样，都是为了月神的魔力而来的。'}",
	[106435] = "{id = 106435, Speaker = 2527, Text = '月神族部落曾经驻扎在斐扬南部，你去那儿应该能找到月神族的踪迹。', Option = '好的'}",
	[106436] = "{id = 106436, Speaker = 0, Text = '神秘的人影被大脚熊包围了，太危险了，先救她吧！'}",
	[106437] = "{id = 106437, Speaker = 4807, Text = '……谢…谢谢你救了我。'}",
	[106438] = "{id = 106438, Speaker = 4807, Text = '对不起，我之前还打伤了你。'}",
	[106439] = "{id = 106439, Speaker = 4807, Text = '真是没想到最后是你救了我。', Option = '没关系的'}",
	[106440] = "{id = 106440, Speaker = 4807, Text = '对了，我的手帕……', Option = '交出手帕'}",
	[106441] = "{id = 106441, Speaker = 4807, Text = '你…已经知道我的身份了吧？', Option = '嗯'}",
	[106442] = "{id = 106442, Speaker = 4807, Text = '我是月神族最后的血脉，找到月神曼尼的圣物是我们月神族的使命。'}",
	[106443] = "{id = 106443, Speaker = 4807, Text = '我已经确认，月神的呢喃就被封印在这棵千年古树之下。'}",
	[106444] = "{id = 106444, Speaker = 4807, Text = '但是…我的魔力太弱了，不仅没解开封印，还吸引来了附近的魔物。', Option = '我来试试？'}",
	[106445] = "{id = 106445, Speaker = 4807, Text = '不行的吧…这可是月神族才能解除的封印啊！', Option = '试一下吧'}",
	[106446] = "{id = 106446, Speaker = 4807, Text = '好吧，记住咒语：归来吧，皎洁的月光将会照耀梦境。', Option = '{记住了,2},{再听一次,3}'}",
	[106447] = "{id = 106447, Speaker = 0, Text = '咒语念错了并没有起效，重新试一次吧。'}",
	[106448] = "{id = 106448, Speaker = 4807, Text = '没想到……月神最后居然认可了一个冒险者，而不是我这个月神族人。'}",
	[106449] = "{id = 106449, Speaker = 4807, Text = '既然能得到月神的眷顾，你一定是传说中的天选之人。'}",
	[106450] = "{id = 106450, Speaker = 4807, Text = '去吧，请代替我们守护好这把琴。'}",
	[106451] = "{id = 106451, Speaker = 4807, Text = '后会有期，我愿月神会永远保佑你。'}",
	[106452] = "{id = 106452, Speaker = 7045, Text = '看来你已经找到了月神的呢喃，快让我见识见识！'}",
	[106453] = "{id = 106453, Speaker = 4809, Text = '哈哈哈，拂晓之光终于是我的啦！'}",
	[106454] = "{id = 106454, Speaker = 7042, Text = '不好了，出大事了！冒险者！', Option = '{怎么了,2},{不感兴趣,3}'}",
	[106455] = "{id = 106455, Speaker = 7042, Text = '吉芬塔地下洞穴出现了一股失控的魔力异变！', Option = '魔力波动？'}",
	[106456] = "{id = 106456, Speaker = 7042, Text = '是的，据说这股魔力波动与暗精灵族可能有关！', Option = '暗精灵？'}",
	[106457] = "{id = 106457, Speaker = 7042, Text = '暗精灵曾经是精灵族的死敌，已经近百年没有出现过了。'}",
	[106458] = "{id = 106458, Speaker = 7042, Text = '这次他们卷土重来，很有可能是为了那件精灵族至宝。', Option = '至宝？'}",
	[106459] = "{id = 106459, Speaker = 7042, Text = '嗯，一定是拂晓之光！'}",
	[106460] = "{id = 106460, Speaker = 7042, Text = '那是精灵族先代长老的遗物，一条具有星辰力量的魔鞭。', Option = '原来如此'}",
	[106461] = "{id = 106461, Speaker = 7042, Text = '事不宜迟，你快去吉芬塔地下控制住魔力异变吧！'}",
	[106462] = "{id = 106462, Speaker = 7042, Text = '另外可能还会找到拂晓之光的线索的！', Option = '明白了'}",
	[106463] = "{id = 106463, Speaker = 3080, Text = '冒险者，你是来帮忙解决魔力异变的么？', Option = '是的'}",
	[106464] = "{id = 106464, Speaker = 3080, Text = '其实我们已经找到了异变的原因。'}",
	[106465] = "{id = 106465, Speaker = 3080, Text = '哎……居然真的是暗精灵复苏了。', Option = '怎么会？'}",
	[106466] = "{id = 106466, Speaker = 3080, Text = '他们这次回来就是为了夺回埋葬在吉芬地下的拂晓之光。'}",
	[106467] = "{id = 106467, Speaker = 3080, Text = '魔法公会已经尽力阻止他们。'}",
	[106468] = "{id = 106468, Speaker = 3080, Text = '哎，但是暗精灵真的太强了……'}",
	[106469] = "{id = 106469, Speaker = 3080, Text = '我们虽然击败了几个暗精灵…'}",
	[106470] = "{id = 106470, Speaker = 3080, Text = '但他们的首领趁乱带着拂晓之光逃了。', Option = '糟了'}",
	[106471] = "{id = 106471, Speaker = 3080, Text = '还好，魔法公会有一项专门探测暗精灵魔力的仪器。'}",
	[106472] = "{id = 106472, Speaker = 3080, Text = '这种仪器需要一些暗物质才能运作。'}",
	[106473] = "{id = 106473, Speaker = 3080, Text = '你能帮我们去小恶魔的身上找一些暗物质来么？', Option = '好的'}",
	[106474] = "{id = 106474, Speaker = 3080, Text = '太好了，有了这些暗物质就能启动仪器了。'}",
	[106475] = "{id = 106475, Speaker = 3080, Text = '稍等一下，我将仪器充能后给你。'}",
	[106476] = "{id = 106476, Speaker = 3080, Text = '好了，你来试试吧。'}",
	[106477] = "{id = 106477, Speaker = 3080, Text = '成功了！暗精灵首领一定藏在东南方向。'}",
	[106478] = "{id = 106478, Speaker = 3080, Text = '快去吧，别让他又跑了！', Option = '明白了'}",
	[106479] = "{id = 106479, Speaker = 4809, Text = '该死，你究竟是怎么找到我的？'}",
	[106480] = "{id = 106480, Speaker = 4809, Text = '哼，看来你是魔法公会的那帮废物派来的。', Option = '……'}",
	[106481] = "{id = 106481, Speaker = 4809, Text = '呵呵，几百年了，我才终于等到了这天……'}",
	[106482] = "{id = 106482, Speaker = 4809, Text = '你以为我会这么束手就擒么？'}",
	[106483] = "{id = 106483, Speaker = 4809, Text = '拂晓之光只能是我的！是我的！'}",
	[106484] = "{id = 106484, Speaker = 4809, Text = '啊！可恶啊，拂晓之光居然会反噬我！'}",
	[106485] = "{id = 106485, Speaker = 4809, Text = '难道它已经认可了你区区一个人类么？！'}",
	[106486] = "{id = 106486, Speaker = 4809, Text = '我不甘心啊！'}",
	[106487] = "{id = 106487, Speaker = 0, Text = '周围的魔力异变消失了，拂晓之光与你的魔力完全融为了一体。'}",
	[106488] = "{id = 106488, Speaker = 7042, Text = '吉芬塔下的魔力异变已经消失了？果然是暗精灵啊！'}",
	[106489] = "{id = 106489, Speaker = 7042, Text = '不过看来你也已经得到了拂晓之光的认可，恭喜你。'}",
	[106490] = "{id = 106490, Speaker = 500480, Text = '嗯？我的名字居然不是吉米了~'}",
	[106491] = "{id = 106491, Speaker = 500480, Text = '嗷呜~是阳光~啦啦啦啦啦啦 ~~'}",
	[106492] = "{id = 106492, Speaker = 500480, Text = '超喜欢主人的呢~主人要不要和我一起~唱首歌~'}",
	[106493] = "{id = 106493, Speaker = 500480, Text = '要回去了嘛QAQ蛋里面可没有太阳~要记得有空放我出来哦！'}",
	[106494] = "{id = 106494, Speaker = 500480, Text = '这里真舒服呀~好想在这个地方做一个安静的盆栽~'}",
	[106495] = "{id = 106495, Speaker = 500480, Text = '成功啦成功啦~主人太棒了！'}",
	[106496] = "{id = 106496, Speaker = 500480, Text = '唔……别难过~我唱首歌给你听~'}",
	[106497] = "{id = 106497, Speaker = 500480, Text = '这是我给主人准备的礼物~主人看看喜不喜欢~'}",
	[106498] = "{id = 106498, Speaker = 500480, Text = '你看看我是不是长高一点了~~主人给我多浇水我能长的更快哦！'}",
	[106499] = "{id = 106499, Speaker = 500480, Text = '快……快给我浇浇水……救救小仙人掌吧……'}",
	[106500] = "{id = 106500, Speaker = 500480, Text = '跟着主人真是开心~主人是我最喜欢的人呢！'}",
	[106501] = "{id = 106501, Speaker = 500480, Text = '有没有棉花糖呀~~饿的没力气了呢！'}",
	[106502] = "{id = 106502, Speaker = 500480, Text = '再吃下去我就要变成胖胖仙人掌了呢！'}",
	[106503] = "{id = 106503, Speaker = 500480, Text = '我要为主人的归来~高歌一曲~'}",
	[106504] = "{id = 106504, Speaker = 500480, Text = '嘿！咚咚咚咚咚！看我的棒槌攻击！'}",
	[106505] = "{id = 106505, Speaker = 500480, Text = '嘿！看我这结实的肌肉！都是这样练出来的！'}",
	[106506] = "{id = 106506, Speaker = 500480, Text = '主人我是不是也出了一份力了呢~夸夸我嘛~'}",
	[106507] = "{id = 106507, Speaker = 500480, Text = '要不要我唱个歌帮你们配个背景音乐呀~'}",
	[106508] = "{id = 106508, Speaker = 500480, Text = '升级啦升级啦~~~我也想升级~！'}",
	[106509] = "{id = 106509, Speaker = 500480, Text = '主人我给你浇浇水，你快快醒过来好不好~'}",
	[106510] = "{id = 106510, Speaker = 500480, Text = '虽然我超喜欢主人的抚摸~但是主人要当心手手哦！'}",
	[106511] = "{id = 106511, Speaker = 500480, Text = '主人！快补充水份！我来帮你浇水~！'}",
	[106512] = "{id = 106512, Speaker = 500480, Text = '哇~新衣服！快看看我是不是变得更可爱了~'}",
	[106513] = "{id = 106513, Speaker = 500480, Text = '既然没什么事~我要不要就在这里开一场只给主人演唱会！~'}",
	[106514] = "{id = 106514, Speaker = 500480, Text = '主人可不可以摸摸我~我可把刺都清理干净了~主人放心摸！'}",
	[106515] = "{id = 106515, Speaker = 500480, Text = '主人主人~人家肚子饿了呢~没力气咚咚咚了！'}",
	[106516] = "{id = 106516, Speaker = 500480, Text = '主人的手超巧的~做出来的东西太棒了！~我也想戴~'}",
	[106517] = "{id = 106517, Speaker = 500480, Text = '是给我的嘛！主人真是对我太好了~好喜欢哦！'}",
	[106518] = "{id = 106518, Speaker = 500480, Text = '嗯~有点想念沙漠了呢~扎根在暖洋洋的沙子里……呼噜噜……'}",
	[106519] = "{id = 106519, Speaker = 4810, Text = '这是一口透着庞大魔力的深井。'}",
	[106520] = "{id = 106520, Speaker = 7045, Text = '冒险者，你知道智慧巨人密米尔的故事么？', Option = '{我知道,2},{不敢兴趣,3}'}",
	[106521] = "{id = 106521, Speaker = 7045, Text = '传说智慧巨人密米尔，拥有一本可以窥探一切的全知魔神书。'}",
	[106522] = "{id = 106522, Speaker = 7045, Text = '巨人族覆灭之际，密米尔将那本书藏在了他的智慧井里。', Option = '智慧井？'}",
	[106523] = "{id = 106523, Speaker = 7045, Text = '智慧井是密米尔所守护的秘宝之一，据说那口井也蕴藏着许多知识。'}",
	[106524] = "{id = 106524, Speaker = 7045, Text = '但想要获得智慧井的认可，必须要完成智慧巨人留下的谜题。', Option = '谜题？'}",
	[106525] = "{id = 106525, Speaker = 7045, Text = '嗯，智慧巨人的谜题博古通今，整个大陆能回答出的人恐怕寥寥无几。', Option = '这么难呀？'}",
	[106526] = "{id = 106526, Speaker = 7045, Text = '我听说艾因布洛克原野，最近有人看到过智慧井的幻影。'}",
	[106527] = "{id = 106527, Speaker = 7045, Text = '冒险者，如果你有自信和勇气的话，不妨可以去挑战试试。', Option = '没问题'}",
	[106528] = "{id = 106528, Speaker = 4810, Text = '愚钝的人类啊，你想要挑战智慧巨人留下的谜题么？', Option = '{我要挑战,2},{不了吧,3}'}",
	[106529] = "{id = 106529, Speaker = 4810, Text = '第一题：请问伟大的巨人始祖的名字是…？', Option = '{伊米尔,4},{密米尔,5},{拉米尔,5}'}",
	[106530] = "{id = 106530, Speaker = 4810, Text = '第二题：请问伊米尔所属的族系是…？', Option = '{冰霜巨人,6},{火焰巨人,7},{雷电巨人,7}'}",
	[106531] = "{id = 106531, Speaker = 4810, Text = '第三题：请问与伊米尔同生同灭的那件至宝名为…？', Option = '{伊米尔之心,8},{伊米尔之书,9},{伊米尔之靴,9}'}",
	[106532] = "{id = 106532, Speaker = 4810, Text = '愚蠢的人啊，你根本不配获得密米尔的认可！'}",
	[106533] = "{id = 106533, Speaker = 4810, Text = '没想到，你居然有能力通过智慧巨人的谜题。'}",
	[106534] = "{id = 106534, Speaker = 4810, Text = '但智慧巨人的认可又怎么会这么轻易获得呢？'}",
	[106535] = "{id = 106535, Speaker = 4810, Text = '你还需要通过一项最后的终极试炼。'}",
	[106536] = "{id = 106536, Speaker = 4810, Text = '在此之前，用[c][ffff00]99枚慧眼[-][/c]来作为钥匙，开启终极试炼吧！', Option = '好吧'}",
	[106537] = "{id = 106537, Speaker = 4810, Text = '你的慧眼还远远不够，再去好好努力收集一些吧！'}",
	[106538] = "{id = 106538, Speaker = 4810, Text = '很好，看来你已经有能力迎接终极试炼了。'}",
	[106539] = "{id = 106539, Speaker = 4810, Text = '准备好迎接终极试炼了么？', Option = '{准备好了,2},{还没有,3}'}",
	[106540] = "{id = 106540, Speaker = 4810, Text = '那么请记住这条提示，它将决定你的生死！'}",
	[106541] = "{id = 106541, Speaker = 4810, Text = '[c][ffff00]冰霜巨人是冰川孕育的最强种族，只有找到克制他们的自然元素才能轻易将其击败！[-][/c]', Option = '我知道了'}",
	[106542] = "{id = 106542, Speaker = 4810, Text = '你做得很好……'}",
	[106543] = "{id = 106543, Speaker = 4810, Text = '看来你已经拥有足够的智慧去接纳真正的全知全能了。', Option = '嗯'}",
	[106544] = "{id = 106544, Speaker = 4810, Text = '我将全知魔神书赐给你……'}",
	[106545] = "{id = 106545, Speaker = 4810, Text = '愿未来的你也能在探索知识的道路上永不停歇。', Option = '我明白了'}",
	[106546] = "{id = 106546, Speaker = 7045, Text = '看来你已经得到智慧巨人的认可了，希望你在今后也能善用自己的智慧。'}",
	[106547] = "{id = 106547, Speaker = 4810, Text = '是否使用[c][ffff00]99枚慧眼[-][/c]来开启终极试炼？', Option = '{交99枚慧眼,13},{再等等,14}'}",
	[106548] = "{id = 106548, Speaker = 7421, Text = '嘘！安静。'}",
