	[131001] = "{id = 131001, Speaker = 2278, Text = '愚蠢的人类，看看你都干了些什么！竟敢放肆屠戮我的子民们，准备接受我的怒火吧！'}",
	[131016] = "{id = 131016, Speaker = 1250, Text = '很不好。冒险者，请你先行入城，我组织好军队立刻就会前去援助你。', Option = '好的'}",
	[131066] = "{id = 131066, Text = '这里是普勒花园，看着风景，真是令人迷醉啊！'}",
	[131067] = "{id = 131067, Text = '说起来西门还隐藏着一个骑士的“劲敌”呢！'}",
