	[140002] = "{id = 140002, Speaker = 9201, Text = '冒险者可以在他这里领取“KFC炸鸡传说”任务，完成任务即可获得[c][ffff00]「KFC吃鸡餐盒」[-][/c]料理制作配方和[c][ffff00]「劲爆鸡米花兑换券」[-][/c]一张。'}",
	[140003] = "{id = 140003, Speaker = 9201, Text = '吃30份[c][ffff00]「KFC吃鸡餐盒」[-][/c]时解锁成就称号[c][ffff00]「吃鸡达人」[-][/c]。'}",
	[140004] = "{id = 140004, Speaker = 9201, Text = '请冒险者留意活动公告，不要错过哟！'}",
	[140006] = "{id = 140006, Speaker = 9202, Text = '炸鸡的配方，可是世界机密，不能随意交给一个陌生的冒险者。', Option = '炸鸡配方？'}",
	[140007] = "{id = 140007, Speaker = 9202, Text = '不错！嘘，小声点。'}",
	[140008] = "{id = 140008, Speaker = 9202, Text = '（小声地说）其实我是来自于异次元KFC的外卖员，带着目标而来！', Option = '什么？'}",
	[140009] = "{id = 140009, Speaker = 9202, Text = '我为这个世界带来了我们KFC的炸鸡配方，你不知道这个配方有多值钱呢！', Emoji = 28}",
	[140010] = "{id = 140010, Speaker = 9202, Text = '完成我考验后，我就会告诉你这个配方，准备好了就来找我吧！', Option = '{准备好了,2},{没准备好,3}'}",
	[140011] = "{id = 140011, Speaker = 9202, Text = '嗯！不错，你很可靠嘛！'}",
	[140012] = "{id = 140012, Speaker = 9202, Text = '喂，时不待人，美味的炸鸡更不待人！'}",
	[140013] = "{id = 140013, Speaker = 9202, Text = '看来是个经验丰富的冒险者，我的考验可不简单。', Option = '太棒了！'}",
	[140014] = "{id = 140014, Speaker = 9202, Text = '喂，你不要搞错了，我不会放水的！', Option = '迫不及待'}",
	[140015] = "{id = 140015, Speaker = 9202, Text = '（小声地说）看你这么高兴的样子，真不爽，不如整整你咯~~~'}",
	[140016] = "{id = 140016, Speaker = 9202, Text = '那么，这位朋友，作为一个烹饪家一定要会的事情是……', Option = '是什么？'}",
