	[140020] = "{id = 140020, Speaker = 9202, Text = '想必你应该记住了哦，那么该你唱啦，第一句。', Option = '{咪～拉西哆来咪,6},{咪～哆拉西咪来,7},{咪～拉哆拉西咪,7}'}",
	[140021] = "{id = 140021, Speaker = 9202, Text = '你唱错了，好好回忆一下我刚刚唱了些什么！'}",
	[140022] = "{id = 140022, Speaker = 9202, Text = '第二句，唱出来吧，冒险者。', Option = '{咪哆～咪哆～,8},{哆发～哆发～,8},{哆咪～哆咪～,9}'}",
	[140023] = "{id = 140023, Speaker = 9202, Text = '你唱错了，好好回忆一下我刚刚唱了些什么！'}",
	[140024] = "{id = 140024, Speaker = 9202, Text = '唱得不错，还有最后一句，唱给我听吧。', Option = '{哆咪发咪拉西～,10},{拉西拉哆发拉～,10},{拉哆拉发哆拉～,11}'}",
