	[140026] = "{id = 140026, Speaker = 9202, Text = '……（小声地说）怎么这么厉害！'}",
	[140027] = "{id = 140027, Speaker = 9202, Text = '咳，你做得不错，嗯……'}",
	[140028] = "{id = 140028, Speaker = 9202, Text = '我还有更难的考验等着你！'}",
	[140029] = "{id = 140029, Speaker = 9202, Text = '炸鸡炸鸡，不炸怎么能做成炸鸡呢~'}",
	[140030] = "{id = 140030, Speaker = 9202, Text = '我有不少新鲜的鸡肉，可是鸡蛋用完了，我需要一些鸡蛋做配料。'}",
	[140031] = "{id = 140031, Speaker = 9202, Text = '梦罗克有一些大嘴鸟蛋，你去帮我取一些来吧~'}",
	[140032] = "{id = 140032, Speaker = 9202, Text = '（碎碎念）没有用大嘴鸟蛋制作过炸鸡，不知道味道会怎么样呢。'}",
	[140033] = "{id = 140033, Speaker = 9202, Text = '（碎碎念）不管怎么样也要试一下……', Option = '对'}",
	[140034] = "{id = 140034, Speaker = 9202, Text = '（吹了一下胡子）嗯，不要偷听我自言自语。'}",
