	[140038] = "{id = 140038, Speaker = 9202, Text = '看来平时也有认真的锻炼厨艺~'}",
	[140039] = "{id = 140039, Speaker = 9202, Text = '很好，继续努力吧。'}",
	[140040] = "{id = 140040, Speaker = 9202, Text = '最后一步就是炸鸡了！'}",
