	[140074] = "{id = 140074, Speaker = 9205, Text = '可怜的小家伙，一个人在草丛瑟瑟发抖，被我发现啦！'}",
	[140075] = "{id = 140075, Speaker = 9205, Text = '哈哈哈哈，我是不是很伟大。'}",
