	[140077] = "{id = 140077, Speaker = 9205, Text = '你真是个不解风情的家伙。'}",
	[140078] = "{id = 140078, Speaker = 9205, Text = '总之，这小家伙好像是饿了！', Option = '怎么办？'}",
	[140079] = "{id = 140079, Speaker = 9205, Text = '可是哥布灵平时都吃什么！？'}",
	[140080] = "{id = 140080, Speaker = 9205, Text = '要不你问问他，他要吃什么…'}",
	[140081] = "{id = 140081, Speaker = 9205, Text = '他从刚才开始就只会噗呀噗呀的乱叫！'}",
	[140082] = "{id = 140082, Speaker = 9206, Text = '噗呀！！！'}",
	[140083] = "{id = 140083, Speaker = 9206, Text = '噗…呀！！！', Emoji = 15}",
	[140084] = "{id = 140084, Speaker = 0, Text = '他似乎很害怕你，试着抚摸他一下？'}",
	[140085] = "{id = 140085, Speaker = 9206, Text = '噗呀！！噗呀！！！', Emoji = 18}",
	[140086] = "{id = 140086, Speaker = 9206, Text = '噗呀噗呀！！！噗呀！！！', Emoji = 12}",
	[140087] = "{id = 140087, Speaker = 9205, Text = '我的天，你怎么还把他给吓着了。', Option = '呃…'}",
	[140088] = "{id = 140088, Speaker = 9205, Text = '忘了告诉你，这小家伙还是有点脾气的。', Option = '不早说'}",
	[140089] = "{id = 140089, Speaker = 9205, Text = '哈哈哈哈哈，可能是上了年纪记性不好。'}",
	[140090] = "{id = 140090, Speaker = 9205, Text = '算了，也不知道他喜欢吃什么，给他随便搞一些食材吧。', Option = '随便？'}",
	[140091] = "{id = 140091, Speaker = 9205, Text = '那我也没有办法不是吗~'}",
	[140092] = "{id = 140092, Speaker = 9205, Text = '这样吧，你先去海底神殿捕些刺身鱼来。'}",
	[140093] = "{id = 140093, Speaker = 9205, Text = '新鲜的刺身鱼肉入口即化，可是极品美味。'}",
	[140094] = "{id = 140094, Speaker = 9205, Text = '嗯…不错！就是这个，我日思夜想的…', Option = '什么！？'}",
	[140095] = "{id = 140095, Speaker = 9205, Text = '哦不，没什么，这小家伙一定会喜欢的。', Option = '是嘛'}",
	[140096] = "{id = 140096, Speaker = 9205, Text = '不过为了保险起见，可以再弄些苏克拉特沙漠的太阳蛋。'}",
	[140097] = "{id = 140097, Speaker = 9205, Text = '那可是苏克拉特沙漠特有的美味，外焦里嫩！'}",
	[140098] = "{id = 140098, Speaker = 9205, Text = '也拜托你了，哈哈哈哈哈哈，万一这小家伙不喜欢吃鱼呢！'}",
	[140099] = "{id = 140099, Speaker = 9205, Text = '哇！！这香味！！', Emoji = 28}",
	[140100] = "{id = 140100, Speaker = 9205, Text = '我当然不是自己想吃，你怎么能这么想我！？', Option = '{我什么也没说,5},{你克制一点,5}'}",
	[140101] = "{id = 140101, Speaker = 9206, Text = '噗呀！！！！', Emoji = 18}",
	[140102] = "{id = 140102, Speaker = 9205, Text = '你看，小家伙都饿了，等不及要吃东西了呢！', Option = '对啊'}",
	[140103] = "{id = 140103, Speaker = 9205, Text = '我做的料理可是很好吃的。'}",
	[140104] = "{id = 140104, Speaker = 9205, Text = '我当年可是吉芬有名的料理小王子！'}",
	[140105] = "{id = 140105, Speaker = 9205, Text = '看我给你露一手！'}",
	[140106] = "{id = 140106, Speaker = 9205, Text = '怎么样厉害吧？', Option = '这？'}",
	[140107] = "{id = 140107, Speaker = 9205, Text = '不要在意一些细节，让小家伙自己选吧！'}",
	[140108] = "{id = 140108, Speaker = 9206, Text = '噗呀噗呀！！', Emoji = 28}",
	[140109] = "{id = 140109, Speaker = 9206, Text = '噗呀噗呀噗呀！！！', Emoji = 9}",
	[140110] = "{id = 140110, Speaker = 9205, Text = '你看！！我就知道他喜欢吃我烧糊的…'}",
	[140111] = "{id = 140111, Speaker = 9205, Text = '哦不，我的原创料理！', Option = '是嘛……'}",
	[140112] = "{id = 140112, Speaker = 9205, Text = '嗯，我说了不要在意这些细节。'}",
	[140113] = "{id = 140113, Speaker = 9205, Text = '那他不喜欢吃的汤和鱼我就自己享用了，咳咳。', Option = '你……'}",
	[140114] = "{id = 140114, Speaker = 9205, Text = '嗯，这些都不重要，重要的是，我们现在还没找到小家伙的父母！', Option = '对啊！'}",
	[140115] = "{id = 140115, Speaker = 9205, Text = '那么在这之前，先给他取个名字吧。'}",
	[140116] = "{id = 140116, Speaker = 9205, Text = '小家伙总是噗呀噗呀的叫唤，就叫他普亚吧。'}",
	[140117] = "{id = 140117, Speaker = 9205, Text = '你觉得如何？', Option = '普亚！'}",
	[140118] = "{id = 140118, Speaker = 9206, Text = '噗呀！！', Emoji = 9}",
	[140119] = "{id = 140119, Speaker = 9207, Text = '噗呀噗呀！！', Emoji = 9}",
	[140120] = "{id = 140120, Speaker = 0, Text = '小哥布灵欢快的跺了跺脚，似乎是在回应你。'}",
	[140121] = "{id = 140121, Speaker = 9205, Text = '你看，他渐渐跟你熟悉起来啦~因为你给他带了好吃的！', Option = '嗯'}",
	[140122] = "{id = 140122, Speaker = 9205, Text = '好了今天也不早了，你先回去吧，剩下的交给我。'}",
	[140123] = "{id = 140123, Speaker = 9205, Text = '不要打扰我吃饭。'}",
	[140124] = "{id = 140124, Speaker = 9205, Text = '嗯…我是说，你明天再来见我吧！'}",
	[140125] = "{id = 140125, Speaker = 9212, Text = '阿黄，你总算是来了。'}",
	[140126] = "{id = 140126, Speaker = 9212, Text = '你知不知道我为了准备庆典的事情都忙疯了！', Option = '是吗？'}",
	[140127] = "{id = 140127, Speaker = 9212, Text = '我还要照顾普亚，你却来的这么迟！！！', Option = '好吧'}",
	[140128] = "{id = 140128, Speaker = 9212, Text = '嗯，总之，这小家伙好像看上去长大了些！？', Option = '是嘛'}",
	[140129] = "{id = 140129, Speaker = 9211, Text = '噗呀噗呀！', Emoji = 9}",
	[140130] = "{id = 140130, Speaker = 0, Text = '普亚手舞足蹈的表示见到你很高兴。'}",
	[140131] = "{id = 140131, Speaker = 9212, Text = '哈哈，看来普亚开始喜欢你了，不是吗？'}",
	[140132] = "{id = 140132, Speaker = 9212, Text = '我昨天仔细观察了一下，真没看出来这小家伙的父母是哪种哥布灵。', Option = '嗯？'}",
	[140133] = "{id = 140133, Speaker = 9212, Text = '我这边还有庆典的事脱不开身，但要把找普亚父母的事完全委托给你。'}",
	[140134] = "{id = 140134, Speaker = 9212, Text = '我还稍有点不放心。', Option = '为什么？'}",
	[140135] = "{id = 140135, Speaker = 9212, Text = '那么我问你，你了解哥布灵吗？ 	', Option = '{了解,2},{不了解,3}'}",
	[140136] = "{id = 140136, Speaker = 9212, Text = '那你回去补补功课再回来吧！'}",
	[140137] = "{id = 140137, Speaker = 9212, Text = '嘿嘿，很有自信嘛年轻人。'}",
	[140138] = "{id = 140138, Speaker = 9212, Text = '那我就来考考你！'}",
	[140139] = "{id = 140139, Speaker = 9212, Text = '在地面行走的哥布灵一共有几种呢？', Option = '{5种,4},{6种,4},{7种,5},{8种,4}'}",
	[140140] = "{id = 140140, Speaker = 9212, Text = '那么可以空中飞行的哥布灵一共有几种呢？', Option = '{2种,6},{3种,7},{4种,7},{5种,7}'}",
	[140141] = "{id = 140141, Speaker = 9212, Text = '那么会射箭的哥布灵叫什么名字呢？', Option = '{刺刀哥布灵,8},{弓箭哥布灵,9},{大斧哥布灵,8},{喷射哥布灵,8}'}",
	[140142] = "{id = 140142, Speaker = 9212, Text = '你还是回去补补功课再回来吧！'}",
	[140143] = "{id = 140143, Speaker = 9212, Text = '看来你对哥布灵还是有点了解的嘛。'}",
	[140144] = "{id = 140144, Speaker = 9212, Text = '嗯，但光凭这些是没办法确定普亚的父母的。'}",
	[140145] = "{id = 140145, Speaker = 9212, Text = '吉芬图书馆里有关于哥布灵这种魔物的详细资料。'}",
	[140146] = "{id = 140146, Speaker = 9212, Text = '替我向图书馆理事多斯·福克纳借一本来吧！'}",
	[140147] = "{id = 140147, Speaker = 3308, Text = '有什么可以帮你吗，冒险者？', Option = '说明来由'}",
	[140148] = "{id = 140148, Speaker = 3308, Text = '真是遗憾了，这本资料已经绝版了。', Option = '那怎么办？'}",
	[140149] = "{id = 140149, Speaker = 3308, Text = '我凭记忆还能给你本自己写的手抄本，你需要吗？', Option = '需要！'}",
	[140150] = "{id = 140150, Speaker = 3308, Text = '那你可得帮我做点事，要知道天下没有免费的午餐。', Option = '没问题'}",
	[140151] = "{id = 140151, Speaker = 3308, Text = '我需要一些粘稠液体来辅助我的研究，带一些给我吧！'}",
	[140152] = "{id = 140152, Speaker = 3308, Text = '你已经收集到了？正好我资料也写完了。', Option = '{给100份粘稠液体,10},{没有,11}'}",
	[140153] = "{id = 140153, Speaker = 3308, Text = '很好，这些足够我完成自己的研究啦！'}",
	[140154] = "{id = 140154, Speaker = 3308, Text = '年轻人，做人要诚实一些的好，你身上根本没有足够的粘稠液体。'}",
	[140155] = "{id = 140155, Speaker = 3308, Text = '拿去吧年轻人。', Option = '谢谢'}",
	[140156] = "{id = 140156, Speaker = 3308, Text = '谢什么，我并不是白送给你的~能帮到你我也很高兴。'}",
	[140157] = "{id = 140157, Speaker = 9212, Text = '阿黄你可真是神速啊！多斯那家伙，每次我让他做点事他都很不情愿。', Option = '查看资料'}",
	[140158] = "{id = 140158, Speaker = 9212, Text = '好吧，总之，让我看看…'}",
	[140159] = "{id = 140159, Speaker = 9212, Text = '嗯…', Emoji = 14}",
	[140160] = "{id = 140160, Speaker = 9212, Text = '嗯嗯…原来如此。', Option = '怎么？'}",
	[140161] = "{id = 140161, Speaker = 9212, Text = '原来哥布灵都不喜欢人类抚摸他们，这是对他们的不尊重呢！', Option = '这样啊'}",
	[140162] = "{id = 140162, Speaker = 9212, Text = '怪不得之前普亚对你发脾气。'}",
	[140163] = "{id = 140163, Speaker = 9212, Text = '不过你向他鞠躬，他会很高兴的。'}",
	[140164] = "{id = 140164, Speaker = 9211, Text = '噗呀！', Emoji = 9}",
	[140165] = "{id = 140165, Speaker = 0, Text = '普亚似乎接受了你的道歉，开心的蹭了你一下。'}",
	[140166] = "{id = 140166, Speaker = 9212, Text = '看来普亚原谅你之前的无礼了。'}",
	[140167] = "{id = 140167, Speaker = 9212, Text = '嗯，我要好好的研究一下这本资料。'}",
	[140168] = "{id = 140168, Speaker = 9212, Text = '应该能找出许多线索~', Option = '加油'}",
	[140169] = "{id = 140169, Speaker = 9212, Text = '你明天再来找我和普亚吧，今天就不要打扰我喝酒了。'}",
	[140170] = "{id = 140170, Speaker = 9212, Text = '咳咳，不对，是阅读资料。'}",
	[140171] = "{id = 140171, Speaker = 9212, Text = '今天你倒是来的很准时嘛！'}",
	[140172] = "{id = 140172, Speaker = 9212, Text = '我可都还没睡醒呢…', Option = '好吧', Emoji = 25}",
	[140173] = "{id = 140173, Speaker = 9212, Text = '普亚似乎是有点渴了刚才一直叫唤个不停。'}",
	[140174] = "{id = 140174, Speaker = 9212, Text = '是吧普亚？'}",
	[140175] = "{id = 140175, Speaker = 9211, Text = '噗呀！！噗呀！'}",
	[140176] = "{id = 140176, Speaker = 9212, Text = '你看，我知道你最疼他，我还想再去睡一会。'}",
	[140177] = "{id = 140177, Speaker = 9212, Text = '帮我去普隆德拉南门取些新鲜的露水来。'}",
	[140178] = "{id = 140178, Speaker = 9212, Text = '怎么这么快！？我还想再多睡一会儿呢！！', Emoji = 1}",
	[140179] = "{id = 140179, Speaker = 9212, Text = '我绝对不是因为自己想多睡一会，才让你跑到普隆德拉南门这么远去取水的。'}",
	[140180] = "{id = 140180, Speaker = 9212, Text = '你要相信我的为人，你是知道的。', Option = '{我知道,2},{呵呵,2}'}",
	[140181] = "{id = 140181, Speaker = 9212, Text = '嗯，光是露水普亚是不喝的，普亚对不对？'}",
	[140182] = "{id = 140182, Speaker = 9211, Text = '噗呀！'}",
	[140183] = "{id = 140183, Speaker = 9212, Text = '你看他说对~'}",
	[140184] = "{id = 140184, Speaker = 9212, Text = '还需要一些恶作剧魔棒-波利才行，哥布灵是最喜欢这些的。'}",
	[140185] = "{id = 140185, Speaker = 9212, Text = '这是我在资料上查到的，不要怀疑我。'}",
	[140186] = "{id = 140186, Speaker = 9212, Text = '你怎么每次动作都这么快？快让我看看你有没有10个恶作剧魔棒-波利吧！', Option = '{上交恶作剧魔棒,3},{我只是路过,4}'}",
	[140187] = "{id = 140187, Speaker = 9212, Text = '普亚，你的小玩具来喽~'}",
	[140188] = "{id = 140188, Speaker = 9212, Text = '原来你也只是在欺骗我，你身上并没有足够的恶作剧魔棒-波利。'}",
	[140189] = "{id = 140189, Speaker = 9212, Text = '还是你喂给他水吧，我两天没有洗澡，普亚似乎不想让我靠近他。', Option = '好吧'}",
	[140190] = "{id = 140190, Speaker = 9211, Text = '噗呀噗呀！', Emoji = 25}",
	[140191] = "{id = 140191, Speaker = 0, Text = '普亚将水喝的一干二净，擦了擦嘴表示很满意。'}",
	[140192] = "{id = 140192, Speaker = 9212, Text = '看来他现在该有些信任你了。'}",
	[140193] = "{id = 140193, Speaker = 9212, Text = '不如你试着呼唤他，看他会不会想跟着你呢？'}",
	[140194] = "{id = 140194, Speaker = 9211, Text = '噗呀！', Emoji = 19}",
	[140195] = "{id = 140195, Speaker = 0, Text = '普亚竟然成功的跟了过来。'}",
	[140196] = "{id = 140196, Speaker = 9212, Text = '看吧，我说他会愿意跟着你！'}",
	[140197] = "{id = 140197, Speaker = 9212, Text = '带着普亚去哥布灵森林逛逛吧。'}",
	[140198] = "{id = 140198, Speaker = 9212, Text = '说不定能遇到它的父母呢。', Option = '好主意'}",
	[140199] = "{id = 140199, Speaker = 9211, Text = '噗呀…', Emoji = 8}",
	[140200] = "{id = 140200, Speaker = 0, Text = '普亚东张西望了一会儿，似乎没有看到他的父母。'}",
	[140201] = "{id = 140201, Speaker = 9211, Text = '噗…呀…', Emoji = 24}",
	[140202] = "{id = 140202, Speaker = 0, Text = '普亚看着别的小哥布灵与父母在一起，难过的低下了头。'}",
	[140203] = "{id = 140203, Speaker = 9212, Text = '还是没有看见他的父母吗？'}",
	[140204] = "{id = 140204, Speaker = 9212, Text = '看着小家伙垂头丧气的样子还真是有点心酸呀！', Option = '是啊'}",
	[140205] = "{id = 140205, Speaker = 9212, Text = '不过我就快在资料里找出端倪了！'}",
	[140206] = "{id = 140206, Speaker = 9212, Text = '先把普亚交给我吧，我会逗他开心的。', Option = '你确定？'}",
	[140207] = "{id = 140207, Speaker = 9212, Text = '放心吧！你明天再来找我，一定会有结果的。'}",
	[140208] = "{id = 140208, Speaker = 9212, Text = '阿黄！！我找到了！！', Option = '什么？'}",
	[140209] = "{id = 140209, Speaker = 9212, Text = '我发现，普亚不是普通的哥布灵呢！', Option = '什么！'}",
	[140210] = "{id = 140210, Speaker = 9212, Text = '信我肯定没问题，而且我有一个大胆的推测。', Option = '你说'}",
	[140211] = "{id = 140211, Speaker = 9212, Text = '他应该是大斧哥布灵的后代！', Option = '你确定吗？'}",
	[140212] = "{id = 140212, Speaker = 9212, Text = '不确定，哈哈哈哈哈哈，都说了是推测。'}",
	[140213] = "{id = 140213, Speaker = 9212, Text = '不如你去哥布灵森林寻找一下大斧哥布灵，拍张照片带给我。'}",
	[140214] = "{id = 140214, Speaker = 9212, Text = '我们给普亚看看！看他认不认识，有没有反应。', Option = '好主意'}",
	[140215] = "{id = 140215, Speaker = 9212, Text = '嗯嗯，很好，我来给小家伙看看。'}",
	[140216] = "{id = 140216, Speaker = 9212, Text = '普亚，你的父母是长这样子吗？'}",
	[140217] = "{id = 140217, Speaker = 9211, Text = '噗呀！！！！', Emoji = 1}",
	[140218] = "{id = 140218, Speaker = 0, Text = '普亚看到照片似乎很兴奋。'}",
	[140219] = "{id = 140219, Speaker = 9212, Text = '你看，我就说他有反应！'}",
	[140220] = "{id = 140220, Speaker = 9212, Text = '好了，这事儿总算有些眉目。', Option = '是啊'}",
	[140221] = "{id = 140221, Speaker = 9212, Text = '我还有个大胆的推测！', Option = '你说'}",
	[140222] = "{id = 140222, Speaker = 9212, Text = '他应该是带★大斧哥布灵的后代！', Option = '你确定吗？'}",
	[140223] = "{id = 140223, Speaker = 9212, Text = '哎呀，都说了是推测了。'}",
	[140224] = "{id = 140224, Speaker = 9212, Text = '不如来证实一下，干脆制作一把大斧哥布灵的大斧吧！', Option = '为什么？'}",
	[140225] = "{id = 140225, Speaker = 9212, Text = '如果他会使用大斧，那就说明我的猜测是正确的呢！', Option = '好吧'}",
	[140226] = "{id = 140226, Speaker = 9212, Text = '那就请你带给我一些柔毛吧，适当的使用柔毛装饰可以使大斧更具舒适感。'}",
	[140227] = "{id = 140227, Speaker = 9212, Text = '你的动作真是一次比一次快。', Option = '{给100份柔毛,2},{我只是路过,3}'}",
	[140228] = "{id = 140228, Speaker = 9212, Text = '这些材料都可以装饰两把武器了呢！'}",
	[140229] = "{id = 140229, Speaker = 9212, Text = '很抱歉，如果你真的想帮普亚，那就请你携带足够的柔毛过来吧。'}",
	[140230] = "{id = 140230, Speaker = 9212, Text = '看我的吧，我当年可是吉芬的锻造小王子！'}",
