	[140259] = "{id = 140259, Speaker = 9218, Text = '告诉你吧，他往西边去了。', Option = '谢谢'}",
	[140260] = "{id = 140260, Speaker = 0, Text = '地上有些小脚印似乎是普亚的。'}",
