	[140318] = "{id = 140318, Speaker = 9212, Text = '不错，看上去你很爱这小家伙呢。'}",
	[140319] = "{id = 140319, Speaker = 9212, Text = '冒险者，做事可不能敷衍了事，你身上并没有足够的恶作剧魔棒-冰波利哦。'}",
	[140320] = "{id = 140320, Speaker = 9218, Text = '那么人类来吧，剪刀、拳头、布！', Option = '{拳头,40},{剪刀,50},{布,60}'}",
	[140321] = "{id = 140321, Speaker = 9218, Text = '哈哈，我说的没错吧，人类怎么可能比我们哥布灵聪明呢！'}",
	[140322] = "{id = 140322, Speaker = 9218, Text = '既然这样，再来一次吧！'}",
	[140323] = "{id = 140323, Speaker = 9212, Text = '真是太令我感动了，我相信以后我们一定会和小普亚再见面的。', Option = '嗯！'}",
	[140324] = "{id = 140324, Speaker = 9212, Text = '糟了……', Option = '怎么了？'}",
	[140325] = "{id = 140325, Speaker = 9212, Text = '这两天都在忙着帮小普亚寻找父母了，我都没有时间去处理哥布灵王国庆典的筹备事宜了。', Option = '那该怎么办？'}",
	[140326] = "{id = 140326, Speaker = 9212, Text = '也不用太担心，事实上庆典筹备只缺少一点物资了，你能帮帮我吗？', Option = '好吧'}",
	[140327] = "{id = 140327, Speaker = 9212, Text = '我就知道阿黄对我最好了！'}",
	[140328] = "{id = 140328, Speaker = 9212, Text = '那么请带给我一些粘稠液体和一些伪装卷轴吧，相信这些在庆典仪式上都能派上用场。'}",
	[140329] = "{id = 140329, Speaker = 9212, Text = '冒险者，你的效率真是太惊人了，你带来了我需要的粘稠液体吗？', Option = '{给100份粘稠液体,3},{没有,4}'}",
	[140330] = "{id = 140330, Speaker = 9212, Text = '阿黄，我真是越来越佩服你了。'}",
	[140331] = "{id = 140331, Speaker = 9212, Text = '嗯？阿黄，你好像并没有听懂我的话，你身上并没有足够的粘稠液体。'}",
	[140332] = "{id = 140332, Speaker = 9212, Text = '另外，你准备好了10份伪装卷轴吗？', Option = '{给10份伪装卷轴,5},{没有,6}'}",
	[140333] = "{id = 140333, Speaker = 9212, Text = '阿黄，做人一定要善始善终哦，请带给我足够的伪装卷轴吧。'}",
	[140354] = "{id = 140354, Speaker = 9223, Text = '不过请注意啦，这次国王准备的谢礼数量有限，所以，如果勇士你确定要协助这次庆典的话，同账号下的其他角色将无法再次接取哦！', Option = '{知道啦,3},{我想想,2}', Emoji = 7}",
	[140355] = "{id = 140355, Speaker = 9223, Text = '好的，那我们继续接下来的任务吧~'}",
	[140356] = "{id = 140356, Speaker = 9223, Text = '考虑好再来找我哦~'}",
	[140357] = "{id = 140357, Speaker = 9225, Text = '冒险者，快！快过来！'}",
	[140358] = "{id = 140358, Speaker = 9225, Text = '（小声地说）悄悄告诉你，除了我们KFC的炸鸡配方以外，我还从我的世界带来了一份小礼物！', Option = '什么礼物?'}",
	[140359] = "{id = 140359, Speaker = 9225, Text = '你来猜猜看！猜对了我就把它送给你！'}",
	[140360] = "{id = 140360, Speaker = 9225, Text = '咳咳，给你个提示，是可以贴身穿戴的！', Option = '衣服?'}",
