	[140554] = "{id = 140554, Speaker = 9227, Text = '我并没有看到3瓶苹果汁哦，我快渴死了，麻烦你快点。'}",
	[140555] = "{id = 140555, Speaker = 9226, Text = '好消息！活动期间，冒险者们每天都可以在我这里领取一瓶“波利成长秘药”！记得来领取哦。', Option = '{领取秘药,2},{我再想想,1}'}",
	[140557] = "{id = 140557, Speaker = 9226, Text = '喏~拿好了，可别摔碎了！它能使你事半功倍。', Option = '谢谢'}",
	[140558] = "{id = 140558, Speaker = 9239, Text = '波利庆典，我一定是最美的焦点。', Emoji = 3}",
	[140559] = "{id = 140559, Speaker = 9240, Text = '唔…吃的好撑。波利庆典期间每天都是大鱼大肉。', Emoji = 28}",
	[140560] = "{id = 140560, Speaker = 9241, Text = '锻炼身体，保卫波利！谁也别想在庆典的时候捣乱！', Emoji = 20}",
	[140561] = "{id = 140561, Speaker = 9242, Text = '跟我读，我是波亿利，不是波利，是波亿利，我没有在教你念拼音！', Emoji = 12}",
	[140562] = "{id = 140562, Speaker = 9243, Text = '庆典期间，可以收集奖章换购波利派对礼盒哦~', Emoji = 17}",
	[140563] = "{id = 140563, Speaker = 9503, Text = '你有没有对妈妈大声地说出我爱你呢~'}",
	[140564] = "{id = 140564, Speaker = 9503, Text = '嘿~冒险者~你好！让我来自我介绍一下吧~', Option = '好的'}",
	[140565] = "{id = 140565, Speaker = 9503, Text = '我是专门为了母亲节而来到普隆德拉的萱之使者哦~', Emoji = 27}",
	[140566] = "{id = 140566, Speaker = 9503, Text = '呐，今天可是母亲节哦~记得要多陪陪妈妈，妈妈可是世界上最伟大的人呢~'}",
	[140567] = "{id = 140567, Speaker = 9503, Text = '如果你乖乖听话的话，这个萱之礼盒就当做我送给你的小礼物吧~'}",
	[140568] = "{id = 140568, Speaker = 9510, Text = '你好~冒险者！斐扬正在布置一年一度的端午节庆典！'}",
	[140569] = "{id = 140569, Speaker = 9510, Text = '有兴趣加入我们吗~', Option = '{有兴趣,2},{没兴趣,3}', Emoji = 4}",
	[140570] = "{id = 140570, Speaker = 9510, Text = '好极了，欢迎你加入我们的端午庆典。', Option = '很荣幸'}",
	[140571] = "{id = 140571, Speaker = 9510, Text = '这样吧~我的女儿梨香和你年龄相仿。', Emoji = 30}",
	[140572] = "{id = 140572, Speaker = 9510, Text = '她是今年斐扬的端午节负责人。'}",
	[140573] = "{id = 140573, Speaker = 9510, Text = '你带着我的介绍信去斐扬找她吧~'}",
	[140574] = "{id = 140574, Speaker = 9510, Text = '相信你们会很聊得来的。'}",
	[140575] = "{id = 140575, Speaker = 9511, Text = '嗨，你也是来参加端午节庆典的么？', Option = '递交信件', Emoji = 21}",
	[140576] = "{id = 140576, Speaker = 9511, Text = '原来是父亲的朋友啊，欢迎你的到来！'}",
	[140577] = "{id = 140577, Speaker = 9511, Text = '看你的打扮，你不是斐扬本地人吧？', Option = '嗯', Emoji = 9}",
	[140578] = "{id = 140578, Speaker = 9511, Text = '嘻嘻，那么作为远道而来的客人。'}",
	[140579] = "{id = 140579, Speaker = 9511, Text = '我想先考考你，对端午的了解有多少~', Option = '没问题'}",
	[140580] = "{id = 140580, Speaker = 9511, Text = '第一问~在米德加尔特大陆，端午是哪个地区独有的节日？', Option = '{普隆德拉,3},{吉芬,3},{斐扬,2}'}",
	[140581] = "{id = 140581, Speaker = 9511, Text = '第二问~端午节是每年的什么时候才会举办的节日？', Option = '{秋末,4},{仲夏,5},{深冬,4}'}",
	[140582] = "{id = 140582, Speaker = 9511, Text = '第三问~以下哪一项不是端午节会做的节庆活动？', Option = '{吃粽子,6},{划龙舟,6},{赏菊花,7}'}",
	[140583] = "{id = 140583, Speaker = 9511, Text = '不对噢，你再仔细想想。'}",
	[140584] = "{id = 140584, Speaker = 9511, Text = '哇，都答对了！', Emoji = 20}",
	[140585] = "{id = 140585, Speaker = 9511, Text = '看来你来之前有做过准备的嘛~', Option = '嗯'}",
	[140586] = "{id = 140586, Speaker = 9511, Text = '既然你已经对端午节这么了解~'}",
	[140587] = "{id = 140587, Speaker = 9511, Text = '嘿嘿嘿……能请你帮个忙吗~？', Emoji = 5}",
	[140588] = "{id = 140588, Speaker = 9511, Text = '唉，其实是这样的。'}",
	[140589] = "{id = 140589, Speaker = 9511, Text = '大部分斐扬居民对待外乡人吧……', Emoji = 24}",
	[140590] = "{id = 140590, Speaker = 9511, Text = '比较冷漠，不太善于沟通。', Option = '嗯嗯'}",
	[140591] = "{id = 140591, Speaker = 9511, Text = '今年的端午节，有很多其他城市的客人慕名来参观。'}",
	[140592] = "{id = 140592, Speaker = 9511, Text = '作为负责人，我担心斐扬的居民们不擅长接待这些远道而来的客人。', Option = '嗯'}",
	[140593] = "{id = 140593, Speaker = 9511, Text = '所以能遇见你这样既了解端午又热心的冒险者，可真是我的大救星了。', Option = '哈哈', Emoji = 26}",
	[140594] = "{id = 140594, Speaker = 9511, Text = '说起来，今天早上就来了一位让我很头疼的客人呢。', Option = '是谁？', Emoji = 18}",
	[140595] = "{id = 140595, Speaker = 9511, Text = '普隆德拉的铁匠，忽克连。', Option = '他也来了？'}",
	[140596] = "{id = 140596, Speaker = 9511, Text = '对呀，忽克连先生长途跋涉来斐扬参加端午庆典。'}",
	[140597] = "{id = 140597, Speaker = 9511, Text = '是为了能吃他期待已久的粽子。', Option = '吃货…'}",
	[140598] = "{id = 140598, Speaker = 9511, Text = '我给了他刚出炉的熟粽子。'}",
	[140599] = "{id = 140599, Speaker = 9511, Text = '可他实在是太有钻研精神了。'}",
	[140600] = "{id = 140600, Speaker = 9511, Text = '他居然说希望能自己动手做一次粽子。', Option = '哈哈', Emoji = 15}",
	[140601] = "{id = 140601, Speaker = 9511, Text = '呜呜，但是我之前准备的糯米都用完了。', Emoji = 24}",
	[140602] = "{id = 140602, Speaker = 9511, Text = '[c][ffff00][PlayerName][-][/c]，你能帮我去借一些糯米么？', Option = '没问题'}",
	[140603] = "{id = 140603, Speaker = 3073, Text = '你找我有什么事么？', Option = '说明情况'}",
	[140604] = "{id = 140604, Speaker = 3073, Text = '噢，原来是梨香需要糯米。'}",
	[140605] = "{id = 140605, Speaker = 3073, Text = '喏，这里还有很多，都拿去吧。'}",
	[140606] = "{id = 140606, Speaker = 9511, Text = '太好了，这样就能教忽克连先生做粽子了。'}",
	[140607] = "{id = 140607, Speaker = 9512, Text = '梨香小姐，材料准备好了没呀？', Emoji = 17}",
	[140608] = "{id = 140608, Speaker = 9511, Text = '好了好了，多亏了[c][ffff00][PlayerName][-][/c]，糯米已经备齐了。'}",
	[140609] = "{id = 140609, Speaker = 9512, Text = '哟，好久不见，原来你也来参加端午庆典了。', Option = '对呀'}",
	[140610] = "{id = 140610, Speaker = 9511, Text = '喏，这里是粽叶和细绳。'}",
	[140611] = "{id = 140611, Speaker = 9511, Text = '[c][ffff00][PlayerName][-][/c]，你也和忽克连先生一起学包粽子吧！'}",
	[140612] = "{id = 140612, Speaker = 9511, Text = '真的很有趣的！', Option = '嗯嗯'}",
	[140613] = "{id = 140613, Speaker = 9511, Text = '先把粽叶折一条窄边…'}",
	[140614] = "{id = 140614, Speaker = 9511, Text = '然后卷起，做成漏斗形…'}",
	[140615] = "{id = 140615, Speaker = 9511, Text = '之后倒入糯米和其他辅料…'}",
	[140616] = "{id = 140616, Speaker = 9511, Text = '最后包好，用绳子紧紧扎起来！'}",
	[140617] = "{id = 140617, Speaker = 9511, Text = '好耶，这样就算基本做好了！'}",
	[140618] = "{id = 140618, Speaker = 9511, Text = '这些生粽子，我帮你们冷藏起来。'}",
	[140619] = "{id = 140619, Speaker = 9512, Text = '那什么时候，才能吃到自己亲手包的粽子呀。', Emoji = 28}",
	[140620] = "{id = 140620, Speaker = 9511, Text = '别急嘛，今天料理组已经休息了。'}",
	[140621] = "{id = 140621, Speaker = 9511, Text = '等明天一早，我就把你们的生粽子给他们蒸熟。'}",
	[140622] = "{id = 140622, Speaker = 9511, Text = '到时候就能吃到新鲜的热粽子了哟~', Emoji = 20}",
	[140623] = "{id = 140623, Speaker = 9512, Text = '这样啊，好吧。'}",
	[140624] = "{id = 140624, Speaker = 9512, Text = '那我明天再来啦！', Emoji = 17}",
	[140625] = "{id = 140625, Speaker = 9511, Text = '今天辛苦你了，你也先回去吧。'}",
	[140626] = "{id = 140626, Speaker = 9511, Text = '等明天，我们一起吃粽子~', Option = '嗯', Emoji = 2}",
	[140627] = "{id = 140627, Speaker = 9511, Text = '嗨，[c][ffff00][PlayerName][-][/c]，你终于来啦！', Emoji = 3}",
	[140628] = "{id = 140628, Speaker = 9511, Text = '忽克连先生可是早就来吃过粽子了呢~'}",
	[140629] = "{id = 140629, Speaker = 9511, Text = '来，这是你的粽子。'}",
	[140630] = "{id = 140630, Speaker = 9511, Text = '快趁热吃吧~', Option = '谢谢'}",
	[140631] = "{id = 140631, Speaker = 9511, Text = '怎么样，是不是很好吃呀？', Option = '嗯嗯'}",
	[140632] = "{id = 140632, Speaker = 9511, Text = '嘿嘿，好吃就对了。'}",
	[140633] = "{id = 140633, Speaker = 9511, Text = '其实这是我做的粽子哟！', Option = '啊？', Emoji = 26}",
	[140634] = "{id = 140634, Speaker = 9511, Text = '都怪你昨天手艺太差了啦。'}",
	[140635] = "{id = 140635, Speaker = 9511, Text = '你的粽子下锅就散开了，根本没法吃了。'}",
	[140636] = "{id = 140636, Speaker = 9511, Text = '所以我昨晚，重新做了几个粽子给你吃。', Option = '你真好…'}",
	[140637] = "{id = 140637, Speaker = 9511, Text = '嘻嘻，你别急着夸我，粽子可不是免费的。', Emoji = 5}",
	[140638] = "{id = 140638, Speaker = 9511, Text = '今天还需要你帮忙的哟。', Option = '包在我身上'}",
	[140639] = "{id = 140639, Speaker = 9511, Text = '事情是这样的。'}",
	[140640] = "{id = 140640, Speaker = 9511, Text = '海陆曼先生，希望能从斐扬带一些艾草回去。', Option = '艾草？'}",
	[140641] = "{id = 140641, Speaker = 9511, Text = '咦，你不知道艾草么？'}",
	[140642] = "{id = 140642, Speaker = 9511, Text = '艾草就是生长在斐扬树林里的一种植物。'}",
	[140643] = "{id = 140643, Speaker = 9511, Text = '它的气味很特别，可以用来做药，做料理……', Option = '嗯'}",
	[140644] = "{id = 140644, Speaker = 9511, Text = '但是艾草最有意义的用途，是在端午节挂在房门上~', Option = '门上？'}",
	[140645] = "{id = 140645, Speaker = 9511, Text = '对呀，传说艾草可以抵抗邪恶污秽的力量。'}",
	[140646] = "{id = 140646, Speaker = 9511, Text = '每年端午节挂在门上，就可以免除一年的灾厄。', Option = '原来如此'}",
	[140647] = "{id = 140647, Speaker = 9511, Text = '好啦，时间紧迫，海陆曼先生下午就要回去了。'}",
	[140648] = "{id = 140648, Speaker = 9511, Text = '麻烦你去野外采集一些新鲜的艾草吧。'}",
	[140649] = "{id = 140649, Speaker = 9511, Text = '辛苦啦，有这么多艾草，海陆曼先生一定会满意的。'}",
	[140650] = "{id = 140650, Speaker = 9511, Text = '海陆曼先生现在应该在喝酒。'}",
	[140651] = "{id = 140651, Speaker = 9511, Text = '你帮他把艾草送过去吧。'}",
	[140652] = "{id = 140652, Speaker = 9513, Text = '你是来帮我送艾草的么？', Option = '递交艾草', Emoji = 7}",
	[140653] = "{id = 140653, Speaker = 9513, Text = '唔，好香的新鲜艾草。', Emoji = 27}",
	[140654] = "{id = 140654, Speaker = 9513, Text = '哈哈，有了这个挂在帐篷上，不仅驱邪消灾…'}",
	[140655] = "{id = 140655, Speaker = 9513, Text = '那些烦人的蚊虫再也不敢靠近我的帐篷了。', Option = '太好了'}",
	[140656] = "{id = 140656, Speaker = 9513, Text = '谢谢你，可惜我就要启程回去了。'}",
	[140657] = "{id = 140657, Speaker = 9513, Text = '请帮我把这份感谢，也传达给梨香小姐。', Option = '好的'}",
	[140658] = "{id = 140658, Speaker = 9511, Text = '艾草送到了么？', Option = '传达谢意'}",
	[140659] = "{id = 140659, Speaker = 9511, Text = '海陆曼可真客气~', Option = '是啊'}",
	[140660] = "{id = 140660, Speaker = 9511, Text = '对了，我有东西要送给你。'}",
	[140661] = "{id = 140661, Speaker = 9511, Text = '怎么样，是不是比艾草还香？', Option = '这是？'}",
	[140662] = "{id = 140662, Speaker = 9511, Text = '这是菖蒲，也是端午节用来消灾去厄的植物。'}",
	[140663] = "{id = 140663, Speaker = 9511, Text = '不过，我可不是随便找了个替代品送给你哦。', Option = '嗯？'}",
	[140664] = "{id = 140664, Speaker = 9511, Text = '这是我从小在自家院子里种的菖蒲。'}",
	[140665] = "{id = 140665, Speaker = 9511, Text = '我自己培育的品种哟。'}",
	[140666] = "{id = 140666, Speaker = 9511, Text = '普通朋友我才不会送给他呢~', Option = '这样啊…'}",
	[140667] = "{id = 140667, Speaker = 9511, Text = '好了，你快回去把菖蒲挂在门上。'}",
	[140668] = "{id = 140668, Speaker = 9511, Text = '明天准时再来找我，我还给你准备了别的惊喜。', Option = '好的'}",
	[140669] = "{id = 140669, Speaker = 9511, Text = '今天来的挺早嘛~'}",
	[140670] = "{id = 140670, Speaker = 9511, Text = '是不是很期待我说的惊喜？', Option = '嗯'}",
	[140671] = "{id = 140671, Speaker = 9511, Text = '惊喜就是……', Emoji = 5}",
	[140672] = "{id = 140672, Speaker = 9511, Text = '这个！'}",
	[140673] = "{id = 140673, Speaker = 9511, Text = '哈哈哈哈，你这个样子超好笑的~', Emoji = 17}",
	[140674] = "{id = 140674, Speaker = 9511, Text = '是不是被我吓到了呀？'}",
	[140675] = "{id = 140675, Speaker = 9511, Text = '这个其实也是端午节的传统活动之一哦。', Option = '诶？'}",
	[140676] = "{id = 140676, Speaker = 9511, Text = '这个叫画额，它也是有典故的。'}",
	[140677] = "{id = 140677, Speaker = 9511, Text = '哈哈哈，我是想给你讲典故的。'}",
	[140678] = "{id = 140678, Speaker = 9511, Text = '但是你现在的样子太好笑了。', Option = '……'}",
	[140679] = "{id = 140679, Speaker = 9511, Text = '咳咳，你去河边打点水。'}",
	[140680] = "{id = 140680, Speaker = 9511, Text = '把额头的法印洗掉吧。'}",
	[140681] = "{id = 140681, Speaker = 9511, Text = '那样你就能变回原样了。'}",
	[140682] = "{id = 140682, Speaker = 9511, Text = '竖起耳朵，好好听我讲故事~', Option = '嗯'}",
	[140683] = "{id = 140683, Speaker = 9511, Text = '相传，以前斐扬闹过一种小孩很容易得的怪病。'}",
	[140684] = "{id = 140684, Speaker = 9511, Text = '得病的孩子会变得手脚发冷，胆小怕生。'}",
	[140685] = "{id = 140685, Speaker = 9511, Text = '那一年的端午节，好多孩子都病了。', Emoji = 24}",
	[140686] = "{id = 140686, Speaker = 9511, Text = '大人们都很担心，端午节也变得死气沉沉的。', Option = '真可怕'}",
	[140687] = "{id = 140687, Speaker = 9511, Text = '好在这个时候，斐扬来了一位神秘的修行者。', Emoji = 25}",
	[140688] = "{id = 140688, Speaker = 9511, Text = '他不忍看到孩子们生病，于是研究了七个昼夜。'}",
	[140689] = "{id = 140689, Speaker = 9511, Text = '终于发现了破解怪病的方法…', Option = '是什么？', Emoji = 11}",
	[140690] = "{id = 140690, Speaker = 9511, Text = '将雄黄石磨成粉末，作为触媒……'}",
	[140691] = "{id = 140691, Speaker = 9511, Text = '然后涂抹在孩子的额头上，画成特定的法印。', Option = '这么神奇？'}",
	[140692] = "{id = 140692, Speaker = 9511, Text = '嗯，雄黄粉具有恰到好处的微量火属性魔法。'}",
	[140693] = "{id = 140693, Speaker = 9511, Text = '利用法印注入体内，就有了治病的效果。'}",
	[140694] = "{id = 140694, Speaker = 9511, Text = '这，就叫做画额。', Option = '原来如此'}",
	[140695] = "{id = 140695, Speaker = 9511, Text = '不过这种病已经消失上百年了。'}",
	[140696] = "{id = 140696, Speaker = 9511, Text = '虽然斐扬的人们不再需要画额来治病。'}",
	[140697] = "{id = 140697, Speaker = 9511, Text = '但依然保留了画额的传统，以纪念那位好心的修行者。', Option = '嗯'}",
	[140698] = "{id = 140698, Speaker = 9511, Text = '经过百年的变迁，画额的触媒已经不止于雄黄粉了。', Emoji = 21}",
	[140699] = "{id = 140699, Speaker = 9511, Text = '用某些特定物质作为触媒，画额便会有让人易容变身的效果。', Option = '我懂了'}",
	[140700] = "{id = 140700, Speaker = 9511, Text = '没错，刚才我把你变成大脚熊。'}",
	[140701] = "{id = 140701, Speaker = 9511, Text = '就是用大脚熊的毛发作为触媒的效果。'}",
	[140702] = "{id = 140702, Speaker = 9511, Text = '嘿嘿，现在我给你一个报仇的机会。', Option = '噢？', Emoji = 5}",
	[140703] = "{id = 140703, Speaker = 9511, Text = '依斯鲁得的卡鲁船长，这几天在斐扬到处用画额戏弄别人。', Emoji = 15}",
	[140704] = "{id = 140704, Speaker = 9511, Text = '就连我也被他欺负过呢。'}",
	[140705] = "{id = 140705, Speaker = 9511, Text = '现在我把画额的技巧也教给你。'}",
	[140706] = "{id = 140706, Speaker = 9511, Text = '嘻嘻，你去帮我教训教训卡鲁船长吧。', Option = '没问题'}",
	[140707] = "{id = 140707, Speaker = 9511, Text = '好了，这里是画额用的几种触媒，都给你。'}",
	[140708] = "{id = 140708, Speaker = 9511, Text = '你快去找卡鲁船长试试吧。'}",
	[140709] = "{id = 140709, Speaker = 9514, Text = '嘿嘿嘿，端午节真好玩。', Option = '{使用黄色触媒,2},{使用蓝色触媒,3},{使用红色触媒,4},{使用绿色触媒,5}', Emoji = 5}",
