	[140726] = "{id = 140726, Speaker = 9511, Text = '哈哈，没想到那个恶趣味的卡鲁船长也有今天。'}",
	[140727] = "{id = 140727, Speaker = 9511, Text = '谢谢你帮我教训他了~', Option = '不用谢'}",
	[140728] = "{id = 140728, Speaker = 9511, Text = '这几天老让你跑腿，真是辛苦你了。', Emoji = 2}",
	[140729] = "{id = 140729, Speaker = 9511, Text = '快回去休息吧。'}",
	[140730] = "{id = 140730, Speaker = 9511, Text = '明天带你玩一些能放松身心的活动吧~', Option = '好的'}",
	[140731] = "{id = 140731, Speaker = 9511, Text = '又见到你了，真高兴啊。'}",
	[140732] = "{id = 140732, Speaker = 9511, Text = '是不是很好奇放松身心的活动是什么呀？', Option = '嗯嗯'}",
	[140733] = "{id = 140733, Speaker = 9511, Text = '答案就是……', Emoji = 9}",
	[140734] = "{id = 140734, Speaker = 9511, Text = '洗澡！', Option = '诶？！'}",
	[140735] = "{id = 140735, Speaker = 9511, Text = '说的当然不是普通的洗澡啦。'}",
	[140736] = "{id = 140736, Speaker = 9511, Text = '是端午节特有的一种沐洗活动——沐兰汤。', Option = '沐兰汤？'}",
	[140737] = "{id = 140737, Speaker = 9511, Text = '还记不记得之前让你去采集的艾草？'}",
	[140738] = "{id = 140738, Speaker = 9511, Text = '还有我送给你的菖蒲。', Option = '当然记得'}",
	[140739] = "{id = 140739, Speaker = 9511, Text = '沐兰汤就是同时发挥这两种植物的魔力。', Emoji = 25}",
	[140740] = "{id = 140740, Speaker = 9511, Text = '先以艾草酿成艾叶水，再用菖蒲打湿后，从头到脚洒在沐浴者的身上。', Option = '有意思'}",
	[140741] = "{id = 140741, Speaker = 9511, Text = '每年的端午节，植物中会天然蕴含一些魔力。'}",
	[140742] = "{id = 140742, Speaker = 9511, Text = '这种魔力可以消除沐浴者的疲劳，让人容光焕发。'}",
	[140743] = "{id = 140743, Speaker = 9511, Text = '是一年才有一次的享受呢！', Option = '等不及了'}",
	[140744] = "{id = 140744, Speaker = 9511, Text = '嘻嘻，就知道你想放松一下了。', Emoji = 26}",
	[140745] = "{id = 140745, Speaker = 9511, Text = '不过……', Option = '怎么了？'}",
	[140746] = "{id = 140746, Speaker = 9511, Text = '吉芬的雷藏先生，很早就找我预约了沐兰汤。'}",
	[140747] = "{id = 140747, Speaker = 9511, Text = '前几天我写信通知过他，端午节快到了。'}",
	[140748] = "{id = 140748, Speaker = 9511, Text = '但可能他太忙了忘了看信。', Emoji = 19}",
	[140749] = "{id = 140749, Speaker = 9511, Text = '导致一直到现在都还没来斐扬。', Option = '嗯'}",
	[140750] = "{id = 140750, Speaker = 9511, Text = '拜托啦拜托啦，雷藏先生曾经帮助过我。'}",
	[140751] = "{id = 140751, Speaker = 9511, Text = '我很希望他能来享受一次难得的沐兰汤。'}",
	[140752] = "{id = 140752, Speaker = 9511, Text = '麻烦你去吉芬提醒他快来吧。', Option = '小事一桩', Emoji = 4}",
	[140753] = "{id = 140753, Speaker = 1321, Text = '[c][ffff00][PlayerName][-][/c]，好久不见啊。'}",
	[140754] = "{id = 140754, Speaker = 1321, Text = '找我有什么事么？', Option = '说明情况'}",
	[140755] = "{id = 140755, Speaker = 1321, Text = '哎呀呀，真是老糊涂了！', Emoji = 1}",
	[140756] = "{id = 140756, Speaker = 1321, Text = '最近沉迷于新的魔法研发。'}",
	[140757] = "{id = 140757, Speaker = 1321, Text = '怎么让我把端午节的沐兰汤给忘了呢。', Option = '哈哈'}",
	[140758] = "{id = 140758, Speaker = 1321, Text = '太谢谢你来提醒我了。'}",
	[140759] = "{id = 140759, Speaker = 1321, Text = '这几年我四处忙碌，搞得身体都有些吃不消了。'}",
	[140760] = "{id = 140760, Speaker = 1321, Text = '太需要一次沐兰汤，放松一下自己了。', Option = '嗯嗯'}",
	[140761] = "{id = 140761, Speaker = 1321, Text = '这会儿我手头还有些小事。'}",
	[140762] = "{id = 140762, Speaker = 1321, Text = '你先回斐扬吧，我处理完立刻就去！', Option = '好的'}",
	[140763] = "{id = 140763, Speaker = 9511, Text = '怎么样，和雷藏先生说了么？', Option = '嗯'}",
	[140764] = "{id = 140764, Speaker = 9511, Text = '那就好，估计他就快来了。'}",
	[140765] = "{id = 140765, Speaker = 9519, Text = '来了来了，劳烦晚辈们等我了。', Emoji = 17}",
	[140766] = "{id = 140766, Speaker = 9519, Text = '梨香啊，好久不见。'}",
	[140767] = "{id = 140767, Speaker = 9519, Text = '比上次我见到你时，你看起来更有精神了。'}",
	[140768] = "{id = 140768, Speaker = 9511, Text = '嘻嘻，但是雷藏先生却看起来很疲惫呢。', Emoji = 26}",
	[140769] = "{id = 140769, Speaker = 9511, Text = '要在吉芬管理魔法师公会大大小小的事务，一定很辛苦吧。'}",
	[140770] = "{id = 140770, Speaker = 9519, Text = '哎，一言难尽…', Option = '是啊…', Emoji = 25}",
	[140771] = "{id = 140771, Speaker = 9511, Text = '所以嘛，快享受沐兰汤吧。'}",
	[140772] = "{id = 140772, Speaker = 9511, Text = '艾草水和菖蒲都准备好了呢~'}",
	[140773] = "{id = 140773, Speaker = 9519, Text = '[c][ffff00][PlayerName][-][/c]，你应该是第一次接触沐兰汤吧？', Emoji = 8}",
	[140774] = "{id = 140774, Speaker = 9519, Text = '我教你一段咏唱怎么样？', Option = '咏唱？'}",
	[140775] = "{id = 140775, Speaker = 9519, Text = '对，沐兰汤是自然精灵魔力的馈赠。'}",
	[140776] = "{id = 140776, Speaker = 9519, Text = '在沐洗的时候配合咏唱特定的咒语，会达到更好的放松效果。', Option = '原来如此'}",
	[140777] = "{id = 140777, Speaker = 9519, Text = '听好了，一共三句，很简单的。'}",
	[140778] = "{id = 140778, Speaker = 9519, Text = '沐洗头发时，咏唱——大自然的精灵啊。'}",
	[140779] = "{id = 140779, Speaker = 9519, Text = '沐洗肩膀时，咏唱——感谢你们的馈赠。'}",
	[140780] = "{id = 140780, Speaker = 9519, Text = '沐洗双腿时，咏唱——让我与健康同在。'}",
	[140781] = "{id = 140781, Speaker = 9519, Text = '记住了么？', Option = '{记住了,2},{还没记住,3}'}",
	[140782] = "{id = 140782, Speaker = 9519, Text = '好，那就请梨香小姐为我们开始沐兰汤吧。'}",
	[140783] = "{id = 140783, Speaker = 9511, Text = '沐洗头发时，应该咏唱——', Option = '{大自然的精灵啊,4},{大自然的馈赠啊,5}'}",
	[140784] = "{id = 140784, Speaker = 9511, Text = '沐洗肩膀时，应该咏唱——', Option = '{感谢你们的馈赠,6},{感谢你们的赐予,7}'}",
	[140785] = "{id = 140785, Speaker = 9511, Text = '沐洗双腿时，应该咏唱——', Option = '{让我与健康同在,8},{让我与生命同在,9}'}",
	[140786] = "{id = 140786, Speaker = 9519, Text = '别着急，你好像念错了。'}",
	[140787] = "{id = 140787, Speaker = 9511, Text = '好了，是不是感觉浑身都舒服了？', Option = '是的'}",
	[140788] = "{id = 140788, Speaker = 9511, Text = '赶紧回去睡一觉，才更能彻底释放压力。'}",
	[140789] = "{id = 140789, Speaker = 9511, Text = '去吧去吧，明天见。'}",
	[140790] = "{id = 140790, Speaker = 9519, Text = '嗯，我也先回吉芬去了，明年端午再见！', Option = '嗯嗯', Emoji = 17}",
	[140791] = "{id = 140791, Speaker = 9511, Text = '[c][ffff00][PlayerName][-][/c]，怎么样，昨晚是不是睡得很香？', Option = '嗯'}",
	[140792] = "{id = 140792, Speaker = 9511, Text = '告诉你个好消息吧。', Emoji = 9}",
	[140793] = "{id = 140793, Speaker = 9511, Text = '那些其他城市的客人基本都已经回去了。'}",
	[140794] = "{id = 140794, Speaker = 9511, Text = '今年的端午节，他们都玩的很尽兴呢。', Option = '那就好'}",
	[140795] = "{id = 140795, Speaker = 9511, Text = '也就是说呀，今天我终于不用麻烦你帮忙啦。', Option = '嗯嗯'}",
	[140796] = "{id = 140796, Speaker = 9511, Text = '嘻嘻，我带你去四处逛逛吧？', Emoji = 2}",
	[140797] = "{id = 140797, Speaker = 9511, Text = '先逛哪里好呢……'}",
	[140798] = "{id = 140798, Speaker = 9520, Text = '不好了，大事不好了！', Option = '发生什么了？', Emoji = 18}",
	[140799] = "{id = 140799, Speaker = 9520, Text = '统帅府突然闹老鼠了！'}",
	[140800] = "{id = 140800, Speaker = 9511, Text = '啊？！'}",
	[140801] = "{id = 140801, Speaker = 9520, Text = '统帅大人珍藏的龙舟啊。', Emoji = 24}",
	[140802] = "{id = 140802, Speaker = 9520, Text = '都被该死的老鼠咬破了。'}",
	[140803] = "{id = 140803, Speaker = 9520, Text = '统帅大人可是准备在端午节划龙舟的呀！', Option = '这…'}",
	[140804] = "{id = 140804, Speaker = 9511, Text = '别急别急，统帅大人的事就是斐扬人的事。', Emoji = 18}",
	[140805] = "{id = 140805, Speaker = 9511, Text = '老鼠的问题，先交给我来解决吧。', Option = '嗯'}",
	[140806] = "{id = 140806, Speaker = 9511, Text = '[c][ffff00][PlayerName][-][/c]，呜，又要忙了。'}",
	[140807] = "{id = 140807, Speaker = 9511, Text = '你愿意帮我一起去赶走老鼠么？', Option = '好呀'}",
	[140808] = "{id = 140808, Speaker = 9511, Text = '呼，终于清理干净了。'}",
	[140809] = "{id = 140809, Speaker = 2527, Text = '年轻人，真是辛苦你了。', Emoji = 6}",
	[140810] = "{id = 140810, Speaker = 2527, Text = '可惜，我的龙舟已经老鼠咬的千疮百孔了。', Option = '哎…'}",
	[140811] = "{id = 140811, Speaker = 2527, Text = '原本想在今年端午节为斐扬的居民们表演龙舟的。'}",
	[140812] = "{id = 140812, Speaker = 2527, Text = '这真是难以料到的情况。', Option = '是啊'}",
	[140813] = "{id = 140813, Speaker = 9511, Text = '别灰心啊，统帅大人。', Emoji = 26}",
	[140814] = "{id = 140814, Speaker = 9511, Text = '龙舟坏了，再修补起来不就好了。', Option = '对啊'}",
	[140815] = "{id = 140815, Speaker = 2527, Text = '可现在这个节骨眼，大家都在过端午节。', Emoji = 15}",
	[140816] = "{id = 140816, Speaker = 2527, Text = '上哪找人去搜集修补用的木料呢？'}",
	[140817] = "{id = 140817, Speaker = 2527, Text = '那可是远在普隆德拉西门的树精身上才有的上乘木料。'}",
	[140818] = "{id = 140818, Speaker = 9511, Text = '诶，这位[c][ffff00][PlayerName][-][/c]就是从普隆德拉来的冒险者。', Emoji = 7}",
	[140819] = "{id = 140819, Speaker = 9511, Text = '[c][ffff00][PlayerName][-][/c]，你知道西门的树精么？', Option = '当然啦'}",
	[140820] = "{id = 140820, Speaker = 2527, Text = '年轻人，看来你现在是我唯一的希望了。', Emoji = 24}",
	[140821] = "{id = 140821, Speaker = 2527, Text = '请求你帮我去找些新的木料来吧。', Option = '好的'}",
	[140822] = "{id = 140822, Speaker = 2527, Text = '太好了，这么多上好的木料。', Emoji = 6}",
	[140823] = "{id = 140823, Speaker = 2527, Text = '一定能把龙舟修复到完好如初。'}",
	[140824] = "{id = 140824, Speaker = 2527, Text = '谢谢你，但是请容我先失陪了。'}",
	[140825] = "{id = 140825, Speaker = 2527, Text = '我决定亲手修好龙舟。', Option = '嗯嗯'}",
	[140826] = "{id = 140826, Speaker = 9511, Text = '怎么样，统帅的龙舟还能修好么？', Option = '告知情况'}",
	[140827] = "{id = 140827, Speaker = 9511, Text = '你真是……'}",
	[140828] = "{id = 140828, Speaker = 9511, Text = '太好了！', Option = '别客气', Emoji = 27}",
	[140829] = "{id = 140829, Speaker = 9511, Text = '呜，都让你帮忙这么多天了。'}",
	[140830] = "{id = 140830, Speaker = 9511, Text = '原本打算今天带你去逛端午的。'}",
	[140831] = "{id = 140831, Speaker = 9511, Text = '结果到最后也没让你闲下来…'}",
	[140832] = "{id = 140832, Speaker = 9511, Text = '很不好意思呢…', Option = '没事的'}",
	[140833] = "{id = 140833, Speaker = 9511, Text = '你真好，能遇到你真是太幸运了。', Emoji = 26}",
	[140834] = "{id = 140834, Speaker = 9511, Text = '我答应你，如果明年还是由我负责端午节。'}",
	[140835] = "{id = 140835, Speaker = 9511, Text = '我一定带你欣赏一整天最有趣的端午。'}",
	[140836] = "{id = 140836, Speaker = 9511, Text = '还有全大陆最美的斐扬！', Option = '一言为定'}",
	[140837] = "{id = 140837, Speaker = 9511, Text = '不过现在，我想送你一份大礼物。', Option = '礼物？', Emoji = 4}",
	[140838] = "{id = 140838, Speaker = 9511, Text = '都是斐扬的居民一起给你准备的。'}",
	[140839] = "{id = 140839, Speaker = 9511, Text = '他们都知道你这几天为斐扬辛苦极了。'}",
	[140840] = "{id = 140840, Speaker = 9511, Text = '我代他们一起向你表示感谢。', Option = '小事而已'}",
	[140841] = "{id = 140841, Speaker = 9511, Text = '虽然很忙碌，但忠心的希望你喜欢我们的端午节。', Option = '我…很喜欢', Emoji = 27}",
	[140844] = "{id = 140844, Speaker = 1058, Text = '测试成功！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！', Emoji = 15}",
	[140848] = "{id = 140848, Speaker = 9536, Text = '冒险者！天气这么热~想不想一起清凉一下呀？', Emoji = 20}",
	[140849] = "{id = 140849, Speaker = 9536, Text = '快来加入我们超Coooool的清凉夏日祭吧！'}",
	[140850] = "{id = 140850, Speaker = 9522, Text = '酷耶，这可真是太好了。'}",
	[140851] = "{id = 140851, Speaker = 9522, Text = '啧啧，我看你都热的出汗了，正需要降降温吧~', Option = '嗯嗯'}",
	[140852] = "{id = 140852, Speaker = 9522, Text = '在这样的炎炎夏日，最能让人畅爽的东西，当然是……', Option = '{雪糕,4},{奶昔,4},{刨冰,4}', Emoji = 25}",
	[140853] = "{id = 140853, Speaker = 9522, Text = 'Bingo~答案是……全都正确！', Emoji = 11}",
	[140854] = "{id = 140854, Speaker = 9522, Text = '果然你也喜欢凉凉的夏日甜品呢！', Option = '嗯啊'}",
	[140855] = "{id = 140855, Speaker = 9522, Text = '今年夏日祭，我们请来了大陆最有名的甜点大师——赫露。', Emoji = 20}",
	[140856] = "{id = 140856, Speaker = 9522, Text = '她现在正为夏日祭研制全新品种的夏日甜点呢~'}",
	[140857] = "{id = 140857, Speaker = 9522, Text = '悄悄告诉你，提前找她预约，就可以吃到定制款的甜点哟！', Option = '这么棒！'}",
	[140858] = "{id = 140858, Speaker = 9522, Text = '哎，其实我之前就向赫露大师预约过定制雪糕了。', Emoji = 24}",
	[140859] = "{id = 140859, Speaker = 9522, Text = '可惜这几天我都要值班，怕是吃不到了呀…'}",
	[140860] = "{id = 140860, Speaker = 9522, Text = '好热呀~好想吃赫露大师为我专门定制的雪糕啊~', Option = '好可怜…'}",
	[140861] = "{id = 140861, Speaker = 9522, Text = '诶，要不你去帮我把雪糕带过来吧？'}",
	[140862] = "{id = 140862, Speaker = 9522, Text = '我那份雪糕可是预约定制，独一无二的哟！'}",
	[140863] = "{id = 140863, Speaker = 9522, Text = '你帮我带来，我就分你一半，我们一起吃~', Option = '没问题！'}",
	[140864] = "{id = 140864, Speaker = 9522, Text = '甜点大师赫露这几天都在皇家料理协会。'}",
	[140865] = "{id = 140865, Speaker = 9522, Text = '你去那儿一定会找到她的。'}",
	[140866] = "{id = 140866, Speaker = 9524, Text = '你好，冒险者。'}",
	[140867] = "{id = 140867, Speaker = 9524, Text = '看你急匆匆的样子，是来试吃我做的甜点么？', Option = '说明情况', Emoji = 19}",
	[140868] = "{id = 140868, Speaker = 9524, Text = '噢，原来是酷乐夏那个小子。', Emoji = 26}",
	[140869] = "{id = 140869, Speaker = 9524, Text = '也真是为难他了，这么热的天还要站在广场宣传夏日祭。', Option = '是啊'}",
	[140870] = "{id = 140870, Speaker = 9524, Text = '他这几天一定为了夏日祭累惨了吧。'}",
	[140871] = "{id = 140871, Speaker = 9524, Text = '嗯…还好我为他定制了恢复体力的冰酷雪糕。'}",
	[140872] = "{id = 140872, Speaker = 9524, Text = '希望他会喜欢吧~', Option = '一定会的'}",
	[140873] = "{id = 140873, Speaker = 9524, Text = '别着急，外面现在这么热，你这样把雪糕带去容易弄化了。'}",
	[140874] = "{id = 140874, Speaker = 9524, Text = '料理师莱厄斯应该有专门隔热的包装盒。'}",
	[140875] = "{id = 140875, Speaker = 9524, Text = '你去问他借一个吧。'}",
	[140876] = "{id = 140876, Speaker = 6447, Text = '什么事，我可是很忙的。', Option = '借包装盒'}",
	[140877] = "{id = 140877, Speaker = 6447, Text = '现在正值夏日祭，包装盒可是很紧缺的东西。', Emoji = 12}",
	[140878] = "{id = 140878, Speaker = 6447, Text = '虽然你说是赫露大师让你来的，但我也不能这么轻易就把包装盒借给一个外人。', Option = '好吧'}",
	[140879] = "{id = 140879, Speaker = 6447, Text = '不过……', Emoji = 5}",
	[140880] = "{id = 140880, Speaker = 6447, Text = '你要是能证明你是赫露大师的朋友，我就愿意把包装盒给你。', Option = '没问题'}",
	[140881] = "{id = 140881, Speaker = 6447, Text = '赫露大师是专攻哪一类料理的？', Option = '{烧烤,2},{甜点,3}'}",
	[140882] = "{id = 140882, Speaker = 6447, Text = '赫露大师的头发是什么颜色的？', Option = '{粉色,4},{蓝色,5}'}",
	[140883] = "{id = 140883, Speaker = 6447, Text = '赫露大师今天穿的披风是什么颜色的？', Option = '{红色,6},{绿色,7}'}",
	[140884] = "{id = 140884, Speaker = 6447, Text = '哼，你确定没搞错么？'}",
	[140885] = "{id = 140885, Speaker = 6447, Text = '好吧，看来你没有说谎。'}",
	[140886] = "{id = 140886, Speaker = 6447, Text = '雪糕我帮你包好了，包装盒你一起拿去吧。'}",
	[140887] = "{id = 140887, Speaker = 6447, Text = '不用还了，就当做个纪念。', Option = '谢谢你'}",
	[140888] = "{id = 140888, Speaker = 9522, Text = '你终于回来啦，隔着老远我就闻到雪糕的香味了。', Option = '交出雪糕', Emoji = 4}",
	[140889] = "{id = 140889, Speaker = 9522, Text = '哇，果然甜点大师的手艺就是厉害啊。'}",
	[140890] = "{id = 140890, Speaker = 9522, Text = '这雪糕看起来就很精致啊，真是酷毙了！', Option = '当然啦'}",
	[140891] = "{id = 140891, Speaker = 9522, Text = '来吧，一起吃吧！', Emoji = 9}",
	[140892] = "{id = 140892, Speaker = 9522, Text = '这可是我们第一天成为朋友的纪念呢！', Option = '好呀'}",
	[140893] = "{id = 140893, Speaker = 9522, Text = '这真是我吃过最好吃的雪糕。', Emoji = 9}",
	[140894] = "{id = 140894, Speaker = 9522, Text = '不过一天吃太多冰的东西也不好。'}",
	[140895] = "{id = 140895, Speaker = 9522, Text = '今天已经满足了~', Option = '嗯嗯'}",
	[140896] = "{id = 140896, Speaker = 9522, Text = '好了，我要继续好好值班了~'}",
	[140897] = "{id = 140897, Speaker = 9522, Text = '宣传夏日祭可是很酷很重要的工作呢！'}",
	[140898] = "{id = 140898, Speaker = 9522, Text = '你先回去吧~明天要是有空，还可以来找我玩！', Option = '好的'}",
	[140899] = "{id = 140899, Speaker = 9522, Text = '哟吼，[c][ffff00][PlayerName][-][/c]，你又来啦~', Option = '嗯', Emoji = 20}",
	[140900] = "{id = 140900, Speaker = 9522, Text = '话说昨天我吃了冰酷雪糕之后，整个人都精神多了呢。'}",
	[140901] = "{id = 140901, Speaker = 9522, Text = '你是不是也有这种感觉？', Option = '是啊'}",
	[140902] = "{id = 140902, Speaker = 9522, Text = '嘿嘿，那看你精神这么饱满，能不能请你帮个忙呀~', Option = '怎么了？'}",
	[140903] = "{id = 140903, Speaker = 9522, Text = '哎，是这样的……'}",
	[140904] = "{id = 140904, Speaker = 9522, Text = '刚才我收到了依斯鲁得的女武神，瓦尔基里小姐的信。', Option = '信？'}",
	[140905] = "{id = 140905, Speaker = 9522, Text = '嗯，之前她代表依斯鲁得向皇室订购了一批冰冻甜品。', Emoji = 25}",
	[140906] = "{id = 140906, Speaker = 9522, Text = '赫露大师昨天早上，就派她的学生南南去送货了……'}",
	[140907] = "{id = 140907, Speaker = 9522, Text = '但是直到今天早上，瓦尔基里小姐在信里说依然没收到甜品。', Option = '原来是这样'}",
	[140908] = "{id = 140908, Speaker = 9522, Text = '我很担心甜点师南南是不是在路上出了什么意外。', Emoji = 24}",
	[140909] = "{id = 140909, Speaker = 9522, Text = '[c][ffff00][PlayerName][-][/c]，你能帮我去找找南南么？', Option = '当然可以'}",
	[140910] = "{id = 140910, Speaker = 9522, Text = '她是往南门方向出发的，你在南门应该可以发现她的踪迹。', Option = '知道了'}",
	[140911] = "{id = 140911, Speaker = 1008, Text = '怎么了冒险者，有什么事么？', Option = '说明情况'}",
	[140912] = "{id = 140912, Speaker = 1008, Text = '噢？甜点师南南？'}",
	[140913] = "{id = 140913, Speaker = 1008, Text = '这么说起来，我确实记得昨天看到过一个甜点师。', Option = '她在哪？'}",
	[140914] = "{id = 140914, Speaker = 1008, Text = '不好意思，我只是有印象，但不清楚她具体去哪了。', Option = '好吧'}",
	[140915] = "{id = 140915, Speaker = 1008, Text = '不过昨天负责巡逻的，是乐园团的宝雅教官。'}",
	[140916] = "{id = 140916, Speaker = 1008, Text = '你去问问她，应该会知道些什么的。', Option = '嗯嗯'}",
	[140917] = "{id = 140917, Speaker = 1054, Text = '[c][ffff00][PlayerName][-][/c]，怎么急匆匆的，有什么事么？', Option = '说明情况', Emoji = 19}",
	[140918] = "{id = 140918, Speaker = 1054, Text = '原来是来找甜点师南南啊…', Emoji = 21}",
	[140919] = "{id = 140919, Speaker = 1054, Text = '她正在我的帐篷里休息呢。', Option = '啊？'}",
	[140920] = "{id = 140920, Speaker = 1054, Text = '昨天我巡逻的时候，发现了半路晕倒的她，就把她带回来了。'}",
	[140921] = "{id = 140921, Speaker = 1054, Text = '都怪这天气太热了，南南又拖着小货车，所以累中暑了。', Option = '原来是这样'}",
	[140922] = "{id = 140922, Speaker = 1054, Text = '我已经给她服了药了，休息几天她应该就没事了。'}",
	[140923] = "{id = 140923, Speaker = 1054, Text = '不过她……', Option = '她怎么了？'}",
	[140924] = "{id = 140924, Speaker = 1054, Text = '南南说她原本是去依斯鲁得送夏日甜品的，不能因为中暑就耽搁了。'}",
	[140925] = "{id = 140925, Speaker = 1054, Text = '所以她想委托我帮她送甜品。'}",
	[140926] = "{id = 140926, Speaker = 1054, Text = '可现在是夏日祭，南门的治安非常重要，我实在脱不开身。', Option = '乐意帮忙', Emoji = 10}",
	[140927] = "{id = 140927, Speaker = 1054, Text = '太好了，相信南南也很放心由你来帮她的。'}",
	[140928] = "{id = 140928, Speaker = 1054, Text = '喏，这是要送过去的甜品，交给你了。', Option = '好的'}",
	[140929] = "{id = 140929, Speaker = 9537, Text = '这是……？', Option = '说明情况'}",
	[140930] = "{id = 140930, Speaker = 9537, Text = '啊？原来南南中暑了，你是代她来送甜品的？', Option = '是的', Emoji = 7}",
	[140931] = "{id = 140931, Speaker = 9537, Text = '愿众神的赐福保佑她尽快好起来……', Option = '嗯嗯'}",
	[140932] = "{id = 140932, Speaker = 9537, Text = '这些冰冻甜品真是来之不易啊。'}",
	[140933] = "{id = 140933, Speaker = 9537, Text = '我代表依斯鲁得的居民感谢你，冒险者。', Option = '举手之劳'}",
	[140934] = "{id = 140934, Speaker = 9522, Text = '怎么样，[c][ffff00][PlayerName][-][/c]，南南和那批冰冻甜品都还好么？', Option = '说明情况', Emoji = 18}",
	[140935] = "{id = 140935, Speaker = 9522, Text = '呼，原来是中暑了，还好人没大碍，甜品也及时送到了。'}",
	[140936] = "{id = 140936, Speaker = 9522, Text = '哎，可之后几天还有要送往其他地方的夏日甜品。'}",
	[140937] = "{id = 140937, Speaker = 9522, Text = '南南病了，该找谁代替她呢？', Option = '我来吧'}",
	[140938] = "{id = 140938, Speaker = 9522, Text = '够义气耶，你果然是个超酷的朋友！', Option = '那当然', Emoji = 20}",
	[140939] = "{id = 140939, Speaker = 9522, Text = '不过甜品现在都还没包装好，要不你明天早上来取货吧。', Option = '没问题'}",
	[140940] = "{id = 140940, Speaker = 9522, Text = '[c][ffff00][PlayerName][-][/c]，早上好呀~', Option = '早上好'}",
	[140941] = "{id = 140941, Speaker = 9522, Text = '今天的夏日甜品都是专门为魔法师定制的MP奶昔~', Emoji = 20}",
	[140942] = "{id = 140942, Speaker = 9522, Text = '是送往吉芬魔法公会的。', Option = '知道了'}",
	[140943] = "{id = 140943, Speaker = 9522, Text = '到了吉芬直接找雷藏大法师吧。'}",
	[140944] = "{id = 140944, Speaker = 9522, Text = '记得路上多喝点水，别像南南一样中暑了~', Option = '嗯', Emoji = 4}",
	[140945] = "{id = 140945, Speaker = 1321, Text = '冒险者，来魔法公会有什么事么？', Option = '交出甜品'}",
	[140946] = "{id = 140946, Speaker = 1321, Text = '天气这么热，真是辛苦你一路送甜品过来啊。', Option = '不用谢', Emoji = 7}",
	[140947] = "{id = 140947, Speaker = 1321, Text = '哎呀，可惜老夫我这几天正为一些事头疼呢。', Emoji = 24}",
	[140948] = "{id = 140948, Speaker = 1321, Text = '实在是没太多心情喝MP奶昔啊。', Option = '怎么了？'}",
	[140949] = "{id = 140949, Speaker = 1321, Text = '哎，都是因为今年夏天太热了…', Emoji = 15}",
	[140950] = "{id = 140950, Speaker = 1321, Text = '我们魔法师的法袍，一直以来用的都是亚麻布。'}",
	[140951] = "{id = 140951, Speaker = 1321, Text = '这种布料虽然穿着舒适，但天变热了，就很不透气。', Option = '这样啊'}",
	[140952] = "{id = 140952, Speaker = 1321, Text = '好多年轻的魔法师最近向我抱怨太热了，都不想穿法袍了。'}",
	[140953] = "{id = 140953, Speaker = 1321, Text = '但法袍是魔法师最基本的传统，怎能说不穿就不穿呢。', Option = '对啊'}",
	[140954] = "{id = 140954, Speaker = 1321, Text = '冒险家，你有什么好主意么？', Option = '{换透气布料,3},{不做魔法师,4}，{不穿法袍,5}'}",
	[140955] = "{id = 140955, Speaker = 1321, Text = '你胡说些什么呢，就没其他办法了么？'}",
	[140956] = "{id = 140956, Speaker = 1321, Text = '对啊，把法袍换一种更透气的布料不就凉快了？', Emoji = 11}",
	[140957] = "{id = 140957, Speaker = 1321, Text = '哎呀呀，老糊涂了，我怎么就没想到呢。', Option = '哈哈'}",
	[140958] = "{id = 140958, Speaker = 1321, Text = '我想起来了，西门有一种叫清凉棉花的植物，天生透着一股清凉气息。'}",
	[140959] = "{id = 140959, Speaker = 1321, Text = '用它制成法袍的内层，穿起来一定凉飕飕的。', Option = '真神奇'}",
	[140960] = "{id = 140960, Speaker = 1321, Text = '冒险家，你能帮我去找些清凉棉花么？', Option = '好的'}",
	[140961] = "{id = 140961, Speaker = 0, Text = '调皮的溜溜猴抢走了清凉棉花，快别让它们跑了。'}",
	[140962] = "{id = 140962, Speaker = 1321, Text = '找到清凉棉花了么？', Option = '交出棉花'}",
	[140963] = "{id = 140963, Speaker = 1321, Text = '妙啊，果然是极好的夏季制衣材料。', Emoji = 17}",
	[140964] = "{id = 140964, Speaker = 1321, Text = '你帮我解决了一个大难题呀，冒险者。'}",
	[140965] = "{id = 140965, Speaker = 1321, Text = '现在我终于可以好好享受我的MP奶昔了。', Option = '不用谢'}",
	[140966] = "{id = 140966, Speaker = 9522, Text = '[c][ffff00][PlayerName][-][/c]，MP奶昔送到了么？', Option = '说明情况'}",
	[140967] = "{id = 140967, Speaker = 9522, Text = '哈哈，那些看起来很潇洒的魔法师们，居然也会因为衣服太热而苦恼啊。', Option = '是呀', Emoji = 1}",
	[140968] = "{id = 140968, Speaker = 9522, Text = '哟吼，魔法师太古板啦~'}",
	[140969] = "{id = 140969, Speaker = 9522, Text = '应该像我一样不穿上衣，才最酷了！', Option = '别吧…'}",
	[140970] = "{id = 140970, Speaker = 9522, Text = '我是开个玩笑嘛~'}",
	[140971] = "{id = 140971, Speaker = 9522, Text = '好了，今天你一定也累了。'}",
	[140972] = "{id = 140972, Speaker = 9522, Text = '明天要送的夏日甜品会比较多，你回去好好休息吧~', Option = '知道了'}",
	[140973] = "{id = 140973, Speaker = 9522, Text = '哟吼，准备好迎接今天的任务了么？', Option = '准备好了'}",
	[140974] = "{id = 140974, Speaker = 9522, Text = '今天的夏日甜品订单都是送往斐扬的~', Option = '斐扬？', Emoji = 20}",
	[140975] = "{id = 140975, Speaker = 9522, Text = '是的，斐扬每年都会给皇室进贡自产的水果。'}",
	[140976] = "{id = 140976, Speaker = 9522, Text = '为了报答他们，皇室允许今年的夏日祭。', Emoji = 2}",
	[140977] = "{id = 140977, Speaker = 9522, Text = '斐扬每位居民都可以定制一款夏日甜品。', Option = '这样啊'}",
	[140978] = "{id = 140978, Speaker = 9522, Text = '前几天为了斐扬的订单，可把甜品大师累坏了。'}",
	[140979] = "{id = 140979, Speaker = 9522, Text = '好在今天都已经做完了，接下来就靠你了。', Option = '嗯嗯'}",
	[140980] = "{id = 140980, Speaker = 9522, Text = '要送的甜品种类很多，你一定要分清楚了。', Emoji = 9}",
	[140981] = "{id = 140981, Speaker = 9522, Text = '草莓冰淇淋送给竹杨酒家老板。'}",
	[140982] = "{id = 140982, Speaker = 9522, Text = '香蕉布丁送给猎人公会会长。'}",
	[140983] = "{id = 140983, Speaker = 9522, Text = '西瓜果冻送给风筝小贩仁贺。'}",
	[140984] = "{id = 140984, Speaker = 9522, Text = '蓝莓冰沙送给斐扬城城主。'}",
	[140985] = "{id = 140985, Speaker = 9522, Text = '记住了么？', Option = '{记住了,3},{再听一次,4}'}",
	[140986] = "{id = 140986, Speaker = 9522, Text = '送错的话，斐扬人可是会生气的，要仔细噢~'}",
	[140987] = "{id = 140987, Speaker = 9522, Text = '对了，去斐扬路途遥远，我这有几张卡普拉传送券可以给你。', Emoji = 11}",
	[140988] = "{id = 140988, Speaker = 9522, Text = '让卡普拉公司直接传送你过去吧，这样比较安全。', Option = '知道了'}",
	[140989] = "{id = 140989, Speaker = 0, Text = '送甜品之前，再回忆一遍应该送给谁吧。'}",
	[140990] = "{id = 140990, Speaker = 0, Text = '第一题：草莓冰淇淋应该送给____？', Option = '{竹杨酒家老板,2},{斐扬城城主,3}'}",
	[140991] = "{id = 140991, Speaker = 0, Text = '第二题：香蕉布丁应该送给____公会的会长？', Option = '{武僧,4},{猎人,5}'}",
	[140992] = "{id = 140992, Speaker = 0, Text = '第三题：风筝小贩仁贺想吃的是____果冻？', Option = '{西瓜,6},{香草,7}'}",
	[140993] = "{id = 140993, Speaker = 0, Text = '第四题：斐扬城城主想吃的是蓝莓____?', Option = '{冰沙,8},{汽水,9}'}",
	[140994] = "{id = 140994, Speaker = 0, Text = '好像不对啊，还是再想想吧。'}",
	[140995] = "{id = 140995, Speaker = 0, Text = '应该没问题了，出发送甜品吧！'}",
	[140996] = "{id = 140996, Speaker = 3061, Text = '谢谢你，这正是我要的草莓冰淇淋。', Emoji = 2}",
	[140997] = "{id = 140997, Speaker = 1235, Text = '不错不错，今年的夏日祭真是大饱口福啊。', Emoji = 2}",
	[140998] = "{id = 140998, Speaker = 3065, Text = '哇，感谢皇室！这看起来可太好吃了。', Emoji = 2}",
	[140999] = "{id = 140999, Speaker = 2527, Text = '年轻人，你找我有什么事么？', Option = '交出冰沙'}",
	[141000] = "{id = 141000, Speaker = 2527, Text = '原来是从普隆德拉来为我送夏日甜品的，真是辛苦你了。', Emoji = 2}",
	[141001] = "{id = 141001, Speaker = 2527, Text = '替我也谢谢酷乐夏那小子吧，我们是老相识了。', Option = '好的'}",
	[141002] = "{id = 141002, Speaker = 9522, Text = '哟吼，[c][ffff00][PlayerName][-][/c]，你回来啦！', Option = '说明情况'}",
	[141003] = "{id = 141003, Speaker = 9522, Text = '哈，原来城主大人居然还记得我。', Emoji = 9}",
	[141004] = "{id = 141004, Speaker = 9522, Text = '小时候我曾经住在斐扬，那时候的夏日祭，是城主大人带我吃甜点呢。', Option = '原来这样'}",
	[141005] = "{id = 141005, Speaker = 9522, Text = '哎呀，我也突然有些怀旧起来了呢。'}",
	[141006] = "{id = 141006, Speaker = 9522, Text = '好想现在去一趟斐扬，和城主大人叙叙旧。', Option = '嗯'}",
	[141007] = "{id = 141007, Speaker = 9522, Text = '但我走了谁来值班呢……？'}",
	[141008] = "{id = 141008, Speaker = 9522, Text = '要不，你替我值班？', Option = '不好吧…', Emoji = 5}",
	[141009] = "{id = 141009, Speaker = 9522, Text = '哈哈哈，我逗你的，瞧你紧张的。', Emoji = 17}",
	[141010] = "{id = 141010, Speaker = 9522, Text = '其实我值班快结束了，稍后我会自己去斐扬的。', Option = '嗯嗯'}",
	[141011] = "{id = 141011, Speaker = 9522, Text = '今天你就先回去吧~'}",
	[141012] = "{id = 141012, Speaker = 9522, Text = '明天虽然没什么需要送的订单，但希望还是能见到你哟~', Option = '没问题'}",
	[141013] = "{id = 141013, Speaker = 9522, Text = '[c][ffff00][PlayerName][-][/c]，不好了不好了，出大事了！', Option = '怎么了', Emoji = 15}",
	[141014] = "{id = 141014, Speaker = 9522, Text = '梦罗克，梦罗克城出大事了！', Option = '梦罗克城？'}",
	[141015] = "{id = 141015, Speaker = 9522, Text = '梦罗克城的蚁群都狂暴了！'}",
	[141016] = "{id = 141016, Speaker = 9522, Text = '它们全都爬出了洞穴，入侵了梦罗克城！', Option = '怎么会？！'}",
	[141017] = "{id = 141017, Speaker = 9522, Text = '之前我们运送了大量的夏日甜品给梦罗克城。', Emoji = 24}",
	[141018] = "{id = 141018, Speaker = 9522, Text = '啊啊啊，一定是蚂蚁们闻到了甜味，所以顺着气味侵入了城市。', Option = '怎么办呢'}",
	[141019] = "{id = 141019, Speaker = 9522, Text = '这件事是因夏日祭引起的，我必须要负责。', Emoji = 18}",
	[141020] = "{id = 141020, Speaker = 9522, Text = '我决定今天立刻前往梦罗克城，赶走讨厌的蚂蚁！'}",
	[141021] = "{id = 141021, Speaker = 9522, Text = '[c][ffff00][PlayerName][-][/c]，你愿意陪我一同前去么。', Option = '没问题'}",
	[141022] = "{id = 141022, Speaker = 9522, Text = '你真是个可靠的人。'}",
	[141023] = "{id = 141023, Speaker = 9522, Text = '你先去，我随后就到！', Option = '知道了'}",
	[141024] = "{id = 141024, Speaker = 1553, Text = '啊，该死的蚁群，数量太多了。', Option = '询问情况', Emoji = 18}",
	[141025] = "{id = 141025, Speaker = 1553, Text = '哎，原本皇室送来夏日甜品应该是很高兴的事情。', Emoji = 15}",
	[141026] = "{id = 141026, Speaker = 1553, Text = '没想到甜品太香了，也会惹出麻烦。'}",
	[141027] = "{id = 141027, Speaker = 1553, Text = '现在城内到处都有蚂蚁，吓的居民和商人都乱套了。', Option = '我来帮忙'}",
	[141028] = "{id = 141028, Speaker = 1553, Text = '现在城内还有很多被蚁群困扰的人，请你先去帮帮他们吧！', Option = '好的'}",
	[141029] = "{id = 141029, Speaker = 3006, Text = '臭蚂蚁，这些雪糕冰棒都是我家的，快滚开！', Option = '询问情况', Emoji = 1}",
	[141030] = "{id = 141030, Speaker = 3006, Text = '太好了，终于有人来救我了！'}",
	[141031] = "{id = 141031, Speaker = 3006, Text = '请你赶走我家的蚂蚁吧，它们实在太讨厌了。', Option = '没问题'}",
	[141032] = "{id = 141032, Speaker = 3006, Text = '谢天谢地，我宝贵的夏日甜品终于保住了！'}",
	[141033] = "{id = 141033, Speaker = 3006, Text = '我刚才看到隔壁的古董店也闹蚂蚁了，去帮帮他们吧。'}",
	[141034] = "{id = 141034, Speaker = 3011, Text = '啊，臭蚂蚁，这些是古董，不是甜品！', Option = '询问情况', Emoji = 1}",
	[141035] = "{id = 141035, Speaker = 3011, Text = '我的天，这些蚂蚁把古董当成甜品盒了，一个劲的往里钻！'}",
	[141036] = "{id = 141036, Speaker = 3011, Text = '再不弄走他们，我可就要破产了！', Option = '明白了'}",
	[141037] = "{id = 141037, Speaker = 3011, Text = '啊，我可爱的古董，看到你们没事就好。'}",
	[141038] = "{id = 141038, Speaker = 3011, Text = '我这里没什么麻烦了，谢谢你。', Option = '不客气'}",
	[141039] = "{id = 141039, Speaker = 1553, Text = '城里居民的麻烦都解决了么？', Option = '嗯'}",
	[141040] = "{id = 141040, Speaker = 1553, Text = '刚才这位酷乐夏先生也来帮忙了，他清理了东面的蚁群。'}",
	[141041] = "{id = 141041, Speaker = 1553, Text = '多亏了你和他，城里现在已经暂时没有蚂蚁了。', Option = '太好了'}",
	[141042] = "{id = 141042, Speaker = 9522, Text = '但是…', Option = '但是？'}",
	[141043] = "{id = 141043, Speaker = 9522, Text = '哎，储藏夏日甜品的仓库被蚂蚁闯入了……', Emoji = 24}",
	[141044] = "{id = 141044, Speaker = 9522, Text = '虽然我及时赶走了蚂蚁。'}",
	[141045] = "{id = 141045, Speaker = 9522, Text = '但是仓库里一半的甜品都被咬坏了，不能吃了。', Option = '啊？'}",
	[141046] = "{id = 141046, Speaker = 9522, Text = '哎，那么多箱夏日甜品，都是特意给梦罗克城的居民和士兵的……', Emoji = 24}",
	[141047] = "{id = 141047, Speaker = 9522, Text = '浪费了啊，只能等明年夏日祭了么……', Option = '别气馁'}",
	[141048] = "{id = 141048, Speaker = 9522, Text = '我也不想这么消极的啊……', Emoji = 15}",
	[141049] = "{id = 141049, Speaker = 9522, Text = '但是夏日祭都快结束了，这种时候发生这样的事。'}",
	[141050] = "{id = 141050, Speaker = 9522, Text = '可能谁都无法挽救了吧。', Option = '……'}",
	[141051] = "{id = 141051, Speaker = 9533, Text = '谁说这样就无法挽救了？'}",
	[141052] = "{id = 141052, Speaker = 9534, Text = '嘻嘻嘻~'}",
	[141053] = "{id = 141053, Speaker = 9522, Text = '赫露大师？南南？你们怎么来了？', Emoji = 8}",
	[141054] = "{id = 141054, Speaker = 9524, Text = '我是甜品大师，南南是甜品匠人，甜品坏了，当然要由我们来救场。', Option = '皇室那边…', Emoji = 17}",
	[141055] = "{id = 141055, Speaker = 9524, Text = '皇室那边的工作我基本都忙完了。'}",
	[141056] = "{id = 141056, Speaker = 9524, Text = '听说了梦罗克城的情况，我立刻就赶了过来。', Option = '谢谢'}",
	[141057] = "{id = 141057, Speaker = 9523, Text = '嘻嘻，师傅可关心你们了。', Option = '嗯', Emoji = 26}",
	[141058] = "{id = 141058, Speaker = 9524, Text = '这丫头中暑的几天都躺在床上，把她憋坏了。', Emoji = 25}",
	[141059] = "{id = 141059, Speaker = 9524, Text = '我看她病也好了，就把她一起带来了。'}",
	[141060] = "{id = 141060, Speaker = 9524, Text = '应该能帮上忙的。', Option = '啊'}",
	[141061] = "{id = 141061, Speaker = 9523, Text = '我是师傅的得力助手嘛，有我在一定效率加倍~', Option = '是呀', Emoji = 26}",
	[141062] = "{id = 141062, Speaker = 9524, Text = '好了，抓紧时间吧。', Emoji = 30}",
	[141063] = "{id = 141063, Speaker = 9524, Text = '我和南南有现有的材料，重新开始制作夏日甜品。'}",
	[141064] = "{id = 141064, Speaker = 9524, Text = '这段时间里，麻烦你和酷乐夏帮我们挡住城外的蚂蚁群。'}",
