	[304562] = "{id = 304562, Speaker = 500600, Text = '主人赛高！我要回去和我的朋友们夸奖主人！'}",
	[304563] = "{id = 304563, Speaker = 500600, Text = '主人万岁！大丰收！'}",
	[304564] = "{id = 304564, Speaker = 500600, Text = '这个坏家伙，我一定要出力帮助主人！'}",
	[304565] = "{id = 304565, Speaker = 500600, Text = '哎呀，人家都不好意思了啦~'}",
	[304566] = "{id = 304566, Speaker = 500600, Text = ' 我的主人一定会成功的，我就知道！'}",
	[304567] = "{id = 304567, Speaker = 500600, Text = '哼，一定是时间不对，主人才不会失败呢！'}",
	[304568] = "{id = 304568, Speaker = 500600, Text = '快穿上！快穿上！我好期待主人的样子！'}",
	[304569] = "{id = 304569, Speaker = 500600, Text = '噔噔~可爱的小驯鹿向主人问好！'}",
	[304570] = "{id = 304570, Speaker = 500600, Text = '主人和我也是最重要的伙伴！'}",
	[304571] = "{id = 304571, Speaker = 500600, Text = '唔~蹭蹭主人'}",
	[304572] = "{id = 304572, Speaker = 500600, Text = '好嫩的树芽！唔嘛~好吃！QWQ'}",
	[304573] = "{id = 304573, Speaker = 500600, Text = '虽然很好吃，但是...嗝~真的，真的吃不下了，主人'}",
	[304574] = "{id = 304574, Speaker = 500600, Text = '人家..才不会想主人呢！'}",
	[304575] = "{id = 304575, Speaker = 500600, Text = '主人，这..这个给你QWQ'}",
	[304576] = "{id = 304576, Speaker = 500600, Text = '主人，我有一个小秘密要告诉你！'}",
	[304577] = "{id = 304577, Speaker = 500600, Text = '你怎么知道我最喜欢这个的！主人你真好！'}",
	[304578] = "{id = 304578, Speaker = 500600, Text = '嘿嘿，我要把这个偷偷藏好，要不然被别人偷走了！'}",
	[305497] = "{id = 305497, Text = '我可是祥鸟，你居然给我取这么幼稚的名字？！', Emoji = 2}",
	[305498] = "{id = 305498, Text = '我是赫赫有名的祥鸟——春兆！'}",
	[305499] = "{id = 305499, Text = '人类…你…你以为这样就能打动我么！'}",
	[305500] = "{id = 305500, Text = '切，没有我的保护你可别受伤！'}",
	[305501] = "{id = 305501, Text = '原来这就是人类和朋友们一起搭建的家园么？'}",
	[305502] = "{id = 305502, Text = '我看中的人，精炼这种小事不可能搞不定！'}",
	[305503] = "{id = 305503, Text = '喂喂，你不会因为这点小挫折就灰心吧！'}",
	[305504] = "{id = 305504, Text = '喂，这个给你！快感谢本祥鸟'}",
	[305505] = "{id = 305505, Text = '本祥鸟感觉最近力量又强大了~'}",
	[305506] = "{id = 305506, Text = '啾！我会一直保护你的，人类'}",
	[305507] = "{id = 305507, Text = '我…我才不喜欢呢…', Emoji = 2}",
	[305508] = "{id = 305508, Text = '嗝~吃不下了啦！我是祥鸟又不是亥神！'}",
	[305509] = "{id = 305509, Text = '喂！今天也要一起冒险哦！'}",
	[305510] = "{id = 305510, Text = '敢对我保护的人动手，你等着瞧！'}",
	[305511] = "{id = 305511, Text = '收拾这个家伙，真是小意思！'}",
	[305512] = "{id = 305512, Text = '呼~这家伙有点能耐嘛！不过还是我们赢了'}",
	[305513] = "{id = 305513, Text = '你不要这样盯着我啦…本祥鸟会…会害羞的…'}",
	[305514] = "{id = 305514, Text = '不错不错~你实力强大了才配得上我嘛~'}",
	[305515] = "{id = 305515, Text = '是谁伤害了你？本祥鸟生气了！！！', Emoji = 12}",
	[305516] = "{id = 305516, Text = '啾啾~啾啾~好痒~'}",
	[305517] = "{id = 305517, Text = '我一点都不饿！咕噜~咕噜~好吧，我饿了'}",
	[305518] = "{id = 305518, Text = '你受伤了快退到我身后！'}",
	[305519] = "{id = 305519, Text = '嘿嘿！这个简直是为本祥鸟量身定做的！'}",
	[305520] = "{id = 305520, Text = '啾啾~本祥鸟哪里比不上凤凰啦！'}",
	[305521] = "{id = 305521, Text = '喂喂！人类快帮帮本祥鸟顺毛'}",
	[305522] = "{id = 305522, Text = '快快把本祥鸟的年年有鱼端上来！'}",
	[305523] = "{id = 305523, Text = '你快戴上让本祥鸟看看！'}",
	[305524] = "{id = 305524, Text = '你收好了！这个是本祥鸟赏你的！才不是因为喜欢你哦！'}",
	[1000104] = "{id = 1000104, Speaker = 650000, Text = '最终的奖励就是——'}",
	[1000105] = "{id = 1000105, Speaker = 650000, Text = '[c][ffff00]新冒险行囊[-][/c]！！～～'}",
	[1000106] = "{id = 1000106, Speaker = 650000, Text = '这个行囊每升10级可以打开一次，一直可以使用到99级呢，每次打开都会有大量[c][ffff00]大量奖励[-][/c]哦！～'}",
	[1000113] = "{id = 1000113, Speaker = 600052, Text = '你说你能读懂哥布灵的表情？'}",
	[1000114] = "{id = 1000114, Speaker = 600052, Text = '不可能，不可能，我安迪就不信！'}",
	[1000115] = "{id = 1000115, Speaker = 600052, Text = '除非，你真的研究了哥布灵的面具？！来吧让我看看你的冒险手册！'}",
	[1000116] = "{id = 1000116, Speaker = 600052, Text = '那么我的第一份奖励，将给予解锁了3个哥布灵面具的冒险者！', Option = '{我没解锁,3},{我解锁了面具,0}'}",
	[1000117] = "{id = 1000117, Speaker = 600052, Text = '果不其然！你这个说大话的冒险者！'}",
	[1000118] = "{id = 1000118, Speaker = 600052, Text = '我是说我需要你先解锁3个哥布灵面具我才给你第一份奖励，是我没说清楚吗？'}",
	[1000119] = "{id = 1000119, Speaker = 600052, Text = '这也很平常嘛！不过你可以领取第一份奖励。'}",
	[1000120] = "{id = 1000120, Speaker = 600052, Text = '你想说你愿意挑战6个面具的试炼？'}",
	[1000121] = "{id = 1000121, Speaker = 600052, Text = '这可是一件非常困难的事情啊！'}",
	[1000122] = "{id = 1000122, Speaker = 600052, Text = '好吧，第二份奖励可是万众期待的“哥布灵喷气背包图纸”'}",
	[1000123] = "{id = 1000123, Speaker = 600052, Text = '如果你解锁了6个哥布灵面具，我就立刻送给你！', Option = '{我没解锁,3},{我解锁了面具,0}'}",
	[1000124] = "{id = 1000124, Speaker = 600052, Text = '恩！这不怪你，我也没有～～'}",
	[1000125] = "{id = 1000125, Speaker = 600052, Text = '我是说解锁6个哥布灵面具我才给你“哥布灵喷气背包图纸”！是我没说清楚吗？'}",
	[1000126] = "{id = 1000126, Speaker = 600052, Text = '这！你简直就是哥布灵首领啊！'}",
	[1000372] = "{id = 1000372, Speaker = 9226, Text = '波利庆典就要开始咯！波利们都开始了自己的假期生活。真是令人惬意呢！', Option = '是啊', Emoji = 17}",
	[1000373] = "{id = 1000373, Speaker = 9226, Text = '但是有些小波利，把自己藏在角落里，宁愿宅在家里也不愿意好好享受假期生活。', Option = '那怎么办？', Emoji = 15}",
	[1000374] = "{id = 1000374, Speaker = 9226, Text = '我想你能去找到他，说服他出来外面的世界看看，那小家伙太自闭了。', Option = '{我试试吧,2},{我不想去,3}'}",
	[1000375] = "{id = 1000375, Speaker = 9226, Text = '那真是太遗憾了，考虑好了再来找我吧，毕竟那小家伙怪可怜的。'}",
	[1000376] = "{id = 1000376, Speaker = 9226, Text = '真是太好了!我替波利们谢谢你！', Emoji = 4}",
	[1000377] = "{id = 1000377, Speaker = 9226, Text = '那小家伙名叫蛋黄，就在普隆德拉南门的城墙附近，你得先找到他。', Option = '没问题'}",
	[1000378] = "{id = 1000378, Speaker = 9226, Text = '蛋黄很好认，你就看哪只波利长的像蛋黄，那就是他没错了。', Option = '好的', Emoji = 17}",
	[1000379] = "{id = 1000379, Speaker = 9227, Text = '……', Option = '是蛋黄吗？'}",
	[1000380] = "{id = 1000380, Speaker = 9227, Text = '你是谁？', Option = '说出缘由'}",
	[1000381] = "{id = 1000381, Speaker = 9227, Text = '哦，谢谢，但是我现在并不想出去…', Option = '为什么'}",
	[1000382] = "{id = 1000382, Speaker = 9227, Text = '其他波利都出去玩了，他们没有叫我，我没心情出去…', Option = '他们在哪'}",
	[1000383] = "{id = 1000383, Speaker = 9227, Text = '他们去了西门…', Option = '我知道了'}",
	[1000384] = "{id = 1000384, Speaker = 9227, Text = '谢谢你的好意，但是我现在只想睡觉…'}",
	[1000385] = "{id = 1000385, Speaker = 0, Text = '还是先去找找其他波利吧……'}",
	[1000386] = "{id = 1000386, Speaker = 9228, Text = '原来西门这里这么美！', Option = '是啊'}",
	[1000387] = "{id = 1000387, Speaker = 9228, Text = '要是蛋黄也能看到那该多好…', Option = '我才见过他'}",
	[1000388] = "{id = 1000388, Speaker = 9228, Text = '真的吗？我们找了他一上午都没找到，只好先过来了…', Emoji = 7}",
	[1000389] = "{id = 1000389, Speaker = 9228, Text = '蛋黄总是把自己关起来，真的很伤脑筋。你可以告诉他我们在这玩吗？虽然不知道他会不会过来。', Option = '他肯定会来的'}",
	[1000390] = "{id = 1000390, Speaker = 9228, Text = '虽然不知道为什么你会这么肯定，不过还是很开心你愿意帮我们跑一趟，我们在这里等你的好消息！', Emoji = 6}",
	[1000391] = "{id = 1000391, Speaker = 9227, Text = 'Zzzzz……', Option = '醒醒别睡啦'}",
	[1000392] = "{id = 1000392, Speaker = 9227, Text = '呃？是你啊，怎么又来找我了？', Option = '转告波波土的话'}",
	[1000393] = "{id = 1000393, Speaker = 9227, Text = '真的吗？他…他们在等我吗？', Option = '是的', Emoji = 1}",
	[1000394] = "{id = 1000394, Speaker = 9227, Text = '太棒了！我还以为他们不理我了呢！', Emoji = 9}",
	[1000395] = "{id = 1000395, Speaker = 9227, Text = '我现在就要去找他们，你可以带我去吗？', Option = '没问题'}",
	[1000396] = "{id = 1000396, Speaker = 9227, Text = '好耶！', Emoji = 23}",
	[1000397] = "{id = 1000397, Speaker = 9228, Text = '哇！蛋黄来啦！', Emoji = 7}",
	[1000398] = "{id = 1000398, Speaker = 9228, Text = '谢谢你，有了蛋黄我们的行程才算完整！', Option = '不客气'}",
	[1000399] = "{id = 1000399, Speaker = 9227, Text = '多亏了你，要不然我今天就打算睡过去了。我要去跟小伙伴们玩耍啦！', Option = '好的'}",
	[1000400] = "{id = 1000400, Speaker = 9227, Text = '还有，那个，明天可以再来找我嘛…', Option = '好啊', Emoji = 29}",
	[1000401] = "{id = 1000401, Speaker = 9227, Text = '真，真的吗？那明天我还会在老地方等你哦！', Option = '没问题', Emoji = 9}",
	[1000404] = "{id = 1000404, Speaker = 9231, Text = '一封信，好像是蛋黄留下的。', Option = '{查看,2},{放回去,3}'}",
	[1000405] = "{id = 1000405, Speaker = 9231, Text = '[c][ffff00][PlayerName][-][/c]，你果然来找我啦，看来你没有失言哦！我今天早早就起床啦，想去看看[c][ffff00]木乃伊[-][/c]，我等你来找我哦！'}",
	[1000406] = "{id = 1000406, Speaker = 0, Text = '蛋黄会去哪里呢？', Option = '{下水道,4},{斐扬洞窟,4},{金字塔,5},{哥布灵森林,4}'}",
	[1000407] = "{id = 1000407, Speaker = 0, Text = '仔细想想，这地方好像没有木乃伊…'}",
	[1000408] = "{id = 1000408, Speaker = 0, Text = '木乃伊…对，一定是去金字塔看木乃伊了！', Emoji = 11}",
	[1000409] = "{id = 1000409, Speaker = 9227, Text = '谢天谢地，你终于来啦！', Emoji = 22}",
	[1000410] = "{id = 1000410, Speaker = 9227, Text = '木乃伊看起来挺呆萌的，可我刚凑过去看他，他就扬刀要砍我！吓死我了，赶紧就跑出来了！', Option = '谁叫你乱跑'}",
	[1000411] = "{id = 1000411, Speaker = 9227, Text = '哎呀，我也是等不及了想出来见见世面，没想到外面的世界虽然精彩，但是也充满危险啊！', Option = '那你以为呢'}",
	[1000412] = "{id = 1000412, Speaker = 9227, Text = '好啦！我以后不乱跑啦！', Option = '这还差不多', Emoji = 26}",
	[1000413] = "{id = 1000413, Speaker = 9227, Text = '我还没看清楚木乃伊，你可以再带我进去看看嘛？', Option = '真拿你没办法', Emoji = 29}",
	[1000414] = "{id = 1000414, Speaker = 9232, Text = '看什么看？没见过活死人啊？'}",
	[1000415] = "{id = 1000415, Speaker = 9227, Text = '虽然突然看到感觉很吓人，但是仔细看一看还是挺呆萌的嘛。'}",
	[1000416] = "{id = 1000416, Speaker = 9232, Text = '你居然说我呆萌，我生气了！兄弟们，砍他!'}",
	[1000417] = "{id = 1000417, Speaker = 9227, Text = '哇哇哇，[c][ffff00][PlayerName][-][/c]保护我啊……', Emoji = 22}",
	[1000418] = "{id = 1000418, Speaker = 9227, Text = '对不起，我太激动了。', Option = '不许这样了', Emoji = 16}",
	[1000419] = "{id = 1000419, Speaker = 9227, Text = '一定一定，你别生气啊！'}",
	[1000420] = "{id = 1000420, Speaker = 9227, Text = '好啦，木乃伊我也看够了，今天很开心，我们回去吧！', Option = '没问题'}",
	[1000421] = "{id = 1000421, Speaker = 9227, Text = '我还要计划一下，明天干什么呢…'}",
	[1000422] = "{id = 1000422, Speaker = 9227, Text = '我先回去啦！', Option = '好的'}",
	[1000424] = "{id = 1000424, Speaker = 9228, Text = '……', Option = '蛋黄呢'}",
	[1000425] = "{id = 1000425, Speaker = 9228, Text = '真是有点苦恼…', Option = '怎么啦'}",
	[1000426] = "{id = 1000426, Speaker = 9228, Text = '昨天晚上蛋黄跑过来跟我说，他有个愿望。', Option = '什么愿望？'}",
	[1000427] = "{id = 1000427, Speaker = 9228, Text = '蛋黄很羡慕那些天使波利，就是那些背后长有翅膀的波利。', Option = '长翅膀的波利？'}",
	[1000428] = "{id = 1000428, Speaker = 9228, Text = '你应该不了解，只有通过西门的天空花园，找到大天使波利，才有机会得到那对翅膀。'}",
	[1000429] = "{id = 1000429, Speaker = 9228, Text = '你愿意帮助蛋黄去试试吗？', Option = '{愿意,2},{不愿意,3}'}",
	[1000430] = "{id = 1000430, Speaker = 9228, Text = '那真是太遗憾了，蛋黄会很失落的。'}",
	[1000431] = "{id = 1000431, Speaker = 9228, Text = '太好了！'}",
	[1000432] = "{id = 1000432, Speaker = 9228, Text = '还不快去，还站在这里干嘛呀。', Option = '你背后是什么？'}",
	[1000433] = "{id = 1000433, Speaker = 9228, Text = '没，没什么，快点去找蛋黄吧…', Option = '可是你背后…', Emoji = 18}",
	[1000434] = "{id = 1000434, Speaker = 9227, Text = '呼…憋死我了…', Emoji = 10}",
	[1000435] = "{id = 1000435, Speaker = 9227, Text = '糟糕！露馅了…', Option = '你怎么在这？'}",
	[1000436] = "{id = 1000436, Speaker = 9227, Text = '哎呀，我也是不好意思跟你说，才委托波波土跟你说的…好尴尬……'}",
	[1000437] = "{id = 1000437, Speaker = 9228, Text = '这个，蛋黄不好意思再麻烦你，所以躲在我这看你会不会答应。你别生气…', Option = '我没有生气'}",
	[1000438] = "{id = 1000438, Speaker = 9228, Text = '那就好那就好，哈哈…哈哈哈……那，蛋黄就交给你咯！', Option = '没问题'}",
	[1000439] = "{id = 1000439, Speaker = 9227, Text = '[c][ffff00][PlayerName][-][/c]，你真好……', Emoji = 17}",
	[1000440] = "{id = 1000440, Speaker = 9227, Text = '去天空花园的路上有好多怪物，你能把我带上去就可以啦！', Option = '那好吧'}",
	[1000441] = "{id = 1000441, Speaker = 9227, Text = '哈哈哈哈哈好开心…我去准备一下，一会在天空花园下面等你。', Emoji = 17}",
	[1000442] = "{id = 1000442, Speaker = 9227, Text = '你终于来啦！等了你好一会呢！', Option = '是啊'}",
	[1000443] = "{id = 1000443, Speaker = 9227, Text = '好啦，我们直接出发吧，要一直到最高处那个地方才是天空花园。', Option = '我来过这'}",
	[1000444] = "{id = 1000444, Speaker = 9227, Text = '跟以前不一样啦，现在这里会有很多魔物出现，总之万事小心吧。', Option = '好的'}",
	[1000445] = "{id = 1000445, Speaker = 9227, Text = '果然还是有恶魔波利阻拦我！'}",
	[1000446] = "{id = 1000446, Speaker = 9227, Text = '厉害！我们继续吧！'}",
	[1000447] = "{id = 1000447, Speaker = 9227, Text = '还来，怎么没完没了？'}",
	[1000448] = "{id = 1000448, Speaker = 9227, Text = '呼…好累，可以给我几瓶苹果汁吗？好渴啊。'}",
	[1000449] = "{id = 1000449, Speaker = 9227, Text = 'OK！身体充满了能量！胜利就在眼前了，我们上！'}",
	[1000450] = "{id = 1000450, Speaker = 9227, Text = '大天使波利呢？怎么没看到他？'}",
	[1000451] = "{id = 1000451, Speaker = 9227, Text = '我们幸幸苦苦跑上来他为什么不在啊？'}",
	[1000452] = "{id = 1000452, Speaker = 9233, Text = '吵死了，谁在这嚷嚷，还让不让本波利睡觉了！'}",
	[1000453] = "{id = 1000453, Speaker = 9233, Text = '你们是谁，找我干嘛？'}",
	[1000454] = "{id = 1000454, Speaker = 9227, Text = '大天使波利大人，我我我…我不知道您在休息，我想求您给我一对天使翅膀！', Emoji = 29}",
	[1000455] = "{id = 1000455, Speaker = 9233, Text = '原来就这点事，东西带来了吗？'}",
	[1000456] = "{id = 1000456, Speaker = 9227, Text = '什么…什么东西？'}",
	[1000457] = "{id = 1000457, Speaker = 9233, Text = '看来你对翅膀一无所知啊，去收集3个波利的结晶，交给我，我会给你一对天使翅膀的。'}",
	[1000458] = "{id = 1000458, Speaker = 9227, Text = '可是，可是我没带来…'}",
	[1000459] = "{id = 1000459, Speaker = 9233, Text = '没带来就去找啊，你问我我会给你吗？', Emoji = 12}",
	[1000460] = "{id = 1000460, Speaker = 9227, Text = '好吧，可是今天有点晚了，会不会打扰到你呢。'}",
	[1000461] = "{id = 1000461, Speaker = 9233, Text = '明天来找我，带着你的材料。可一样都不能少，明白么？'}",
	[1000462] = "{id = 1000462, Speaker = 9227, Text = '好吧…', Emoji = 15}",
	[1000463] = "{id = 1000463, Speaker = 9233, Text = '没有什么是不付出就有回报的，我要继续睡觉了，可别再吵我了。'}",
	[1000464] = "{id = 1000464, Speaker = 9233, Text = '哦对了，再带一个粉红的心，可不是心脏哦，在天津町可以收集到，自己去找找吧…'}",
	[1000465] = "{id = 1000465, Speaker = 9227, Text = '看来还要去收集材料了…', Option = '别灰心'}",
