	[1000622] = "{id = 1000622, Speaker = 0, Text = '这里的风景很不错，可以请偶像少女来观赏。'}",
	[1000623] = "{id = 1000623, Speaker = 600073, Text = '这是你选好的地方吗？让我看看……'}",
	[1000624] = "{id = 1000624, Speaker = 600073, Text = '这些竹子，这里有点像我家呢！'}",
	[1000625] = "{id = 1000625, Speaker = 600073, Text = '斐扬，真是个美丽的地方啊。'}",
	[1000626] = "{id = 1000626, Speaker = 600073, Text = '我还听说，大陆上有些海岛特别美丽，可以请你带我去一个美丽的海岛吗？', Option = '没问题'}",
	[1000627] = "{id = 1000627, Speaker = 600073, Text = '那我就等你的好消息了！', Emoji = 2}",
	[1000628] = "{id = 1000628, Speaker = 0, Text = '这里的海景非常美，可以请偶像少女来观赏。'}",
	[1000629] = "{id = 1000629, Speaker = 600073, Text = '这股海风吹在我的身上，让人心旷神怡呢，你可真会选地方呀。'}",
	[1000630] = "{id = 1000630, Speaker = 600073, Text = '嗯，我记住了，美丽的伊斯鲁德。'}",
	[1000631] = "{id = 1000631, Speaker = 600073, Text = '除了海洋，我还想见识一下大陆其他的风景，你愿意带我去看看吗？', Option = '当然'}",
	[1000632] = "{id = 1000632, Speaker = 600073, Text = '那么交给你了，我相信你选的位置肯定都会非常的美。'}",
	[1000633] = "{id = 1000633, Speaker = 0, Text = '这里的花园非常美，可以请偶像少女来观赏。'}",
	[1000634] = "{id = 1000634, Speaker = 600073, Text = '哇，这里的鲜花好香啊，冒险者感谢你带我来了这么美的花园。'}",
	[1000635] = "{id = 1000635, Speaker = 600073, Text = '我会永远记住这一刻的！'}",
	[1000636] = "{id = 1000636, Speaker = 600073, Text = '感谢你，这是送给你的礼物哦。我要回我的家乡了！'}",
	[1000637] = "{id = 1000637, Speaker = 600073, Text = '以后有机会再见面吧！'}",
	[1000640] = "{id = 1000640, Speaker = 600080, Text = '啊哈~冒险者，告诉你一个令人兴奋的消息！米德加尔特大陆上一年一度的庆典就要开始了！'}",
	[1000641] = "{id = 1000641, Speaker = 600080, Text = '今年的主题是——大冒险！要知道在这片充满危险的大陆上，冒险可是永恒的话题！'}",
	[1000642] = "{id = 1000642, Speaker = 600080, Text = '所有生物的内心都是渴望着冒险的，即使它是只野猪。', Option = '野猪？'}",
	[1000643] = "{id = 1000643, Speaker = 600080, Text = '当然！在接受王国的邀请来到这里的路上，我就曾遭遇到这样一头小野猪。'}",
	[1000644] = "{id = 1000644, Speaker = 600080, Text = '从它热诚的眼中，我看到了冒险的光芒！但是它现在似乎陷入了困境。'}",
	[1000645] = "{id = 1000645, Speaker = 600080, Text = '我很想帮助它！但是我身上还有着王国的使命。'}",
	[1000646] = "{id = 1000646, Speaker = 600080, Text = '你能够去代替我看看它吗？它的名字叫做咿维。', Option = '{好吧,2},{不好,1}'}",
	[1000647] = "{id = 1000647, Speaker = 600080, Text = '它的名字叫做咿维。我相信你一定能帮助到它的。'}",
	[1000649] = "{id = 1000649, Speaker = 600083, Text = '哼哧…冒、冒险者？', Option = '居然会说话'}",
	[1000650] = "{id = 1000650, Speaker = 600083, Text = '哼哧…别小瞧我！我可不是一般的野猪。', Option = '饿了？'}",
	[1000651] = "{id = 1000651, Speaker = 600083, Text = '哼哧…有、有点…你这什么表情！这是一点点饿而已！', Option = '想吃什么'}",
	[1000652] = "{id = 1000652, Speaker = 600083, Text = '嗯…听说人类的海鲜特别好吃~我很想尝一尝…', Option = '…'}",
	[1000653] = "{id = 1000653, Speaker = 600083, Text = '我只是想多吃点而已！哼哧…我真的不是很饿！'}",
	[1000654] = "{id = 1000654, Speaker = 600083, Text = '哼哧…我老远就闻到了蟹肉的香气了！', Emoji = 28}",
	[1000655] = "{id = 1000655, Speaker = 600083, Text = '哼哧…味道真不错…', Option = '…'}",
	[1000656] = "{id = 1000656, Speaker = 600083, Text = '呼…吃撑了…我要消化消化肚子里的食物…'}",
	[1000657] = "{id = 1000657, Speaker = 600083, Text = '从家里出来的时候，我听说米德加尔特王国的风景闻名整个大陆。'}",
	[1000658] = "{id = 1000658, Speaker = 600083, Text = '你能带我去见见王国的风景吗？', Option = '我带你走走'}",
	[1000659] = "{id = 1000659, Speaker = 600083, Text = '真的吗？哼哧…你真是个好人！'}",
	[1000660] = "{id = 1000660, Speaker = 1121, Text = '咦…遛狗的倒是不少…这遛猪的倒是很少见啊…'}",
	[1000661] = "{id = 1000661, Speaker = 1156, Text = '哦…冒险者，好久不见，你这是？', Option = '遛猪！'}",
	[1000662] = "{id = 1000662, Speaker = 1108, Text = '这是你新养的宠物吗？真是可爱呢…'}",
	[1000663] = "{id = 1000663, Speaker = 600083, Text = '啊~~走得太累了…我先回南门了哦，来南门找我吧。'}",
	[1000664] = "{id = 1000664, Speaker = 600083, Text = '哼哧…接下来该找那些波利的麻烦了。', Option = '为什么'}",
	[1000665] = "{id = 1000665, Speaker = 600083, Text = '我刚到南门的时候，这些波利可没少欺负我呢。'}",
	[1000666] = "{id = 1000666, Speaker = 600083, Text = '我要欺负回来…哼哧…你会帮我的，对吗？', Option = '好吧'}",
	[1000667] = "{id = 1000667, Speaker = 600083, Text = '你真是太棒了！我也要像你一样，这样他们就再也不敢欺负我了！'}",
	[1000668] = "{id = 1000668, Speaker = 600083, Text = '我也要去冒险，成为一头强壮的野猪！'}",
	[1000669] = "{id = 1000669, Speaker = 600083, Text = '往后的几天我也会为冒险做准备~我听说冒险者公会的导师有一本书。'}",
	[1000670] = "{id = 1000670, Speaker = 600083, Text = '名叫《冒险指南》！冒险者你能把这本书带给我吗？', Option = '好的'}",
	[1000671] = "{id = 1000671, Speaker = 1024, Text = '《冒险指南》？你现在还需要这种初级的书籍吗？', Option = '是的'}",
	[1000672] = "{id = 1000672, Speaker = 1024, Text = '真是奇怪…拿去吧！'}",
	[1000673] = "{id = 1000673, Speaker = 600083, Text = '啊哈，我要找地方好好读一读这本书。'}",
	[1000674] = "{id = 1000674, Speaker = 600083, Text = '冒险者，我们明天再见哦~哼哧…'}",
	[1000675] = "{id = 1000675, Speaker = 600085, Text = '哼哧…你来啦你来啦！今天带我去哪里玩呢？', Option = '听你的'}",
	[1000676] = "{id = 1000676, Speaker = 600085, Text = '我从《冒险指南》中看到伊斯鲁德有个有名的雕像呢！据说是一个非常强大的人类。'}",
	[1000677] = "{id = 1000677, Speaker = 600085, Text = '我想去看看这个雕像，顺便合张影…哼哧…来过了总得留点纪念呀~哼哧…', Option = '好的！'}",
	[1000678] = "{id = 1000678, Speaker = 600085, Text = '哼哧…拍得真好看…我先去南门洗照片去哦~'}",
	[1000679] = "{id = 1000679, Speaker = 600085, Text = '哼哧…你来的好慢哦…', Option = '不好意思'}",
	[1000680] = "{id = 1000680, Speaker = 600085, Text = '唉…', Option = '怎么了？'}",
	[1000681] = "{id = 1000681, Speaker = 600085, Text = '哼哧…得不到的东西才是最好的…'}",
	[1000682] = "{id = 1000682, Speaker = 600085, Text = '什么东西？'}",
	[1000683] = "{id = 1000683, Speaker = 600085, Text = '跟你说你也不明白啦…唉…让我自己呆着吧…'}",
	[1000684] = "{id = 1000684, Speaker = 600085, Text = '你去找宝雅姐姐玩吧。'}",
	[1000685] = "{id = 1000685, Speaker = 1054, Text = '咿维啊…这么一说我倒是想起来了…'}",
	[1000686] = "{id = 1000686, Speaker = 1054, Text = '它回来的时候看到了天使波利呢。我告诉它这是难得一见的魔物呢。'}",
	[1000687] = "{id = 1000687, Speaker = 1054, Text = '它兴奋了没多久就消沉下去了呢。', Option = '好吧'}",
	[1000688] = "{id = 1000688, Speaker = 600085, Text = '你怎么又回来了？让我自己呆着不好吗？哼哧…', Option = '到底怎么了'}",
	[1000689] = "{id = 1000689, Speaker = 600085, Text = '唔…看在你这么想帮我的份上，就给你个机会猜一猜。', Option = '好的'}",
	[1000690] = "{id = 1000690, Speaker = 600085, Text = '第一个问题，谁是这片大陆上可爱的象征？虽然我不喜欢它…', Option = '{溜溜猴,6},{绿棉虫,6},{波利,3},{疯兔,6}'}",
	[1000691] = "{id = 1000691, Speaker = 600085, Text = '第二个问题，什么才是这片大陆上时尚的象征？', Option = '{闪亮的武器,6},{华美的衣服,6},{酷炫的首饰,6},{精美的头饰,4}'}",
	[1000692] = "{id = 1000692, Speaker = 600085, Text = '第三个问题，什么颜色象征这完美无缺的纯洁？', Option = '{白色,5},{粉色,6},{黄色,6},{红色,6}'}",
	[1000693] = "{id = 1000693, Speaker = 600085, Text = '唉，看来你还没有懂我的心思。'}",
	[1000694] = "{id = 1000694, Speaker = 600085, Text = '波利，白色，头饰，看来你已经明白我想要什么了。'}",
	[1000695] = "{id = 1000695, Speaker = 600085, Text = '其实今天在等食物的时候，我发现了一个超酷的波利，他居然有天使的翅膀！'}",
	[1000696] = "{id = 1000696, Speaker = 600085, Text = '我也要做一只酷猪，一只带着天使翅膀的天使猪！'}",
	[1000697] = "{id = 1000697, Speaker = 600085, Text = '是啊，我想要那个天使波利身上的发圈，但是我完全不明白，应该怎么得到它…', Option = '这好办'}",
	[1000698] = "{id = 1000698, Speaker = 600085, Text = '好办？你可能不理解…传说中的天使波利可是想见一面都难啊…', Option = '交给我吧。'}",
	[1000699] = "{id = 1000699, Speaker = 1054, Text = '天使发圈啊~或许我可以帮你解决。'}",
	[1000700] = "{id = 1000700, Speaker = 1054, Text = '小野猪吗？恩，确实需要订做，不过这难不倒我。'}",
	[1000701] = "{id = 1000701, Speaker = 1054, Text = '但是这世上可没有免费的午餐，你明白的~'}",
	[1000702] = "{id = 1000702, Speaker = 1054, Text = '你帮我收集5个柔毛来，我就帮你制作~', Option = '好的'}",
	[1000703] = "{id = 1000703, Speaker = 1054, Text = '你把我需要的材料带过来了吗？', Option = '{没有,7},{带来了,8}'}",
	[1000704] = "{id = 1000704, Speaker = 1054, Text = '这点材料也想打发我？'}",
	[1000705] = "{id = 1000705, Speaker = 1054, Text = '数量不多不少，刚刚好~'}",
	[1000706] = "{id = 1000706, Speaker = 1054, Text = '喏，拿去吧，这是小野猪定制版天使发圈。', Option = '谢谢'}",
	[1000707] = "{id = 1000707, Speaker = 600086, Text = '真的是…天使发圈…哼哧…这种感觉真是太棒了！'}",
	[1000708] = "{id = 1000708, Speaker = 600086, Text = '我终于是一个天使猪啦~你真是太好了！哼哧…'}",
	[1000709] = "{id = 1000709, Speaker = 600086, Text = '我要炫耀给赛利亚看看去~我们明天见哦~'}",
	[1000710] = "{id = 1000710, Speaker = 600086, Text = '郁闷…哼哧…咦…是你啊…今天也是令人烦恼的一天呢。', Option = '你怎么了？'}",
	[1000711] = "{id = 1000711, Speaker = 600086, Text = '塞尼亚她…唉…还是不要麻烦你了…哼哧…每次都麻烦你…', Option = '塞尼亚？'}",
	[1000712] = "{id = 1000712, Speaker = 1050, Text = '咿维？是啊，一开始它来找我的时候还开开心心的呢。'}",
	[1000713] = "{id = 1000713, Speaker = 1050, Text = '我当时口渴，去喝了两杯苹果汁，回来之后它就闷闷不乐了。', Option = '苹果汁？'}",
	[1000714] = "{id = 1000714, Speaker = 1050, Text = '对啊！也许咿维也想尝尝苹果汁呢！可惜我的苹果汁都喝光了…', Option = '原来是这样'}",
	[1000715] = "{id = 1000715, Speaker = 600086, Text = '哼哧…是的…我很想尝尝苹果汁的滋味…', Option = '真贪吃'}",
	[1000716] = "{id = 1000716, Speaker = 600086, Text = '哼哧…我、我这是享受生活！', Option = '我去弄吧'}",
	[1000717] = "{id = 1000717, Speaker = 600086, Text = '你带来了我想要的苹果汁了吗？', Option = '{没有,3},{带来了,4}'}",
	[1000718] = "{id = 1000718, Speaker = 600086, Text = '哇…这就是苹果汁啊…哼哧…'}",
	[1000719] = "{id = 1000719, Speaker = 600086, Text = '哼哧…这点苹果汁太少了…'}",
	[1000720] = "{id = 1000720, Speaker = 600086, Text = '嗝~~咿维吃饱了…该消化消化食物了…'}",
	[1000721] = "{id = 1000721, Speaker = 600086, Text = '冒险者，今天你可以带我去西门玩吗？'}",
	[1000722] = "{id = 1000722, Speaker = 600086, Text = '我想多拍一些照片。', Option = '当然愿意'}",
	[1000723] = "{id = 1000723, Speaker = 600086, Text = '不玩了不玩了…哼哧…咿维累了呢…我先回南门了…'}",
	[1000724] = "{id = 1000724, Speaker = 600086, Text = '哼哧…玩得真开心啊~明天我还要玩。', Option = '好啊好啊'}",
	[1000725] = "{id = 1000725, Speaker = 600086, Text = '那就明天不见不散啦~哼哧…再见。'}",
	[1000726] = "{id = 1000726, Speaker = 600087, Text = '哼哧…你来啦！你一定想不到，刚刚美羽教会了我一个很有趣的东西。', Option = '什么'}",
	[1000727] = "{id = 1000727, Speaker = 600087, Text = '哼哼，那就是猜拳！你来陪我玩猜拳嘛！', Option = '这…'}",
	[1000728] = "{id = 1000728, Speaker = 600087, Text = '就陪我玩玩嘛。就一小会~~哼哧…', Option = '好吧'}",
	[1000729] = "{id = 1000729, Speaker = 600087, Text = '哼哧…现在开始吧！三！二！一！出拳！', Option = '{拳头,4},{剪刀,3},{布,3}'}",
	[1000730] = "{id = 1000730, Speaker = 600087, Text = '哼哧…不行啊，怎么说你也得赢我一把才行…'}",
	[1000731] = "{id = 1000731, Speaker = 600087, Text = '哼哧…不好玩不好玩…总是输给你…', Option = '运气好'}",
	[1000732] = "{id = 1000732, Speaker = 600087, Text = '接下来啊…哼哧…《冒险指南》里说，大陆上有着各种各样的魔物。'}",
	[1000733] = "{id = 1000733, Speaker = 600087, Text = '学会在魔物的袭击下生存下来才是一个冒险者该做的事呢。'}",
	[1000734] = "{id = 1000734, Speaker = 600087, Text = '我将来去冒险，这些也是要经历的过程呢。'}",
	[1000735] = "{id = 1000735, Speaker = 600087, Text = '你能带我见识一下冒险者的战斗吗？', Option = '好吧'}",
	[1000736] = "{id = 1000736, Speaker = 600087, Text = '这就是战斗…哼哧…我有些累了…'}",
	[1000737] = "{id = 1000737, Speaker = 600087, Text = '这就是战斗么…太残酷了…不是你死就是我亡…'}",
	[1000738] = "{id = 1000738, Speaker = 600087, Text = '哼哧…我曾经把冒险视为娱乐，现在看来，冒险根本不是我想想中的那般美好…'}",
	[1000739] = "{id = 1000739, Speaker = 0, Text = '冒险是人人都向往的，人人都希望能够拥有它！但冒险却不是属于每一个人，它只属于勇敢和无谓的人。'}",
	[1000740] = "{id = 1000740, Speaker = 0, Text = '冒险，不是为了寻求刺激，不是为了显示个人超凡的能力，而是出于对世界的奉献，出于造福世界的责任感。'}",
	[1000741] = "{id = 1000741, Speaker = 0, Text = '我们肩负着这样的责任，保护弱者，崇尚正义，如果失去了冒险的初心，那么你的冒险将变得毫无意义。'}",
	[1000742] = "{id = 1000742, Speaker = 0, Text = '当然，冒险也不仅仅是为了保护他人。我们可以去探索世界的秘密。'}",
	[1000743] = "{id = 1000743, Speaker = 0, Text = '去深入那些广袤的无人区，寻找珍贵的宝藏，得到数之不尽的财富。'}",
	[1000744] = "{id = 1000744, Speaker = 0, Text = '当然，最重要的一点。那就是我们可以在冒险的旅程中结识许许多多的小伙伴。'}",
	[1000745] = "{id = 1000745, Speaker = 0, Text = '在你被魔物围攻的时候，他们会来帮助你；在你深陷古代遗迹的时候，他们会来解救你；在你被人欺负的时候，他们会来保护你。'}",
	[1000746] = "{id = 1000746, Speaker = 0, Text = '在你难过的时候，他们会来安慰你；在你开心的时候，他们会和你一起大笑，分享你的喜悦。'}",
	[1000747] = "{id = 1000747, Speaker = 0, Text = '他们的存在才是冒险中最最珍贵的财富。'}",
	[1000748] = "{id = 1000748, Speaker = 0, Text = '所以，不用害怕冒险中的艰难险阻，你要知道，你的伙伴们在前方等待着你！'}",
	[1000749] = "{id = 1000749, Speaker = 600087, Text = '谢谢你的开导…你一定是位非常出色的冒险者！'}",
	[1000750] = "{id = 1000750, Speaker = 600087, Text = '谢谢夸奖'}",
	[1000751] = "{id = 1000751, Speaker = 600087, Text = '我决定了！哼哧…我一定要成为像你这样的，充满正义的冒险家！'}",
	[1000752] = "{id = 1000752, Speaker = 600087, Text = '我要好好整休一下，明天见~哼哧…'}",
	[1000754] = "{id = 1000754, Speaker = 600088, Text = '你终于来了！咿维已经去找你去了。', Option = '找我？'}",
	[1000755] = "{id = 1000755, Speaker = 600088, Text = '它说它迫不及待地想要见到你了，就去找你了。', Option = '去哪儿了'}",
	[1000756] = "{id = 1000756, Speaker = 600088, Text = '看得出来，今天的它非常开心，告诉我一声它就直接走开了…'}",
	[1000757] = "{id = 1000757, Speaker = 600088, Text = '我也不知道它去哪里了…', Option = '去找它'}",
	[1000758] = "{id = 1000758, Speaker = 600088, Text = '那我在普隆德拉等你的消息！'}",
	[1000759] = "{id = 1000759, Speaker = 1054, Text = '那头可爱的小野猪么？今天还一蹦一跳得从我面前经过呢…'}",
	[1000760] = "{id = 1000760, Speaker = 1054, Text = '我听它嘴里念叨着西门风车什么的…', Option = '谢谢你'}",
	[1000761] = "{id = 1000761, Speaker = 1067, Text = '啊…我采集样本的时候倒是见到过…如果我没看错它去的方向应该是吉芬。', Option = '多谢'}",
	[1000762] = "{id = 1000762, Speaker = 1340, Text = '哈哈哈，那头小野猪么，它在吉芬中可是很显眼呢，但是它好像去哥布灵森林找人了呢。'}",
	[1000763] = "{id = 1000763, Speaker = 1322, Text = '呵…那头摇摇晃晃的猪吗？我在妙勒尼山脉中看到它了…不过它似乎迷路了…'}",
	[1000764] = "{id = 1000764, Speaker = 1322, Text = '我看你得赶紧找到它，毕竟妙勒尼的那些魔物可不是吃素的！', Option = '明白'}",
